    (§ block
        (maybeConnect)
        (§ let #_"PreparedStatement" (§ name ßps) nil)
        (§ block
            (§ try )
            (§ block
                (§ ass (§ name ßps) (.. conn (get) (prepareStatement (getSelectOpenoutputsCountSQL))))
                (.. ßps (setBytes 1, (.. ßhash (getBytes))))
                (§ let #_"ResultSet" (§ name ßresults) (.. ßps (executeQuery)))
                (§ block
                    (§ if (not (.. ßresults (next))))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "Got no results from a COUNT(*) query"))
                    )

                    (§ let #_"int" (§ name ßcount) (.. ßresults (getInt 1)))
                    (§ block
                        (§ return (!= ßcount 0))
                    )
                )
            )
            (§ catch #_"SQLException" (§ name ße))
            (§ block
                (§ throw (§ new #_"BlockStoreException" ße))
            )
            (§ finally )
            (§ block
                (§ if (some? ßps))
                (§ block
                    (§ try )
                    (§ block
                        (.. ßps (close))
                    )
                    (§ catch #_"SQLException" (§ name _))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "Failed to close PreparedStatement"))
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
