     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"H2FullPrunedBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"String" (§ name ßdbName), #_"String" (§ name ßusername), #_"String" (§ name ßpassword), #_"int" (§ name ßfullStoreDepth)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super ßparams, (str DATABASE_CONNECTION_URL_PREFIX ßdbName ";create=true;LOCK_TIMEOUT=60000;DB_CLOSE_ON_EXIT=FALSE"), ßfullStoreDepth, ßusername, ßpassword, nil)
        this
    )

    ;;;
     ; Creates a new H2FullPrunedBlockStore.
