     ;;
    #_public
    (§ constructor #_"H2FullPrunedBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"String" (§ name ßdbName), #_"int" (§ name ßfullStoreDepth), #_"int" (§ name ßcacheSize)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßparams, ßdbName, ßfullStoreDepth)

        (§ try )
        (§ block
            (§ let #_"Statement" (§ name ßs) (.. conn (get) (createStatement)))
