#_(ns org.bitcoinj.store #_"MemoryBlockStore"
    (:import [java.util LinkedHashMap Map])
   (:require [org.bitcoinj.core *]))

;;;
 ; Keeps {@link org.bitcoinj.core.StoredBlock}s in memory.  Used primarily for unit testing.
 ;;
#_public
(§ class #_"MemoryBlockStore" (§ implements #_"BlockStore")
    #_private
    (§ field- #_"LinkedHashMap<Sha256Hash, StoredBlock>" (§ name blockMap) (§ new #_"LinkedHashMap<Sha256Hash, StoredBlock>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, StoredBlock>" (§ name ßeldest)])
            (§ block
                (< 5000 (.. blockMap (size)))
            )
        )))
    #_private
    (§ field- #_"StoredBlock" (§ name chainHead))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))

    #_public
    (§ constructor #_"MemoryBlockStore" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        ;; Insert the genesis block.
        (§ try )
        (§ block
            (§ let #_"Block" (§ name ßgenesisHeader) (.. ßparams (getGenesisBlock) (cloneAsHeader)))
            (§ block
                (§ let #_"StoredBlock" (§ name ßstoredGenesis) (§ new #_"StoredBlock" ßgenesisHeader, (.. ßgenesisHeader (getWork)), 0))
                (§ block
                    (put ßstoredGenesis)
                    (setChainHead ßstoredGenesis)
                    (§ ass (§ name (.. this ßparams)) ßparams)
                )
            )
        )
        (§ catch #_"BlockStoreException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        (§ catch #_"VerificationException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        this
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" (§ name ßblock)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßblock (getHeader) (getHash)))
        (§ block
            (.. blockMap (put ßhash, ßblock))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (.. blockMap (get ßhash))
    )

    #_override
    #_public
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        chainHead
    )

    #_override
    #_public
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (§ ass (§ name (.. this ßchainHead)) ßchainHead)
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (§ ass (§ name blockMap) nil)
        nil
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )
)

#_(ns org.bitcoinj.store #_"MemoryFullPrunedBlockStore"
    (:import [java.util *])
    (:import [com.google.common.base Objects Preconditions]
             [com.google.common.collect Lists])
   (:require [org.bitcoinj.core *]))

;;;
 ; Used as a key for memory map (to avoid having to think about NetworkParameters,
 ; which is required for {@link TransactionOutPoint}.
 ;;
(§ class #_"StoredTransactionOutPoint"
    ;;; Hash of the transaction to which we refer. ;;
    (§ field #_"Sha256Hash" (§ name hash))
    ;;; Which output of that transaction we are talking about. ;;
    (§ field #_"long" (§ name index))

    (§ constructor #_"StoredTransactionOutPoint" [#_"Sha256Hash" (§ name ßhash), #_"long" (§ name ßindex)])
    (§ block
        (§ ass (§ name (.. this ßhash)) ßhash)
        (§ ass (§ name (.. this ßindex)) ßindex)
        this
    )

    (§ constructor #_"StoredTransactionOutPoint" [#_"UTXO" (§ name ßout)])
    (§ block
        (§ ass (§ name (.. this hash)) (.. ßout (getHash)))
        (§ ass (§ name (.. this index)) (.. ßout (getIndex)))
        this
    )

    ;;;
     ; The hash of the transaction to which we refer.
     ;;
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        hash
    )

    ;;;
     ; The index of the output in transaction to which we refer.
     ;;
    (§ method #_"long" (§ fn getIndex) [])
    (§ block
        index
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (getIndex), (getHash))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "Stored transaction out point: " hash ":" index)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"StoredTransactionOutPoint" (§ name ßother) (§ cast #_"StoredTransactionOutPoint" ßo))
        (§ block
            (and (== (getIndex) (.. ßother (getIndex))) (Objects/equal (getHash), (.. ßother (getHash))))
        )
    )
)

;;;
 ; A HashMap<KeyType, ValueType> that is DB transaction-aware.
 ; This class is not thread-safe.
 ;;
(§ class #_"TransactionalHashMap<KeyType, ValueType>"
    (§ field #_"ThreadLocal<HashMap<KeyType, ValueType>>" (§ name tempMap))
    (§ field #_"ThreadLocal<HashSet<KeyType>>" (§ name tempSetRemoved))
    #_private
    (§ field- #_"ThreadLocal<Boolean>" (§ name inTransaction))

    (§ field #_"HashMap<KeyType, ValueType>" (§ name map))

    #_public
    (§ constructor #_"TransactionalHashMap" [])
    (§ block
        (§ ass (§ name tempMap) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name tempSetRemoved) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name inTransaction) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name map) (§ new #_"HashMap<>"))
        this
    )

    #_public
    (§ method #_"void" (§ fn beginDatabaseBatchWrite) [])
    (§ block
        (.. inTransaction (set true))
        nil
    )

    #_public
    (§ method #_"void" (§ fn commitDatabaseBatchWrite) [])
    (§ block
        (§ if (!= (.. tempSetRemoved (get)) nil))
        (§ block
            (§ for (§ var #_"KeyType" (§ name ßkey)) :for (.. tempSetRemoved (get)))
            (§ block
                (.. map (remove ßkey))
            )
        )
        (§ if (!= (.. tempMap (get)) nil))
        (§ block
            (§ for (§ var #_"Map.Entry<KeyType, ValueType>" (§ name ßentry)) :for (.. tempMap (get) (entrySet)))
            (§ block
                (.. map (put (.. ßentry (getKey)), (.. ßentry (getValue))))
            )
        )
        (abortDatabaseBatchWrite)
        nil
    )

    #_public
    (§ method #_"void" (§ fn abortDatabaseBatchWrite) [])
    (§ block
        (.. inTransaction (set false))
        (.. tempSetRemoved (remove))
        (.. tempMap (remove))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn get) [#_"KeyType" (§ name ßkey)])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ if (!= (.. tempMap (get)) nil))
            (§ block
                (§ let #_"ValueType" (§ name ßvalue) (.. tempMap (get) (get ßkey)))
                (§ block
                    (§ if (some? ßvalue))
                    (§ block
                        (§ return ßvalue)
                    )
                )
            )
            (§ if (and (some? (.. tempSetRemoved (get))) (.. tempSetRemoved (get) (contains ßkey))))
            (§ block
                (§ return nil)
            )
        )
        (.. map (get ßkey))
    )

    #_public
    (§ method #_"List<ValueType>" (§ fn values) [])
    (§ block
        (§ let #_"List<ValueType>" (§ name ßvalueTypes) (§ new #_"ArrayList<>"))
        (§ block
            (§ for (§ var #_"KeyType" (§ name ßkeyType)) :for (.. map (keySet)))
            (§ block
                (.. ßvalueTypes (add (get ßkeyType)))
            )
            ßvalueTypes
        )
    )

    #_public
    (§ method #_"void" (§ fn put) [#_"KeyType" (§ name ßkey), #_"ValueType" (§ name ßvalue)])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ if (!= (.. tempSetRemoved (get)) nil))
            (§ block
                (.. tempSetRemoved (get) (remove ßkey))
            )
            (§ if (== (.. tempMap (get)) nil))
            (§ block
                (.. tempMap (set (§ new #_"HashMap<KeyType, ValueType>")))
            )
            (.. tempMap (get) (put ßkey, ßvalue))
        )
        (§ else )
        (§ block
            (.. map (put ßkey, ßvalue))
        )
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn remove) [#_"KeyType" (§ name ßkey)])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ let #_"ValueType" (§ name ßretVal) (.. map (get ßkey)))
            (§ block
                (§ if (some? ßretVal))
                (§ block
                    (§ if (== (.. tempSetRemoved (get)) nil))
                    (§ block
                        (.. tempSetRemoved (set (§ new #_"HashSet<KeyType>")))
                    )
                    (.. tempSetRemoved (get) (add ßkey))
                )
                (§ if (!= (.. tempMap (get)) nil))
                (§ block
                    (§ let #_"ValueType" (§ name ßtempVal) (.. tempMap (get) (remove ßkey)))
                    (§ block
                        (§ if (some? ßtempVal))
                        (§ block
                            (§ return ßtempVal)
                        )
                    )
                )
                (§ return ßretVal)
            )
        )
        (§ else )
        (§ block
            (§ return (.. map (remove ßkey)))
        )
    )
)

;;;
 ; A Map with multiple key types that is DB per-thread-transaction-aware.
 ; However, this class is not thread-safe.
 ; @param <UniqueKeyType> Is a key that must be unique per object.
 ; @param <MultiKeyType> Is a key that can have multiple values.
 ;;
(§ class #_"TransactionalMultiKeyHashMap<UniqueKeyType, MultiKeyType, ValueType>"
    (§ field #_"TransactionalHashMap<UniqueKeyType, ValueType>" (§ name mapValues))
    (§ field #_"HashMap<MultiKeyType, Set<UniqueKeyType>>" (§ name mapKeys))

    #_public
    (§ constructor #_"TransactionalMultiKeyHashMap" [])
    (§ block
        (§ ass (§ name mapValues) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name mapKeys) (§ new #_"HashMap<>"))
        this
    )

    #_public
    (§ method #_"void" (§ fn beginTransaction) [])
    (§ block
        (.. mapValues (beginDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" (§ fn commitTransaction) [])
    (§ block
        (.. mapValues (commitDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" (§ fn abortTransaction) [])
    (§ block
        (.. mapValues (abortDatabaseBatchWrite))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn get) [#_"UniqueKeyType" (§ name ßkey)])
    (§ block
        (.. mapValues (get ßkey))
    )

    #_public
    (§ method #_"void" (§ fn put) [#_"UniqueKeyType" (§ name ßuniqueKey), #_"MultiKeyType" (§ name ßmultiKey), #_"ValueType" (§ name ßvalue)])
    (§ block
        (.. mapValues (put ßuniqueKey, ßvalue))
        (§ let #_"Set<UniqueKeyType>" (§ name ßset) (.. mapKeys (get ßmultiKey)))
        (§ block
            (§ if (nil? ßset))
            (§ block
                (§ ass (§ name ßset) (§ new #_"HashSet<>"))
                (.. ßset (add ßuniqueKey))
                (.. mapKeys (put ßmultiKey, ßset))
            )
            (§ else )
            (§ block
                (.. ßset (add ßuniqueKey))
            )
            nil
        )
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn removeByUniqueKey) [#_"UniqueKeyType" (§ name ßkey)])
    (§ block
        (.. mapValues (remove ßkey))
    )

    #_public
    (§ method #_"void" (§ fn removeByMultiKey) [#_"MultiKeyType" (§ name ßkey)])
    (§ block
        (§ let #_"Set<UniqueKeyType>" (§ name ßset) (.. mapKeys (remove ßkey)))
        (§ block
            (§ if (some? ßset))
            (§ block
                (§ for (§ var #_"UniqueKeyType" (§ name ßuniqueKey)) :for ßset)
                (§ block
                    (removeByUniqueKey ßuniqueKey)
                )
            )
            nil
        )
    )
)

;;;
 ; Keeps {@link StoredBlock}s, {@link StoredUndoableBlock}s and {@link org.bitcoinj.core.UTXO}s in memory.
 ; Used primarily for unit testing.
 ;;
#_public
(§ class #_"MemoryFullPrunedBlockStore" (§ implements #_"FullPrunedBlockStore")
    #_protected
    #_static
    (§ class #_"StoredBlockAndWasUndoableFlag"
        #_public
        (§ field #_"StoredBlock" (§ name block))
        #_public
        (§ field #_"boolean" (§ name wasUndoable))
        #_public
        (§ constructor #_"StoredBlockAndWasUndoableFlag" [#_"StoredBlock" (§ name ßblock), #_"boolean" (§ name ßwasUndoable)])
        (§ block
            (§ ass (§ name (.. this ßblock)) ßblock)
            (§ ass (§ name (.. this ßwasUndoable)) ßwasUndoable)
            this
        )
    )
    #_private
    (§ field- #_"TransactionalHashMap<Sha256Hash, StoredBlockAndWasUndoableFlag>" (§ name blockMap))
    #_private
    (§ field- #_"TransactionalMultiKeyHashMap<Sha256Hash, Integer, StoredUndoableBlock>" (§ name fullBlockMap))
    ;; TODO: Use something more suited to remove-heavy use?
    #_private
    (§ field- #_"TransactionalHashMap<StoredTransactionOutPoint, UTXO>" (§ name transactionOutputMap))
    #_private
    (§ field- #_"StoredBlock" (§ name chainHead))
    #_private
    (§ field- #_"StoredBlock" (§ name verifiedChainHead))
    #_private
    (§ field- #_"int" (§ name fullStoreDepth))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))

    ;;;
     ; Set up the MemoryFullPrunedBlockStore.
     ; @param params The network parameters of this block store - used to get genesis block.
     ; @param fullStoreDepth The depth of blocks to keep FullStoredBlocks instead of StoredBlocks.
     ;;
    #_public
    (§ constructor #_"MemoryFullPrunedBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"int" (§ name ßfullStoreDepth)])
    (§ block
        (§ ass (§ name blockMap) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name fullBlockMap) (§ new #_"TransactionalMultiKeyHashMap<>"))
        (§ ass (§ name transactionOutputMap) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name (.. this ßfullStoreDepth)) (if (< 0 ßfullStoreDepth) ßfullStoreDepth 1))
        ;; Insert the genesis block.
        (§ try )
        (§ block
            (§ let #_"StoredBlock" (§ name ßstoredGenesisHeader) (§ new #_"StoredBlock" (.. ßparams (getGenesisBlock) (cloneAsHeader)), (.. ßparams (getGenesisBlock) (getWork)), 0))
            (§ block
                ;; The coinbase in the genesis block is not spendable.
                (§ let #_"List<Transaction>" (§ name ßgenesisTransactions) (Lists/newLinkedList))
                (§ block
                    (§ let #_"StoredUndoableBlock" (§ name ßstoredGenesis) (§ new #_"StoredUndoableBlock" (.. ßparams (getGenesisBlock) (getHash)), ßgenesisTransactions))
                    (§ block
                        (put ßstoredGenesisHeader, ßstoredGenesis)
                        (setChainHead ßstoredGenesisHeader)
                        (setVerifiedChainHead ßstoredGenesisHeader)
                        (§ ass (§ name (.. this ßparams)) ßparams)
                    )
                )
            )
        )
        (§ catch #_"BlockStoreException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        (§ catch #_"VerificationException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        this
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" (§ name ßblock)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßblock (getHeader) (getHash)))
        (§ block
            (.. blockMap (put ßhash, (§ new #_"StoredBlockAndWasUndoableFlag" ßblock, false)))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" (§ name ßstoredBlock), #_"StoredUndoableBlock" (§ name ßundoableBlock)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßstoredBlock (getHeader) (getHash)))
        (§ block
            (.. fullBlockMap (put ßhash, (.. ßstoredBlock (getHeight)), ßundoableBlock))
            (.. blockMap (put ßhash, (§ new #_"StoredBlockAndWasUndoableFlag" ßstoredBlock, true)))
            nil
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"StoredBlockAndWasUndoableFlag" (§ name ßstoredBlock) (.. blockMap (get ßhash)))
        (§ block
            (when (some? ßstoredBlock) (.. ßstoredBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getOnceUndoableStoredBlock) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"StoredBlockAndWasUndoableFlag" (§ name ßstoredBlock) (.. blockMap (get ßhash)))
        (§ block
            (when (and (some? ßstoredBlock) (.. ßstoredBlock wasUndoable)) (.. ßstoredBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredUndoableBlock" (§ fn getUndoBlock) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull fullBlockMap, "MemoryFullPrunedBlockStore is closed")
        (.. fullBlockMap (get ßhash))
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        chainHead
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ ass (§ name (.. this ßchainHead)) ßchainHead)
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getVerifiedChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        verifiedChainHead
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setVerifiedChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ ass (§ name (.. this verifiedChainHead)) ßchainHead)
        (§ if (< (.. this ßchainHead (getHeight)) (.. ßchainHead (getHeight))))
        (§ block
            (setChainHead ßchainHead)
        )
        ;; Potential leak here if not all blocks get setChainHead'd.
        ;; Though the FullPrunedBlockStore allows for this, the current AbstractBlockChain will not do it.
        (.. fullBlockMap (removeByMultiKey (- (.. ßchainHead (getHeight)) fullStoreDepth)))
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (§ ass (§ name blockMap) nil)
        (§ ass (§ name fullBlockMap) nil)
        (§ ass (§ name transactionOutputMap) nil)
        nil
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"UTXO" (§ fn getTransactionOutput) [#_"Sha256Hash" (§ name ßhash), #_"long" (§ name ßindex)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (.. transactionOutputMap (get (§ new #_"StoredTransactionOutPoint" ßhash, ßindex)))
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn addUnspentTransactionOutput) [#_"UTXO" (§ name ßout)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (.. transactionOutputMap (put (§ new #_"StoredTransactionOutPoint" ßout), ßout))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn removeUnspentTransactionOutput) [#_"UTXO" (§ name ßout)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (§ if (== (.. transactionOutputMap (remove (§ new #_"StoredTransactionOutPoint" ßout))) nil))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "Tried to remove a UTXO from MemoryFullPrunedBlockStore that it didn't have!"))
        )
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn beginDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (beginDatabaseBatchWrite))
        (.. fullBlockMap (beginTransaction))
        (.. transactionOutputMap (beginDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn commitDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (commitDatabaseBatchWrite))
        (.. fullBlockMap (commitTransaction))
        (.. transactionOutputMap (commitDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn abortDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (abortDatabaseBatchWrite))
        (.. fullBlockMap (abortTransaction))
        (.. transactionOutputMap (abortDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn hasUnspentOutputs) [#_"Sha256Hash" (§ name ßhash), #_"int" (§ name ßnumOutputs)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumOutputs) :for (§ recur (+ ßi 1)))
        (§ block
            (§ if (!= (getTransactionOutput ßhash, ßi) nil))
            (§ block
                (§ return true)
            )
        )
        false
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )
)

#_(ns org.bitcoinj.store #_"PostgresFullPrunedBlockStore"
    (:import [java.io ByteArrayOutputStream IOException]
             [java.sql PreparedStatement ResultSet SQLException Types]
             [java.util ArrayList List])
    (:import [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]))

;;;
 ; <p>A full pruned block store using the Postgres database engine.  As an added bonus an address index is calculated,
 ; so you can use {@link #calculateBalanceForAddress(org.bitcoinj.core.Address)} to quickly look up
 ; the quantity of bitcoins controlled by that address.</p>
 ;;
#_public
(§ class #_"PostgresFullPrunedBlockStore" (§ extends #_"DatabaseFullPrunedBlockStore")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"PostgresFullPrunedBlockStore")))

    #_private
    #_static
    (§ def- #_"String" (§ name POSTGRES_DUPLICATE_KEY_ERROR_CODE) "23505")
    #_private
    #_static
    (§ def- #_"String" (§ name DATABASE_DRIVER_CLASS) "org.postgresql.Driver")
    #_private
    #_static
    (§ def- #_"String" (§ name DATABASE_CONNECTION_URL_PREFIX) "jdbc:postgresql://")

    ;; create table SQL
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_SETTINGS_TABLE) (str "CREATE TABLE settings ("
            "name character varying(32) NOT NULL, "
            "value bytea, "
            "CONSTRAINT setting_pk PRIMARY KEY (name)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_HEADERS_TABLE) (str "CREATE TABLE headers ("
            "hash bytea NOT NULL, "
            "chainwork bytea NOT NULL, "
            "height integer NOT NULL, "
            "header bytea NOT NULL, "
            "wasundoable boolean NOT NULL, "
            "CONSTRAINT headers_pk PRIMARY KEY (hash)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_UNDOABLE_TABLE) (str "CREATE TABLE undoableblocks ("
            "hash bytea NOT NULL, "
            "height integer NOT NULL, "
            "txoutchanges bytea, "
            "transactions bytea, "
            "CONSTRAINT undoableblocks_pk PRIMARY KEY (hash)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_OPEN_OUTPUT_TABLE) (str "CREATE TABLE openoutputs ("
            "hash bytea NOT NULL, "
            "index integer NOT NULL, "
            "height integer NOT NULL, "
            "value bigint NOT NULL, "
            "scriptbytes bytea NOT NULL, "
            "toaddress character varying(35), "
            "addresstargetable smallint, "
            "coinbase boolean, "
            "CONSTRAINT openoutputs_pk PRIMARY KEY (hash,index)"
            ")"))

    ;; Some indexes to speed up inserts.
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_OUTPUTS_ADDRESS_MULTI_INDEX) "CREATE INDEX openoutputs_hash_index_num_height_toaddress_idx ON openoutputs USING btree (hash, index, height, toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_OUTPUTS_TOADDRESS_INDEX) "CREATE INDEX openoutputs_toaddress_idx ON openoutputs USING btree (toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX) "CREATE INDEX openoutputs_addresstargetable_idx ON openoutputs USING btree (addresstargetable)")
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_OUTPUTS_HASH_INDEX) "CREATE INDEX openoutputs_hash_idx ON openoutputs USING btree (hash)")
    #_private
    #_static
    (§ def- #_"String" (§ name CREATE_UNDOABLE_TABLE_INDEX) "CREATE INDEX undoableblocks_height_idx ON undoableBlocks USING btree (height)")

    #_private
    #_static
    (§ def- #_"String" (§ name SELECT_UNDOABLEBLOCKS_EXISTS_SQL) "select 1 from undoableblocks where hash = ?")

    ;;;
     ; Creates a new PostgresFullPrunedBlockStore.
     ;
     ; @param params A copy of the NetworkParameters used.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param hostname The hostname of the database to connect to.
     ; @param dbName The database to connect to.
     ; @param username The database username.
     ; @param password The password to the database.
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"PostgresFullPrunedBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"int" (§ name ßfullStoreDepth), #_"String" (§ name ßhostname), #_"String" (§ name ßdbName), #_"String" (§ name ßusername), #_"String" (§ name ßpassword)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super ßparams, (str DATABASE_CONNECTION_URL_PREFIX ßhostname "/" ßdbName), ßfullStoreDepth, ßusername, ßpassword, nil)
        this
    )

    ;;;
     ; <p>Create a new PostgresFullPrunedBlockStore, storing the tables in the schema specified.  You may want to
     ; specify a schema to avoid name collisions, or just to keep the database better organized.  The schema is not
     ; required, and if one is not provided than the default schema for the username will be used.  See
     ; <a href="http://www.postgres.org/docs/9.3/static/ddl-schemas.html">the postgres schema docs</a>
     ; for more on schemas.</p>
     ;
     ; @param params A copy of the NetworkParameters used.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param hostname The hostname of the database to connect to.
     ; @param dbName The database to connect to.
     ; @param username The database username.
     ; @param password The password to the database.
     ; @param schemaName The name of the schema to put the tables in.  May be null if no schema is being used.
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"PostgresFullPrunedBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"int" (§ name ßfullStoreDepth), #_"String" (§ name ßhostname), #_"String" (§ name ßdbName), #_"String" (§ name ßusername), #_"String" (§ name ßpassword), #_nilable #_"String" (§ name ßschemaName)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super ßparams, (str DATABASE_CONNECTION_URL_PREFIX ßhostname "/" ßdbName), ßfullStoreDepth, ßusername, ßpassword, ßschemaName)
        this
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDuplicateKeyErrorCode) [])
    (§ block
        POSTGRES_DUPLICATE_KEY_ERROR_CODE
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateTablesSQL) [])
    (§ block
        (§ let #_"List<String>" (§ name ßsql) (§ new #_"ArrayList<>"))
        (§ block
            (.. ßsql (add CREATE_SETTINGS_TABLE))
            (.. ßsql (add CREATE_HEADERS_TABLE))
            (.. ßsql (add CREATE_UNDOABLE_TABLE))
            (.. ßsql (add CREATE_OPEN_OUTPUT_TABLE))
            ßsql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateIndexesSQL) [])
    (§ block
        (§ let #_"List<String>" (§ name ßsql) (§ new #_"ArrayList<>"))
        (§ block
            (.. ßsql (add CREATE_UNDOABLE_TABLE_INDEX))
            (.. ßsql (add CREATE_OUTPUTS_ADDRESS_MULTI_INDEX))
            (.. ßsql (add CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX))
            (.. ßsql (add CREATE_OUTPUTS_HASH_INDEX))
            (.. ßsql (add CREATE_OUTPUTS_TOADDRESS_INDEX))
            ßsql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateSchemeSQL) [])
    (§ block
        (§ let #_"List<String>" (§ name ßsql) (§ new #_"ArrayList<>"))
        (§ block
            (.. ßsql (add (str "CREATE SCHEMA IF NOT EXISTS " schemaName)))
            (.. ßsql (add (str "set search_path to '" schemaName "'")))
            ßsql
        )
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDatabaseDriverClass) [])
    (§ block
        DATABASE_DRIVER_CLASS
    )

    #_override
    #_public
    (§ method #_"void" (§ fn put) [#_"StoredBlock" (§ name ßstoredBlock), #_"StoredUndoableBlock" (§ name ßundoableBlock)])
        (§ throws #_"BlockStoreException")
    (§ block
        (maybeConnect)
        ;; We skip the first 4 bytes because (on mainnet) the minimum target has 4 0-bytes.
        (§ let #_"byte[]" (§ name ßhashBytes) (§ new #_"byte[]" (§ count 28)))
        (§ block
            (System/arraycopy (.. ßstoredBlock (getHeader) (getHash) (getBytes)), 4, ßhashBytes, 0, 28)
            (§ let #_"int" (§ name ßheight) (.. ßstoredBlock (getHeight)))
            (§ block
                (§ let #_"byte[]" (§ name ßtransactions) nil)
                (§ block
                    (§ let #_"byte[]" (§ name ßtxOutChanges) nil)
                    (§ block
                        (§ try )
                        (§ block
                            (§ let #_"ByteArrayOutputStream" (§ name ßbos) (§ new #_"ByteArrayOutputStream"))
                            (§ block
                                (§ if (!= (.. ßundoableBlock (getTxOutChanges)) nil))
                                (§ block
                                    (.. ßundoableBlock (getTxOutChanges) (serializeToStream ßbos))
                                    (§ ass (§ name ßtxOutChanges) (.. ßbos (toByteArray)))
                                )
                                (§ else )
                                (§ block
                                    (§ let #_"int" (§ name ßnumTxn) (.. ßundoableBlock (getTransactions) (size)))
                                    (§ block
                                        (.. ßbos (write (& 0xff ßnumTxn)))
                                        (.. ßbos (write (& 0xff (>> ßnumTxn 8))))
                                        (.. ßbos (write (& 0xff (>> ßnumTxn 16))))
                                        (.. ßbos (write (& 0xff (>> ßnumTxn 24))))
                                        (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. ßundoableBlock (getTransactions)))
                                        (§ block
                                            (.. ßtx (bitcoinSerialize ßbos))
                                        )
                                        (§ ass (§ name ßtransactions) (.. ßbos (toByteArray)))
                                    )
                                )
                                (.. ßbos (close))
                            )
                        )
                        (§ catch #_"IOException" (§ name ße))
                        (§ block
                            (§ throw (§ new #_"BlockStoreException" ße))
                        )

                        (§ try )
                        (§ block
                            (§ if (.. log (isDebugEnabled)))
                            (§ block
                                (.. log (debug (str "Looking for undoable block with hash: " (.. Utils/HEX (encode ßhashBytes)))))
                            )

                            (§ let #_"PreparedStatement" (§ name ßfindS) (.. conn (get) (prepareStatement SELECT_UNDOABLEBLOCKS_EXISTS_SQL)))
                            (§ block
                                (.. ßfindS (setBytes 1, ßhashBytes))

                                (§ let #_"ResultSet" (§ name ßrs) (.. ßfindS (executeQuery)))
                                (§ block
                                    (§ if (.. ßrs (next)))
                                    (§ block
                                        ;; We already have this output, update it.
                                        (.. ßfindS (close))

                                        ;; Postgres insert-or-updates are very complex (and finnicky).
                                        ;; This level of transaction isolation seems to work for bitcoinj.
                                        (§ let #_"PreparedStatement" (§ name ßps) (.. conn (get) (prepareStatement (getUpdateUndoableBlocksSQL))))
                                        (§ block
                                            (.. ßps (setBytes 3, ßhashBytes))

                                            (§ if (.. log (isDebugEnabled)))
                                            (§ block
                                                (.. log (debug (str "Updating undoable block with hash: " (.. Utils/HEX (encode ßhashBytes)))))
                                            )

                                            (§ if (nil? ßtransactions))
                                            (§ block
                                                (.. ßps (setBytes 1, ßtxOutChanges))
                                                (.. ßps (setNull 2, Types/BINARY))
                                            )
                                            (§ else )
                                            (§ block
                                                (.. ßps (setNull 1, Types/BINARY))
                                                (.. ßps (setBytes 2, ßtransactions))
                                            )
                                            (.. ßps (executeUpdate))
                                            (.. ßps (close))

                                            (§ return nil)
                                        )
                                    )

                                    (§ let #_"PreparedStatement" (§ name ßps) (.. conn (get) (prepareStatement (getInsertUndoableBlocksSQL))))
                                    (§ block
                                        (.. ßps (setBytes 1, ßhashBytes))
                                        (.. ßps (setInt 2, ßheight))

                                        (§ if (.. log (isDebugEnabled)))
                                        (§ block
                                            (.. log (debug (str "Inserting undoable block with hash: " (.. Utils/HEX (encode ßhashBytes))  " at height " ßheight)))
                                        )

                                        (§ if (nil? ßtransactions))
                                        (§ block
                                            (.. ßps (setBytes 3, ßtxOutChanges))
                                            (.. ßps (setNull 4, Types/BINARY))
                                        )
                                        (§ else )
                                        (§ block
                                            (.. ßps (setNull 3, Types/BINARY))
                                            (.. ßps (setBytes 4, ßtransactions))
                                        )
                                        (.. ßps (executeUpdate))
                                        (.. ßps (close))
                                        (§ try )
                                        (§ block
                                            (putUpdateStoredBlock ßstoredBlock, true)
                                        )
                                        (§ catch #_"SQLException" (§ name ße))
                                        (§ block
                                            (§ throw (§ new #_"BlockStoreException" ße))
                                        )
                                    )
                                )
                            )
                        )
                        (§ catch #_"SQLException" (§ name ße))
                        (§ block
                            (§ if (not (.. ße (getSQLState) (equals POSTGRES_DUPLICATE_KEY_ERROR_CODE))))
                            (§ block
                                (§ throw (§ new #_"BlockStoreException" ße))
                            )
                        )
                        nil
                    )
                )
            )
        )
    )
)

#_(ns org.bitcoinj.store #_"SPVBlockStore"
    (:import [java.io *]
             [java.nio *]
             [java.nio.channels *]
             [java.util *]
             [java.util.concurrent.locks *])
    (:import [com.google.common.base Charsets Preconditions]
             [org.slf4j *])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.utils *]))

;; TODO: Lose the mmap in this class.  There are too many platform bugs that require odd workarounds.

;;;
 ; An SPVBlockStore holds a limited number of block headers in a memory mapped ring buffer.  With such a store,
 ; you may not be able to process very deep re-orgs and could be disconnected from the chain (requiring a replay),
 ; but as they are virtually unheard of this is not a significant risk.
 ;;
#_public
(§ class #_"SPVBlockStore" (§ implements #_"BlockStore")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"SPVBlockStore")))

    ;;; The default number of headers that will be stored in the ring buffer. ;;
    #_public
    #_static
    (§ def #_"int" (§ name DEFAULT_CAPACITY) 5000)
    #_public
    #_static
    (§ def #_"String" (§ name HEADER_MAGIC) "SPVB")

    #_protected
    #_volatile
    (§ field #_"MappedByteBuffer" (§ name buffer))
    #_protected
    (§ field #_"int" (§ name capacity))
    #_protected
    (§ field #_"NetworkParameters" (§ name params))

    #_protected
    (§ field #_"ReentrantLock" (§ name lock) (Threading/lock "SPVBlockStore"))

    ;; The entire ring-buffer is mmapped and accessing it should be as fast as accessing regular memory once it's
    ;; faulted in.  Unfortunately, in theory practice and theory are the same.  In practice they aren't.
    ;;
    ;; MMapping a file in Java does not give us a byte[] as you may expect but rather a ByteBuffer, and whilst on
    ;; the OpenJDK/Oracle JVM calls into the get() methods are compiled down to inlined native code on Android each
    ;; get() call is actually a full-blown JNI method under the hood, meaning it's unbelievably slow.  The caches
    ;; below let us stay in the JIT-compiled Java world without expensive JNI transitions and make a 10x difference!
    #_protected
    (§ field #_"LinkedHashMap<Sha256Hash, StoredBlock>" (§ name blockCache) (§ new #_"LinkedHashMap<Sha256Hash, StoredBlock>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, StoredBlock>" (§ name ßentry)])
            (§ block
                (< 2050 (size)) ;; Slightly more than the difficulty transition period.
            )
        )))

    ;; Use a separate cache to track get() misses.  This is to efficiently handle the case of an unconnected block
    ;; during chain download.  Each new block will do a get() on the unconnected block so if we haven't seen it yet
    ;; we must efficiently respond.
    ;;
    ;; We don't care about the value in this cache.  It is always notFoundMarker.  Unfortunately LinkedHashSet does
    ;; not provide the removeEldestEntry control.
    #_private
    #_static
    (§ def- #_"Object" (§ name NOT_FOUND_MARKER) (§ new #_"Object"))
    #_protected
    (§ field #_"LinkedHashMap<Sha256Hash, Object>" (§ name notFoundCache) (§ new #_"LinkedHashMap<Sha256Hash, Object>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, Object>" (§ name ßentry)])
            (§ block
                (< 100 (size)) ;; This was chosen arbitrarily.
            )
        )))

    ;; Used to stop other applications/processes from opening the store.
    #_protected
    (§ field #_"FileLock" (§ name fileLock))
    #_protected
    (§ field #_"RandomAccessFile" (§ name randomAccessFile))

    ;;;
     ; Creates and initializes an SPV block store that can hold {@link #DEFAULT_CAPACITY} blocks.
     ; Will create the given file if it's missing.  This operation will block on disk.
     ; @param file File to use for the block store.
     ; @throws BlockStoreException if something goes wrong.
     ;;
    #_public
    (§ constructor #_"SPVBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"File" (§ name ßfile)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßparams, ßfile, DEFAULT_CAPACITY)
        this
    )

    ;;;
     ; Creates and initializes an SPV block store that can hold a given amount of blocks.
     ; Will create the given file if it's missing.  This operation will block on disk.
     ; @param file File to use for the block store.
     ; @param capacity Custom capacity.
     ; @throws BlockStoreException if something goes wrong.
     ;;
    #_public
    (§ constructor #_"SPVBlockStore" [#_"NetworkParameters" (§ name ßparams), #_"File" (§ name ßfile), #_"int" (§ name ßcapacity)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull ßfile)
        (§ ass (§ name (.. this ßparams)) (Preconditions/checkNotNull ßparams))
        (Preconditions/checkArgument (> ßcapacity 0))

        (§ ass (§ name (.. this ßcapacity)) ßcapacity)
        (§ try )
        (§ block
            (§ let #_"boolean" (§ name ßexists) (.. ßfile (exists)))
            (§ block
                ;; Set up the backing file.
                (§ ass (§ name randomAccessFile) (§ new #_"RandomAccessFile" ßfile, "rw"))
                (§ let #_"long" (§ name ßfileSize) (getFileSize ßcapacity))
                (§ block
                    (§ if (not ßexists))
                    (§ block
                        (.. log (info (str "Creating new SPV block chain ßfile " ßfile)))
                        (.. randomAccessFile (setLength ßfileSize))
                    )
                    (§ elseif (!= (.. randomAccessFile (length)) ßfileSize))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" (str "File size on disk does not match expected size: " (.. randomAccessFile (length)) " vs " ßfileSize)))
                    )

                    (§ let #_"FileChannel" (§ name ßchannel) (.. randomAccessFile (getChannel)))
                    (§ block
                        (§ ass (§ name fileLock) (.. ßchannel (tryLock)))
                        (§ if (nil? fileLock))
                        (§ block
                            (§ throw (§ new #_"ChainFileLockedException" "Store file is already locked by another process"))
                        )

                        ;; Map it into memory read/write.  The kernel will take care of flushing writes to disk at the most
                        ;; efficient times, which may mean that until the map is deallocated the data on disk is randomly
                        ;; inconsistent.  However the only process accessing it is us, via this mapping, so our own view will
                        ;; always be correct.  Once we establish the mmap the underlying file and channel can go away.  Note
                        ;; that the details of mmapping vary between platforms.
                        (§ ass (§ name buffer) (.. ßchannel (map FileChannel/MapMode/READ_WRITE, 0, ßfileSize)))

                        ;; Check or initialize the header bytes to ensure we don't try to open some random file.
                        (§ let #_"byte[]" (§ name ßheader))
                        (§ block
                            (§ if ßexists)
                            (§ block
                                (§ ass (§ name ßheader) (§ new #_"byte[]" (§ count 4)))
                                (.. buffer (get ßheader))
                                (§ if (not (.. (§ new #_"String" ßheader, Charsets/US_ASCII) (equals HEADER_MAGIC))))
                                (§ block
                                    (§ throw (§ new #_"BlockStoreException" (str "Header bytes do not equal " HEADER_MAGIC)))
                                )
                            )
                            (§ else )
                            (§ block
                                (initNewStore ßparams)
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße1))
        (§ block
            (§ try )
            (§ block
                (§ if (some? randomAccessFile))
                (§ block
                    (.. randomAccessFile (close))
                )
            )
            (§ catch #_"IOException" (§ name ße2))
            (§ block
                (§ throw (§ new #_"BlockStoreException" ße2))
            )
            (§ throw (§ new #_"BlockStoreException" ße1))
        )
        this
    )

    #_private
    (§ method- #_"void" (§ fn initNewStore) [#_"NetworkParameters" (§ name ßparams)])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"byte[]" (§ name ßheader) (.. HEADER_MAGIC (getBytes "US-ASCII")))
        (§ block
            (.. buffer (put ßheader))
            ;; Insert the genesis block.
            (.. lock (lock))
            (§ try )
            (§ block
                (setRingCursor buffer, FILE_PROLOGUE_BYTES)
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            (§ let #_"Block" (§ name ßgenesis) (.. ßparams (getGenesisBlock) (cloneAsHeader)))
            (§ block
                (§ let #_"StoredBlock" (§ name ßstoredGenesis) (§ new #_"StoredBlock" ßgenesis, (.. ßgenesis (getWork)), 0))
                (§ block
                    (put ßstoredGenesis)
                    (setChainHead ßstoredGenesis)
                    nil
                )
            )
        )
    )

    ;;; Returns the size in bytes of the file that is used to store the chain with the current parameters. ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn getFileSize) [#_"int" (§ name ßcapacity)])
    (§ block
        (+ (* RECORD_SIZE ßcapacity) FILE_PROLOGUE_BYTES) ;; extra kilobyte for stuff
    )

    #_override
    #_public
    (§ method #_"void" (§ fn put) [#_"StoredBlock" (§ name ßblock)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" (§ name ßbuffer) (.. this buffer))
        (§ block
            (§ if (nil? ßbuffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ let #_"int" (§ name ßcursor) (getRingCursor ßbuffer))
                (§ block
                    ;; Wrapped around.
                    (§ if (== ßcursor (getFileSize capacity)))
                    (§ block
                        (§ ass (§ name ßcursor) FILE_PROLOGUE_BYTES)
                    )
                    (.. ßbuffer (position ßcursor))
                    (§ let #_"Sha256Hash" (§ name ßhash) (.. ßblock (getHeader) (getHash)))
                    (§ block
                        (.. notFoundCache (remove ßhash))
                        (.. ßbuffer (put (.. ßhash (getBytes))))
                        (.. ßblock (serializeCompact ßbuffer))
                        (setRingCursor ßbuffer, (.. ßbuffer (position)))
                        (.. blockCache (put ßhash, ßblock))
                    )
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            nil
        )
    )

    #_override
    #_nilable
    #_public
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" (§ name ßbuffer) (.. this buffer))
        (§ block
            (§ if (nil? ßbuffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ let #_"StoredBlock" (§ name ßcacheHit) (.. blockCache (get ßhash)))
                (§ block
                    (§ if (some? ßcacheHit))
                    (§ block
                        (§ return ßcacheHit)
                    )
                    (§ if (!= (.. notFoundCache (get ßhash)) nil))
                    (§ block
                        (§ return nil)
                    )

                    ;; Starting from the current tip of the ring work backwards until we have either
                    ;; found the block or wrapped around.
                    (§ let #_"int" (§ name ßcursor) (getRingCursor ßbuffer))
                    (§ block
                        (§ let #_"int" (§ name ßstartingPoint) ßcursor)
                        (§ block
                            (§ let #_"int" (§ name ßfileSize) (getFileSize capacity))
                            (§ block
                                (§ let #_"byte[]" (§ name ßtargetHashBytes) (.. ßhash (getBytes)))
                                (§ block
                                    (§ let #_"byte[]" (§ name ßscratch) (§ new #_"byte[]" (§ count 32)))
                                    (§ block
                                        (§ do )
                                        (§ block
                                            (§ ass (§ name ßcursor) (- ßcursor RECORD_SIZE))
                                            ;; We hit the start, so wrap around.
                                            (§ if (< ßcursor FILE_PROLOGUE_BYTES))
                                            (§ block
                                                (§ ass (§ name ßcursor) (- ßfileSize RECORD_SIZE))
                                            )
                                            ;; Cursor is now at the start of the next record to check, so read the hash and compare it.
                                            (.. ßbuffer (position ßcursor))
                                            (.. ßbuffer (get ßscratch))
                                            (§ if (Arrays/equals ßscratch, ßtargetHashBytes))
                                            (§ block
                                                ;; Found the target.
                                                (§ let #_"StoredBlock" (§ name ßstoredBlock) (StoredBlock/deserializeCompact params, ßbuffer))
                                                (§ block
                                                    (.. blockCache (put ßhash, ßstoredBlock))
                                                    (§ return ßstoredBlock)
                                                )
                                            )
                                        )
                                        (§ again (!= ßcursor ßstartingPoint))

                                        ;; Not found.
                                        (.. notFoundCache (put ßhash, NOT_FOUND_MARKER))
                                        (§ return nil)
                                    )
                                )
                            )
                        )
                    )
                )
            )
            (§ catch #_"ProtocolException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
        )
    )

    #_protected
    (§ field #_"StoredBlock" (§ name lastChainHead))

    #_override
    #_public
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" (§ name ßbuffer) (.. this buffer))
        (§ block
            (§ if (nil? ßbuffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ if (nil? lastChainHead))
                (§ block
                    (§ let #_"byte[]" (§ name ßheadHash) (§ new #_"byte[]" (§ count 32)))
                    (§ block
                        (.. ßbuffer (position 8))
                        (.. ßbuffer (get ßheadHash))
                        (§ let #_"Sha256Hash" (§ name ßhash) (Sha256Hash/wrap ßheadHash))
                        (§ block
                            (§ let #_"StoredBlock" (§ name ßblock) (get ßhash))
                            (§ block
                                (§ if (nil? ßblock))
                                (§ block
                                    (§ throw (§ new #_"BlockStoreException" (str "Corrupted block store: could not find chain head: " ßhash)))
                                )

                                (§ ass (§ name lastChainHead) ßblock)
                            )
                        )
                    )
                )
                (§ return lastChainHead)
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" (§ name ßbuffer) (.. this buffer))
        (§ block
            (§ if (nil? ßbuffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ ass (§ name lastChainHead) ßchainHead)
                (§ let #_"byte[]" (§ name ßheadHash) (.. ßchainHead (getHeader) (getHash) (getBytes)))
                (§ block
                    (.. ßbuffer (position 8))
                    (.. ßbuffer (put ßheadHash))
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            nil
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ try )
        (§ block
            (.. buffer (force))
            (§ ass (§ name buffer) nil) ;; Allow it to be GCd and the underlying file mapping to go away.
            (.. randomAccessFile (close))
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"BlockStoreException" ße))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )

    #_protected
    #_static
    (§ def #_"int" (§ name RECORD_SIZE) (+ 32 StoredBlock/COMPACT_SERIALIZED_SIZE)) ;; hash

    ;; File format:
    ;;   4 header bytes = "SPVB"
    ;;   4 cursor bytes, which indicate the offset from the first kb where the next block header should be written
    ;;   32 bytes for the hash of the chain head
    ;;
    ;; For each header (128 bytes)
    ;;   32 bytes hash of the header
    ;;   12 bytes of chain work
    ;;    4 bytes of height
    ;;   80 bytes of block header data
    #_protected
    #_static
    (§ def #_"int" (§ name FILE_PROLOGUE_BYTES) 1024)

    ;;; Returns the offset from the file start where the latest block should be written (end of prev block). ;;
    #_private
    (§ method- #_"int" (§ fn getRingCursor) [#_"ByteBuffer" (§ name ßbuffer)])
    (§ block
        (§ let #_"int" (§ name ßc) (.. ßbuffer (getInt 4)))
        (§ block
            (Preconditions/checkState (<= FILE_PROLOGUE_BYTES ßc), "Integer overflow")
            ßc
        )
    )

    #_private
    (§ method- #_"void" (§ fn setRingCursor) [#_"ByteBuffer" (§ name ßbuffer), #_"int" (§ name ßnewCursor)])
    (§ block
        (Preconditions/checkArgument (<= 0 ßnewCursor))
        (.. ßbuffer (putInt 4, ßnewCursor))
        nil
    )
)

#_(ns org.bitcoinj.uri #_"BitcoinURI"
    (:import [java.io UnsupportedEncodingException]
             [java.net URI URISyntaxException URLDecoder URLEncoder]
             [java.util ArrayList Collections LinkedHashMap List Locale Map])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Address AddressFormatException Coin NetworkParameters]
             [org.bitcoinj.params AbstractBitcoinNetParams]))

;;;
 ; <p>Provides a standard implementation of a Bitcoin URI with support for the following:</p>
 ;
 ; <ul>
 ; <li>URLEncoded URIs (as passed in by IE on the command line)</li>
 ; <li>BIP21 names (including the "req-" prefix handling requirements)</li>
 ; </ul>
 ;
 ; <h2>Accepted formats</h2>
 ;
 ; <p>The following input forms are accepted:</p>
 ;
 ; <ul>
 ; <li>{@code bitcoin:<address>}</li>
 ; <li>{@code bitcoin:<address>?<name1>=<value1>&<name2>=<value2>} with multiple additional name/value pairs</li>
 ; </ul>
 ;
 ; <p>The name/value pairs are processed as follows.</p>
 ; <ol>
 ; <li>URL encoding is stripped and treated as UTF-8</li>
 ; <li>names prefixed with {@code req-} are treated as required and if unknown or conflicting cause a parse exception</li>
 ; <li>Unknown names not prefixed with {@code req-} are added to a Map, accessible by parameter name</li>
 ; <li>Known names not prefixed with {@code req-} are processed unless they are malformed</li>
 ; </ol>
 ;
 ; <p>The following names are known and have the following formats:</p>
 ; <ul>
 ; <li>{@code amount} decimal value to 8 dp (e.g. 0.12345678) <b>Note that the exponent notation is not supported any more.</b></li>
 ; <li>{@code label} any URL encoded alphanumeric</li>
 ; <li>{@code message} any URL encoded alphanumeric</li>
 ; </ul>
 ;
 ; @author Andreas Schildbach (initial code)
 ; @author Jim Burton (enhancements for MultiBit)
 ; @author Gary Rowe (BIP21 support)
 ; @see <a href="https://github.com/bitcoin/bips/blob/master/bip-0021.mediawiki">BIP 0021</a>
 ;;
#_public
(§ class #_"BitcoinURI"
    ;; Not worth turning into an enum.
    #_public
    #_static
    (§ def #_"String" (§ name FIELD_MESSAGE) "message")
    #_public
    #_static
    (§ def #_"String" (§ name FIELD_LABEL) "label")
    #_public
    #_static
    (§ def #_"String" (§ name FIELD_AMOUNT) "amount")
    #_public
    #_static
    (§ def #_"String" (§ name FIELD_ADDRESS) "address")
    #_public
    #_static
    (§ def #_"String" (§ name FIELD_PAYMENT_REQUEST_URL) "r")

    ;;;
     ; URI for Bitcoin network.  Use {@link org.bitcoinj.params.AbstractBitcoinNetParams#BITCOIN_SCHEME}
     ; if you specifically need Bitcoin, or use {@link org.bitcoinj.core.NetworkParameters#getUriScheme}
     ; to get the scheme from network parameters.
     ;;
    #_deprecated
    #_public
    #_static
    (§ def #_"String" (§ name BITCOIN_SCHEME) "bitcoin")
    #_private
    #_static
    (§ def- #_"String" (§ name ENCODED_SPACE_CHARACTER) "%20")
    #_private
    #_static
    (§ def- #_"String" (§ name AMPERSAND_SEPARATOR) "&")
    #_private
    #_static
    (§ def- #_"String" (§ name QUESTION_MARK_SEPARATOR) "?")

    ;;;
     ; Contains all the parameters in the order in which they were processed.
     ;;
    #_private
    (§ field- #_"Map<String, Object>" (§ name parameterMap) (§ new #_"LinkedHashMap<>"))

    ;;;
     ; Constructs a new BitcoinURI from the given string.  Can be for any network.
     ;
     ; @param uri The raw URI data to be parsed (see class comments for accepted formats).
     ; @throws BitcoinURIParseException if the URI is not syntactically or semantically valid.
     ;;
    #_public
    (§ constructor #_"BitcoinURI" [#_"String" (§ name ßuri)])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (§ this nil, ßuri)
        this
    )

    ;;;
     ; Constructs a new object by trying to parse the input as a valid Bitcoin URI.
     ;
     ; @param params The network parameters that determine which network the URI is from, or null if you
     ;               don't have any expectation about what network the URI is for and wish to check yourself.
     ; @param input The raw URI data to be parsed (see class comments for accepted formats).
     ;
     ; @throws BitcoinURIParseException if the input fails Bitcoin URI syntax and semantic checks.
     ;;
    #_public
    (§ constructor #_"BitcoinURI" [#_nilable #_"NetworkParameters" (§ name ßparams), #_"String" (§ name ßinput)])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (Preconditions/checkNotNull ßinput)

        (§ let #_"String" (§ name ßscheme) (if (some? ßparams) (.. ßparams (getUriScheme)) AbstractBitcoinNetParams/BITCOIN_SCHEME))
        (§ block

            ;; Attempt to form the URI (fail fast syntax checking to official standards).
            (§ let #_"URI" (§ name ßuri))
            (§ block
                (§ try )
                (§ block
                    (§ ass (§ name ßuri) (§ new #_"URI" ßinput))
                )
                (§ catch #_"URISyntaxException" (§ name ße))
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" "Bad URI syntax", ße))
                )

                ;; URI is formed as  bitcoin:<address>?<query parameters>
                ;; blockchain.info generates URIs of non-BIP compliant form bitcoin://address?....
                ;; We support both until Ben fixes his code.

                ;; Remove the bitcoin scheme.
                ;; (Note: getSchemeSpecificPart() is not used as it unescapes the label and parse then fails.
                ;; For instance with  bitcoin:129mVqKUmJ9uwPxKJBnNdABbuaaNfho4Ha?amount=0.06&label=Tom%20%26%20Jerry
                ;; the & (%26) in Tom and Jerry gets interpreted as a separator and the label then gets parsed
                ;; as 'Tom ' instead of 'Tom & Jerry'.)
                (§ let #_"String" (§ name ßblockchainInfoScheme) (str ßscheme "://"))
                (§ block
                    (§ let #_"String" (§ name ßcorrectScheme) (str ßscheme ":"))
                    (§ block
                        (§ let #_"String" (§ name ßschemeSpecificPart))
                        (§ block
                            (§ if (.. ßinput (startsWith ßblockchainInfoScheme)))
                            (§ block
                                (§ ass (§ name ßschemeSpecificPart) (.. ßinput (substring (.. ßblockchainInfoScheme (length)))))
                            )
                            (§ elseif (.. ßinput (startsWith ßcorrectScheme)))
                            (§ block
                                (§ ass (§ name ßschemeSpecificPart) (.. ßinput (substring (.. ßcorrectScheme (length)))))
                            )
                            (§ else )
                            (§ block
                                (§ throw (§ new #_"BitcoinURIParseException" (str "Unsupported URI scheme: " (.. ßuri (getScheme)))))
                            )

                            ;; Split off the address from the rest of the query parameters.
                            (§ let #_"String[]" (§ name ßaddressSplitTokens) (.. ßschemeSpecificPart (split "\\?", 2)))
                            (§ block
                                (§ if (== (.. ßaddressSplitTokens length) 0))
                                (§ block
                                    (§ throw (§ new #_"BitcoinURIParseException" "No data found after the bitcoin: prefix"))
                                )

                                (§ let #_"String" (§ name ßaddressToken) (§ ai ßaddressSplitTokens 0)) ;; may be empty!
                                (§ block

                                    (§ let #_"String[]" (§ name ßnameValuePairTokens))
                                    (§ block
                                        (§ if (== (.. ßaddressSplitTokens length) 1))
                                        (§ block
                                            ;; Only an address is specified - use an empty '<name>=<value>' token array.
                                            (§ ass (§ name ßnameValuePairTokens) (§ new #_"String[]"))
                                        )
                                        (§ else )
                                        (§ block
                                            ;; Split into '<name>=<value>' tokens.
                                            (§ ass (§ name ßnameValuePairTokens) (.. (§ ai ßaddressSplitTokens 1) (split "&")))
                                        )

                                        ;; Attempt to parse the rest of the URI parameters.
                                        (parseParameters ßparams, ßaddressToken, ßnameValuePairTokens)

                                        (§ if (not (.. ßaddressToken (isEmpty))))
                                        (§ block
                                            ;; Attempt to parse the addressToken as a Bitcoin address for this network.
                                            (§ try )
                                            (§ block
                                                (§ let #_"Address" (§ name ßaddress) (Address/fromBase58 ßparams, ßaddressToken))
                                                (§ block
                                                    (putWithValidation FIELD_ADDRESS, ßaddress)
                                                )
                                            )
                                            (§ catch [#_"AddressFormatException" (§ name ße)])
                                            (§ block
                                                (§ throw (§ new #_"BitcoinURIParseException" "Bad address", ße))
                                            )
                                        )

                                        (§ if (and (.. ßaddressToken (isEmpty)) (nil? (getPaymentRequestUrl))))
                                        (§ block
                                            (§ throw (§ new #_"BitcoinURIParseException" "No address and no r= parameter found"))
                                        )
                                        this
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; @param params The network parameters or null.
     ; @param nameValuePairTokens The tokens representing the name value pairs (assumed to be separated by '=' e.g. 'amount=0.2').
     ;;
    #_private
    (§ method- #_"void" (§ fn parseParameters) [#_nilable #_"NetworkParameters" (§ name ßparams), #_"String" (§ name ßaddressToken), #_"String[]" (§ name ßnameValuePairTokens)])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        ;; Attempt to decode the rest of the tokens into a parameter map.
        (§ for (§ var #_"String" (§ name ßnameValuePairToken)) :for ßnameValuePairTokens)
        (§ block
            (§ let #_"int" (§ name ßsepIndex) (.. ßnameValuePairToken (indexOf "=")))
            (§ block
                (§ if (== ßsepIndex -1))
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" (str "Malformed Bitcoin URI - no separator in '" ßnameValuePairToken "'")))
                )
                (§ if (== ßsepIndex 0))
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" (str "Malformed Bitcoin URI - empty name '" ßnameValuePairToken "'")))
                )

                (§ let #_"String" (§ name ßnameToken) (.. ßnameValuePairToken (substring 0, ßsepIndex) (toLowerCase Locale/ENGLISH)))
                (§ block
                    (§ let #_"String" (§ name ßvalueToken) (.. ßnameValuePairToken (substring (+ ßsepIndex 1))))
                    (§ block

                        ;; Parse the amount.
                        (§ if (.. FIELD_AMOUNT (equals ßnameToken)))
                        (§ block
                            ;; Decode the amount (contains an optional decimal component to 8dp).
                            (§ try )
                            (§ block
                                (§ let #_"Coin" (§ name ßamount) (Coin/parseCoin ßvalueToken))
                                (§ block
                                    (§ if (and (some? ßparams) (.. ßamount (isGreaterThan (.. ßparams (getMaxMoney))))))
                                    (§ block
                                        (§ throw (§ new #_"BitcoinURIParseException" "Max number of coins exceeded"))
                                    )
                                    (§ if (< (.. ßamount (signum)) 0))
                                    (§ block
                                        (§ throw (§ new #_"ArithmeticException" "Negative coins specified"))
                                    )

                                    (putWithValidation FIELD_AMOUNT, ßamount)
                                )
                            )
                            (§ catch #_"IllegalArgumentException" (§ name ße))
                            (§ block
                                (§ throw (§ new #_"OptionalFieldValidationException" (String/format Locale/US, "'%s' is not a valid amount", ßvalueToken), ße))
                            )
                            (§ catch #_"ArithmeticException" (§ name ße))
                            (§ block
                                (§ throw (§ new #_"OptionalFieldValidationException" (String/format Locale/US, "'%s' has too many decimal places", ßvalueToken), ße))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ if (.. ßnameToken (startsWith "req-")))
                            (§ block
                                ;; A required parameter that we do not know about.
                                (§ throw (§ new #_"RequiredFieldValidationException" (str "'" ßnameToken "' is required but not known, this URI is not valid")))
                            )

                            ;; Known fields and unknown parameters that are optional.
                            (§ try )
                            (§ block
                                (§ if (< 0 (.. ßvalueToken (length))))
                                (§ block
                                    (putWithValidation ßnameToken, (URLDecoder/decode ßvalueToken, "UTF-8"))
                                )
                            )
                            (§ catch #_"UnsupportedEncodingException" (§ name ße))
                            (§ block
                                (§ throw (§ new #_"RuntimeException" ße)) ;; Can't happen.
                            )
                        )
                    )
                )
            )
        )

        ;; Note to the future:
        ;; when you want to implement 'req-expires', have a look at commit 410a53791841 which had it in.
        nil
    )

    ;;;
     ; Put the value against the key in the map checking for duplication.  This avoids address field overwrite etc.
     ;
     ; @param key The key for the map.
     ; @param value The value to store.
     ;;
    #_private
    (§ method- #_"void" (§ fn putWithValidation) [#_"String" (§ name ßkey), #_"Object" (§ name ßvalue)])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (§ if (.. parameterMap (containsKey ßkey)))
        (§ block
            (§ throw (§ new #_"BitcoinURIParseException" (String/format Locale/US, "'%s' is duplicated, URI is invalid", ßkey)))
        )

        (.. parameterMap (put ßkey, ßvalue))
        nil
    )

    ;;;
     ; The Bitcoin Address from the URI, if one was present.  It's possible to have Bitcoin URI's with no address
     ; if a r= payment protocol parameter is specified, though this form is not recommended as older wallets
     ; can't understand it.
     ;;
    #_nilable
    #_public
    (§ method #_"Address" (§ fn getAddress) [])
    (§ block
        (§ cast #_"Address" (.. parameterMap (get FIELD_ADDRESS)))
    )

    ;;;
     ; @return The amount name encoded using a pure integer value based at 10,000,000 units is 1 BTC.
     ;         May be null if no amount is specified.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getAmount) [])
    (§ block
        (§ cast #_"Coin" (.. parameterMap (get FIELD_AMOUNT)))
    )

    ;;;
     ; @return the label from the URI.
     ;;
    #_public
    (§ method #_"String" (§ fn getLabel) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get FIELD_LABEL)))
    )

    ;;;
     ; @return the message from the URI.
     ;;
    #_public
    (§ method #_"String" (§ fn getMessage) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get FIELD_MESSAGE)))
    )

    ;;;
     ; @return the URL where a payment request (as specified in BIP 70) may be fetched.
     ;;
    #_public
    (§ method #_"String" (§ fn getPaymentRequestUrl) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get FIELD_PAYMENT_REQUEST_URL)))
    )

    ;;;
     ; Returns the URLs where a payment request (as specified in BIP 70) may be fetched.
     ; The first URL is the main URL, all subsequent URLs are fallbacks.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn getPaymentRequestUrls) [])
    (§ block
        (§ let #_"ArrayList<String>" (§ name ßurls) (§ new #_"ArrayList<>"))
        (§ block
            (§ while true)
            (§ block
                (§ let #_"int" (§ name ßi) (.. ßurls (size)))
                (§ block
                    (§ let #_"String" (§ name ßparamName) (str FIELD_PAYMENT_REQUEST_URL (if (< 0 ßi) (Integer/toString ßi) "")))
                    (§ block
                        (§ let #_"String" (§ name ßurl) (§ cast #_"String" (.. parameterMap (get ßparamName))))
                        (§ block
                            (§ if (nil? ßurl))
                            (§ block
                                (§ break )
                            )
                            (.. ßurls (add ßurl))
                        )
                    )
                )
            )
            (Collections/reverse ßurls)
            ßurls
        )
    )

    ;;;
     ; @param name The name of the parameter.
     ; @return the parameter value, or null if not present.
     ;;
    #_public
    (§ method #_"Object" (§ fn getParameterByName) [#_"String" (§ name ßname)])
    (§ block
        (.. parameterMap (get ßname))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder" "BitcoinURI["))
        (§ block
            (§ let #_"boolean" (§ name ßfirst) true)
            (§ block
                (§ for (§ var #_"Map.Entry<String, Object>" (§ name ßentry)) :for (.. parameterMap (entrySet)))
                (§ block
                    (§ if ßfirst)
                    (§ block
                        (§ ass (§ name ßfirst) false)
                    )
                    (§ else )
                    (§ block
                        (.. ßsb (append ","))
                    )
                    (.. ßsb (append "'") (append (.. ßentry (getKey))) (append "'='") (append (.. ßentry (getValue))) (append "'"))
                )
                (.. ßsb (append "]"))
                (.. ßsb (toString))
            )
        )
    )

    ;;;
     ; Simple Bitcoin URI builder using known good fields.
     ;
     ; @param address The Bitcoin address.
     ; @param amount The amount.
     ; @param label A label.
     ; @param message A message.
     ; @return a String containing the Bitcoin URI.
     ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn convertToBitcoinURI) [#_"Address" (§ name ßaddress), #_"Coin" (§ name ßamount), #_"String" (§ name ßlabel), #_"String" (§ name ßmessage)])
    (§ block
        (convertToBitcoinURI (.. ßaddress (getParameters)), (.. ßaddress (toString)), ßamount, ßlabel, ßmessage)
    )

    ;;;
     ; Simple Bitcoin URI builder using known good fields.
     ;
     ; @param params The network parameters that determine which network the URI is for.
     ; @param address The Bitcoin address.
     ; @param amount The amount.
     ; @param label A label.
     ; @param message A message.
     ; @return a String containing the Bitcoin URI.
     ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn convertToBitcoinURI) [#_"NetworkParameters" (§ name ßparams), #_"String" (§ name ßaddress), #_nilable #_"Coin" (§ name ßamount), #_nilable #_"String" (§ name ßlabel), #_nilable #_"String" (§ name ßmessage)])
    (§ block
        (Preconditions/checkNotNull ßparams)
        (Preconditions/checkNotNull ßaddress)

        (§ if (and (some? ßamount) (< (.. ßamount (signum)) 0)))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" "Coin must be positive"))
        )

        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
        (§ block
            (§ let #_"String" (§ name ßscheme) (.. ßparams (getUriScheme)))
            (§ block
                (.. ßsb (append ßscheme) (append ":") (append ßaddress))

                (§ let #_"boolean" (§ name ßquestionMarkHasBeenOutput) false)
                (§ block

                    (§ if (some? ßamount))
                    (§ block
                        (.. ßsb (append QUESTION_MARK_SEPARATOR) (append FIELD_AMOUNT) (append "="))
                        (.. ßsb (append (.. ßamount (toPlainString))))
                        (§ ass (§ name ßquestionMarkHasBeenOutput) true)
                    )

                    (§ if (and (some? ßlabel) (not (.. "" (equals ßlabel)))))
                    (§ block
                        (§ if ßquestionMarkHasBeenOutput)
                        (§ block
                            (.. ßsb (append AMPERSAND_SEPARATOR))
                        )
                        (§ else )
                        (§ block
                            (.. ßsb (append QUESTION_MARK_SEPARATOR))
                            (§ ass (§ name ßquestionMarkHasBeenOutput) true)
                        )
                        (.. ßsb (append FIELD_LABEL) (append "=") (append (encodeURLString ßlabel)))
                    )

                    (§ if (and (some? ßmessage) (not (.. "" (equals ßmessage)))))
                    (§ block
                        (§ if ßquestionMarkHasBeenOutput)
                        (§ block
                            (.. ßsb (append AMPERSAND_SEPARATOR))
                        )
                        (§ else )
                        (§ block
                            (.. ßsb (append QUESTION_MARK_SEPARATOR))
                        )
                        (.. ßsb (append FIELD_MESSAGE) (append "=") (append (encodeURLString ßmessage)))
                    )

                    (.. ßsb (toString))
                )
            )
        )
    )

    ;;;
     ; Encode a string using URL encoding.
     ;
     ; @param stringToEncode The string to URL encode.
     ;;
    #_static
    (§ defn #_"String" (§ fn encodeURLString) [#_"String" (§ name ßstringToEncode)])
    (§ block
        (§ try )
        (§ block
            (§ return (.. (URLEncoder/encode ßstringToEncode, "UTF-8") (replace "+", ENCODED_SPACE_CHARACTER)))
        )
        (§ catch #_"UnsupportedEncodingException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Can't happen.
        )
    )
)

#_(ns org.bitcoinj.uri #_"BitcoinURIParseException")

;;;
 ; <p>Exception to provide the following to {@link BitcoinURI}:</p>
 ; <ul>
 ; <li>Provision of parsing error messages.</li>
 ; </ul>
 ; <p>This base exception acts as a general failure mode not attributable to a specific cause (other than
 ; that reported in the exception message).  Since this is in English, it may not be worth reporting directly
 ; to the user other than as part of a "general failure to parse" response.</p>
 ;;
#_public
(§ class #_"BitcoinURIParseException" (§ extends #_"Exception")
    #_public
    (§ constructor #_"BitcoinURIParseException" [#_"String" (§ name ßs)])
    (§ block
        (§ super ßs)
        this
    )

    #_public
    (§ constructor #_"BitcoinURIParseException" [#_"String" (§ name ßs), #_"Throwable" (§ name ßthrowable)])
    (§ block
        (§ super ßs, ßthrowable)
        this
    )
)

#_(ns org.bitcoinj.uri #_"OptionalFieldValidationException")

;;;
