;;;
 ; A {@link java.util.concurrent.ThreadFactory} that propagates a {@link org.bitcoinj.core.Context}
 ; from the creating thread into the new thread.  This factory creates daemon threads.
 ;;
#_public
(§ class #_"ContextPropagatingThreadFactory" (§ implements #_"ThreadFactory")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"ContextPropagatingThreadFactory")))

    #_private
    (§ field- #_"String" (§ name name))
    #_private
    (§ field- #_"int" (§ name priority))

    #_public
    (§ constructor #_"ContextPropagatingThreadFactory" [#_"String" (§ name ßname), #_"int" (§ name ßpriority)])
    (§ block
        (§ ass (§ name (.. this ßname)) ßname)
        (§ ass (§ name (.. this ßpriority)) ßpriority)
        this
    )

    #_public
    (§ constructor #_"ContextPropagatingThreadFactory" [#_"String" (§ name ßname)])
    (§ block
        (§ this ßname, Thread/NORM_PRIORITY)
        this
    )

    #_override
    #_public
    (§ method #_"Thread" (§ fn newThread) [#_"Runnable" (§ name ßr)])
    (§ block
        (§ let #_"Context" (§ name ßcontext) (Context/get))
        (§ block
            (§ let #_"Thread" (§ name ßthread) (§ new #_"Thread" (§ new #_"Runnable")
            (§ block
            (§ anon
                    #_override
                    #_public
                    (§ method #_"void" (§ fn run) [])
                    (§ block
                        (§ try )
                        (§ block
                            (Context/propagate ßcontext)
                            (.. ßr (run))
                        )
                        (§ catch #_"Exception" (§ name ße))
                        (§ block
                            (.. log (error "Exception in ßthread", ße))
                            (Throwables/propagate ße)
                        )
                        nil
                    )
                )
            ), name))
            (.. thread (setPriority priority))
            (.. thread (setDaemon true))
            (§ let #_"Thread.UncaughtExceptionHandler" (§ name ßhandler) Threading/uncaughtExceptionHandler)
            (§ block
                (§ if (some? ßhandler))
                (§ block
                    (.. thread (setUncaughtExceptionHandler ßhandler))
                )
                thread
            )
        )
    )
)

#_(ns org.bitcoinj.utils #_"DaemonThreadFactory"
