    #_public
    #_static
    (§ defn #_"Fiat" (§ fn valueOf) [#_"String" (§ name ßcurrencyCode), #_"long" (§ name ßvalue)])
    (§ block
        (§ new #_"Fiat" ßcurrencyCode, ßvalue)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn smallestUnitExponent) [])
    (§ block
        SMALLEST_UNIT_EXPONENT
