    ;;;
     ; Returns the number of "smallest units" of this monetary value.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getValue) [])
    (§ block
        value
    )

    #_public
    (§ method #_"String" (§ fn getCurrencyCode) [])
    (§ block
        currencyCode
    )

    ;;;
     ; Parses an amount expressed in the way humans are used to.
     ;
     ; This takes string in a format understood by {@link BigDecimal#BigDecimal(String)},
     ; for example "0", "1", "0.10", "1.23E3", "1234.5E-5".
     ;
     ; @throws IllegalArgumentException if you try to specify more than 4 digits after the comma, or a value out of range.
     ;;
    #_public
    #_static
    (§ defn #_"Fiat" (§ fn parseFiat) [#_"String" (§ name ßcurrencyCode), #_"String" (§ name ßstr)])
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" (§ name ßval) (.. (§ new #_"BigDecimal" ßstr) (movePointRight SMALLEST_UNIT_EXPONENT) (longValueExact)))
            (§ block
                (§ return (Fiat/valueOf ßcurrencyCode, ßval))
