        (§ new #_"Fiat" currencyCode, (LongMath/checkedAdd (.. this ßvalue), (.. ßvalue ßvalue)))
    )

    #_public
    (§ method #_"Fiat" (§ fn subtract) [#_"Fiat" (§ name ßvalue)])
    (§ block
        (Preconditions/checkArgument (.. ßvalue currencyCode (equals currencyCode)))

        (§ new #_"Fiat" currencyCode, (LongMath/checkedSubtract (.. this ßvalue), (.. ßvalue ßvalue)))
    )

    #_public
    (§ method #_"Fiat" (§ fn multiply) [#_"long" (§ name ßfactor)])
    (§ block
        (§ new #_"Fiat" currencyCode, (LongMath/checkedMultiply (.. this value), ßfactor))
    )

    #_public
    (§ method #_"Fiat" (§ fn divide) [#_"long" (§ name ßdivisor)])
    (§ block
        (§ new #_"Fiat" currencyCode, (/ (.. this value) ßdivisor))
    )

    #_public
    (§ method #_"Fiat[]" (§ fn divideAndRemainder) [#_"long" (§ name ßdivisor)])
    (§ block
        (§ new #_"Fiat[]" (§ coll (§ new #_"Fiat" currencyCode, (/ (.. this value) ßdivisor)), (§ new #_"Fiat" currencyCode, (% (.. this value) ßdivisor)) ))
    )

    #_public
    (§ method #_"long" (§ fn divide) [#_"Fiat" (§ name ßdivisor)])
    (§ block
        (Preconditions/checkArgument (.. ßdivisor currencyCode (equals currencyCode)))

        (/ (.. this value) (.. ßdivisor value))
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value greater than zero, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPositive) [])
    (§ block
        (== (signum) 1)
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value less than zero, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isNegative) [])
    (§ block
        (== (signum) -1)
    )

    ;;;
     ; Returns true if and only if this instance represents zero monetary value, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isZero) [])
    (§ block
        (== (signum) 0)
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is greater than that of the given other Fiat,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isGreaterThan) [#_"Fiat" (§ name ßother)])
    (§ block
        (> (compareTo ßother) 0)
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is less than that of the given other Fiat,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isLessThan) [#_"Fiat" (§ name ßother)])
    (§ block
        (< (compareTo ßother) 0)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn signum) [])
    (§ block
        (if (== (.. this value) 0) 0 (if (< (.. this value) 0) -1 1))
    )

    #_public
    (§ method #_"Fiat" (§ fn negate) [])
    (§ block
        (§ new #_"Fiat" currencyCode, (- (.. this value)))
    )

    ;;;
     ; Returns the number of "smallest units" of this monetary value.
     ; It's deprecated in favour of accessing {@link #value} directly.
     ;;
    #_public
    (§ method #_"long" (§ fn longValue) [])
    (§ block
        (.. this value)
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name FRIENDLY_FORMAT) (.. MonetaryFormat/FIAT (postfixCode)))

    ;;;
     ; Returns the value as a 0.12 type string.
     ; More digits after the decimal place will be used if necessary, but two will always be present.
     ;;
    #_public
    (§ method #_"String" (§ fn toFriendlyString) [])
    (§ block
        (.. FRIENDLY_FORMAT (code 0, currencyCode) (format this) (toString))
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name PLAIN_FORMAT) (.. MonetaryFormat/FIAT (minDecimals 0) (repeatOptionalDecimals 1, 4) (noCode)))

    ;;;
     ; Returns the value as a plain string.  The result is unformatted with no trailing zeroes.
     ; For instance, a value of 150000 "smallest units" gives an output string of "0.0015".
     ;;
    #_public
    (§ method #_"String" (§ fn toPlainString) [])
    (§ block
        (.. PLAIN_FORMAT (format this) (toString))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (Long/toString value)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== ßo this))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (.. ßo (getClass)) (getClass))))
        (§ block
            (§ return false)
        )
        (§ let #_"Fiat" (§ name ßother) (§ cast #_"Fiat" ßo))
        (§ block
            (and (== (.. this value) (.. ßother value)) (.. this currencyCode (equals (.. ßother currencyCode))))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode value, currencyCode)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn compareTo) [#_"Fiat" (§ name ßother)])
    (§ block
        (if (.. this currencyCode (equals (.. ßother currencyCode))) (Longs/compare (.. this value), (.. ßother value)) (.. this currencyCode (compareTo (.. ßother currencyCode))))
    )
)

#_(ns org.bitcoinj.utils #_"ListenerRegistration"
    (:import [java.util List]
             [java.util.concurrent Executor])
    (:import [com.google.common.base Preconditions]))

;;;
 ; A simple wrapper around a listener and an executor, with some utility methods.
 ;;
#_public
(§ class #_"ListenerRegistration<T>"
    #_public
    (§ field #_"T" (§ name listener))
    #_public
    (§ field #_"Executor" (§ name executor))

    #_public
    (§ constructor #_"ListenerRegistration" [#_"T" (§ name ßlistener), #_"Executor" (§ name ßexecutor)])
    (§ block
        (§ ass (§ name (.. this ßlistener)) (Preconditions/checkNotNull ßlistener))
        (§ ass (§ name (.. this ßexecutor)) (Preconditions/checkNotNull ßexecutor))
        this
    )

    ;;; Returns true if the listener was removed, else false. ;;
    #_public
    #_static
    (§ defn #_"<T> boolean" (§ fn removeFromList) [#_"T" (§ name ßlistener), #_"List<? extends ListenerRegistration<T>>" (§ name ßlist)])
    (§ block
        (Preconditions/checkNotNull ßlistener)

        (§ let #_"ListenerRegistration<T>" (§ name ßitem) nil)
        (§ block
            (§ for (§ var #_"ListenerRegistration<T>" (§ name ßregistration)) :for ßlist)
            (§ block
                (§ if (== (.. ßregistration ßlistener) ßlistener))
                (§ block
                    (§ ass (§ name ßitem) ßregistration)
                    (§ break )
                )
            )
            (and (some? ßitem) (.. ßlist (remove ßitem)))
        )
    )
)

#_(ns org.bitcoinj.utils #_"MonetaryFormat"
    (:import [java.math RoundingMode]
             [java.text DecimalFormatSymbols]
             [java.util ArrayList Arrays List Locale])
    (:import [com.google.common.base Preconditions]
             #_static [com.google.common.math.LongMath checkedMultiply]
             #_static [com.google.common.math.LongMath checkedPow]
             #_static [com.google.common.math.LongMath divide])
   (:require [org.bitcoinj.core Coin Monetary]))

;;;
 ; Utility for formatting and parsing coin values to and from human readable form.
 ;
 ; MonetaryFormat instances are immutable.  Invoking a configuration method has no effect on the receiving instance;
 ; you must store and use the new instance it returns, instead.  Instances are thread safe, so they may be stored safely
 ; as static constants.
 ;;
#_public
(§ class #_"MonetaryFormat"
    ;;; Standard format for the BTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name BTC) (.. (§ new #_"MonetaryFormat") (shift 0) (minDecimals 2) (repeatOptionalDecimals 2, 3)))
    ;;; Standard format for the mBTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name MBTC) (.. (§ new #_"MonetaryFormat") (shift 3) (minDecimals 2) (optionalDecimals 2)))
    ;;; Standard format for the µBTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name UBTC) (.. (§ new #_"MonetaryFormat") (shift 6) (minDecimals 0) (optionalDecimals 2)))
    ;;; Standard format for fiat amounts. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name FIAT) (.. (§ new #_"MonetaryFormat") (shift 0) (minDecimals 2) (repeatOptionalDecimals 2, 1)))
    ;;; Currency code for base 1 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name CODE_BTC) "BTC")
    ;;; Currency code for base 1/1000 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name CODE_MBTC) "mBTC")
    ;;; Currency code for base 1/1000000 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name CODE_UBTC) "µBTC")

    #_public
    #_static
    (§ def #_"int" (§ name MAX_DECIMALS) 8)

    #_private
    (§ field- #_"char" (§ name negativeSign))
    #_private
    (§ field- #_"char" (§ name positiveSign))
    #_private
    (§ field- #_"char" (§ name zeroDigit))
    #_private
    (§ field- #_"char" (§ name decimalMark))
    #_private
    (§ field- #_"int" (§ name minDecimals))
    #_private
    (§ field- #_"List<Integer>" (§ name decimalGroups))
    #_private
    (§ field- #_"int" (§ name shift))
    #_private
    (§ field- #_"RoundingMode" (§ name roundingMode))
    #_private
    (§ field- #_"String[]" (§ name codes))
    #_private
    (§ field- #_"char" (§ name codeSeparator))
    #_private
    (§ field- #_"boolean" (§ name codePrefixed))

    #_private
    #_static
    (§ def- #_"String" (§ name DECIMALS_PADDING) "0000000000000000") ;; a few more than necessary for Bitcoin

    ;;;
     ; Set character to prefix negative values.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn negativeSign) [#_"char" (§ name ßnegativeSign)])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit ßnegativeSign)))
        (Preconditions/checkArgument (< 0 ßnegativeSign))

        (if (== ßnegativeSign (.. this ßnegativeSign)) this (§ new #_"MonetaryFormat" ßnegativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character to prefix positive values.  A zero value means no sign is used in this case.
     ; For parsing, a missing sign will always be interpreted as if the positive sign was used.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn positiveSign) [#_"char" (§ name ßpositiveSign)])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit ßpositiveSign)))

        (if (== ßpositiveSign (.. this ßpositiveSign)) this (§ new #_"MonetaryFormat" negativeSign, ßpositiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character range to use for representing digits.  It starts with the specified character representing zero.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn digits) [#_"char" (§ name ßzeroDigit)])
    (§ block
        (if (== ßzeroDigit (.. this ßzeroDigit)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, ßzeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character to use as the decimal mark.  If the formatted value does not have any decimals,
     ; no decimal mark is used either.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn decimalMark) [#_"char" (§ name ßdecimalMark)])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit ßdecimalMark)))
        (Preconditions/checkArgument (< 0 ßdecimalMark))

        (if (== ßdecimalMark (.. this ßdecimalMark)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, ßdecimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set minimum number of decimals to use for formatting.  If the value precision exceeds all decimals specified
     ; (including additional decimals specified by {@link #optionalDecimals(int...)} or {@link #repeatOptionalDecimals(int, int)}),
     ; the value will be rounded.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn minDecimals) [#_"int" (§ name ßminDecimals)])
    (§ block
        (if (== ßminDecimals (.. this ßminDecimals)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, ßminDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set additional groups of decimals to use after the minimum decimals, if they are useful for expressing precision.
     ; Each value is a number of decimals in that group.  If the value precision exceeds all decimals specified
     ; (including minimum decimals), the value will be rounded.  This configuration is not relevant for parsing.
     ;
     ; For example, if you pass <tt>4,2</tt> it will add four decimals to your formatted string if needed, and then add
     ; another two decimals if needed.  At this point, rather than adding further decimals the value will be rounded.
     ;
     ; @param groups Any number numbers of decimals, one for each group.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn optionalDecimals) [#_"int..." (§ name ßgroups)])
    (§ block
        (§ let #_"List<Integer>" (§ name ßdecimalGroups) (§ new #_"ArrayList<>" (.. ßgroups length)))
        (§ block
            (§ for (§ var #_"int" (§ name ßgroup)) :for ßgroups)
            (§ block
                (.. ßdecimalGroups (add ßgroup))
            )
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, ßdecimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Set repeated additional groups of decimals to use after the minimum decimals, if they are useful for expressing
     ; precision.  If the value precision exceeds all decimals specified (including minimum decimals), the value will be
     ; rounded.  This configuration is not relevant for parsing.
     ;
     ; For example, if you pass <tt>1,8</tt> it will up to eight decimals to your formatted string if needed.
     ; After these have been used up, rather than adding further decimals the value will be rounded.
     ;
     ; @param decimals Value of the group to be repeated.
     ; @param repetitions Number of repetitions.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn repeatOptionalDecimals) [#_"int" (§ name ßdecimals), #_"int" (§ name ßrepetitions)])
    (§ block
        (Preconditions/checkArgument (<= 0 ßrepetitions))

        (§ let #_"List<Integer>" (§ name ßdecimalGroups) (§ new #_"ArrayList<>" ßrepetitions))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßrepetitions) :for (§ recur (+ ßi 1)))
            (§ block
                (.. ßdecimalGroups (add ßdecimals))
            )
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, ßdecimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Set number of digits to shift the decimal separator to the right, coming from the standard BTC notation that was
     ; common pre-2014.  Note this will change the currency code if enabled.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn shift) [#_"int" (§ name ßshift)])
    (§ block
        (if (== ßshift (.. this ßshift)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, ßshift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set rounding mode to use when it becomes necessary.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn roundingMode) [#_"RoundingMode" (§ name ßroundingMode)])
    (§ block
        (if (== ßroundingMode (.. this ßroundingMode)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, ßroundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Don't display currency code when formatting.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn noCode) [])
    (§ block
        (if (nil? codes) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, nil, codeSeparator, codePrefixed))
    )

    ;;;
     ; Configure currency code for given decimal separator shift.  This configuration is not relevant for parsing.
     ;
     ; @param codeShift Decimal separator shift, see {@link #shift}.
     ; @param code Currency code.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn code) [#_"int" (§ name ßcodeShift), #_"String" (§ name ßcode)])
    (§ block
        (Preconditions/checkArgument (<= 0 ßcodeShift))

        (§ let #_"String[]" (§ name ßcodes) (if (some? (.. this codes)) (Arrays/copyOf (.. this codes), (.. this codes length)) (§ new #_"String[]" (§ count MAX_DECIMALS))))
        (§ block

            (§ ass (§ name (§ ai ßcodes ßcodeShift)) ßcode)
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, ßcodes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Separator between currency code and formatted value.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn codeSeparator) [#_"char" (§ name ßcodeSeparator)])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit ßcodeSeparator)))
        (Preconditions/checkArgument (< 0 ßcodeSeparator))

        (if (== ßcodeSeparator (.. this ßcodeSeparator)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, ßcodeSeparator, codePrefixed))
    )

    ;;;
     ; Prefix formatted output by currency code.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn prefixCode) [])
    (§ block
        (if codePrefixed this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, true))
    )

    ;;;
     ; Postfix formatted output with currency code.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn postfixCode) [])
    (§ block
        (if (not codePrefixed) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, false))
    )

    ;;;
     ; Configure this instance with values from a {@link Locale}.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn withLocale) [#_"Locale" (§ name ßlocale)])
    (§ block
        (§ let #_"DecimalFormatSymbols" (§ name ßdfs) (§ new #_"DecimalFormatSymbols" ßlocale))
        (§ block
            (§ let #_"char" (§ name ßnegativeSign) (.. ßdfs (getMinusSign)))
            (§ block
                (§ let #_"char" (§ name ßzeroDigit) (.. ßdfs (getZeroDigit)))
                (§ block
                    (§ let #_"char" (§ name ßdecimalMark) (.. ßdfs (getMonetaryDecimalSeparator)))
                    (§ block
                        (§ new #_"MonetaryFormat" ßnegativeSign, positiveSign, ßzeroDigit, ßdecimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
                    )
                )
            )
        )
    )

    #_public
    (§ constructor #_"MonetaryFormat" [])
    (§ block
        ;; defaults
        (§ ass (§ name (.. this negativeSign)) \-)
        (§ ass (§ name (.. this positiveSign)) 0) ;; none
        (§ ass (§ name (.. this zeroDigit)) \0)
        (§ ass (§ name (.. this decimalMark)) \.)
        (§ ass (§ name (.. this minDecimals)) 2)
        (§ ass (§ name (.. this decimalGroups)) nil)
        (§ ass (§ name (.. this shift)) 0)
        (§ ass (§ name (.. this roundingMode)) RoundingMode/HALF_UP)
        (§ ass (§ name (.. this codes)) (§ new #_"String[]" (§ count MAX_DECIMALS)))
        (§ ass (§ name this.codes[0]) CODE_BTC)
        (§ ass (§ name this.codes[3]) CODE_MBTC)
        (§ ass (§ name this.codes[6]) CODE_UBTC)
        (§ ass (§ name (.. this codeSeparator)) \space)
        (§ ass (§ name (.. this codePrefixed)) true)
        this
    )

    #_private
    (§ constructor- #_"MonetaryFormat" [#_"char" (§ name ßnegativeSign), #_"char" (§ name ßpositiveSign), #_"char" (§ name ßzeroDigit), #_"char" (§ name ßdecimalMark), #_"int" (§ name ßminDecimals), #_"List<Integer>" (§ name ßdecimalGroups), #_"int" (§ name ßshift), #_"RoundingMode" (§ name ßroundingMode), #_"String[]" (§ name ßcodes), #_"char" (§ name ßcodeSeparator), #_"boolean" (§ name ßcodePrefixed)])
    (§ block
        (§ ass (§ name (.. this ßnegativeSign)) ßnegativeSign)
        (§ ass (§ name (.. this ßpositiveSign)) ßpositiveSign)
        (§ ass (§ name (.. this ßzeroDigit)) ßzeroDigit)
        (§ ass (§ name (.. this ßdecimalMark)) ßdecimalMark)
        (§ ass (§ name (.. this ßminDecimals)) ßminDecimals)
        (§ ass (§ name (.. this ßdecimalGroups)) ßdecimalGroups)
        (§ ass (§ name (.. this ßshift)) ßshift)
        (§ ass (§ name (.. this ßroundingMode)) ßroundingMode)
        (§ ass (§ name (.. this ßcodes)) ßcodes)
        (§ ass (§ name (.. this ßcodeSeparator)) ßcodeSeparator)
        (§ ass (§ name (.. this ßcodePrefixed)) ßcodePrefixed)
        this
    )

    ;;;
     ; Format the given monetary value to a human readable form.
     ;;
    #_public
    (§ method #_"CharSequence" (§ fn format) [#_"Monetary" (§ name ßmonetary)])
    (§ block
        ;; preparation
        (§ let #_"int" (§ name ßmaxDecimals) minDecimals)
        (§ block
            (§ if (some? decimalGroups))
            (§ block
                (§ for (§ var #_"int" (§ name ßgroup)) :for decimalGroups)
                (§ block
                    (§ ass (§ name ßmaxDecimals) (+ ßmaxDecimals ßgroup))
                )
            )
            (§ let #_"int" (§ name ßsmallestUnitExponent) (.. ßmonetary (smallestUnitExponent)))
            (§ block
                (Preconditions/checkState (<= ßmaxDecimals ßsmallestUnitExponent), "The maximum possible number of decimals (%s) cannot exceed %s.", ßmaxDecimals, ßsmallestUnitExponent)

                ;; rounding
                (§ let #_"long" (§ name ßsatoshis) (Math/abs (.. ßmonetary (getValue))))
                (§ block
                    (§ let #_"long" (§ name ßprecisionDivisor) (checkedPow 10, (- ßsmallestUnitExponent shift ßmaxDecimals)))
                    (§ block
                        (§ ass (§ name ßsatoshis) (checkedMultiply (divide ßsatoshis, ßprecisionDivisor, roundingMode), ßprecisionDivisor))

                        ;; shifting
                        (§ let #_"long" (§ name ßshiftDivisor) (checkedPow 10, (- ßsmallestUnitExponent shift)))
                        (§ block
                            (§ let #_"long" (§ name ßnumbers) (/ ßsatoshis ßshiftDivisor))
                            (§ block
                                (§ let #_"long" (§ name ßdecimals) (% ßsatoshis ßshiftDivisor))
                                (§ block

                                    ;; formatting
                                    (§ let #_"String" (§ name ßdecimalsStr) (String/format Locale/US, (str "%0" (- ßsmallestUnitExponent shift) "d"), ßdecimals))
                                    (§ block
                                        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder" ßdecimalsStr))
                                        (§ block
                                            (§ while (and (< minDecimals (.. ßsb (length))) (== (.. ßsb (charAt (- (.. ßsb (length)) 1))) \0)))
                                            (§ block
                                                (.. ßsb (setLength (- (.. ßsb (length)) 1))) ;; trim trailing zero
                                            )
                                            (§ let #_"int" (§ name ßi) minDecimals)
                                            (§ block
                                                (§ if (some? decimalGroups))
                                                (§ block
                                                    (§ for (§ var #_"int" (§ name ßgroup)) :for decimalGroups)
                                                    (§ block
                                                        (§ if (and (< ßi (.. ßsb (length))) (< (.. ßsb (length)) (+ ßi ßgroup))))
                                                        (§ block
                                                            (§ while (< (.. ßsb (length)) (+ ßi ßgroup)))
                                                            (§ block
                                                                (.. ßsb (append "0"))
                                                            )
                                                            (§ break )
                                                        )
                                                        (§ ass (§ name ßi) (+ ßi ßgroup))
                                                    )
                                                )
                                                (§ if (< 0 (.. ßsb (length))))
                                                (§ block
                                                    (.. ßsb (insert 0, decimalMark))
                                                )
                                                (.. ßsb (insert 0, ßnumbers))
                                                (§ if (< (.. ßmonetary (getValue)) 0))
                                                (§ block
                                                    (.. ßsb (insert 0, negativeSign))
                                                )
                                                (§ elseif (!= positiveSign 0))
                                                (§ block
                                                    (.. ßsb (insert 0, positiveSign))
                                                )
                                                (§ if (some? codes))
                                                (§ block
                                                    (§ if codePrefixed)
                                                    (§ block
                                                        (.. ßsb (insert 0, codeSeparator) (insert 0, (code)))
                                                    )
                                                    (§ else )
                                                    (§ block
                                                        (.. ßsb (append codeSeparator) (append (code)))
                                                    )
                                                )

                                                ;; Convert to non-arabic digits.
                                                (§ if (!= zeroDigit \0))
                                                (§ block
                                                    (§ let #_"int" (§ name ßoffset) (- zeroDigit \0))
                                                    (§ block
                                                        (§ for (§ var #_"int" (§ name ßd) 0) :for (< ßd (.. ßsb (length))) :for (§ recur (+ ßd 1)))
                                                        (§ block
                                                            (§ let #_"char" (§ name ßc) (.. ßsb (charAt ßd)))
                                                            (§ block
                                                                (§ if (Character/isDigit ßc))
                                                                (§ block
                                                                    (.. ßsb (setCharAt ßd, (§ cast #_"char" (+ ßc ßoffset))))
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                                ßsb
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Parse a human readable coin value to a {@link org.bitcoinj.core.Coin} instance.
     ;
     ; @throws NumberFormatException if the string cannot be parsed for some reason.
     ;;
    #_public
    (§ method #_"Coin" (§ fn parse) [#_"String" (§ name ßstr)])
        (§ throws #_"NumberFormatException")
    (§ block
        (Coin/valueOf (parseValue ßstr, Coin/SMALLEST_UNIT_EXPONENT))
    )

    ;;;
     ; Parse a human readable fiat value to a {@link org.bitcoinj.utils.Fiat} instance.
     ;
     ; @throws NumberFormatException if the string cannot be parsed for some reason.
     ;;
    #_public
    (§ method #_"Fiat" (§ fn parseFiat) [#_"String" (§ name ßcurrencyCode), #_"String" (§ name ßstr)])
        (§ throws #_"NumberFormatException")
    (§ block
        (Fiat/valueOf ßcurrencyCode, (parseValue ßstr, Fiat/SMALLEST_UNIT_EXPONENT))
    )

    #_private
    (§ method- #_"long" (§ fn parseValue) [#_"String" (§ name ßstr), #_"int" (§ name ßsmallestUnitExponent)])
    (§ block
        (Preconditions/checkState (<= ßsmallestUnitExponent (.. DECIMALS_PADDING (length))))

        (§ if (.. ßstr (isEmpty)))
        (§ block
            (§ throw (§ new #_"NumberFormatException" "empty string"))
        )

        (§ let #_"char" (§ name ßfirst) (.. ßstr (charAt 0)))
        (§ block
            (§ if (or (== ßfirst negativeSign) (== ßfirst positiveSign)))
            (§ block
                (§ ass (§ name ßstr) (.. ßstr (substring 1)))
            )
            (§ let #_"String" (§ name ßnumbers))
            (§ block
                (§ let #_"String" (§ name ßdecimals))
                (§ block
                    (§ let #_"int" (§ name ßdecimalMarkIndex) (.. ßstr (indexOf decimalMark)))
                    (§ block
                        (§ if (!= ßdecimalMarkIndex -1))
                        (§ block
                            (§ ass (§ name ßnumbers) (.. ßstr (substring 0, ßdecimalMarkIndex)))
                            (§ ass (§ name ßdecimals) (.. (str ßstr DECIMALS_PADDING) (substring (+ ßdecimalMarkIndex 1))))
                            (§ if (!= (.. ßdecimals (indexOf decimalMark)) -1))
                            (§ block
                                (§ throw (§ new #_"NumberFormatException" "more than one decimal mark"))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass (§ name ßnumbers) ßstr)
                            (§ ass (§ name ßdecimals) DECIMALS_PADDING)
                        )

                        (§ let #_"String" (§ name ßsatoshis) (+ ßnumbers (.. ßdecimals (substring 0, (- ßsmallestUnitExponent shift)))))
                        (§ block
                            (§ for (§ var #_"char" (§ name ßc)) :for (.. ßsatoshis (toCharArray)))
                            (§ block
                                (§ if (not (Character/isDigit ßc)))
                                (§ block
                                    (§ throw (§ new #_"NumberFormatException" (str "illegal character: " ßc)))
                                )
                            )

                            ;; Non-arabic digits allowed here.
                            (§ let #_"long" (§ name ßvalue) (Long/parseLong ßsatoshis))
                            (§ block
                                (§ if (== ßfirst negativeSign))
                                (§ block
                                    (§ ass (§ name ßvalue) (- ßvalue))
                                )
                                ßvalue
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Get currency code that will be used for current shift.
     ;;
    #_public
    (§ method #_"String" (§ fn code) [])
    (§ block
        (§ if (nil? codes))
        (§ block
            (§ return nil)
        )
        (§ if (nil? (§ ai codes shift)))
        (§ block
            (§ throw (§ new #_"NumberFormatException" (str "missing code for shift: " shift)))
        )
        (§ ai codes shift)
    )
)

#_(ns org.bitcoinj.utils #_"Threading"
    (:import [java.util.concurrent *]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.util.concurrent CycleDetectingLockFactory ListeningExecutorService MoreExecutors Uninterruptibles]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]))

;;;
 ; Various threading related utilities.  Provides a wrapper around explicit lock creation that lets you control whether
 ; bitcoinj performs cycle detection or not.  Cycle detection is useful to detect bugs but comes with a small cost.
 ; Also provides a worker thread that is designed for event listeners to be dispatched on.
 ;;
#_public
(§ class #_"Threading"
    ;;;
     ; An executor with one thread that is intended for running event listeners on.  This ensures all event listener
     ; code runs without any locks being held.  It's intended for the API user to run things on.  Callbacks registered
     ; by bitcoinj internally shouldn't normally run here, although currently there are a few exceptions.
     ;;
    #_public
    #_static
    (§ def #_"Executor" (§ name USER_THREAD))

    ;;;
     ; A dummy executor that just invokes the runnable immediately.  Use this over
     ; {@link com.google.common.util.concurrent.MoreExecutors#sameThreadExecutor()} because the latter creates
     ; a new object each time in order to implement the more complex {@link ExecutorService} interface, which is
     ; overkill for our needs.
     ;;
    #_public
    #_static
    (§ def #_"Executor" (§ name SAME_THREAD))

    ;;;
     ; Put a dummy task into the queue and wait for it to be run.  Because it's single threaded, this means all
     ; tasks submitted before this point are now completed.  Usually you won't want to use this method - it's a
     ; convenience primarily used in unit testing.  If you want to wait for an event to be called the right thing
     ; to do is usually to create a {@link com.google.common.util.concurrent.SettableFuture} and then call set
     ; on it.  You can then either block on that future, compose it, add listeners to it and so on.
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn waitForUserCode) [])
    (§ block
        (§ let #_"CountDownLatch" (§ name ßlatch) (§ new #_"CountDownLatch" 1))
        (§ block
            (.. USER_THREAD (execute (§ new #_"Runnable")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (.. ßlatch (countDown))
                    nil
                )
            )))
            (Uninterruptibles/awaitUninterruptibly ßlatch)
            nil
        )
    )

    ;;;
     ; An exception handler that will be invoked for any exceptions that occur in the user thread, and any unhandled
     ; exceptions that are caught whilst the framework is processing network traffic or doing other background tasks.
     ; The purpose of this is to allow you to report back unanticipated crashes from your users to a central collection
     ; center for analysis and debugging.  You should configure this <b>before</b> any bitcoinj library code is run,
     ; setting it after you started network traffic and other forms of processing may result in the change not taking effect.
     ;;
    #_nilable
    #_public
    #_static
    #_volatile
    (§ def #_"Thread.UncaughtExceptionHandler" (§ name uncaughtExceptionHandler))

    #_public
    #_static
    (§ class #_"UserThread" (§ extends #_"Thread") (§ implements #_"Executor")
        #_private
        #_static
        (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"UserThread")))

        ;; 10,000 pending tasks is entirely arbitrary and may or may not be appropriate for the device we're running on.
        #_public
        #_static
        (§ def #_"int" (§ name WARNING_THRESHOLD) 10000)

        #_private
        (§ field- #_"LinkedBlockingQueue<Runnable>" (§ name tasks))

        #_public
        (§ constructor #_"UserThread" [])
        (§ block
            (§ super "bitcoinj user thread")

            (setDaemon true)
            (§ ass (§ name tasks) (§ new #_"LinkedBlockingQueue<>"))
            (start)
            this
        )

        #_suppress(§ opt "InfiniteLoopStatement")
        #_override
        #_public
        (§ method #_"void" (§ fn run) [])
        (§ block
            (§ while true)
            (§ block
                (§ let #_"Runnable" (§ name ßtask) (Uninterruptibles/takeUninterruptibly tasks))
                (§ block
                    (§ try )
                    (§ block
                        (.. ßtask (run))
                    )
                    (§ catch #_"Throwable" (§ name ßt))
                    (§ block
                        (.. log (warn "Exception in user thread", ßt))
                        (§ let #_"Thread.UncaughtExceptionHandler" (§ name ßhandler) uncaughtExceptionHandler)
                        (§ block
                            (§ if (some? ßhandler))
                            (§ block
                                (.. ßhandler (uncaughtException this, ßt))
                            )
                        )
                    )
                )
            )
            nil
        )

        #_override
        #_public
        (§ method #_"void" (§ fn execute) [#_"Runnable" (§ name ßcommand)])
        (§ block
            (§ let #_"int" (§ name ßsize) (.. tasks (size)))
            (§ block
                (§ if (== ßsize WARNING_THRESHOLD))
                (§ block
                    (.. log (warn (str "User thread has {} pending tasks, memory exhaustion may occur.\n" "If you see this message, check your memory consumption and see if it's problematic or excessively spikey.\n" "If it is, check for deadlocked or slow event handlers. If it isn't, try adjusting the constant \n" "Threading.UserThread.WARNING_THRESHOLD upwards until it's a suitable level for your app, or Integer.MAX_VALUE to disable."), ßsize))
                )
                (Uninterruptibles/putUninterruptibly tasks, ßcommand)
                nil
            )
        )
    )

    #_static
    (§ block
        ;; Default policy goes here.  If you want to change this, use one of the static methods before instantiating
        ;; any bitcoinj objects.  The policy change will take effect only on new objects from that point onwards.
        (throwOnLockCycles)

        (§ ass (§ name USER_THREAD) (§ new #_"UserThread"))
        (§ ass (§ name SAME_THREAD) (§ new #_"Executor"
        (§ anon
            #_override
            #_public
            (§ method #_"void" (§ fn execute) [#_non-nil #_"Runnable" (§ name ßrunnable)])
            (§ block
                (.. ßrunnable (run))
                nil
            )
        )))
    )

    #_private
    #_static
    (§ def- #_"CycleDetectingLockFactory.Policy" (§ name policy))
    #_public
    #_static
    (§ def #_"CycleDetectingLockFactory" (§ name factory))

    #_public
    #_static
    (§ defn #_"ReentrantLock" (§ fn lock) [#_"String" (§ name ßname)])
    (§ block
        (if (Utils/isAndroidRuntime) (§ new #_"ReentrantLock" true) (.. factory (newReentrantLock ßname)))
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn warnOnLockCycles) [])
    (§ block
        (setPolicy CycleDetectingLockFactory/Policies/WARN)
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn throwOnLockCycles) [])
    (§ block
        (setPolicy CycleDetectingLockFactory/Policies/THROW)
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn ignoreLockCycles) [])
    (§ block
        (setPolicy CycleDetectingLockFactory/Policies/DISABLED)
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn setPolicy) [#_"CycleDetectingLockFactory.Policy" (§ name ßpolicy)])
    (§ block
        (§ ass (§ name (.. Threading ßpolicy)) ßpolicy)
        (§ ass (§ name factory) (CycleDetectingLockFactory/newInstance ßpolicy))
        nil
    )

    #_public
    #_static
    (§ defn #_"CycleDetectingLockFactory.Policy" (§ fn getPolicy) [])
    (§ block
        policy
    )

    ;;; A caching thread pool that creates daemon threads, which won't keep the JVM alive waiting for more work. ;;
    #_public
    #_static
    (§ def #_"ListeningExecutorService" (§ name THREAD_POOL) (MoreExecutors/listeningDecorator (Executors/newCachedThreadPool (§ new #_"ThreadFactory")
        (§ anon
            #_override
            #_public
            (§ method #_"Thread" (§ fn newThread) [#_"Runnable" (§ name ßr)])
            (§ block
                (§ let #_"Thread" (§ name ßt) (§ new #_"Thread" ßr))
                (§ block
                    (.. ßt (setName "Threading.THREAD_POOL worker"))
                    (.. ßt (setDaemon true))
                    ßt
                )
            )
        ))))
)

#_(ns org.bitcoinj.utils #_"VersionTally"
    (:import [java.util Stack])
   (:require [org.bitcoinj.core NetworkParameters StoredBlock]
             [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Caching counter for the block versions within a moving window.  This class is NOT thread safe
 ; (as if two threads are trying to use it concurrently, there's risk of getting versions out of sequence).
 ;
 ; @see org.bitcoinj.core.NetworkParameters#getMajorityWindow()
 ; @see org.bitcoinj.core.NetworkParameters#getMajorityEnforceBlockUpgrade()
 ; @see org.bitcoinj.core.NetworkParameters#getMajorityRejectBlockOutdated()
 ;;
#_public
(§ class #_"VersionTally"
    ;;;
     ; Cache of version numbers.
     ;;
    #_private
    (§ field- #_"long[]" (§ name versionWindow))

    ;;;
     ; Offset within the version window at which the next version will be written.
     ;;
    #_private
    (§ field- #_"int" (§ name versionWriteHead) 0)

    ;;;
     ; Number of versions written into the tally.  Until this matches the length
     ; of the version window, we do not have sufficient data to return values.
     ;;
    #_private
    (§ field- #_"int" (§ name versionsStored) 0)

    #_public
    (§ constructor #_"VersionTally" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ ass (§ name versionWindow) (§ new #_"long[]" (§ count (.. ßparams (getMajorityWindow)))))
        this
    )

    ;;;
     ; Add a new block version to the tally, and return the count for that version within the window.
     ;
     ; @param version The block version to add.
     ;;
    #_public
    (§ method #_"void" (§ fn add) [#_"long" (§ name ßversion)])
    (§ block
        (§ ass (§ name (§ ai versionWindow versionWriteHead)) ßversion)
        (§ ass (§ name versionWriteHead) (+ versionWriteHead 1))
        (§ if (== versionWriteHead (.. versionWindow length)))
        (§ block
            (§ ass (§ name versionWriteHead) 0)
        )
        (§ ass (§ name versionsStored) (+ versionsStored 1))
        nil
    )

    ;;;
     ; Get the count of blocks at or above the given version, within the window.
     ;
     ; @param version The block version to query.
     ; @return the count for the block version, or null if the window is not yet full.
     ;;
    #_public
    (§ method #_"Integer" (§ fn getCountAtOrAbove) [#_"long" (§ name ßversion)])
    (§ block
        (§ if (< versionsStored (.. versionWindow length)))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" (§ name ßcount) 0)
        (§ block
            (§ for (§ var #_"int" (§ name ßversionIdx) 0) :for (< ßversionIdx (.. versionWindow length)) :for (§ recur (+ ßversionIdx 1)))
            (§ block
                (§ if (<= ßversion (§ ai versionWindow ßversionIdx)))
                (§ block
                    (§ ass (§ name ßcount) (+ ßcount 1))
                )
            )

            ßcount
        )
    )

    ;;;
     ; Initialize the version tally from the block store.  Note this does not search backwards past
     ; the start of the block store, so if starting from a checkpoint this may not fill the window.
     ;
     ; @param blockStore Block store to load blocks from.
     ; @param chainHead Current chain tip.
     ;;
    #_public
    (§ method #_"void" (§ fn initialize) [#_"BlockStore" (§ name ßblockStore), #_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"StoredBlock" (§ name ßversionBlock) ßchainHead)
        (§ block
            (§ let #_"Stack<Long>" (§ name ßversions) (§ new #_"Stack<>"))
            (§ block

                ;; We don't know how many blocks back we can go, so load what we can first.
                (.. ßversions (push (.. ßversionBlock (getHeader) (getVersion))))
                (§ for (§ var #_"int" (§ name ßheadOffset) 0) :for (< ßheadOffset (.. versionWindow length)) :for (§ recur (+ ßheadOffset 1)))
                (§ block
                    (§ ass (§ name ßversionBlock) (.. ßversionBlock (getPrev ßblockStore)))
                    (§ if (nil? ßversionBlock))
                    (§ block
                        (§ break )
                    )
                    (.. ßversions (push (.. ßversionBlock (getHeader) (getVersion))))
                )

                ;; Replay the versions into the tally.
                (§ while (not (.. ßversions (isEmpty))))
                (§ block
                    (add (.. ßversions (pop)))
                )
                nil
            )
        )
    )

    ;;;
     ; Get the size of the version window.
     ;;
    #_public
    (§ method #_"int" (§ fn size) [])
    (§ block
        (.. versionWindow length)
    )
)

