    )
)

#_(ns org.bitcoinj.wallet #_"BasicKeyChain"
    (:import [java.util *]
             [java.util.concurrent CopyOnWriteArrayList Executor]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect ImmutableList Lists]
             [com.google.protobuf ByteString]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core BloomFilter ECKey]
             [org.bitcoinj.crypto *]
             [org.bitcoinj.utils ListenerRegistration Threading]
             [org.bitcoinj.wallet.listeners KeyChainEventListener]))

;;;
 ; A {@link KeyChain} that implements the simplest model possible: it can have keys imported into it, and just
 ; acts as a dumb bag of keys.  It will, left to its own devices, always return the same key for usage by the wallet,
 ; although it will automatically add one to itself if it's empty or if encryption is requested.
 ;;
#_public
(§ class #_"BasicKeyChain" (§ implements #_"EncryptableKeyChain")
    #_private
    (§ field- #_"ReentrantLock" (§ name lock) (Threading/lock "BasicKeyChain"))

    ;; Maps used to let us quickly look up a key given data we find in transcations or the block chain.
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" (§ name hashToKeys))
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" (§ name pubkeyToKeys))
    #_nilable
    #_private
    (§ field- #_"KeyCrypter" (§ name keyCrypter))
    #_private
    (§ field- #_"boolean" (§ name isWatching))

    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<KeyChainEventListener>>" (§ name listeners))

    #_public
    (§ constructor #_"BasicKeyChain" [])
    (§ block
        (§ this nil)
        this
    )

    #_public
    (§ constructor #_"BasicKeyChain" [#_nilable #_"KeyCrypter" (§ name ßcrypter)])
    (§ block
        (§ ass (§ name (.. this keyCrypter)) ßcrypter)
        (§ ass (§ name hashToKeys) (§ new #_"LinkedHashMap<>"))
        (§ ass (§ name pubkeyToKeys) (§ new #_"LinkedHashMap<>"))
        (§ ass (§ name listeners) (§ new #_"CopyOnWriteArrayList<>"))
        this
    )

    ;;; Returns the {@link KeyCrypter} in use or null if the key chain is not encrypted. ;;
    #_override
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return keyCrypter)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"ECKey" (§ fn getKey) [#_nilable #_"KeyPurpose" (§ name ßignored)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (.. hashToKeys (isEmpty)))
            (§ block
                ;; We will refuse to encrypt an empty key chain.
                (Preconditions/checkState (nil? keyCrypter))

                (§ let #_"ECKey" (§ name ßkey) (§ new #_"ECKey"))
                (§ block
                    (importKeyLocked ßkey)
                    (queueOnKeysAdded (ImmutableList/of ßkey))
                )
            )
            (§ return (.. hashToKeys (values) (iterator) (next)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"List<ECKey>" (§ fn getKeys) [#_nilable #_"KeyPurpose" (§ name ßpurpose), #_"int" (§ name ßnumberOfKeys)])
    (§ block
        (Preconditions/checkArgument (< 0 ßnumberOfKeys))

        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (< (.. hashToKeys (size)) ßnumberOfKeys))
            (§ block
                (Preconditions/checkState (nil? keyCrypter))

                (§ let #_"List<ECKey>" (§ name ßkeys) (§ new #_"ArrayList<>"))
                (§ block
                    (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (- ßnumberOfKeys (.. hashToKeys (size)))) :for (§ recur (+ ßi 1)))
                    (§ block
                        (.. ßkeys (add (§ new #_"ECKey")))
                    )

                    (§ let #_"ImmutableList<ECKey>" (§ name ßimmutableKeys) (ImmutableList/copyOf ßkeys))
                    (§ block
                        (importKeysLocked ßimmutableKeys)
                        (queueOnKeysAdded ßimmutableKeys)
                    )
                )
            )

            (§ let #_"List<ECKey>" (§ name ßkeysToReturn) (§ new #_"ArrayList<>"))
            (§ block
                (§ let #_"int" (§ name ßcount) 0)
                (§ block
                    (§ while (and (.. hashToKeys (values) (iterator) (hasNext)) (!= ßnumberOfKeys ßcount)))
                    (§ block
                        (.. ßkeysToReturn (add (.. hashToKeys (values) (iterator) (next))))
                        (§ ass (§ name ßcount) (+ ßcount 1))
                    )
                    (§ return ßkeysToReturn)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;; Returns a copy of the list of keys that this chain is managing. ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getKeys) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (§ new #_"ArrayList<>" (.. hashToKeys (values))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"int" (§ fn importKeys) [#_"ECKey..." (§ name ßkeys)])
    (§ block
        (importKeys (ImmutableList/copyOf ßkeys))
    )

    #_public
    (§ method #_"int" (§ fn importKeys) [#_"List<? extends ECKey>" (§ name ßkeys)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; Check that if we're encrypted, the keys are all encrypted, and if we're not, that none are.
            ;; We are NOT checking that the actual password matches here because we don't have access to the
            ;; password at this point: if you screw up and import keys with mismatched passwords, you lose!
            ;; So make sure the password is checked first.
            (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßkeys)
            (§ block
                (checkKeyEncryptionStateMatches ßkey)
            )

            (§ let #_"List<ECKey>" (§ name ßactuallyAdded) (§ new #_"ArrayList<>" (.. ßkeys (size))))
            (§ block
                (§ for (§ var ECKey key) :for ßkeys)
                (§ block
                    (§ if (not (hasKey key)))
                    (§ block
                        (.. ßactuallyAdded (add key))
                        (importKeyLocked key)
                    )
                )
                (§ if (< 0 (.. ßactuallyAdded (size))))
                (§ block
                    (queueOnKeysAdded ßactuallyAdded)
                )
                (§ return (.. ßactuallyAdded (size)))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkKeyEncryptionStateMatches) [#_"ECKey" (§ name ßkey)])
    (§ block
        (§ if (and (nil? keyCrypter) (.. ßkey (isEncrypted))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key is encrypted but chain is not"))
        )
        (§ if (and (some? keyCrypter) (not (.. ßkey (isEncrypted)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key is not encrypted but chain is"))
        )
        (§ if (and (some? keyCrypter) (some? (.. ßkey (getKeyCrypter))) (not (.. ßkey (getKeyCrypter) (equals keyCrypter)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key encrypted under different parameters to chain"))
        )
        nil
    )

    #_private
    (§ method- #_"void" (§ fn importKeyLocked) [#_"ECKey" (§ name ßkey)])
    (§ block
        (§ if (.. hashToKeys (isEmpty)))
        (§ block
            (§ ass (§ name isWatching) (.. ßkey (isWatching)))
        )
        (§ else )
        (§ block
            (§ if (and (.. ßkey (isWatching)) (not isWatching)))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" "Key is watching but chain is not"))
            )
            (§ if (and (not (.. ßkey (isWatching))) isWatching))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" "Key is not watching but chain is"))
            )
        )
        (§ let #_"ECKey" (§ name ßpreviousKey) (.. pubkeyToKeys (put (ByteString/copyFrom (.. ßkey (getPubKey))), ßkey)))
        (§ block
            (.. hashToKeys (put (ByteString/copyFrom (.. ßkey (getPubKeyHash))), ßkey))
            (Preconditions/checkState (nil? ßpreviousKey))
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn importKeysLocked) [#_"List<ECKey>" (§ name ßkeys)])
    (§ block
        (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßkeys)
        (§ block
            (importKeyLocked ßkey)
        )
        nil
    )

    ;;;
     ; Imports a key to the key chain.  If key is present in the key chain, ignore it.
     ;;
    #_public
    (§ method #_"void" (§ fn importKey) [#_"ECKey" (§ name ßkey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (checkKeyEncryptionStateMatches ßkey)
            (§ if (hasKey ßkey))
            (§ block
                (§ return nil)
            )

            (importKeyLocked ßkey)
            (queueOnKeysAdded (ImmutableList/of ßkey))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" (§ name ßpubkeyHash)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (.. hashToKeys (get (ByteString/copyFrom ßpubkeyHash))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubKey) [#_"byte[]" (§ name ßpubkey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (.. pubkeyToKeys (get (ByteString/copyFrom ßpubkey))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn hasKey) [#_"ECKey" (§ name ßkey)])
    (§ block
        (!= (findKeyFromPubKey (.. ßkey (getPubKey))) nil)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn numKeys) [])
    (§ block
        (.. pubkeyToKeys (size))
    )

    ;;; Whether this basic key chain is empty, full of regular (usable for signing) keys, or full of watching keys. ;;
    #_public
    (§ enum #_"State"
        (§ item EMPTY)
        (§ item WATCHING)
        (§ item REGULAR)
    )

    ;;;
     ; Returns whether this chain consists of pubkey only (watching) keys, regular keys (usable for signing),
     ; or has no keys in it yet at all (thus we cannot tell).
     ;;
    #_public
    (§ method #_"State" (§ fn isWatching) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (if (.. hashToKeys (isEmpty)) State/EMPTY (if isWatching State/WATCHING State/REGULAR)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Removes the given key from the keychain.  Be very careful with this - losing a private key
     ; <b>destroys the money associated with it</b>.
     ; @return whether the key was removed or not.
     ;;
    #_public
    (§ method #_"boolean" (§ fn removeKey) [#_"ECKey" (§ name ßkey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" (§ name ßa) (!= (.. hashToKeys (remove (ByteString/copyFrom (.. ßkey (getPubKeyHash))))) nil))
            (§ block
                (§ let #_"boolean" (§ name ßb) (!= (.. pubkeyToKeys (remove (ByteString/copyFrom (.. ßkey (getPubKey))))) nil))
                (§ block
                    (Preconditions/checkState (== ßa ßb)) ;; Should be in both maps or neither.
                    (§ return ßa)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"long" (§ fn getEarliestKeyCreationTime) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"long" (§ name ßtime) Long/MAX_VALUE)
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (§ ass (§ name ßtime) (Math/min (.. ßkey (getCreationTimeSeconds)), ßtime))
                )
                (§ return ßtime)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"List<ListenerRegistration<KeyChainEventListener>>" (§ fn getListeners) [])
    (§ block
        (§ new #_"ArrayList<>" listeners)
    )

    (§ method #_"Map<ECKey, Protos.Key.Builder>" (§ fn serializeToEditableProtobufs) [])
    (§ block
        (§ let #_"Map<ECKey, Protos.Key.Builder>" (§ name ßresult) (§ new #_"LinkedHashMap<>"))
        (§ block
            (§ for (§ var #_"ECKey" (§ name ßecKey)) :for (.. hashToKeys (values)))
            (§ block
                (§ let #_"Protos.Key.Builder" (§ name ßprotoKey) (serializeEncryptableItem ßecKey))
                (§ block
                    (.. ßprotoKey (setPublicKey (ByteString/copyFrom (.. ßecKey (getPubKey)))))
                    (.. ßresult (put ßecKey, ßprotoKey))
                )
            )
            ßresult
        )
    )

    #_override
    #_public
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])
    (§ block
        (§ let #_"Collection<Protos.Key.Builder>" (§ name ßbuilders) (.. (serializeToEditableProtobufs) (values)))
        (§ block
            (§ let #_"List<Protos.Key>" (§ name ßresult) (§ new #_"ArrayList<>" (.. ßbuilders (size))))
            (§ block
                (§ for (§ var #_"Protos.Key.Builder" (§ name ßbuilder)) :for ßbuilders)
                (§ block
                    (.. ßresult (add (.. ßbuilder (build))))
                )
                ßresult
            )
        )
    )

    #_static
    (§ defn #_"Protos.Key.Builder" (§ fn serializeEncryptableItem) [#_"EncryptableItem" (§ name ßitem)])
    (§ block
        (§ let #_"Protos.Key.Builder" (§ name ßproto) (.. Protos/Key (newBuilder)))
        (§ block
            (.. ßproto (setCreationTimestamp (* (.. ßitem (getCreationTimeSeconds)) 1000)))
            (§ if (and (.. ßitem (isEncrypted)) (some? (.. ßitem (getEncryptedData)))))
            (§ block
                ;; The encrypted data can be missing for an "encrypted" key in the case of a deterministic wallet
                ;; for which the leaf keys chain to an encrypted parent and rederive their private keys on the fly.
                ;; In that case the caller in DeterministicKeyChain will take care of setting the type.
                (§ let #_"EncryptedData" (§ name ßdata) (.. ßitem (getEncryptedData)))
                (§ block
                    (.. ßproto (getEncryptedDataBuilder) (setEncryptedPrivateKey (ByteString/copyFrom (.. ßdata encryptedBytes))) (setInitialisationVector (ByteString/copyFrom (.. ßdata initialisationVector))))
                    ;; We don't allow mixing of encryption types at the moment.
                    (Preconditions/checkState (== (.. ßitem (getEncryptionType)) Protos/Wallet/EncryptionType/ENCRYPTED_SCRYPT_AES))
                    (.. ßproto (setType Protos/Key/Type/ENCRYPTED_SCRYPT_AES))
                )
            )
            (§ else )
            (§ block
                (§ let #_"byte[]" (§ name ßsecret) (.. ßitem (getSecretBytes)))
                (§ block
                    ;; The secret might be missing in the case of a watching wallet, or a key for which the private key
                    ;; is expected to be rederived on the fly from its parent.
                    (§ if (some? ßsecret))
                    (§ block
                        (.. ßproto (setSecretBytes (ByteString/copyFrom ßsecret)))
                    )
                    (.. ßproto (setType Protos/Key/Type/ORIGINAL))
                )
            )
            ßproto
        )
    )

    ;;;
     ; Returns a new BasicKeyChain that contains all basic, ORIGINAL type keys extracted from the list.
     ; Unrecognised key types are ignored.
     ;;
    #_public
    #_static
    (§ defn #_"BasicKeyChain" (§ fn fromProtobufUnencrypted) [#_"List<Protos.Key>" (§ name ßkeys)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"BasicKeyChain" (§ name ßchain) (§ new #_"BasicKeyChain"))
        (§ block
            (.. ßchain (deserializeFromProtobuf ßkeys))
            ßchain
        )
    )

    ;;;
     ; Returns a new BasicKeyChain that contains all basic, ORIGINAL type keys and also any encrypted keys
     ; extracted from the list.  Unrecognised key types are ignored.
     ; @throws org.bitcoinj.wallet.UnreadableWalletException.BadPassword if the password doesn't seem to match.
     ; @throws org.bitcoinj.wallet.UnreadableWalletException if the data structures are corrupted/inconsistent.
     ;;
    #_public
    #_static
    (§ defn #_"BasicKeyChain" (§ fn fromProtobufEncrypted) [#_"List<Protos.Key>" (§ name ßkeys), #_"KeyCrypter" (§ name ßcrypter)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"BasicKeyChain" (§ name ßchain) (§ new #_"BasicKeyChain" (Preconditions/checkNotNull ßcrypter)))
        (§ block
            (.. ßchain (deserializeFromProtobuf ßkeys))
            ßchain
        )
    )

    #_private
    (§ method- #_"void" (§ fn deserializeFromProtobuf) [#_"List<Protos.Key>" (§ name ßkeys)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (.. hashToKeys (isEmpty)), "Tried to deserialize into a non-empty chain")
            (§ for (§ var #_"Protos.Key" (§ name ßkey)) :for ßkeys)
            (§ block
                (§ if (and (!= (.. ßkey (getType)) Protos/Key/Type/ORIGINAL) (!= (.. ßkey (getType)) Protos/Key/Type/ENCRYPTED_SCRYPT_AES)))
                (§ block
                    (§ continue )
                )

                (§ let #_"boolean" (§ name ßencrypted) (== (.. ßkey (getType)) Protos/Key/Type/ENCRYPTED_SCRYPT_AES))
                (§ block
                    (§ let #_"byte[]" (§ name ßpriv) (when (.. ßkey (hasSecretBytes)) (.. ßkey (getSecretBytes) (toByteArray))))
                    (§ block
                        (§ if (not (.. ßkey (hasPublicKey))))
                        (§ block
                            (§ throw (§ new #_"UnreadableWalletException" "Public key missing"))
                        )

                        (§ let #_"byte[]" (§ name ßpub) (.. ßkey (getPublicKey) (toByteArray)))
                        (§ block
                            (§ let #_"ECKey" (§ name ßecKey))
                            (§ block
                                (§ if ßencrypted)
                                (§ block
                                    (Preconditions/checkState (some? keyCrypter), "This wallet is ßencrypted but encrypt() was not called prior to deserialization")
                                    (§ if (not (.. ßkey (hasEncryptedData))))
                                    (§ block
                                        (§ throw (§ new #_"UnreadableWalletException" "Encrypted private key data missing"))
                                    )

                                    (§ let #_"Protos.EncryptedData" (§ name ßproto) (.. ßkey (getEncryptedData)))
                                    (§ block
                                        (§ let #_"EncryptedData" (§ name ße) (§ new #_"EncryptedData" (.. ßproto (getInitialisationVector) (toByteArray)), (.. ßproto (getEncryptedPrivateKey) (toByteArray))))
                                        (§ block
                                            (§ ass (§ name ßecKey) (ECKey/fromEncrypted ße, keyCrypter, ßpub))
                                        )
                                    )
                                )
                                (§ else )
                                (§ block
                                    (§ ass (§ name ßecKey) (if (some? ßpriv) (ECKey/fromPrivateAndPrecalculatedPublic ßpriv, ßpub) (ECKey/fromPublicOnly ßpub)))
                                )
                                (.. ßecKey (setCreationTimeSeconds (/ (.. ßkey (getCreationTimestamp)) 1000)))
                                (importKeyLocked ßecKey)
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" (§ name ßlistener)])
    (§ block
        (addEventListener ßlistener, Threading/USER_THREAD)
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" (§ name ßlistener), #_"Executor" (§ name ßexecutor)])
    (§ block
        (.. listeners (add (§ new #_"ListenerRegistration<>" ßlistener, ßexecutor)))
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn removeEventListener) [#_"KeyChainEventListener" (§ name ßlistener)])
    (§ block
        (ListenerRegistration/removeFromList ßlistener, listeners)
    )

    #_private
    (§ method- #_"void" (§ fn queueOnKeysAdded) [#_"List<ECKey>" (§ name ßkeys)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ for (§ var ListenerRegistration<KeyChainEventListener> registration) :for listeners)
        (§ block
            (.. registration executor (execute (§ new #_"Runnable")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (.. registration listener (onKeysAdded ßkeys))
                    nil
                )
            )))
        )
        nil
    )

    ;;;
     ; Convenience wrapper around {@link #toEncrypted(org.bitcoinj.crypto.KeyCrypter, org.spongycastle.crypto.params.KeyParameter)}
     ; which uses the default Scrypt key derivation algorithm and parameters, derives a key from the given password and returns
     ; the created key.
     ;;
    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toEncrypted) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull ßpassword)
        (Preconditions/checkArgument (< 0 (.. ßpassword (length))))

        (§ let #_"KeyCrypter" (§ name ßscrypt) (§ new #_"KeyCrypterScrypt"))
        (§ block
            (§ let #_"KeyParameter" (§ name ßderivedKey) (.. ßscrypt (deriveKey ßpassword)))
            (§ block
                (toEncrypted ßscrypt, ßderivedKey)
            )
        )
    )

    ;;;
     ; Encrypt the wallet using the KeyCrypter and the AES key.  A good default KeyCrypter to use is
     ; {@link org.bitcoinj.crypto.KeyCrypterScrypt}.
     ;
     ; @param keyCrypter The KeyCrypter that specifies how to encrypt/ decrypt a key.
     ; @param aesKey AES key to use (normally created using KeyCrypter#deriveKey and cached as it is time consuming
     ;               to create from a password).
     ; @throws KeyCrypterException if the wallet encryption fails.  If so, the wallet state is unchanged.
     ;;
    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toEncrypted) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkNotNull ßkeyCrypter)
            (Preconditions/checkState (nil? (.. this ßkeyCrypter)), "Key chain is already encrypted")

            (§ let #_"BasicKeyChain" (§ name ßencrypted) (§ new #_"BasicKeyChain" ßkeyCrypter))
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"ECKey" (§ name ßencryptedKey) (.. ßkey (encrypt ßkeyCrypter, ßaesKey)))
                    (§ block
                        ;; Check that the encrypted key can be successfully decrypted.
                        ;; This is done as it is a critical failure if the private key cannot be decrypted successfully
                        ;; (all bitcoin controlled by that private key is lost forever).
                        ;; For a correctly constructed keyCrypter the encryption should always be reversible so it is just
                        ;; being as cautious as possible.
                        (§ if (not (ECKey/encryptionIsReversible ßkey, ßencryptedKey, ßkeyCrypter, ßaesKey)))
                        (§ block
                            (§ throw (§ new #_"KeyCrypterException" (str "The key " ßkey " cannot be successfully decrypted after encryption so aborting wallet encryption.")))
                        )
                        (.. ßencrypted (importKeyLocked ßencryptedKey))
                    )
                )
                (§ return ßencrypted)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toDecrypted) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull keyCrypter, "Wallet is already decrypted")

        (toDecrypted (.. keyCrypter (deriveKey ßpassword)))
    )

    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toDecrypted) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (some? keyCrypter), "Wallet is already decrypted")

            ;; Do an up-front check.
            (§ if (and (< 0 (numKeys)) (not (checkAESKey ßaesKey))))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "Password/key was incorrect."))
            )

            (§ let #_"BasicKeyChain" (§ name ßdecrypted) (§ new #_"BasicKeyChain"))
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (.. ßdecrypted (importKeyLocked (.. ßkey (decrypt ßaesKey))))
                )
                (§ return ßdecrypted)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns whether the given password is correct for this key chain.
     ; @throws IllegalStateException if the chain is not encrypted at all.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn checkPassword) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull ßpassword)
        (Preconditions/checkState (some? keyCrypter), "Key chain not encrypted")

        (checkAESKey (.. keyCrypter (deriveKey ßpassword)))
    )

    ;;;
     ; Check whether the AES key can decrypt the first encrypted key in the wallet.
     ;
     ; @return true if AES key supplied can decrypt the first encrypted private key in the wallet, false otherwise.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; If no keys then cannot decrypt.
            (§ if (.. hashToKeys (isEmpty)))
            (§ block
                (§ return false)
            )

            (Preconditions/checkState (some? keyCrypter), "Key chain is not encrypted")

            ;; Find the first encrypted key in the wallet.
            (§ let #_"ECKey" (§ name ßfirst) nil)
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (§ if (.. ßkey (isEncrypted)))
                    (§ block
                        (§ ass (§ name ßfirst) ßkey)
                        (§ break )
                    )
                )
                (Preconditions/checkState (some? ßfirst), "No encrypted keys in the wallet")

                (§ try )
                (§ block
                    (§ let #_"ECKey" (§ name ßrebornKey) (.. ßfirst (decrypt ßaesKey)))
                    (§ block
                        (§ return (Arrays/equals (.. ßfirst (getPubKey)), (.. ßrebornKey (getPubKey))))
                    )
                )
                (§ catch #_"KeyCrypterException" (§ name _))
                (§ block
                    ;; The AES key supplied is incorrect.
                    (§ return false)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"BloomFilter" (§ fn getFilter) [#_"int" (§ name ßsize), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßtweak)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"BloomFilter" (§ name ßfilter) (§ new #_"BloomFilter" ßsize, ßfalsePositiveRate, ßtweak))
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (.. ßfilter (insert ßkey))
                )
                (§ return ßfilter)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn numBloomFilterEntries) [])
    (§ block
        (* (numKeys) 2)
    )

    ;;; Returns the first ECKey created after the given UNIX time, or null if there is none. ;;
    #_nilable
    #_public
    (§ method #_"ECKey" (§ fn findOldestKeyAfter) [#_"long" (§ name ßtimeSecs)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"ECKey" (§ name ßoldest) nil)
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"long" (§ name ßkeyTime) (.. ßkey (getCreationTimeSeconds)))
                    (§ block
                        (§ if (and (< ßtimeSecs ßkeyTime) (or (nil? ßoldest) (< ßkeyTime (.. ßoldest (getCreationTimeSeconds))))))
                        (§ block
                            (§ ass (§ name ßoldest) ßkey)
                        )
                    )
                )
                (§ return ßoldest)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;; Returns a list of all ECKeys created after the given UNIX time. ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn findKeysBefore) [#_"long" (§ name ßtimeSecs)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"List<ECKey>" (§ name ßresults) (Lists/newLinkedList))
            (§ block
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"long" (§ name ßkeyTime) (.. ßkey (getCreationTimeSeconds)))
                    (§ block
                        (§ if (< ßkeyTime ßtimeSecs))
                        (§ block
                            (.. ßresults (add ßkey))
                        )
                    )
                )
                (§ return ßresults)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )
)

#_(ns org.bitcoinj.wallet #_"CoinSelection"
    (:import [java.util Collection])
   (:require [org.bitcoinj.core Coin TransactionOutput]))

;;;
 ; Represents the results of a {@link CoinSelector#select(Coin, java.util.List)} operation.
 ; A coin selection represents a list of spendable transaction outputs that sum together to give valueGathered.
 ; Different coin selections could be produced by different coin selectors from the same input set, according
 ; to their varying policies.
 ;;
#_public
(§ class #_"CoinSelection"
    #_public
    (§ field #_"Coin" (§ name valueGathered))
    #_public
    (§ field #_"Collection<TransactionOutput>" (§ name gathered))

    #_public
    (§ constructor #_"CoinSelection" [#_"Coin" (§ name ßvalueGathered), #_"Collection<TransactionOutput>" (§ name ßgathered)])
    (§ block
        (§ ass (§ name (.. this ßvalueGathered)) ßvalueGathered)
        (§ ass (§ name (.. this ßgathered)) ßgathered)
        this
    )
)

#_(ns org.bitcoinj.wallet #_"CoinSelector"
    (:import [java.util List])
   (:require [org.bitcoinj.core Coin TransactionOutput]))

;;;
 ; A CoinSelector is responsible for picking some outputs to spend, from the list of all possible outputs.
 ; It allows you to customize the policies for creation of transactions to suit your needs.  The select operation
 ; may return a {@link CoinSelection} that has a valueGathered lower than the requested target, if there's not
 ; enough money in the wallet.
 ;;
#_public
(§ interface #_"CoinSelector"
    ;;;
     ; Creates a CoinSelection that tries to meet the target amount of value.  The candidates list is given to
     ; this call and can be edited freely.  See the docs for CoinSelection to learn more, or look a the implementation
     ; of {@link DefaultCoinSelector}.
     ;;
    (§ method #_"CoinSelection" (§ fn select) [#_"Coin" __target, #_"List<TransactionOutput>" __candidates])
)

#_(ns org.bitcoinj.wallet #_"DecryptingKeyBag"
    (:import [java.util ArrayList List])
    (:import [com.google.common.base Preconditions]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core ECKey]))

;;;
 ; A DecryptingKeyBag filters a pre-existing key bag, decrypting keys as they are requested using the provided AES key.
 ; If the keys are encrypted and no AES key provided, {@link org.bitcoinj.core.ECKey.KeyIsEncryptedException} will be thrown.
 ;;
#_public
(§ class #_"DecryptingKeyBag" (§ implements #_"KeyBag")
    #_protected
    (§ field #_"KeyBag" (§ name target))
    #_protected
    (§ field #_"KeyParameter" (§ name aesKey))

    #_public
    (§ constructor #_"DecryptingKeyBag" [#_"KeyBag" (§ name ßtarget), #_nilable #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (§ ass (§ name (.. this ßtarget)) (Preconditions/checkNotNull ßtarget))
        (§ ass (§ name (.. this ßaesKey)) ßaesKey)
        this
    )

    #_nilable
    #_private
    (§ method- #_"ECKey" (§ fn maybeDecrypt) [#_"ECKey" (§ name ßkey)])
    (§ block
        (§ if (nil? ßkey))
        (§ block
            (§ return nil)
        )

        (§ if (.. ßkey (isEncrypted)))
        (§ block
            (§ if (nil? aesKey))
            (§ block
                (§ throw (§ new #_"ECKey.KeyIsEncryptedException"))
            )
            (§ return (.. ßkey (decrypt aesKey)))
        )

        ßkey
    )

    #_private
    (§ method- #_"RedeemData" (§ fn maybeDecrypt) [#_"RedeemData" (§ name ßredeemData)])
    (§ block
        (§ let #_"List<ECKey>" (§ name ßdecryptedKeys) (§ new #_"ArrayList<>"))
        (§ block
            (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. ßredeemData keys))
            (§ block
                (.. ßdecryptedKeys (add (maybeDecrypt ßkey)))
            )
            (RedeemData/of ßdecryptedKeys, (.. ßredeemData redeemScript))
        )
    )

    #_nilable
    #_override
    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" (§ name ßpubkeyHash)])
    (§ block
        (maybeDecrypt (.. target (findKeyFromPubHash ßpubkeyHash)))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubKey) [#_"byte[]" (§ name ßpubkey)])
    (§ block
        (maybeDecrypt (.. target (findKeyFromPubKey ßpubkey)))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"RedeemData" (§ fn findRedeemDataFromScriptHash) [#_"byte[]" (§ name ßscriptHash)])
    (§ block
        (maybeDecrypt (.. target (findRedeemDataFromScriptHash ßscriptHash)))
    )
)

#_(ns org.bitcoinj.wallet #_"DefaultCoinSelector"
    (:import [java.math BigInteger]
             [java.util *])
    (:import [com.google.common.annotations VisibleForTesting])
   (:require [org.bitcoinj.core Coin NetworkParameters Transaction TransactionConfidence TransactionOutput]))

;;;
 ; This class implements a {@link CoinSelector} which attempts to get the highest priority possible.
 ; This means that the transaction is the most likely to get confirmed.  Note that this means we may end up
 ; "spending" more priority than would be required to get the transaction we are creating confirmed.
 ;;
#_public
(§ class #_"DefaultCoinSelector" (§ implements #_"CoinSelector")
    #_override
    #_public
    (§ method #_"CoinSelection" (§ fn select) [#_"Coin" (§ name ßtarget), #_"List<TransactionOutput>" (§ name ßcandidates)])
    (§ block
        (§ let #_"ArrayList<TransactionOutput>" (§ name ßselected) (§ new #_"ArrayList<>"))
        (§ block
            ;; Sort the inputs by age * value, so we get the highest "coindays" spent.
            ;; TODO: Consider changing the wallets internal format to track just outputs and keep them ordered.
            (§ let #_"ArrayList<TransactionOutput>" (§ name ßsortedOutputs) (§ new #_"ArrayList<>" ßcandidates))
            (§ block
                ;; When calculating the wallet balance, we may be asked to select all possible coins, if so, avoid
                ;; sorting them in order to improve performance.
                ;; TODO: Take in network parameters when instanatiated, and then test against the current network.
                ;; Or just have a boolean parameter for "give me everything".
                (§ if (not (.. ßtarget (equals NetworkParameters/MAX_MONEY))))
                (§ block
                    (sortOutputs ßsortedOutputs)
                )
                ;; Now iterate over the sorted outputs until we have got as close to the target as possible or
                ;; a little bit over (excessive value will be change).
                (§ let #_"long" (§ name ßtotal) 0)
                (§ block
                    (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for ßsortedOutputs)
                    (§ block
                        (§ if (<= (.. ßtarget value) ßtotal))
                        (§ block
                            (§ break )
                        )
                        ;; Only pick chain-included transactions, or transactions that are ours and pending.
                        (§ if (shouldSelect (.. ßoutput (getParentTransaction))))
                        (§ block
                            (.. ßselected (add ßoutput))
                            (§ ass (§ name ßtotal) (+ ßtotal (.. ßoutput (getValue) value)))
                        )
                    )
                    ;; Total may be lower than the target here if the given candidates were insufficient to create
                    ;; the requested transaction.
                    (§ new #_"CoinSelection" (Coin/valueOf ßtotal), ßselected)
                )
            )
        )
    )

    #_testing
    #_static
    (§ defn #_"void" (§ fn sortOutputs) [#_"ArrayList<TransactionOutput>" (§ name ßoutputs)])
    (§ block
        (Collections/sort ßoutputs, (§ new #_"Comparator<TransactionOutput>")
        (§ anon
            #_override
            #_public
            (§ method #_"int" (§ fn compare) [#_"TransactionOutput" (§ name ßa), #_"TransactionOutput" (§ name ßb)])
            (§ block
                (§ let #_"int" (§ name ßdepth1) (.. ßa (getParentTransactionDepthInBlocks)))
                (§ block
                    (§ let #_"int" (§ name ßdepth2) (.. ßb (getParentTransactionDepthInBlocks)))
                    (§ block
                        (§ let #_"Coin" (§ name ßaValue) (.. ßa (getValue)))
                        (§ block
                            (§ let #_"Coin" (§ name ßbValue) (.. ßb (getValue)))
                            (§ block
                                (§ let #_"BigInteger" (§ name ßaCoinDepth) (.. (BigInteger/valueOf (.. ßaValue value)) (multiply (BigInteger/valueOf ßdepth1))))
                                (§ block
                                    (§ let #_"BigInteger" (§ name ßbCoinDepth) (.. (BigInteger/valueOf (.. ßbValue value)) (multiply (BigInteger/valueOf ßdepth2))))
                                    (§ block
                                        (§ let #_"int" (§ name ßc1) (.. ßbCoinDepth (compareTo ßaCoinDepth)))
                                        (§ block
                                            (§ if (!= ßc1 0))
                                            (§ block
                                                (§ return ßc1)
                                            )

                                            ;; The "coin * days" destroyed are equal, sort by value alone to get the lowest transaction size.
                                            (§ let #_"int" (§ name ßc2) (.. ßbValue (compareTo ßaValue)))
                                            (§ block
                                                (§ if (!= ßc2 0))
                                                (§ block
                                                    (§ return ßc2)
                                                )

                                                ;; They are entirely equivalent (possibly pending) so sort by hash to ensure a total ordering.
                                                (§ let #_"BigInteger" (§ name ßaHash) (.. ßa (getParentTransactionHash) (toBigInteger)))
                                                (§ block
                                                    (§ let #_"BigInteger" (§ name ßbHash) (.. ßb (getParentTransactionHash) (toBigInteger)))
                                                    (§ block
                                                        (.. ßaHash (compareTo ßbHash))
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        ))
        nil
    )

    ;;; Sub-classes can override this to just customize whether transactions are usable, but keep age sorting. ;;
    #_protected
    (§ method #_"boolean" (§ fn shouldSelect) [#_"Transaction" (§ name ßtx)])
    (§ block
        (if (some? ßtx) (isSelectable ßtx) true)
    )

    #_public
    #_static
    (§ defn #_"boolean" (§ fn isSelectable) [#_"Transaction" (§ name ßtx)])
    (§ block
        ;; Only pick chain-included transactions, or transactions that are ours and pending.
        (§ let #_"TransactionConfidence" (§ name ßconfidence) (.. ßtx (getConfidence)))
        (§ block
            (§ let #_"TransactionConfidence.ConfidenceType" (§ name ßtype) (.. ßconfidence (getConfidenceType)))
            (§ block
                ;; TODO: The value 1 below dates from a time when transactions we broadcast *to* were counted, set to 0.
                (or (.. ßtype (equals TransactionConfidence/ConfidenceType/BUILDING)) (and (.. ßtype (equals TransactionConfidence/ConfidenceType/PENDING)) (.. ßconfidence (getSource) (equals TransactionConfidence/Source/SELF)) (< 1 (.. ßconfidence (numBroadcastPeers)))))
            )
        )
    )
)

#_(ns org.bitcoinj.wallet #_"DefaultKeyChainFactory"
   (:require [org.bitcoinj.crypto *]))

;;;
 ; Default factory for creating keychains while de-serializing.
 ;;
#_public
(§ class #_"DefaultKeyChainFactory" (§ implements #_"KeyChainFactory")
    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn makeKeyChain) [#_"Protos.Key" (§ name ßkey), #_"Protos.Key" (§ name ßfirstSubKey), #_"DeterministicSeed" (§ name ßseed), #_"KeyCrypter" (§ name ßcrypter), #_"boolean" (§ name ßisMarried)])
    (§ block
        (if ßisMarried (§ new #_"MarriedKeyChain" ßseed, ßcrypter) (§ new #_"DeterministicKeyChain" ßseed, ßcrypter))
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn makeWatchingKeyChain) [#_"Protos.Key" (§ name ßkey), #_"Protos.Key" (§ name ßfirstSubKey), #_"DeterministicKey" (§ name ßaccountKey), #_"boolean" (§ name ßisFollowingKey), #_"boolean" (§ name ßisMarried)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ if (not (.. ßaccountKey (getPath) (equals DeterministicKeyChain/ACCOUNT_ZERO_PATH))))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException" (str "Expecting account key but found key with path: " (HDUtils/formatPath (.. ßaccountKey (getPath))))))
        )

        (if ßisMarried (§ new #_"MarriedKeyChain" ßaccountKey) (§ new #_"DeterministicKeyChain" ßaccountKey, ßisFollowingKey))
    )
)

#_(ns org.bitcoinj.wallet #_"DefaultRiskAnalysis"
    (:import [java.util List])
    (:import [com.google.common.base Preconditions]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core Coin ECKey]
             [org.bitcoinj.core.ECKey ECDSASignature]
             [org.bitcoinj.core NetworkParameters Transaction TransactionConfidence TransactionInput TransactionOutput]
             [org.bitcoinj.crypto TransactionSignature]
             [org.bitcoinj.script ScriptChunk]))

;;;
 ; <p>The default risk analysis.  Currently, it only is concerned with whether a tx/dependency is non-final or not,
 ; and whether a tx/dependency violates the dust rules.  Outside of specialised protocols you should not encounter
 ; non-final transactions.</p>
 ;;
#_public
(§ class #_"DefaultRiskAnalysis" (§ implements #_"RiskAnalysis")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"DefaultRiskAnalysis")))

    ;;;
     ; Any standard output smaller than this value (in satoshis) will be considered risky, as it's most likely
     ; be rejected by the network.  This is usually the same as {@link Transaction#MIN_NONDUST_OUTPUT} but can
     ; be different when the fee is about to change in Bitcoin Core.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name MIN_ANALYSIS_NONDUST_OUTPUT) Transaction/MIN_NONDUST_OUTPUT)

    #_protected
    (§ field #_"Transaction" (§ name tx))
    #_protected
    (§ field #_"List<Transaction>" (§ name dependencies))
    #_nilable
    #_protected
    (§ field #_"Wallet" (§ name wallet))

    #_private
    (§ field- #_"Transaction" (§ name nonStandard))
    #_protected
    (§ field #_"Transaction" (§ name nonFinal))
    #_protected
    (§ field #_"boolean" (§ name analyzed))

    #_private
    (§ constructor- #_"DefaultRiskAnalysis" [#_"Wallet" (§ name ßwallet), #_"Transaction" (§ name ßtx), #_"List<Transaction>" (§ name ßdependencies)])
    (§ block
        (§ ass (§ name (.. this ßtx)) ßtx)
        (§ ass (§ name (.. this ßdependencies)) ßdependencies)
        (§ ass (§ name (.. this ßwallet)) ßwallet)
        this
    )

    #_override
    #_public
    (§ method #_"Result" (§ fn analyze) [])
    (§ block
        (Preconditions/checkState (not analyzed))
        (§ ass (§ name analyzed) true)

        (§ let #_"Result" (§ name ßresult) (analyzeIsFinal))
        (§ block
            (§ if (and (some? ßresult) (!= ßresult Result/OK)))
            (§ block
                (§ return ßresult)
            )

            (analyzeIsStandard)
        )
    )

    #_nilable
    #_private
    (§ method- #_"Result" (§ fn analyzeIsFinal) [])
    (§ block
        ;; Transactions we create ourselves are, by definition, not at risk of double spending against us.
        (§ if (== (.. tx (getConfidence) (getSource)) TransactionConfidence/Source/SELF))
        (§ block
            (§ return Result/OK)
        )

        ;; We consider transactions that opt into replace-by-fee at risk of double spending.
        (§ if (.. tx (isOptInFullRBF)))
        (§ block
            (§ ass (§ name nonFinal) tx)
            (§ return Result/NON_FINAL)
        )

        (§ if (nil? wallet))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" (§ name ßheight) (.. wallet (getLastBlockSeenHeight)))
        (§ block
            (§ let #_"long" (§ name ßtime) (.. wallet (getLastBlockSeenTimeSecs)))
            (§ block
                ;; If the transaction has a lock time specified in blocks, we consider that if the tx would become final
                ;; in the next block, it is not risky (as it would confirm normally).
                (§ let #_"int" (§ name ßadjustedHeight) (+ ßheight 1))
                (§ block

                    (§ if (not (.. tx (isFinal ßadjustedHeight, ßtime))))
                    (§ block
                        (§ ass (§ name nonFinal) tx)
                        (§ return Result/NON_FINAL)
                    )

                    (§ for (§ var #_"Transaction" (§ name ßdep)) :for dependencies)
                    (§ block
                        (§ if (not (.. ßdep (isFinal ßadjustedHeight, ßtime))))
                        (§ block
                            (§ ass (§ name nonFinal) ßdep)
                            (§ return Result/NON_FINAL)
                        )
                    )

                    Result/OK
                )
            )
        )
    )

    ;;;
     ; The reason a transaction is considered non-standard, returned by {@link #isStandard(org.bitcoinj.core.Transaction)}.
     ;;
    #_public
    (§ enum #_"RuleViolation"
        (§ item NONE)
        (§ item VERSION)
        (§ item DUST)
        (§ item SHORTEST_POSSIBLE_PUSHDATA)
        (§ item NONEMPTY_STACK) ;; Not yet implemented (for post 0.12)
        (§ item SIGNATURE_CANONICAL_ENCODING)
    )

    ;;;
     ; <p>Checks if a transaction is considered "standard" by Bitcoin Core's IsStandardTx and AreInputsStandard functions.</p>
     ;
     ; <p>Note that this method currently only implements a minimum of checks.  More to be added later.</p>
     ;;
    #_public
    #_static
    (§ defn #_"RuleViolation" (§ fn isStandard) [#_"Transaction" (§ name ßtx)])
    (§ block
        ;; TODO: Finish this function off.
        (§ if (or (< 1 (.. ßtx (getVersion))) (< (.. ßtx (getVersion)) 1)))
        (§ block
            (.. log (warn "TX considered non-standard due to unknown version number {}", (.. ßtx (getVersion))))
            (§ return RuleViolation/VERSION)
        )

        (§ let #_"List<TransactionOutput>" (§ name ßoutputs) (.. ßtx (getOutputs)))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßoutputs (size))) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßoutputs (get ßi)))
                (§ block
                    (§ let #_"RuleViolation" (§ name ßviolation) (isOutputStandard ßoutput))
                    (§ block
                        (§ if (!= ßviolation RuleViolation/NONE))
                        (§ block
                            (.. log (warn "TX considered non-standard due to ßoutput {} violating rule {}", ßi, ßviolation))
                            (§ return ßviolation)
                        )
                    )
                )
            )

            (§ let #_"List<TransactionInput>" (§ name ßinputs) (.. ßtx (getInputs)))
            (§ block
                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßinputs (size))) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ let #_"TransactionInput" (§ name ßinput) (.. ßinputs (get ßi)))
                    (§ block
                        (§ let #_"RuleViolation" (§ name ßviolation) (isInputStandard ßinput))
                        (§ block
                            (§ if (!= ßviolation RuleViolation/NONE))
                            (§ block
                                (.. log (warn "TX considered non-standard due to ßinput {} violating rule {}", ßi, ßviolation))
                                (§ return ßviolation)
                            )
                        )
                    )
                )

                RuleViolation/NONE
            )
        )
    )

    ;;;
     ; Checks the output to see if the script violates a standardness rule.  Not complete.
     ;;
    #_public
    #_static
    (§ defn #_"RuleViolation" (§ fn isOutputStandard) [#_"TransactionOutput" (§ name ßoutput)])
    (§ block
        (§ if (< (.. ßoutput (getValue) (compareTo MIN_ANALYSIS_NONDUST_OUTPUT)) 0))
        (§ block
            (§ return RuleViolation/DUST)
        )

        (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßoutput (getScriptPubKey) (getChunks)))
        (§ block
            (§ if (and (.. ßchunk (isPushData)) (not (.. ßchunk (isShortestPossiblePushData)))))
            (§ block
                (§ return RuleViolation/SHORTEST_POSSIBLE_PUSHDATA)
            )
        )

        RuleViolation/NONE
    )

    ;;; Checks if the given input passes some of the AreInputsStandard checks.  Not complete. ;;
    #_public
    #_static
    (§ defn #_"RuleViolation" (§ fn isInputStandard) [#_"TransactionInput" (§ name ßinput)])
    (§ block
        (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßinput (getScriptSig) (getChunks)))
        (§ block
            (§ if (and (some? (.. ßchunk data)) (not (.. ßchunk (isShortestPossiblePushData)))))
            (§ block
                (§ return RuleViolation/SHORTEST_POSSIBLE_PUSHDATA)
            )

            (§ if (.. ßchunk (isPushData)))
            (§ block
                (§ let #_"ECDSASignature" (§ name ßsignature))
                (§ block
                    (§ try )
                    (§ block
                        (§ ass (§ name ßsignature) (.. ECKey/ECDSASignature (decodeFromDER (.. ßchunk data))))
                    )
                    (§ catch #_"IllegalArgumentException" (§ name _))
                    (§ block
                        ;; Doesn't look like a signature.
                        (§ ass (§ name ßsignature) nil)
                    )
                    (§ if (some? ßsignature))
                    (§ block
                        (§ if (not (TransactionSignature/isEncodingCanonical (.. ßchunk data))))
                        (§ block
                            (§ return RuleViolation/SIGNATURE_CANONICAL_ENCODING)
                        )
                        (§ if (not (.. ßsignature (isCanonical))))
                        (§ block
                            (§ return RuleViolation/SIGNATURE_CANONICAL_ENCODING)
                        )
                    )
                )
            )
        )
        RuleViolation/NONE
    )

    #_private
    (§ method- #_"Result" (§ fn analyzeIsStandard) [])
    (§ block
        ;; The IsStandard rules don't apply on testnet, because they're just a safety mechanism and we don't
        ;; want to crush innovation with valueless test coins.
        (§ if (and (some? wallet) (not (.. wallet (getNetworkParameters) (getId) (equals NetworkParameters/ID_MAINNET)))))
        (§ block
            (§ return Result/OK)
        )

        (§ let #_"RuleViolation" (§ name ßruleViolation) (isStandard tx))
        (§ block
            (§ if (!= ßruleViolation RuleViolation/NONE))
            (§ block
                (§ ass (§ name nonStandard) tx)
                (§ return Result/NON_STANDARD)
            )

            (§ for (§ var #_"Transaction" (§ name ßdep)) :for dependencies)
            (§ block
                (§ ass (§ name ßruleViolation) (isStandard ßdep))
                (§ if (!= ßruleViolation RuleViolation/NONE))
                (§ block
                    (§ ass (§ name nonStandard) ßdep)
                    (§ return Result/NON_STANDARD)
                )
            )

            Result/OK
        )
    )

    ;;; Returns the transaction that was found to be non-standard, or null. ;;
    #_nilable
    #_public
    (§ method #_"Transaction" (§ fn getNonStandard) [])
    (§ block
        nonStandard
    )

    ;;; Returns the transaction that was found to be non-final, or null. ;;
    #_nilable
    #_public
    (§ method #_"Transaction" (§ fn getNonFinal) [])
    (§ block
        nonFinal
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ if (not analyzed))
        (§ block
            (§ return (str "Pending risk analysis for " (.. tx (getHashAsString))))
        )
        (§ if (some? nonFinal))
        (§ block
            (§ return (str "Risky due to non-finality of " (.. nonFinal (getHashAsString))))
        )
        (§ if (some? nonStandard))
        (§ block
            (§ return (str "Risky due to non-standard tx " (.. nonStandard (getHashAsString))))
        )

        "Non-risky"
    )

    #_public
    #_static
    (§ class #_"Analyzer" (§ implements #_"RiskAnalysis.Analyzer")
        #_override
        #_public
        (§ method #_"DefaultRiskAnalysis" (§ fn create) [#_"Wallet" (§ name ßwallet), #_"Transaction" (§ name ßtx), #_"List<Transaction>" (§ name ßdependencies)])
        (§ block
            (§ new #_"DefaultRiskAnalysis" ßwallet, ßtx, ßdependencies)
        )
    )

    #_public
    #_static
    (§ def #_"Analyzer" (§ name FACTORY) (§ new #_"Analyzer"))
)

#_(ns org.bitcoinj.wallet #_"DeterministicKeyChain"
    (:import [java.math BigInteger]
             [java.security SecureRandom]
             [java.util *]
             [java.util.concurrent Executor]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.base Preconditions Stopwatch]
             [com.google.common.collect ImmutableList Iterators]
             #_static [com.google.common.collect.Lists newArrayList]
             #_static [com.google.common.collect.Lists newLinkedList]
             [com.google.common.collect PeekingIterator]
             [com.google.protobuf ByteString]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core BloomFilter ECKey NetworkParameters Utils]
             [org.bitcoinj.crypto *]
             [org.bitcoinj.script Script]
             [org.bitcoinj.utils Threading]
             [org.bitcoinj.wallet.listeners KeyChainEventListener]))

;;;
 ; <p>A deterministic key chain is a {@link KeyChain} that uses the
 ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0032.mediawiki">BIP 32 standard</a>, as implemented by
 ; {@link org.bitcoinj.crypto.DeterministicHierarchy}, to derive all the keys in the keychain from a master seed.
 ; This type of wallet is extremely convenient and flexible.  Although backing up full wallet files is always a good
 ; idea, to recover money only the root seed needs to be preserved and that is a number small enough that it can be
 ; written down on paper or, when represented using a BIP 39 {@link org.bitcoinj.crypto.MnemonicCode},
 ; dictated over the phone (possibly even memorized).</p>
 ;
 ; <p>Deterministic key chains have other advantages: parts of the key tree can be selectively revealed to allow
 ; for auditing, and new public keys can be generated without access to the private keys, yielding a highly secure
 ; configuration for web servers which can accept payments into a wallet but not spend from them.  This does not work
 ; quite how you would expect due to a quirk of elliptic curve mathematics and the techniques used to deal with it.
 ; A watching wallet is not instantiated using the public part of the master key as you may imagine.  Instead, you
 ; need to take the account key (first child of the master key) and provide the public part of that to the watching
 ; wallet instead.  You can do this by calling {@link #getWatchingKey()} and then serializing it with
 ; {@link org.bitcoinj.crypto.DeterministicKey#serializePubB58(org.bitcoinj.core.NetworkParameters)}.  The resulting
 ; "xpub..." string encodes sufficient information about the account key to create a watching chain via
 ; {@link org.bitcoinj.crypto.DeterministicKey#deserializeB58(org.bitcoinj.crypto.DeterministicKey, String, org.bitcoinj.core.NetworkParameters)}
 ; (with null as the first parameter) and then
 ; {@link DeterministicKeyChain#DeterministicKeyChain(org.bitcoinj.crypto.DeterministicKey)}.</p>
 ;
 ; <p>This class builds on {@link org.bitcoinj.crypto.DeterministicHierarchy} and {@link org.bitcoinj.crypto.DeterministicKey}
 ; by adding support for serialization to and from protobufs, and encryption of parts of the key tree.  Internally it
 ; arranges itself as per the BIP 32 spec, with the seed being used to derive a master key, which is then used to derive
 ; an account key, the account key is used to derive two child keys called the <i>internal</i> and <i>external</i> parent
 ; keys (for change and handing out addresses respectively) and finally the actual leaf keys that users use hanging off
 ; the end.  The leaf keys are special in that they don't internally store the private part at all, instead choosing to
 ; rederive the private key from the parent when needed for signing.  This simplifies the design for encrypted key chains.</p>
 ;
 ; <p>The key chain manages a <i>lookahead zone</i>.  This zone is required because when scanning the chain, you don't
 ; know exactly which keys might receive payments.  The user may have handed out several addresses and received payments
 ; on them, but for latency reasons the block chain is requested from remote peers in bulk, meaning you must
 ; "look ahead" when calculating keys to put in the Bloom filter.  The default lookahead zone is 100 keys, meaning
 ; if the user hands out more than 100 addresses and receives payment on them before the chain is next scanned, some
 ; transactions might be missed.  100 is a reasonable choice for consumer wallets running on CPU constrained devices.
 ; For industrial wallets that are receiving keys all the time, a higher value is more appropriate.  Ideally DKC and
 ; the wallet would know how to adjust this value automatically, but that's not implemented at the moment.</p>
 ;
 ; <p>In fact the real size of the lookahead zone is larger than requested, by default, it's one third larger.  This
 ; is because the act of deriving new keys means recalculating the Bloom filters and this is an expensive operation.
 ; Thus, to ensure we don't have to recalculate on every single new key/address requested or seen we add more buffer
 ; space and only extend the lookahead zone when that buffer is exhausted.  For example with a lookahead zone of 100
 ; keys, you can request 33 keys before more keys will be calculated and the Bloom filter rebuilt and rebroadcast.
 ; But even when you are requesting the 33rd key, you will still be looking 100 keys ahead.</p>
 ;
 ; @author Andreas Schildbach
 ;;
#_suppress(§ opt "PublicStaticCollectionField")
#_public
(§ class #_"DeterministicKeyChain" (§ implements #_"EncryptableKeyChain")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"DeterministicKeyChain")))
    #_public
    #_static
    (§ def #_"String" (§ name DEFAULT_PASSPHRASE_FOR_MNEMONIC) "")

    #_protected
    (§ field #_"ReentrantLock" (§ name lock) (Threading/lock "DeterministicKeyChain"))

    #_private
    (§ field- #_"DeterministicHierarchy" (§ name hierarchy))
    #_nilable
    #_private
    (§ field- #_"DeterministicKey" (§ name rootKey))
    #_nilable
    #_private
    (§ field- #_"DeterministicSeed" (§ name seed))

    ;; Paths through the key tree.  External keys are ones that are communicated to other parties.  Internal keys are
    ;; keys created for change addresses, coinbases, mixing, etc - anything that isn't communicated.  The distinction
    ;; is somewhat arbitrary but can be useful for audits.  The first number is the "account number" but we don't use
    ;; that feature yet.  In future we might hand out different accounts for cases where we wish to hand payers
    ;; a payment request that can generate lots of addresses independently.
    ;; The account path may be overridden by subclasses.
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name ACCOUNT_ZERO_PATH) (ImmutableList/of ChildNumber/ZERO_HARDENED))
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name EXTERNAL_SUBPATH) (ImmutableList/of ChildNumber/ZERO))
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name INTERNAL_SUBPATH) (ImmutableList/of ChildNumber/ONE))
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name EXTERNAL_PATH) (HDUtils/concat ACCOUNT_ZERO_PATH, EXTERNAL_SUBPATH))
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name INTERNAL_PATH) (HDUtils/concat ACCOUNT_ZERO_PATH, INTERNAL_SUBPATH))
    ;; m / 44' / 0' / 0'
    #_public
    #_static
    (§ def #_"ImmutableList<ChildNumber>" (§ name BIP44_ACCOUNT_ZERO_PATH) (ImmutableList/of (§ new #_"ChildNumber" 44, true), ChildNumber/ZERO_HARDENED, ChildNumber/ZERO_HARDENED))

    ;; We try to ensure we have at least this many keys ready and waiting to be handed out via getKey().  See docs
    ;; for getLookaheadSize() for more info on what this is for.  The -1 value means it hasn't been calculated yet.
    ;; For new chains it's set to whatever the default is, unless overridden by setLookaheadSize.  For deserialized
    ;; chains, it will be calculated on demand from the number of loaded keys.
    #_private
    #_static
    (§ def- #_"int" (§ name LAZY_CALCULATE_LOOKAHEAD) -1)
    #_protected
    (§ field #_"int" (§ name lookaheadSize) 100)
    ;; The lookahead threshold causes us to batch up creation of new keys to minimize the frequency of Bloom filter
    ;; regenerations, which are expensive and will (in future) trigger chain download stalls/retries.  One third is
    ;; an efficiency tradeoff.
    #_protected
    (§ field #_"int" (§ name lookaheadThreshold) (calcDefaultLookaheadThreshold))

    #_private
    (§ method- #_"int" (§ fn calcDefaultLookaheadThreshold) [])
    (§ block
        (/ lookaheadSize 3)
    )

    ;; The parent keys for external keys (handed out to other people) and internal keys (used for change addresses).
    #_private
    (§ field- #_"DeterministicKey" (§ name externalParentKey))
    #_private
    (§ field- #_"DeterministicKey" (§ name internalParentKey))
    ;; How many keys on each path have actually been used.  This may be fewer than the number that have been
    ;; deserialized or held in memory, because of the lookahead zone.
    #_private
    (§ field- #_"int" (§ name issuedExternalKeys))
    #_private
    (§ field- #_"int" (§ name issuedInternalKeys))
    ;; A counter that is incremented each time a key in the lookahead threshold zone is marked as used and lookahead
    ;; is triggered.  The Wallet/KCG reads these counters and combines them so it can tell the Peer whether to throw
    ;; away the current block (and any future blocks in the same download batch) and restart chain sync once a new filter
    ;; has been calculated.  This field isn't persisted to the wallet as it's only relevant within a network session.
    #_private
    (§ field- #_"int" (§ name keyLookaheadEpoch))

    ;; We simplify by wrapping a basic key chain and that way we get some functionality like key lookup and event
    ;; listeners "for free".  All keys in the key tree appear here, even if they aren't meant to be used for
    ;; receiving money.
    #_private
    (§ field- #_"BasicKeyChain" (§ name basicKeyChain))

    ;; If set this chain is following another chain in a married KeyChainGroup.
    #_private
    (§ field- #_"boolean" (§ name isFollowing))

    ;; Holds a number of signatures required to spend.  It's the N from N-of-M CHECKMULTISIG script for P2SH transactions
    ;; and always 1 for other transaction types.
    #_protected
    (§ field #_"int" (§ name sigsRequiredToSpend) 1)

    #_public
    #_static
    (§ class #_"Builder<T extends Builder<T>>"
        #_protected
        (§ field #_"SecureRandom" (§ name random))
        #_protected
        (§ field #_"int" (§ name bits) 128)
        #_protected
        (§ field #_"String" (§ name passphrase))
        #_protected
        (§ field #_"long" (§ name seedCreationTimeSecs))
        #_protected
        (§ field #_"byte[]" (§ name entropy))
        #_protected
        (§ field #_"DeterministicSeed" (§ name seed))
        #_protected
        (§ field #_"DeterministicKey" (§ name watchingKey))

        #_protected
        (§ constructor #_"Builder" [])
        (§ block
            this
        )

        #_suppress(§ opt "unchecked")
        #_protected
        (§ method #_"T" (§ fn self) [])
        (§ block
            (§ cast #_"T" this)
        )

        ;;;
         ; Creates a deterministic key chain starting from the given entropy.  All keys yielded by this chain will be the
         ; same if the starting entropy is the same.  You should provide the creation time in seconds since the UNIX epoch
         ; for the seed: this lets us know from what part of the chain we can expect to see derived keys appear.
         ;;
        #_public
        (§ method #_"T" (§ fn entropy) [#_"byte[]" (§ name ßentropy)])
        (§ block
            (§ ass (§ name (.. this ßentropy)) ßentropy)
            (self)
        )

        ;;;
         ; Creates a deterministic key chain starting from the given seed.  All keys yielded by this chain will be the same
         ; if the starting seed is the same.
         ;;
        #_public
        (§ method #_"T" (§ fn seed) [#_"DeterministicSeed" (§ name ßseed)])
        (§ block
            (§ ass (§ name (.. this ßseed)) ßseed)
            (self)
        )

        ;;;
         ; Generates a new key chain with entropy selected randomly from the given {@link java.security.SecureRandom} object
         ; and of the requested size in bits.  The derived seed is further protected with a user selected passphrase (see BIP 39).
         ; @param random The random number generator - use new SecureRandom().
         ; @param bits The number of bits of entropy to use when generating entropy.  Either 128 (default), 192 or 256.
         ;;
        #_public
        (§ method #_"T" (§ fn random) [#_"SecureRandom" (§ name ßrandom), #_"int" (§ name ßbits)])
        (§ block
            (§ ass (§ name (.. this ßrandom)) ßrandom)
            (§ ass (§ name (.. this ßbits)) ßbits)
            (self)
        )

        ;;;
         ; Generates a new key chain with 128 bits of entropy selected randomly from the given {@link java.security.SecureRandom}
         ; object.  The derived seed is further protected with a user selected passphrase (see BIP 39).
         ; @param random The random number generator - use new SecureRandom().
         ;;
        #_public
        (§ method #_"T" (§ fn random) [#_"SecureRandom" (§ name ßrandom)])
        (§ block
            (§ ass (§ name (.. this ßrandom)) ßrandom)
            (self)
        )

        #_public
        (§ method #_"T" (§ fn watchingKey) [#_"DeterministicKey" (§ name ßwatchingKey)])
        (§ block
            (§ ass (§ name (.. this ßwatchingKey)) ßwatchingKey)
            (self)
        )

        #_public
        (§ method #_"T" (§ fn seedCreationTimeSecs) [#_"long" (§ name ßseedCreationTimeSecs)])
        (§ block
            (§ ass (§ name (.. this ßseedCreationTimeSecs)) ßseedCreationTimeSecs)
            (self)
        )

        ;;;
         ; The passphrase to use with the generated mnemonic, or null if you would like to use the default empty string.
         ; Currently must be the empty string.
         ;;
        #_public
        (§ method #_"T" (§ fn passphrase) [#_"String" (§ name ßpassphrase)])
        (§ block
            ;; FIXME support non-empty passphrase
            (§ ass (§ name (.. this ßpassphrase)) ßpassphrase)
            (self)
        )

        #_public
        (§ method #_"DeterministicKeyChain" (§ fn build) [])
        (§ block
            (Preconditions/checkState (or (some? random) (some? entropy) (some? seed) (some? watchingKey)), "Must provide either entropy or random or seed or watchingKey")
            (Preconditions/checkState (or (nil? passphrase) (nil? seed)), "Passphrase must not be specified with seed")

            ;; Default passphrase to "" if not specified.
            (§ if (some? random))
            (§ block
                (§ return (§ new #_"DeterministicKeyChain" random, bits, (getPassphrase), seedCreationTimeSecs))
            )

            (§ if (some? entropy))
            (§ block
                (§ return (§ new #_"DeterministicKeyChain" entropy, (getPassphrase), seedCreationTimeSecs))
            )

            (§ if (some? seed))
            (§ block
                (.. seed (setCreationTimeSeconds seedCreationTimeSecs))
                (§ return (§ new #_"DeterministicKeyChain" seed))
            )

            (.. watchingKey (setCreationTimeSeconds seedCreationTimeSecs))
            (§ new #_"DeterministicKeyChain" watchingKey)
        )

        #_protected
        (§ method #_"String" (§ fn getPassphrase) [])
        (§ block
            (or passphrase DEFAULT_PASSPHRASE_FOR_MNEMONIC)
        )
    )

    #_public
    #_static
    (§ defn #_"Builder<?>" (§ fn builder) [])
    (§ block
        (§ new #_"Builder")
    )

    ;;;
     ; Generates a new key chain with entropy selected randomly from the given {@link java.security.SecureRandom}
     ; object and the default entropy size.
     ;;
    #_public
    (§ constructor #_"DeterministicKeyChain" [#_"SecureRandom" (§ name ßrandom)])
    (§ block
        (§ this ßrandom, DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS, DEFAULT_PASSPHRASE_FOR_MNEMONIC, (Utils/currentTimeSeconds))
        this
    )

    ;;;
     ; Generates a new key chain with entropy selected randomly from the given {@link java.security.SecureRandom}
     ; object and of the requested size in bits.
     ;;
    #_public
    (§ constructor #_"DeterministicKeyChain" [#_"SecureRandom" (§ name ßrandom), #_"int" (§ name ßbits)])
    (§ block
        (§ this ßrandom, ßbits, DEFAULT_PASSPHRASE_FOR_MNEMONIC, (Utils/currentTimeSeconds))
        this
    )

    ;;;
     ; Generates a new key chain with entropy selected randomly from the given {@link java.security.SecureRandom}
     ; object and of the requested size in bits.  The derived seed is further protected with a user selected passphrase
     ; (see BIP 39).
     ;;
    #_public
    (§ constructor #_"DeterministicKeyChain" [#_"SecureRandom" (§ name ßrandom), #_"int" (§ name ßbits), #_"String" (§ name ßpassphrase), #_"long" (§ name ßseedCreationTimeSecs)])
    (§ block
        (§ this (§ new #_"DeterministicSeed" ßrandom, ßbits, ßpassphrase, ßseedCreationTimeSecs))
        this
    )

    ;;;
     ; Creates a deterministic key chain starting from the given entropy.  All keys yielded by this chain will be the
     ; same if the starting seed is the same.  You should provide the creation time in seconds since the UNIX epoch
     ; for the seed: this lets us know from what part of the chain we can expect to see derived keys appear.
     ;;
    #_public
    (§ constructor #_"DeterministicKeyChain" [#_"byte[]" (§ name ßentropy), #_"String" (§ name ßpassphrase), #_"long" (§ name ßseedCreationTimeSecs)])
    (§ block
        (§ this (§ new #_"DeterministicSeed" ßentropy, ßpassphrase, ßseedCreationTimeSecs))
        this
    )

    ;;;
     ; Creates a deterministic key chain starting from the given seed.  All keys yielded by this chain will be the
     ; same if the starting seed is the same.
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicSeed" (§ name ßseed)])
    (§ block
        (§ this ßseed, nil)
        this
    )

    ;;;
     ; Creates a deterministic key chain that watches the given (public only) root key.  You can use this to calculate
     ; balances and generally follow along, but spending is not possible with such a chain.  Currently you can't use
     ; this method to watch an arbitrary fragment of some other tree, this limitation may be removed in future.
     ;;
    #_public
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicKey" (§ name ßwatchingKey)])
    (§ block
        (Preconditions/checkArgument (.. ßwatchingKey (isPubKeyOnly)), "Private subtrees not currently supported: if you got this key from DKC.getWatchingKey() then use .dropPrivate().dropParent() on it first.")
        (Preconditions/checkArgument (== (.. ßwatchingKey (getPath) (size)) (.. (getAccountPath) (size))), "You can only watch an account key currently")

        (§ ass (§ name basicKeyChain) (§ new #_"BasicKeyChain"))
        (§ ass (§ name (.. this seed)) nil)
        (§ ass (§ name (.. this rootKey)) nil)
        (.. basicKeyChain (importKey ßwatchingKey))
        (§ ass (§ name hierarchy) (§ new #_"DeterministicHierarchy" ßwatchingKey))
        (initializeHierarchyUnencrypted ßwatchingKey)
        this
    )

    ;;;
     ; <p>Creates a deterministic key chain with the given watch key.  If <code>isFollowing</code> flag is set then this
     ; keychain follows some other keychain.  In a married wallet following keychain represents "spouse's" keychain.</p>
     ; <p>Watch key has to be an account key.</p>
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicKey" (§ name ßwatchKey), #_"boolean" (§ name ßisFollowing)])
    (§ block
        (§ this ßwatchKey)
        (§ ass (§ name (.. this ßisFollowing)) ßisFollowing)
        this
    )

    ;;;
     ; Creates a deterministic key chain with the given watch key and that follows some other keychain.
     ; In a married wallet following keychain represents "spouse".
     ; Watch key has to be an account key.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKeyChain" (§ fn watchAndFollow) [#_"DeterministicKey" (§ name ßwatchKey)])
    (§ block
        (§ new #_"DeterministicKeyChain" ßwatchKey, true)
    )

    ;;;
     ; Creates a key chain that watches the given account key.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKeyChain" (§ fn watch) [#_"DeterministicKey" (§ name ßaccountKey)])
    (§ block
        (§ new #_"DeterministicKeyChain" ßaccountKey)
    )

    ;;;
     ; For use in {@link KeyChainFactory} during deserialization.
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicSeed" (§ name ßseed), #_nilable #_"KeyCrypter" (§ name ßcrypter)])
    (§ block
        (§ ass (§ name (.. this ßseed)) ßseed)
        (§ ass (§ name basicKeyChain) (§ new #_"BasicKeyChain" ßcrypter))
        (§ if (not (.. ßseed (isEncrypted))))
        (§ block
            (§ ass (§ name rootKey) (HDKeyDerivation/createMasterPrivateKey (Preconditions/checkNotNull (.. ßseed (getSeedBytes)))))
            (.. rootKey (setCreationTimeSeconds (.. ßseed (getCreationTimeSeconds))))
            (.. basicKeyChain (importKey rootKey))
            (§ ass (§ name hierarchy) (§ new #_"DeterministicHierarchy" rootKey))
            (§ for (§ var #_"int" (§ name ßi) 1) :for (<= ßi (.. (getAccountPath) (size))) :for (§ recur (+ ßi 1)))
            (§ block
                (.. basicKeyChain (importKey (.. hierarchy (get (.. (getAccountPath) (subList 0, ßi)), false, true))))
            )
            (initializeHierarchyUnencrypted rootKey)
        )
        ;; Else...
        ;; We can't initialize ourselves with just an encrypted seed, so we expected deserialization code
        ;; to do the rest of the setup (loading the root key).
        this
    )

    ;;;
     ; For use in encryption when {@link #toEncrypted(KeyCrypter, KeyParameter)} is called,
     ; so that subclasses can override that method and create an instance of the right class.
     ;
     ; See also {@link #makeKeyChainFromSeed(DeterministicSeed)}.
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"KeyCrypter" (§ name ßcrypter), #_"KeyParameter" (§ name ßaesKey), #_"DeterministicKeyChain" (§ name ßchain)])
    (§ block
        ;; Can't encrypt a watching chain.
        (Preconditions/checkNotNull (.. ßchain rootKey))
        (Preconditions/checkNotNull (.. ßchain seed))

        (Preconditions/checkArgument (not (.. ßchain rootKey (isEncrypted))), "Chain already encrypted")

        (§ ass (§ name (.. this issuedExternalKeys)) (.. ßchain issuedExternalKeys))
        (§ ass (§ name (.. this issuedInternalKeys)) (.. ßchain issuedInternalKeys))

        (§ ass (§ name (.. this lookaheadSize)) (.. ßchain lookaheadSize))
        (§ ass (§ name (.. this lookaheadThreshold)) (.. ßchain lookaheadThreshold))

        (§ ass (§ name (.. this seed)) (.. ßchain seed (encrypt ßcrypter, ßaesKey)))
        (§ ass (§ name basicKeyChain) (§ new #_"BasicKeyChain" ßcrypter))
        ;; The first number is the "account number" but we don't use that feature.
        (§ ass (§ name rootKey) (.. ßchain rootKey (encrypt ßcrypter, ßaesKey, nil)))
        (§ ass (§ name hierarchy) (§ new #_"DeterministicHierarchy" rootKey))
        (.. basicKeyChain (importKey rootKey))

        (§ for (§ var #_"int" (§ name ßi) 1) :for (< ßi (.. (getAccountPath) (size))) :for (§ recur (+ ßi 1)))
        (§ block
            (encryptNonLeaf ßaesKey, ßchain, rootKey, (.. (getAccountPath) (subList 0, ßi)))
        )

        (§ let #_"DeterministicKey" (§ name ßaccount) (encryptNonLeaf ßaesKey, ßchain, rootKey, (getAccountPath)))
        (§ block
            (§ ass (§ name externalParentKey) (encryptNonLeaf ßaesKey, ßchain, ßaccount, (HDUtils/concat (getAccountPath), EXTERNAL_SUBPATH)))
            (§ ass (§ name internalParentKey) (encryptNonLeaf ßaesKey, ßchain, ßaccount, (HDUtils/concat (getAccountPath), INTERNAL_SUBPATH)))

            ;; Now copy the (pubkey only) leaf keys across to avoid rederiving them.  The private key bytes are missing
            ;; anyway so there's nothing to encrypt.
            (§ for (§ var #_"ECKey" (§ name ßeckey)) :for (.. ßchain basicKeyChain (getKeys)))
            (§ block
                (§ let #_"DeterministicKey" (§ name ßkey) (§ cast #_"DeterministicKey" ßeckey))
                (§ block
                    (§ if (!= (.. ßkey (getPath) (size)) (+ (.. (getAccountPath) (size)) 2)))
                    (§ block
                        (§ continue ) ;; Not a leaf key.
                    )

                    (§ let #_"DeterministicKey" (§ name ßparent) (.. hierarchy (get (.. (Preconditions/checkNotNull (.. ßkey (getParent))) (getPath)), false, false)))
                    (§ block
                        ;; Clone the key to the new encrypted hierarchy.
                        (§ ass (§ name ßkey) (§ new #_"DeterministicKey" (.. ßkey (dropPrivateBytes)), ßparent))
                        (.. hierarchy (putKey ßkey))
                        (.. basicKeyChain (importKey ßkey))
                    )
                )
            )
            this
        )
    )

    ;;; Override in subclasses to use a different account derivation path. ;;
    #_protected
    (§ method #_"ImmutableList<ChildNumber>" (§ fn getAccountPath) [])
    (§ block
        ACCOUNT_ZERO_PATH
    )

    #_private
    (§ method- #_"DeterministicKey" (§ fn encryptNonLeaf) [#_"KeyParameter" (§ name ßaesKey), #_"DeterministicKeyChain" (§ name ßchain), #_"DeterministicKey" (§ name ßparent), #_"ImmutableList<ChildNumber>" (§ name ßpath)])
    (§ block
        (§ let #_"DeterministicKey" (§ name ßkey) (.. ßchain hierarchy (get ßpath, false, false)))
        (§ block
            (§ ass (§ name ßkey) (.. ßkey (encrypt (Preconditions/checkNotNull (.. basicKeyChain (getKeyCrypter))), ßaesKey, ßparent)))
            (.. hierarchy (putKey ßkey))
            (.. basicKeyChain (importKey ßkey))
            ßkey
        )
    )

    ;; Derives the account path keys and inserts them into the basic key chain.
    ;; This is important to preserve their order for serialization, amongst other things.
    #_private
    (§ method- #_"void" (§ fn initializeHierarchyUnencrypted) [#_"DeterministicKey" (§ name ßbaseKey)])
    (§ block
        (§ ass (§ name externalParentKey) (.. hierarchy (deriveChild (getAccountPath), false, false, ChildNumber/ZERO)))
        (§ ass (§ name internalParentKey) (.. hierarchy (deriveChild (getAccountPath), false, false, ChildNumber/ONE)))
        (.. basicKeyChain (importKey externalParentKey))
        (.. basicKeyChain (importKey internalParentKey))
        nil
    )

    ;;; Returns a freshly derived key that has not been returned by this method before. ;;
    #_override
    #_public
    (§ method #_"DeterministicKey" (§ fn getKey) [#_"KeyPurpose" (§ name ßpurpose)])
    (§ block
        (.. (getKeys ßpurpose, 1) (get 0))
    )

    ;;; Returns freshly derived key/s that have not been returned by this method before. ;;
    #_override
    #_public
    (§ method #_"List<DeterministicKey>" (§ fn getKeys) [#_"KeyPurpose" (§ name ßpurpose), #_"int" (§ name ßnumberOfKeys)])
    (§ block
        (Preconditions/checkArgument (< 0 ßnumberOfKeys))
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"DeterministicKey" (§ name ßparentKey))
            (§ block
                (§ let #_"int" (§ name ßindex))
                (§ block
                    (§ switch ßpurpose)
                    (§ block
                        ;; Map both REFUND and RECEIVE_KEYS to the same branch for now.  Refunds are a feature of the BIP 70
                        ;; payment protocol.  Later we may wish to map it to a different branch (in a new wallet version?).
                        ;; This would allow a watching wallet to only be able to see inbound payments, but not change
                        ;; (i.e. spends) or refunds.  Might be useful for auditing ...
                        (§ case RECEIVE_FUNDS)
                        (§ case REFUND)
                        (§ block
                            (§ ass (§ name issuedExternalKeys) (+ issuedExternalKeys ßnumberOfKeys))
                            (§ ass (§ name ßindex) issuedExternalKeys)
                            (§ ass (§ name ßparentKey) externalParentKey)
                            (§ break )
                        )
                        (§ case AUTHENTICATION)
                        (§ case CHANGE)
                        (§ block
                            (§ ass (§ name issuedInternalKeys) (+ issuedInternalKeys ßnumberOfKeys))
                            (§ ass (§ name ßindex) issuedInternalKeys)
                            (§ ass (§ name ßparentKey) internalParentKey)
                            (§ break )
                        )
                        (§ default )
                        (§ block
                            (§ throw (§ new #_"UnsupportedOperationException"))
                        )
                    )
                    ;; Optimization: potentially do a very quick key generation for just the number of keys we need if we
                    ;; didn't already create them, ignoring the configured lookahead size.  This ensures we'll be able to
                    ;; retrieve the keys in the following loop, but if we're totally fresh and didn't get a chance to
                    ;; calculate the lookahead keys yet, this will not block waiting to calculate 100+ EC point multiplies.
                    ;; On slow/crappy Android phones looking ahead 100 keys can take ~5 seconds but the OS will kill us
                    ;; if we block for just one second on the UI thread.  Because UI threads may need an address in order
                    ;; to render the screen, we need getKeys to be fast even if the wallet is totally brand new and lookahead
                    ;; didn't happen yet.
                    ;;
                    ;; It's safe to do this because when a network thread tries to calculate a Bloom filter, we'll go ahead
                    ;; and calculate the full lookahead zone there, so network requests will always use the right amount.
                    (§ let #_"List<DeterministicKey>" (§ name ßlookahead) (maybeLookAhead ßparentKey, ßindex, 0, 0))
                    (§ block
                        (.. basicKeyChain (importKeys ßlookahead))
                        (§ let #_"List<DeterministicKey>" (§ name ßkeys) (§ new #_"ArrayList<>" ßnumberOfKeys))
                        (§ block
                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumberOfKeys) :for (§ recur (+ ßi 1)))
                            (§ block
                                (§ let #_"ImmutableList<ChildNumber>" (§ name ßpath) (HDUtils/append (.. ßparentKey (getPath)), (§ new #_"ChildNumber" (+ (- ßindex ßnumberOfKeys) ßi), false)))
                                (§ block
                                    (§ let #_"DeterministicKey" (§ name ßk) (.. hierarchy (get ßpath, false, false)))
                                    (§ block
                                        ;; Just a last minute sanity check before we hand the key out to the app for usage.  This isn't
                                        ;; inspired by any real problem reports from bitcoinj users, but I've heard of cases via the grapevine
                                        ;; of places that lost money due to bitflips causing addresses to not match keys.  Of course in an
                                        ;; environment with flaky RAM there's no real way to always win: bitflips could be introduced at any
                                        ;; other layer.  But as we're potentially retrieving from long term storage here, check anyway.
                                        (checkForBitFlip ßk)
                                        (.. ßkeys (add ßk))
                                    )
                                )
                            )
                            (§ return ßkeys)
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkForBitFlip) [#_"DeterministicKey" (§ name ßk)])
    (§ block
        (§ let #_"DeterministicKey" (§ name ßparent) (Preconditions/checkNotNull (.. ßk (getParent))))
        (§ block
            (§ let #_"byte[]" (§ name ßrederived) (.. (HDKeyDerivation/deriveChildKeyBytesFromPublic ßparent, (.. ßk (getChildNumber)), HDKeyDerivation/PublicDeriveMode/WITH_INVERSION) keyBytes))
            (§ block
                (§ let #_"byte[]" (§ name ßactual) (.. ßk (getPubKey)))
                (§ block
                    (§ if (not (Arrays/equals ßrederived, ßactual)))
                    (§ block
                        (§ throw (§ new #_"IllegalStateException" (String/format Locale/US, "Bit-flip check failed: %s vs %s", (Arrays/toString ßrederived), (Arrays/toString ßactual))))
                    )
                    nil
                )
            )
        )
    )

    ;;;
     ; Mark the DeterministicKey as used.
     ; Also correct the issued{Internal|External}Keys counter, because all lower children seem to be requested already.
     ; If the counter was updated, we also might trigger lookahead.
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn markKeyAsUsed) [#_"DeterministicKey" (§ name ßk)])
    (§ block
        (§ let #_"int" (§ name ßnumChildren) (+ (.. ßk (getChildNumber) (i)) 1))
        (§ block

            (§ if (== (.. ßk (getParent)) internalParentKey))
            (§ block
                (§ if (< issuedInternalKeys ßnumChildren))
                (§ block
                    (§ ass (§ name issuedInternalKeys) ßnumChildren)
                    (maybeLookAhead)
                )
            )
            (§ elseif (== (.. ßk (getParent)) externalParentKey))
            (§ block
                (§ if (< issuedExternalKeys ßnumChildren))
                (§ block
                    (§ ass (§ name issuedExternalKeys) ßnumChildren)
                    (maybeLookAhead)
                )
            )
            ßk
        )
    )

