    #_override
    #_public
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])
    (§ block
        (§ let #_"List<Protos.Key>" (§ name ßresult) (newArrayList))
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (.. ßresult (addAll (serializeMyselfToProtobuf)))
