    #_protected
    (§ method #_"List<Protos.Key>" (§ fn serializeMyselfToProtobuf) [])
    (§ block
        ;; Most of the serialization work is delegated to the basic key chain, which will serialize the bulk of the
        ;; data (handling encryption along the way), and letting us patch it up with the extra data we care about.
        (§ let #_"LinkedList<Protos.Key>" (§ name ßentries) (newLinkedList))
        (§ block
            (§ if (some? seed))
            (§ block
                (§ let #_"Protos.Key.Builder" (§ name ßmnemonicEntry) (BasicKeyChain/serializeEncryptableItem seed))
                (§ block
                    (.. ßmnemonicEntry (setType Protos/Key/Type/DETERMINISTIC_MNEMONIC))
                    (serializeSeedEncryptableItem seed, ßmnemonicEntry)
                    (.. ßentries (add (.. ßmnemonicEntry (build))))
                )
            )
            (§ let #_"Map<ECKey, Protos.Key.Builder>" (§ name ßkeys) (.. basicKeyChain (serializeToEditableProtobufs)))
            (§ block
                (§ for (§ var #_"Map.Entry<ECKey, Protos.Key.Builder>" (§ name ßentry)) :for (.. ßkeys (entrySet)))
                (§ block
                    (§ let #_"DeterministicKey" (§ name ßkey) (§ cast #_"DeterministicKey" (.. ßentry (getKey))))
                    (§ block
                        (§ let #_"Protos.Key.Builder" (§ name ßproto) (.. ßentry (getValue)))
                        (§ block
                            (.. ßproto (setType Protos/Key/Type/DETERMINISTIC_KEY))
                            (§ let #_"Protos.DeterministicKey.Builder" (§ name ßdetKey) (.. ßproto (getDeterministicKeyBuilder)))
                            (§ block
                                (.. ßdetKey (setChainCode (ByteString/copyFrom (.. ßkey (getChainCode)))))
                                (§ for (§ var #_"ChildNumber" (§ name ßnum)) :for (.. ßkey (getPath)))
                                (§ block
                                    (.. ßdetKey (addPath (.. ßnum (i))))
                                )
                                (§ if (.. ßkey (equals externalParentKey)))
                                (§ block
                                    (.. ßdetKey (setIssuedSubkeys issuedExternalKeys))
                                    (.. ßdetKey (setLookaheadSize lookaheadSize))
                                    (.. ßdetKey (setSigsRequiredToSpend (getSigsRequiredToSpend)))
                                )
                                (§ elseif (.. ßkey (equals internalParentKey)))
                                (§ block
                                    (.. ßdetKey (setIssuedSubkeys issuedInternalKeys))
                                    (.. ßdetKey (setLookaheadSize lookaheadSize))
                                    (.. ßdetKey (setSigsRequiredToSpend (getSigsRequiredToSpend)))
                                )
                                ;; Flag the very first key of following keychain.
                                (§ if (and (.. ßentries (isEmpty)) (isFollowing)))
                                (§ block
                                    (.. ßdetKey (setIsFollowing true))
                                )
                                ;; HD keys inherit the timestamp of their parent if they have one, so no need to serialize it.
                                (§ if (!= (.. ßkey (getParent)) nil))
                                (§ block
                                    (.. ßproto (clearCreationTimestamp))
                                )
                                (.. ßentries (add (.. ßproto (build))))
                            )
                        )
                    )
                )
                ßentries
            )
        )
    )

    #_static
    (§ defn #_"List<DeterministicKeyChain>" (§ fn fromProtobuf) [#_"List<Protos.Key>" (§ name ßkeys), #_nilable #_"KeyCrypter" (§ name ßcrypter)])
