    )

    ;;;
     ; Returns all the key chains found in the given list of keys.  Typically there will only be one,
     ; but in the case of key rotation it can happen that there are multiple chains found.
     ;;
    #_public
    #_static
    (§ defn #_"List<DeterministicKeyChain>" (§ fn fromProtobuf) [#_"List<Protos.Key>" (§ name ßkeys), #_nilable #_"KeyCrypter" (§ name ßcrypter), #_"KeyChainFactory" (§ name ßfactory)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"List<DeterministicKeyChain>" (§ name ßchains) (newLinkedList))
        (§ block
            (§ let #_"DeterministicSeed" (§ name ßseed) nil)
            (§ block
                (§ let #_"DeterministicKeyChain" (§ name ßchain) nil)
                (§ block

                    (§ let #_"int" (§ name ßlookaheadSize) -1)
                    (§ block
                        (§ let #_"int" (§ name ßsigsRequiredToSpend) 1)
                        (§ block

                            (§ let #_"PeekingIterator<Protos.Key>" (§ name ßiter) (Iterators/peekingIterator (.. ßkeys (iterator))))
                            (§ block
                                (§ while (.. ßiter (hasNext)))
                                (§ block
                                    (§ let #_"Protos.Key" (§ name ßkey) (.. ßiter (next)))
                                    (§ block
                                        (§ let #_"Protos.Key.Type" (§ name ßt) (.. ßkey (getType)))
                                        (§ block
                                            (§ if (== ßt Protos/Key/Type/DETERMINISTIC_MNEMONIC))
                                            (§ block
                                                (§ if (some? ßchain))
                                                (§ block
                                                    (Preconditions/checkState (<= 0 ßlookaheadSize))

                                                    (.. ßchain (setLookaheadSize ßlookaheadSize))
                                                    (.. ßchain (setSigsRequiredToSpend ßsigsRequiredToSpend))
                                                    (.. ßchain (maybeLookAhead))
                                                    (.. ßchains (add ßchain))
                                                    (§ ass (§ name ßchain) nil)
                                                )
                                                (§ let #_"long" (§ name ßtimestamp) (/ (.. ßkey (getCreationTimestamp)) 1000))
                                                (§ block
                                                    (§ let #_"String" (§ name ßpassphrase) DEFAULT_PASSPHRASE_FOR_MNEMONIC) ;; FIXME allow non-empty passphrase
                                                    (§ block
                                                        (§ if (.. ßkey (hasSecretBytes)))
                                                        (§ block
                                                            (§ if (.. ßkey (hasEncryptedDeterministicSeed)))
                                                            (§ block
                                                                (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " ßkey)))
                                                            )

                                                            (§ let #_"byte[]" (§ name ßseedBytes) nil)
                                                            (§ block
                                                                (§ if (.. ßkey (hasDeterministicSeed)))
                                                                (§ block
                                                                    (§ ass (§ name ßseedBytes) (.. ßkey (getDeterministicSeed) (toByteArray)))
                                                                )
                                                                (§ ass (§ name ßseed) (§ new #_"DeterministicSeed" (.. ßkey (getSecretBytes) (toStringUtf8)), ßseedBytes, ßpassphrase, ßtimestamp))
                                                            )
                                                        )
                                                        (§ elseif (.. ßkey (hasEncryptedData)))
                                                        (§ block
                                                            (§ if (.. ßkey (hasDeterministicSeed)))
                                                            (§ block
                                                                (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " ßkey)))
                                                            )

                                                            (§ let #_"EncryptedData" (§ name ßdata) (§ new #_"EncryptedData" (.. ßkey (getEncryptedData) (getInitialisationVector) (toByteArray)), (.. ßkey (getEncryptedData) (getEncryptedPrivateKey) (toByteArray))))
                                                            (§ block
                                                                (§ let #_"EncryptedData" (§ name ßencryptedSeedBytes) nil)
                                                                (§ block
                                                                    (§ if (.. ßkey (hasEncryptedDeterministicSeed)))
                                                                    (§ block
                                                                        (§ let #_"Protos.EncryptedData" (§ name ßencryptedSeed) (.. ßkey (getEncryptedDeterministicSeed)))
                                                                        (§ block
                                                                            (§ ass (§ name ßencryptedSeedBytes) (§ new #_"EncryptedData" (.. ßencryptedSeed (getInitialisationVector) (toByteArray)), (.. ßencryptedSeed (getEncryptedPrivateKey) (toByteArray))))
                                                                        )
                                                                    )
                                                                    (§ ass (§ name ßseed) (§ new #_"DeterministicSeed" ßdata, ßencryptedSeedBytes, ßtimestamp))
                                                                )
                                                            )
                                                        )
                                                        (§ else )
                                                        (§ block
                                                            (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " ßkey)))
                                                        )

                                                        (§ if (.. log (isDebugEnabled)))
                                                        (§ block
                                                            (.. log (debug "Deserializing: DETERMINISTIC_MNEMONIC: {}", ßseed))
                                                        )
                                                    )
                                                )
                                            )
                                            (§ elseif (== ßt Protos/Key/Type/DETERMINISTIC_KEY))
                                            (§ block
                                                (§ if (not (.. ßkey (hasDeterministicKey))))
                                                (§ block
                                                    (§ throw (§ new #_"UnreadableWalletException" (str "Deterministic key missing extra data: " ßkey)))
                                                )

                                                (§ let #_"byte[]" (§ name ßchainCode) (.. ßkey (getDeterministicKey) (getChainCode) (toByteArray)))
                                                (§ block
                                                    ;; Deserialize the path through the tree.
                                                    (§ let #_"LinkedList<ChildNumber>" (§ name ßpath) (newLinkedList))
                                                    (§ block
                                                        (§ for (§ var #_"int" (§ name ßi)) :for (.. ßkey (getDeterministicKey) (getPathList)))
                                                        (§ block
                                                            (.. ßpath (add (§ new #_"ChildNumber" ßi)))
                                                        )

                                                        ;; Deserialize the public key and path.
                                                        (§ let #_"LazyECPoint" (§ name ßpubkey) (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), (.. ßkey (getPublicKey) (toByteArray))))
                                                        (§ block
                                                            (§ let #_"ImmutableList<ChildNumber>" (§ name ßimmutablePath) (ImmutableList/copyOf ßpath))
                                                            (§ block
                                                                ;; Possibly create the chain, if we didn't already do so yet.
                                                                (§ let #_"boolean" (§ name ßisWatchingAccountKey) false)
                                                                (§ block
                                                                    (§ let #_"boolean" (§ name ßisFollowingKey) false)
                                                                    (§ block
                                                                        ;; Save previous chain if any if the key is marked as following.  Current key and the next ones
                                                                        ;; are to be placed in new following key chain.
                                                                        (§ if (.. ßkey (getDeterministicKey) (getIsFollowing)))
                                                                        (§ block
                                                                            (§ if (some? ßchain))
                                                                            (§ block
                                                                                (Preconditions/checkState (<= 0 ßlookaheadSize))

                                                                                (.. ßchain (setLookaheadSize ßlookaheadSize))
                                                                                (.. ßchain (setSigsRequiredToSpend ßsigsRequiredToSpend))
                                                                                (.. ßchain (maybeLookAhead))
                                                                                (.. ßchains (add ßchain))
                                                                                (§ ass (§ name ßchain) nil)
                                                                                (§ ass (§ name ßseed) nil)
                                                                            )
                                                                            (§ ass (§ name ßisFollowingKey) true)
                                                                        )

                                                                        (§ if (nil? ßchain))
                                                                        (§ block
                                                                            ;; If this is not a following chain and previous was, this must be married.
                                                                            (§ let #_"boolean" (§ name ßisMarried) (and (not ßisFollowingKey) (not (.. ßchains (isEmpty))) (.. ßchains (get (- (.. ßchains (size)) 1)) (isFollowing))))
                                                                            (§ block
                                                                                (§ if (nil? ßseed))
                                                                                (§ block
                                                                                    (§ let #_"DeterministicKey" (§ name ßaccountKey) (§ new #_"DeterministicKey" ßimmutablePath, ßchainCode, ßpubkey, nil, nil))
                                                                                    (§ block
                                                                                        (.. ßaccountKey (setCreationTimeSeconds (/ (.. ßkey (getCreationTimestamp)) 1000)))
                                                                                        (§ ass (§ name ßchain) (.. ßfactory (makeWatchingKeyChain ßkey, (.. ßiter (peek)), ßaccountKey, ßisFollowingKey, ßisMarried)))
                                                                                        (§ ass (§ name ßisWatchingAccountKey) true)
                                                                                    )
                                                                                )
                                                                                (§ else )
                                                                                (§ block
                                                                                    (§ ass (§ name ßchain) (.. ßfactory (makeKeyChain ßkey, (.. ßiter (peek)), ßseed, ßcrypter, ßisMarried)))
                                                                                    (§ ass (§ name (.. ßchain ßlookaheadSize)) LAZY_CALCULATE_LOOKAHEAD)
                                                                                    ;; If the seed is encrypted, then the chain is incomplete at this point.  However, we will load
                                                                                    ;; it up below as we parse in the keys.  We just need to check at the end that we've loaded
                                                                                    ;; everything afterwards.
                                                                                )
                                                                            )
                                                                        )
                                                                        ;; Find the parent key assuming this is not the root key, and not an account key for a watching chain.
                                                                        (§ let #_"DeterministicKey" (§ name ßparent) nil)
                                                                        (§ block
                                                                            (§ if (and (not (.. ßpath (isEmpty))) (not ßisWatchingAccountKey)))
                                                                            (§ block
                                                                                (§ let #_"ChildNumber" (§ name ßindex) (.. ßpath (removeLast)))
                                                                                (§ block
                                                                                    (§ ass (§ name ßparent) (.. ßchain hierarchy (get ßpath, false, false)))
                                                                                    (.. ßpath (add ßindex))
                                                                                )
                                                                            )
                                                                            (§ let #_"DeterministicKey" (§ name ßdetkey))
                                                                            (§ block
                                                                                (§ if (.. ßkey (hasSecretBytes)))
                                                                                (§ block
                                                                                    ;; Not encrypted: private key is available.
                                                                                    (§ let #_"BigInteger" (§ name ßpriv) (§ new #_"BigInteger" 1, (.. ßkey (getSecretBytes) (toByteArray))))
                                                                                    (§ block
                                                                                        (§ ass (§ name ßdetkey) (§ new #_"DeterministicKey" ßimmutablePath, ßchainCode, ßpubkey, ßpriv, ßparent))
                                                                                    )
                                                                                )
                                                                                (§ else )
                                                                                (§ block
                                                                                    (§ if (.. ßkey (hasEncryptedData)))
                                                                                    (§ block
                                                                                        (§ let #_"Protos.EncryptedData" (§ name ßproto) (.. ßkey (getEncryptedData)))
                                                                                        (§ block
                                                                                            (§ let #_"EncryptedData" (§ name ßdata) (§ new #_"EncryptedData" (.. ßproto (getInitialisationVector) (toByteArray)), (.. ßproto (getEncryptedPrivateKey) (toByteArray))))
                                                                                            (§ block
                                                                                                (Preconditions/checkNotNull ßcrypter, "Encountered an encrypted ßkey but no ßkey ßcrypter provided")
                                                                                                (§ ass (§ name ßdetkey) (§ new #_"DeterministicKey" ßimmutablePath, ßchainCode, ßcrypter, ßpubkey, ßdata, ßparent))
                                                                                            )
                                                                                        )
                                                                                    )
                                                                                    (§ else )
                                                                                    (§ block
                                                                                        ;; No secret key bytes and key is not encrypted: either a watching key or private key bytes
                                                                                        ;; will be rederived on the fly from the parent.
                                                                                        (§ ass (§ name ßdetkey) (§ new #_"DeterministicKey" ßimmutablePath, ßchainCode, ßpubkey, nil, ßparent))
                                                                                    )
                                                                                )
                                                                                (§ if (.. ßkey (hasCreationTimestamp)))
                                                                                (§ block
                                                                                    (.. ßdetkey (setCreationTimeSeconds (/ (.. ßkey (getCreationTimestamp)) 1000)))
                                                                                )
                                                                                (§ if (.. log (isDebugEnabled)))
                                                                                (§ block
                                                                                    (.. log (debug "Deserializing: DETERMINISTIC_KEY: {}", ßdetkey))
                                                                                )
                                                                                (§ if (not ßisWatchingAccountKey))
                                                                                (§ block
                                                                                    ;; If the non-encrypted case, the non-leaf keys (account, internal, external) have already
                                                                                    ;; been rederived and inserted at this point.  In the encrypted case though, we can't
                                                                                    ;; rederive and we must reinsert, potentially building the heirarchy object if need be.
                                                                                    (§ if (== (.. ßpath (size)) 0))
                                                                                    (§ block
                                                                                        ;; Master key.
                                                                                        (§ if (== (.. ßchain rootKey) nil))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. ßchain rootKey)) ßdetkey)
                                                                                            (§ ass (§ name (.. ßchain hierarchy)) (§ new #_"DeterministicHierarchy" ßdetkey))
                                                                                        )
                                                                                    )
                                                                                    (§ elseif (== (.. ßpath (size)) (+ (.. ßchain (getAccountPath) (size)) 1)))
                                                                                    (§ block
                                                                                        (§ if (== (.. ßdetkey (getChildNumber) (num)) 0))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. ßchain externalParentKey)) ßdetkey)
                                                                                            (§ ass (§ name (.. ßchain issuedExternalKeys)) (.. ßkey (getDeterministicKey) (getIssuedSubkeys)))
                                                                                            (§ ass (§ name ßlookaheadSize) (Math/max ßlookaheadSize, (.. ßkey (getDeterministicKey) (getLookaheadSize))))
                                                                                            (§ ass (§ name ßsigsRequiredToSpend) (.. ßkey (getDeterministicKey) (getSigsRequiredToSpend)))
                                                                                        )
                                                                                        (§ elseif (== (.. ßdetkey (getChildNumber) (num)) 1))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. ßchain internalParentKey)) ßdetkey)
                                                                                            (§ ass (§ name (.. ßchain issuedInternalKeys)) (.. ßkey (getDeterministicKey) (getIssuedSubkeys)))
                                                                                        )
                                                                                    )
                                                                                )
                                                                                (.. ßchain hierarchy (putKey ßdetkey))
                                                                                (.. ßchain basicKeyChain (importKey ßdetkey))
                                                                            )
                                                                        )
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                                (§ if (some? ßchain))
                                (§ block
                                    (Preconditions/checkState (>= ßlookaheadSize 0))
                                    (.. ßchain (setLookaheadSize ßlookaheadSize))
                                    (.. ßchain (setSigsRequiredToSpend ßsigsRequiredToSpend))
                                    (.. ßchain (maybeLookAhead))
                                    (.. ßchains (add ßchain))
                                )
                                ßchains
                            )
                        )
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toEncrypted) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull ßpassword)
        (Preconditions/checkArgument (< 0 (.. ßpassword (length))))
        (Preconditions/checkState (some? seed), "Attempt to encrypt a watching chain.")
        (Preconditions/checkState (not (.. seed (isEncrypted))))

        (§ let #_"KeyCrypter" (§ name ßscrypt) (§ new #_"KeyCrypterScrypt"))
        (§ block
            (§ let #_"KeyParameter" (§ name ßderivedKey) (.. ßscrypt (deriveKey ßpassword)))
            (§ block
                (toEncrypted ßscrypt, ßderivedKey)
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toEncrypted) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (§ new #_"DeterministicKeyChain" ßkeyCrypter, ßaesKey, this)
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toDecrypted) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull ßpassword)
        (Preconditions/checkArgument (< 0 (.. ßpassword (length))))

        (§ let #_"KeyCrypter" (§ name ßcrypter) (getKeyCrypter))
        (§ block
            (Preconditions/checkState (some? ßcrypter), "Chain not encrypted")
            (§ let #_"KeyParameter" (§ name ßderivedKey) (.. ßcrypter (deriveKey ßpassword)))
            (§ block
                (toDecrypted ßderivedKey)
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toDecrypted) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkState (some? (getKeyCrypter)), "Key chain not encrypted")
        (Preconditions/checkState (some? seed), "Can't decrypt a watching chain")
        (Preconditions/checkState (.. seed (isEncrypted)))

        (§ let #_"String" (§ name ßpassphrase) DEFAULT_PASSPHRASE_FOR_MNEMONIC) ;; FIXME allow non-empty passphrase
        (§ block
            (§ let #_"DeterministicSeed" (§ name ßdecSeed) (.. seed (decrypt (getKeyCrypter), ßpassphrase, ßaesKey)))
            (§ block
                (§ let #_"DeterministicKeyChain" (§ name ßchain) (makeKeyChainFromSeed ßdecSeed))
                (§ block
                    ;; Now double check that the keys match to catch the case where the key is wrong but padding didn't catch it.
                    (§ if (not (.. ßchain (getWatchingKey) (getPubKeyPoint) (equals (.. (getWatchingKey) (getPubKeyPoint))))))
                    (§ block
                        (§ throw (§ new #_"KeyCrypterException" "Provided AES key is wrong"))
                    )

                    (§ ass (§ name (.. ßchain lookaheadSize)) lookaheadSize)
                    ;; Now copy the (pubkey only) leaf keys across to avoid rederiving them.  The private key bytes are missing
                    ;; anyway so there's nothing to decrypt.
                    (§ for (§ var #_"ECKey" (§ name ßeckey)) :for (.. basicKeyChain (getKeys)))
                    (§ block
                        (§ let #_"DeterministicKey" (§ name ßkey) (§ cast #_"DeterministicKey" ßeckey))
                        (§ block
                            (§ if (!= (.. ßkey (getPath) (size)) (+ (.. (getAccountPath) (size)) 2)))
                            (§ block
                                (§ continue ) ;; Not a leaf key.
                            )

                            (Preconditions/checkState (.. ßkey (isEncrypted)))
                            (§ let #_"DeterministicKey" (§ name ßparent) (.. ßchain hierarchy (get (.. (Preconditions/checkNotNull (.. ßkey (getParent))) (getPath)), false, false)))
                            (§ block
                                ;; Clone the key to the new decrypted hierarchy.
                                (§ ass (§ name ßkey) (§ new #_"DeterministicKey" (.. ßkey (dropPrivateBytes)), ßparent))
                                (.. ßchain hierarchy (putKey ßkey))
                                (.. ßchain basicKeyChain (importKey ßkey))
                            )
                        )
                    )
                    (§ ass (§ name (.. ßchain issuedExternalKeys)) issuedExternalKeys)
                    (§ ass (§ name (.. ßchain issuedInternalKeys)) issuedInternalKeys)
                    ßchain
                )
            )
        )
    )

    ;;;
     ; Factory method to create a key chain from a seed.
     ; Subclasses should override this to create an instance of the subclass instead of a plain DKC.
     ; This is used in encryption/decryption.
     ;;
    #_protected
    (§ method #_"DeterministicKeyChain" (§ fn makeKeyChainFromSeed) [#_"DeterministicSeed" (§ name ßseed)])
    (§ block
        (§ new #_"DeterministicKeyChain" ßseed)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn checkPassword) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkNotNull ßpassword)
        (Preconditions/checkState (some? (getKeyCrypter)), "Key chain not encrypted")

        (checkAESKey (.. (getKeyCrypter) (deriveKey ßpassword)))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkState (some? rootKey), "Can't check password for a watching chain")
        (Preconditions/checkNotNull ßaesKey)
        (Preconditions/checkState (some? (getKeyCrypter)), "Key chain not encrypted")

        (§ try )
        (§ block
            (§ return (.. rootKey (decrypt ßaesKey) (getPubKeyPoint) (equals (.. rootKey (getPubKeyPoint)))))
        )
        (§ catch #_"KeyCrypterException" (§ name _))
        (§ block
            (§ return false)
        )
    )

    #_nilable
    #_override
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        (.. basicKeyChain (getKeyCrypter))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn numBloomFilterEntries) [])
    (§ block
        (* (numKeys) 2)
    )

    #_override
    #_public
    (§ method #_"BloomFilter" (§ fn getFilter) [#_"int" (§ name ßsize), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßtweak)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkArgument (<= (numBloomFilterEntries) ßsize))
            (maybeLookAhead)
            (§ return (.. basicKeyChain (getFilter ßsize, ßfalsePositiveRate, ßtweak)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; <p>The number of public keys we should pre-generate on each path before they are requested by the app.  This is
     ; required so that when scanning through the chain given only a seed, we can give enough keys to the remote node
     ; via the Bloom filter such that we see transactions that are "from the future", for example transactions created
     ; by a different app that's sharing the same seed, or transactions we made before but we're replaying the chain
     ; given just the seed. The default is 100.</p>
     ;;
    #_public
    (§ method #_"int" (§ fn getLookaheadSize) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return lookaheadSize)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Sets a new lookahead size.  See {@link #getLookaheadSize()} for details on what this is.  Setting a new size
     ; that's larger than the current size will return immediately and the new size will only take effect next time
     ; a fresh filter is requested (e.g. due to a new peer being connected).  So you should set this before starting
     ; to sync the chain, if you want to modify it.  If you haven't modified the lookahead threshold manually then
     ; it will be automatically set to be a third of the new size.
     ;;
    #_public
    (§ method #_"void" (§ fn setLookaheadSize) [#_"int" (§ name ßlookaheadSize)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" (§ name ßreadjustThreshold) (== (.. this lookaheadThreshold) (calcDefaultLookaheadThreshold)))
            (§ block
                (§ ass (§ name (.. this ßlookaheadSize)) ßlookaheadSize)
                (§ if ßreadjustThreshold)
                (§ block
                    (§ ass (§ name (.. this lookaheadThreshold)) (calcDefaultLookaheadThreshold))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Sets the threshold for the key pre-generation.  This is used to avoid adding new keys and thus re-calculating
     ; Bloom filters every time a new key is calculated.  Without a lookahead threshold, every time we received
     ; a relevant transaction, we'd extend the lookahead zone and generate a new filter, which is inefficient.
     ;;
    #_public
    (§ method #_"void" (§ fn setLookaheadThreshold) [#_"int" (§ name ßnum)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (<= lookaheadSize ßnum))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" "Threshold larger or equal to the lookaheadSize"))
            )

            (§ ass (§ name (.. this lookaheadThreshold)) ßnum)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Gets the threshold for the key pre-generation.  See {@link #setLookaheadThreshold(int)} for details on what
     ; this is.  The default is a third of the lookahead size (100 / 3 == 33).  If you don't modify it explicitly,
     ; then this value will always be one third of the lookahead size.
     ;;
    #_public
    (§ method #_"int" (§ fn getLookaheadThreshold) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (if (< lookaheadThreshold lookaheadSize) lookaheadThreshold 0))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Pre-generate enough keys to reach the lookahead size.  You can call this if you need to explicitly invoke
     ; the lookahead procedure, but it's normally unnecessary as it will be done automatically when needed.
     ;;
    #_public
    (§ method #_"void" (§ fn maybeLookAhead) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"List<DeterministicKey>" (§ name ßkeys) (maybeLookAhead externalParentKey, issuedExternalKeys))
            (§ block
                (.. ßkeys (addAll (maybeLookAhead internalParentKey, issuedInternalKeys)))
                (§ if (.. ßkeys (isEmpty)))
                (§ block
                    (§ return nil)
                )

                (§ ass (§ name keyLookaheadEpoch) (+ keyLookaheadEpoch 1))
                ;; Batch add all keys at once so there's only one event listener invocation, as this will be listened to
                ;; by the wallet and used to rebuild/broadcast the Bloom filter.  That's expensive so we don't want to do
                ;; it more often than necessary.
                (.. basicKeyChain (importKeys ßkeys))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_private
    (§ method- #_"List<DeterministicKey>" (§ fn maybeLookAhead) [#_"DeterministicKey" (§ name ßparent), #_"int" (§ name ßissued)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (maybeLookAhead ßparent, ßissued, (getLookaheadSize), (getLookaheadThreshold))
    )

    ;;;
     ; Pre-generate enough keys to reach the lookahead size, but only if there are more than the lookaheadThreshold
     ; to be generated, so that the Bloom filter does not have to be regenerated that often.
     ;
     ; The returned mutable list of keys must be inserted into the basic key chain.
     ;;
    #_private
    (§ method- #_"List<DeterministicKey>" (§ fn maybeLookAhead) [#_"DeterministicKey" (§ name ßparent), #_"int" (§ name ßissued), #_"int" (§ name ßlookaheadSize), #_"int" (§ name ßlookaheadThreshold)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ let #_"int" (§ name ßnumChildren) (.. hierarchy (getNumChildren (.. ßparent (getPath)))))
        (§ block
            (§ let #_"int" (§ name ßneeded) (- (+ ßissued ßlookaheadSize ßlookaheadThreshold) ßnumChildren))
            (§ block

                (§ if (<= ßneeded ßlookaheadThreshold))
                (§ block
                    (§ return (§ new #_"ArrayList<>"))
                )

                (.. log (info "{} keys ßneeded for {} = {} ßissued + {} lookahead size + {} lookahead threshold - {} num children", ßneeded, (.. ßparent (getPathAsString)), ßissued, ßlookaheadSize, ßlookaheadThreshold, ßnumChildren))

                (§ let #_"List<DeterministicKey>" (§ name ßresult) (§ new #_"ArrayList<>" ßneeded))
                (§ block
                    (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
                    (§ block
                        (§ let #_"int" (§ name ßnextChild) ßnumChildren)
                        (§ block
                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßneeded) :for (§ recur (+ ßi 1)))
                            (§ block
                                (§ let #_"DeterministicKey" (§ name ßkey) (HDKeyDerivation/deriveThisOrNextChildKey ßparent, ßnextChild))
                                (§ block
                                    (§ ass (§ name ßkey) (.. ßkey (dropPrivateBytes)))
                                    (.. hierarchy (putKey ßkey))
                                    (.. ßresult (add ßkey))
                                    (§ ass (§ name ßnextChild) (+ (.. ßkey (getChildNumber) (num)) 1))
                                )
                            )
                            (.. ßwatch (stop))
                            (.. log (info "Took {}", ßwatch))
                            ßresult
                        )
                    )
                )
            )
        )
    )

    ;;; Housekeeping call to call when lookahead might be needed.  Normally called automatically by KeychainGroup. ;;
    #_public
    (§ method #_"void" (§ fn maybeLookAheadScripts) [])
    (§ block
        nil
    )

    ;;;
     ; Returns number of keys used on external path.  This may be fewer than the number that have been deserialized
     ; or held in memory, because of the lookahead zone.
     ;;
    #_public
    (§ method #_"int" (§ fn getIssuedExternalKeys) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return issuedExternalKeys)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns number of keys used on internal path.  This may be fewer than the number that have been deserialized
     ; or held in memory, because of the lookahead zone.
     ;;
    #_public
    (§ method #_"int" (§ fn getIssuedInternalKeys) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return issuedInternalKeys)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;; Returns the seed or null if this chain is a watching chain. ;;
    #_nilable
    #_public
    (§ method #_"DeterministicSeed" (§ fn getSeed) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return seed)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;; For internal usage only.
    (§ method #_"List<ECKey>" (§ fn getKeys) [#_"boolean" (§ name ßincludeLookahead), #_"boolean" (§ name ßincludeParents)])
    (§ block
        (§ let #_"List<ECKey>" (§ name ßkeys) (.. basicKeyChain (getKeys)))
        (§ block
            (§ if (not ßincludeLookahead))
            (§ block
                (§ let #_"int" (§ name ßtreeSize) (.. internalParentKey (getPath) (size)))
                (§ block
                    (§ let #_"List<ECKey>" (§ name ßissuedKeys) (§ new #_"LinkedList<>"))
                    (§ block
                        (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßkeys)
                        (§ block
                            (§ let #_"DeterministicKey" (§ name ßdetkey) (§ cast #_"DeterministicKey" ßkey))
                            (§ block
                                (§ let #_"DeterministicKey" (§ name ßparent) (.. ßdetkey (getParent)))
                                (§ block
                                    (§ if (and (not ßincludeParents) (nil? ßparent)))
                                    (§ block
                                        (§ continue )
                                    )
                                    (§ if (and (not ßincludeParents) (<= (.. ßdetkey (getPath) (size)) ßtreeSize)))
                                    (§ block
                                        (§ continue )
                                    )
                                    (§ if (and (.. internalParentKey (equals ßparent)) (<= issuedInternalKeys (.. ßdetkey (getChildNumber) (i)))))
                                    (§ block
                                        (§ continue )
                                    )
                                    (§ if (and (.. externalParentKey (equals ßparent)) (<= issuedExternalKeys (.. ßdetkey (getChildNumber) (i)))))
                                    (§ block
                                        (§ continue )
                                    )
                                    (.. ßissuedKeys (add ßdetkey))
                                )
                            )
                        )
                        (§ return ßissuedKeys)
                    )
                )
            )
            ßkeys
        )
    )

    ;;;
     ; Returns only the external keys that have been issued by this chain, lookahead not included.
     ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getIssuedReceiveKeys) [])
    (§ block
        (§ let #_"List<ECKey>" (§ name ßkeys) (§ new #_"ArrayList<>" (getKeys false, false)))
        (§ block
            (§ for (§ var #_"Iterator<ECKey>" (§ name ßi) (.. ßkeys (iterator))) :for (.. ßi (hasNext)) :for )
            (§ block
                (§ let #_"DeterministicKey" (§ name ßparent) (.. (§ cast #_"DeterministicKey" (.. ßi (next))) (getParent)))
                (§ block
                    (§ if (or (nil? ßparent) (not (.. externalParentKey (equals ßparent)))))
                    (§ block
                        (.. ßi (remove))
                    )
                )
            )
            ßkeys
        )
    )

    ;;;
     ; Returns leaf keys issued by this chain (including lookahead zone).
     ;;
    #_public
    (§ method #_"List<DeterministicKey>" (§ fn getLeafKeys) [])
    (§ block
        (§ let #_"ImmutableList.Builder<DeterministicKey>" (§ name ßkeys) (ImmutableList/builder))
        (§ block
            (§ for (§ var #_"ECKey" (§ name ßkey)) :for (getKeys true, false))
            (§ block
                (§ let #_"DeterministicKey" (§ name ßdKey) (§ cast #_"DeterministicKey" ßkey))
                (§ block
                    (§ if (== (.. ßdKey (getPath) (size)) (+ (.. (getAccountPath) (size)) 2)))
                    (§ block
                        (.. ßkeys (add ßdKey))
                    )
                )
            )
            (.. ßkeys (build))
        )
    )

    #_static
    (§ defn #_"void" (§ fn serializeSeedEncryptableItem) [#_"DeterministicSeed" (§ name ßseed), #_"Protos.Key.Builder" (§ name ßproto)])
    (§ block
        ;; The seed can be missing if we have not derived it yet from the mnemonic.
        ;; This will not normally happen once all the wallets are on the latest code that caches the seed.
        (§ if (and (.. ßseed (isEncrypted)) (some? (.. ßseed (getEncryptedSeedData)))))
        (§ block
            (§ let #_"EncryptedData" (§ name ßdata) (.. ßseed (getEncryptedSeedData)))
            (§ block
                (.. ßproto (getEncryptedDeterministicSeedBuilder) (setEncryptedPrivateKey (ByteString/copyFrom (.. ßdata encryptedBytes))) (setInitialisationVector (ByteString/copyFrom (.. ßdata initialisationVector))))
                ;; We don't allow mixing of encryption types at the moment.
                (Preconditions/checkState (== (.. ßseed (getEncryptionType)) Protos/Wallet/EncryptionType/ENCRYPTED_SCRYPT_AES))
            )
        )
        (§ else )
        (§ block
            (§ let #_"byte[]" (§ name ßsecret) (.. ßseed (getSeedBytes)))
            (§ block
                (§ if (some? ßsecret))
                (§ block
                    (.. ßproto (setDeterministicSeed (ByteString/copyFrom ßsecret)))
                )
            )
        )
        nil
    )

    ;;;
     ; Returns a counter that is incremented each time new keys are generated due to lookahead.
     ; Used by the network code to learn whether to discard the current block and await calculation
     ; of a new filter.
     ;;
    #_public
    (§ method #_"int" (§ fn getKeyLookaheadEpoch) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return keyLookaheadEpoch)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Whether the keychain is married.  A keychain is married when it vends P2SH addresses from
     ; multiple keychains in a multisig relationship.
     ; @see org.bitcoinj.wallet.MarriedKeyChain
     ;;
    #_public
    (§ method #_"boolean" (§ fn isMarried) [])
    (§ block
        false
    )

    ;;; Get redeem data for a key.  Only applicable to married keychains. ;;
    #_public
    (§ method #_"RedeemData" (§ fn getRedeemData) [#_"DeterministicKey" (§ name ßfollowedKey)])
    (§ block
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    ;;; Create a new key and return the matching output script.  Only applicable to married keychains. ;;
    #_public
    (§ method #_"Script" (§ fn freshOutputScript) [#_"KeyPurpose" (§ name ßpurpose)])
    (§ block
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    #_public
    (§ method #_"String" (§ fn toString) [#_"boolean" (§ name ßincludePrivateKeys), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ let #_"DeterministicKey" (§ name ßwatchingKey) (getWatchingKey))
        (§ block
            (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
            (§ block
                (§ if (some? seed))
                (§ block
                    (§ if (.. seed (isEncrypted)))
                    (§ block
                        (.. ßsb (append "Seed is encrypted\n"))
                    )
                    (§ elseif ßincludePrivateKeys)
                    (§ block
                        (§ let #_"List<String>" (§ name ßwords) (.. seed (getMnemonicCode)))
                        (§ block
                            (.. ßsb (append "Seed as ßwords: ") (append (.. Utils/SPACE_JOINER (join ßwords))) (append "\n"))
                            (.. ßsb (append "Seed as hex:   ") (append (.. seed (toHexString))) (append "\n"))
                        )
                    )
                    (.. ßsb (append "Seed birthday: ") (append (.. seed (getCreationTimeSeconds))) (append "  [") (append (Utils/dateTimeFormat (* (.. seed (getCreationTimeSeconds)) 1000))) (append "]\n"))
                )
                (§ else )
                (§ block
                    (.. ßsb (append "Key birthday:  ") (append (.. ßwatchingKey (getCreationTimeSeconds))) (append "  [") (append (Utils/dateTimeFormat (* (.. ßwatchingKey (getCreationTimeSeconds)) 1000))) (append "]\n"))
                )
                (.. ßsb (append "Key to watch:  ") (append (.. ßwatchingKey (serializePubB58 ßparams))) (append "\n"))
                (formatAddresses ßincludePrivateKeys, ßparams, ßsb)
                (.. ßsb (toString))
            )
        )
    )

    #_protected
    (§ method #_"void" (§ fn formatAddresses) [#_"boolean" (§ name ßincludePrivateKeys), #_"NetworkParameters" (§ name ßparams), #_"StringBuilder" (§ name ßbuilder)])
    (§ block
        (§ for (§ var #_"ECKey" (§ name ßkey)) :for (getKeys false, true))
        (§ block
            (.. ßkey (formatKeyWithAddress ßincludePrivateKeys, ßbuilder, ßparams))
        )
        nil
    )

    ;;; The number of signatures required to spend coins received by this keychain. ;;
    #_public
    (§ method #_"void" (§ fn setSigsRequiredToSpend) [#_"int" (§ name ßsigsRequiredToSpend)])
    (§ block
        (§ ass (§ name (.. this ßsigsRequiredToSpend)) ßsigsRequiredToSpend)
        nil
    )

    ;;;
     ; Returns the number of signatures required to spend transactions for this KeyChain.
     ; It's the N from N-of-M CHECKMULTISIG script for P2SH transactions and always 1 for other transaction types.
     ;;
    #_public
    (§ method #_"int" (§ fn getSigsRequiredToSpend) [])
    (§ block
        sigsRequiredToSpend
    )

    ;;; Returns the redeem script by its hash or null if this keychain did not generate the script. ;;
    #_nilable
    #_public
    (§ method #_"RedeemData" (§ fn findRedeemDataByScriptHash) [#_"ByteString" (§ name ßbytes)])
    (§ block
        nil
    )
)

#_(ns org.bitcoinj.wallet #_"DeterministicSeed"
    (:import [java.security SecureRandom]
             [java.util List])
    (:import [com.google.common.base Charsets Objects Preconditions Splitter]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core Utils]
             #_static [org.bitcoinj.core.Utils HEX]
             [org.bitcoinj.crypto *]))

;;;
 ; Holds the seed bytes for the BIP32 deterministic wallet algorithm, inside a {@link DeterministicKeyChain}.
 ; The purpose of this wrapper is to simplify the encryption code.
 ;;
#_public
(§ class #_"DeterministicSeed" (§ implements #_"EncryptableItem")
    ;; It would take more than 10^12 years to brute-force a 128 bit seed using $1B worth of computing equipment.
    #_public
    #_static
    (§ def #_"int" (§ name DEFAULT_SEED_ENTROPY_BITS) 128)
    #_public
    #_static
    (§ def #_"int" (§ name MAX_SEED_ENTROPY_BITS) 512)

    #_nilable
    #_private
    (§ field- #_"byte[]" (§ name seed))
    #_nilable
    #_private
    (§ field- #_"List<String>" (§ name mnemonicCode)) ;; only one of mnemonicCode/encryptedMnemonicCode will be set
    #_nilable
    #_private
    (§ field- #_"EncryptedData" (§ name encryptedMnemonicCode))
    #_nilable
    #_private
    (§ field- #_"EncryptedData" (§ name encryptedSeed))
    #_private
    (§ field- #_"long" (§ name creationTimeSeconds))

    #_public
    (§ constructor #_"DeterministicSeed" [#_"String" (§ name ßmnemonicCode), #_"byte[]" (§ name ßseed), #_"String" (§ name ßpassphrase), #_"long" (§ name ßcreationTimeSeconds)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ this (decodeMnemonicCode ßmnemonicCode), ßseed, ßpassphrase, ßcreationTimeSeconds)
        this
    )

    #_public
    (§ constructor #_"DeterministicSeed" [#_"byte[]" (§ name ßseed), #_"List<String>" (§ name ßmnemonic), #_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ ass (§ name (.. this ßseed)) (Preconditions/checkNotNull ßseed))
        (§ ass (§ name (.. this mnemonicCode)) (Preconditions/checkNotNull ßmnemonic))
        (§ ass (§ name (.. this encryptedMnemonicCode)) nil)
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        this
    )

    #_public
    (§ constructor #_"DeterministicSeed" [#_"EncryptedData" (§ name ßencryptedMnemonic), #_nilable #_"EncryptedData" (§ name ßencryptedSeed), #_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ ass (§ name (.. this seed)) nil)
        (§ ass (§ name (.. this mnemonicCode)) nil)
        (§ ass (§ name (.. this encryptedMnemonicCode)) (Preconditions/checkNotNull ßencryptedMnemonic))
        (§ ass (§ name (.. this ßencryptedSeed)) ßencryptedSeed)
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param mnemonicCode A list of words.
     ; @param seed The derived seed, or pass null to derive it from mnemonicCode (slow).
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor #_"DeterministicSeed" [#_"List<String>" (§ name ßmnemonicCode), #_nilable #_"byte[]" (§ name ßseed), #_"String" (§ name ßpassphrase), #_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ this (or ßseed (MnemonicCode/toSeed ßmnemonicCode, (Preconditions/checkNotNull ßpassphrase))), ßmnemonicCode, ßcreationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param random Entropy source.
     ; @param bits Number of bits, must be divisible by 32.
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor #_"DeterministicSeed" [#_"SecureRandom" (§ name ßrandom), #_"int" (§ name ßbits), #_"String" (§ name ßpassphrase), #_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ this (getEntropy ßrandom, ßbits), (Preconditions/checkNotNull ßpassphrase), ßcreationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param entropy Entropy bits, length must be divisible by 32.
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor #_"DeterministicSeed" [#_"byte[]" (§ name ßentropy), #_"String" (§ name ßpassphrase), #_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (Preconditions/checkArgument (== (% (.. ßentropy length) 4) 0), "entropy size in bits not divisible by 32")
        (Preconditions/checkArgument (<= DEFAULT_SEED_ENTROPY_BITS (* (.. ßentropy length) 8)), "entropy size too small")
        (Preconditions/checkNotNull ßpassphrase)

        (§ try )
        (§ block
            (§ ass (§ name (.. this mnemonicCode)) (.. MnemonicCode/INSTANCE (toMnemonic ßentropy)))
        )
        (§ catch #_"MnemonicException.MnemonicLengthException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        (§ ass (§ name (.. this seed)) (MnemonicCode/toSeed mnemonicCode, ßpassphrase))
        (§ ass (§ name (.. this encryptedMnemonicCode)) nil)
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        this
    )

    #_private
    #_static
    (§ defn- #_"byte[]" (§ fn getEntropy) [#_"SecureRandom" (§ name ßrandom), #_"int" (§ name ßbits)])
    (§ block
        (Preconditions/checkArgument (<= ßbits MAX_SEED_ENTROPY_BITS), "requested entropy size too large")

        (§ let #_"byte[]" (§ name ßseed) (§ new #_"byte[]" (§ count ßbits / 8)))
        (§ block
            (.. ßrandom (nextBytes ßseed))
            ßseed
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn isEncrypted) [])
    (§ block
        (Preconditions/checkState (or (some? mnemonicCode) (some? encryptedMnemonicCode)))
        (some? encryptedMnemonicCode)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (if (isEncrypted) "DeterministicSeed [encrypted]" (str "DeterministicSeed " (toHexString) " " (.. Utils/SPACE_JOINER (join mnemonicCode))))
    )

    ;;; Returns the seed as hex or null if encrypted. ;;
    #_nilable
    #_public
    (§ method #_"String" (§ fn toHexString) [])
    (§ block
        (when (some? seed) (.. HEX (encode seed)))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"byte[]" (§ fn getSecretBytes) [])
    (§ block
        (getMnemonicAsBytes)
    )

    #_nilable
    #_public
    (§ method #_"byte[]" (§ fn getSeedBytes) [])
    (§ block
        seed
    )

    #_nilable
    #_override
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedData) [])
    (§ block
        encryptedMnemonicCode
    )

    #_override
    #_public
    (§ method #_"Protos.Wallet.EncryptionType" (§ fn getEncryptionType) [])
    (§ block
        Protos/Wallet/EncryptionType/ENCRYPTED_SCRYPT_AES
    )

    #_nilable
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedSeedData) [])
    (§ block
        encryptedSeed
    )

    #_override
    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        creationTimeSeconds
    )

    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        nil
    )

    #_public
    (§ method #_"DeterministicSeed" (§ fn encrypt) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkState (nil? encryptedMnemonicCode), "Trying to encrypt seed twice")
        (Preconditions/checkState (some? mnemonicCode), "Mnemonic missing so cannot encrypt")

        (§ let #_"EncryptedData" (§ name ßencryptedMnemonic) (.. ßkeyCrypter (encrypt (getMnemonicAsBytes), ßaesKey)))
        (§ block
            (§ let #_"EncryptedData" (§ name ßencryptedSeed) (.. ßkeyCrypter (encrypt seed, ßaesKey)))
            (§ block
                (§ new #_"DeterministicSeed" ßencryptedMnemonic, ßencryptedSeed, creationTimeSeconds)
            )
        )
    )

    #_private
    (§ method- #_"byte[]" (§ fn getMnemonicAsBytes) [])
    (§ block
        (.. Utils/SPACE_JOINER (join mnemonicCode) (getBytes Charsets/UTF_8))
    )

    #_public
    (§ method #_"DeterministicSeed" (§ fn decrypt) [#_"KeyCrypter" (§ name ßcrypter), #_"String" (§ name ßpassphrase), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkState (isEncrypted))
        (Preconditions/checkNotNull encryptedMnemonicCode)

        (§ let #_"List<String>" (§ name ßmnemonic) (decodeMnemonicCode (.. ßcrypter (decrypt encryptedMnemonicCode, ßaesKey))))
        (§ block
            (§ let #_"byte[]" (§ name ßseed) (when (some? encryptedSeed) (.. ßcrypter (decrypt encryptedSeed, ßaesKey))))
            (§ block
                (§ new #_"DeterministicSeed" ßmnemonic, ßseed, ßpassphrase, creationTimeSeconds)
            )
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"DeterministicSeed" (§ name ßother) (§ cast #_"DeterministicSeed" ßo))
        (§ block
            (and (== creationTimeSeconds (.. ßother creationTimeSeconds)) (Objects/equal encryptedMnemonicCode, (.. ßother encryptedMnemonicCode)) (Objects/equal mnemonicCode, (.. ßother mnemonicCode)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode creationTimeSeconds, encryptedMnemonicCode, mnemonicCode)
    )

    ;;;
     ; Check if our mnemonic is a valid mnemonic phrase for our word list.
     ; Does nothing if we are encrypted.
     ;
     ; @throws org.bitcoinj.crypto.MnemonicException if check fails.
     ;;
    #_public
    (§ method #_"void" (§ fn check) [])
        (§ throws #_"MnemonicException")
    (§ block
        (§ if (some? mnemonicCode))
        (§ block
            (.. MnemonicCode/INSTANCE (check mnemonicCode))
        )
        nil
    )

    (§ method #_"byte[]" (§ fn getEntropyBytes) [])
        (§ throws #_"MnemonicException")
    (§ block
        (.. MnemonicCode/INSTANCE (toEntropy mnemonicCode))
    )

    ;;; Get the mnemonic code, or null if unknown. ;;
    #_nilable
    #_public
    (§ method #_"List<String>" (§ fn getMnemonicCode) [])
    (§ block
        mnemonicCode
    )

    #_private
    #_static
    (§ defn- #_"List<String>" (§ fn decodeMnemonicCode) [#_"byte[]" (§ name ßmnemonicCode)])
    (§ block
        (decodeMnemonicCode (Utils/toString ßmnemonicCode, "UTF-8"))
    )

    #_private
    #_static
    (§ defn- #_"List<String>" (§ fn decodeMnemonicCode) [#_"String" (§ name ßmnemonicCode)])
    (§ block
        (.. (Splitter/on " ") (splitToList ßmnemonicCode))
    )
)

#_(ns org.bitcoinj.wallet #_"DeterministicUpgradeRequiredException")

;;;
 ; Indicates that an attempt was made to use HD wallet features on a wallet that was deserialized from an old,
 ; pre-HD random wallet without calling upgradeToDeterministic() beforehand.
 ;;
#_public
(§ class #_"DeterministicUpgradeRequiredException" (§ extends #_"RuntimeException"))

#_(ns org.bitcoinj.wallet #_"DeterministicUpgradeRequiresPassword")

;;;
 ; Indicates that the pre-HD random wallet is encrypted, so you should try the upgrade again after getting the
 ; users password.  This is required because HD wallets are upgraded from random using the private key bytes of
 ; the oldest non-rotating key, in order to make the upgrade process itself deterministic.
 ;;
#_public
(§ class #_"DeterministicUpgradeRequiresPassword" (§ extends #_"RuntimeException"))

#_(ns org.bitcoinj.wallet #_"EncryptableKeyChain"
    (:import [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.crypto KeyCrypter KeyCrypterException]))

;;;
 ; An encryptable key chain is a key-chain that can be encrypted with a user-provided password or AES key.
 ;;
#_public
(§ interface #_"EncryptableKeyChain" (§ extends #_"KeyChain")
    ;;;
     ; Takes the given password, which should be strong, derives a key from it and then invokes
     ; {@link #toEncrypted(org.bitcoinj.crypto.KeyCrypter, org.spongycastle.crypto.params.KeyParameter)}
     ; with {@link org.bitcoinj.crypto.KeyCrypterScrypt} as the crypter.
     ;
     ; @return the derived key, in case you wish to cache it for future use.
     ;;
    (§ method #_"EncryptableKeyChain" (§ fn toEncrypted) [#_"CharSequence" __password])

    ;;;
     ; Returns a new keychain holding identical/cloned keys to this chain, but encrypted under the given key.
     ; Old keys and keychains remain valid and so you should ensure you don't accidentally hold references to them.
     ;;
    (§ method #_"EncryptableKeyChain" (§ fn toEncrypted) [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])

    ;;;
     ; Decrypts the key chain with the given password.
     ; See {@link #toDecrypted(org.spongycastle.crypto.params.KeyParameter)} for details.
     ;;
    (§ method #_"EncryptableKeyChain" (§ fn toDecrypted) [#_"CharSequence" __password])

    ;;;
     ; Decrypt the key chain with the given AES key and whatever {@link KeyCrypter} is already set.  Note that
     ; if you just want to spend money from an encrypted wallet, don't decrypt the whole thing first.  Instead,
     ; set the {@link org.bitcoinj.wallet.SendRequest#aesKey} field before asking the wallet to build the send.
     ;
     ; @param aesKey AES key to use (normally created using KeyCrypter#deriveKey and cached as it is time
     ;               consuming to create from a password).
     ; @throws KeyCrypterException if the wallet decryption fails.  If so, the wallet state is unchanged.
     ;;
    (§ method #_"EncryptableKeyChain" (§ fn toDecrypted) [#_"KeyParameter" __aesKey])

    (§ method #_"boolean" (§ fn checkPassword) [#_"CharSequence" __password])
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" __aesKey])

    ;;; Returns the key crypter used by this key chain, or null if it's not encrypted. ;;
    #_nilable
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
)

#_(ns org.bitcoinj.wallet #_"FilteringCoinSelector"
    (:import [java.util HashSet Iterator List])
   (:require [org.bitcoinj.core *]))

;;;
 ; A filtering coin selector delegates to another coin selector, but won't select outputs spent by the given transactions.
 ;;
#_public
(§ class #_"FilteringCoinSelector" (§ implements #_"CoinSelector")
    #_protected
    (§ field #_"CoinSelector" (§ name delegate))
    #_protected
    (§ field #_"HashSet<TransactionOutPoint>" (§ name spent) (§ new #_"HashSet<>"))

    #_public
    (§ constructor #_"FilteringCoinSelector" [#_"CoinSelector" (§ name ßdelegate)])
    (§ block
        (§ ass (§ name (.. this ßdelegate)) ßdelegate)
        this
    )

    #_public
    (§ method #_"void" (§ fn excludeOutputsSpentBy) [#_"Transaction" (§ name ßtx)])
    (§ block
        (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
        (§ block
            (.. spent (add (.. ßinput (getOutpoint))))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"CoinSelection" (§ fn select) [#_"Coin" (§ name ßtarget), #_"List<TransactionOutput>" (§ name ßcandidates)])
    (§ block
        (§ let #_"Iterator<TransactionOutput>" (§ name ßiter) (.. ßcandidates (iterator)))
        (§ block
            (§ while (.. ßiter (hasNext)))
            (§ block
                (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßiter (next)))
                (§ block
                    (§ if (.. spent (contains (.. ßoutput (getOutPointFor)))))
                    (§ block
                        (.. ßiter (remove))
                    )
                )
            )
            (.. delegate (select ßtarget, ßcandidates))
        )
    )
)

#_(ns org.bitcoinj.wallet #_"KeyBag"
   (:require [org.bitcoinj.core ECKey]))

;;;
 ; A KeyBag is simply an object that can map public keys, their 160-bit hashes and script hashes to ECKey
 ; and {@link RedeemData} objects.
 ;;
#_public
(§ interface #_"KeyBag"
    ;;;
     ; Locates a keypair from the keychain given the hash of the public key.  This is needed when finding out
     ; which key we need to use to redeem a transaction output.
     ;
     ; @return ECKey object or null if no such key was found.
     ;;
    #_nilable
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" __pubkeyHash])

    ;;;
     ; Locates a keypair from the keychain given the raw public key bytes.
     ;
     ; @return ECKey or null if no such key was found.
     ;;
    #_nilable
    (§ method #_"ECKey" (§ fn findKeyFromPubKey) [#_"byte[]" __pubkey])

    ;;;
     ; Locates a redeem data (redeem script and keys) from the keychain given the hash of the script.
     ; This is needed when finding out which key and script we need to use to locally sign a P2SH transaction input.
     ; It is assumed that wallet should not have more than one private key for a single P2SH tx for security reasons.
     ;
     ; @return RedeemData object or null if no such data was found.
     ;;
    #_nilable
    (§ method #_"RedeemData" (§ fn findRedeemDataFromScriptHash) [#_"byte[]" __scriptHash])
)

#_(ns org.bitcoinj.wallet #_"KeyChain"
    (:import [java.util List]
             [java.util.concurrent Executor])
   (:require [org.bitcoinj.core BloomFilter ECKey]
             [org.bitcoinj.wallet.listeners KeyChainEventListener]))

;;;
 ; <p>A KeyChain is a class that stores a collection of keys for a {@link org.bitcoinj.wallet.Wallet}.  Key chains
 ; are expected to be able to look up keys given a hash (i.e. address) or pubkey bytes, and provide keys on request
 ; for a given purpose.  They can inform event listeners about new keys being added.</p>
 ;
 ; <p>However it is important to understand what this interface does <i>not</i> provide.  It cannot encrypt or decrypt
 ; keys, for instance you need an implementor of {@link EncryptableKeyChain}.  It cannot have keys imported into it,
 ; that you to use a method of a specific key chain instance, such as {@link BasicKeyChain}.  The reason for these
 ; restrictions is to support key chains that may be handled by external hardware or software, or which are derived
 ; deterministically from a seed (and thus the notion of importing a key is meaningless).</p>
 ;;
#_public
(§ interface #_"KeyChain"
    ;;; Returns true if the given key is in the chain. ;;
    (§ method #_"boolean" (§ fn hasKey) [#_"ECKey" __key])

    (§ enum #_"KeyPurpose"
        (§ item RECEIVE_FUNDS)
        (§ item CHANGE)
        (§ item REFUND)
        (§ item AUTHENTICATION)
    )

    ;;;
     ; Obtains a number of key/s intended for the given purpose.
     ; The chain may create new key/s, derive, or re-use an old one.
     ;;
    (§ method #_"List<? extends ECKey>" (§ fn getKeys) [#_"KeyPurpose" __purpose, #_"int" __numberOfKeys])

    ;;;
     ; Obtains a key intended for the given purpose.
     ; The chain may create a new key, derive one, or re-use an old one.
     ;;
    (§ method #_"ECKey" (§ fn getKey) [#_"KeyPurpose" __purpose])

    ;;; Returns a list of keys serialized to the bitcoinj protobuf format. ;;
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])

    ;;; Adds a listener for events that are run when keys are added, on the user thread. ;;
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" __listener])

    ;;; Adds a listener for events that are run when keys are added, on the given executor. ;;
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" __listener, #_"Executor" __executor])

    ;;; Removes a listener for events that are run when keys are added. ;;
    (§ method #_"boolean" (§ fn removeEventListener) [#_"KeyChainEventListener" __listener])

    ;;; Returns the number of keys this key chain manages. ;;
    (§ method #_"int" (§ fn numKeys) [])

    ;;;
     ; Returns the number of elements this chain wishes to insert into the Bloom filter.
     ; The size passed to {@link #getFilter(int, double, long)} should be at least this large.
     ;;
    (§ method #_"int" (§ fn numBloomFilterEntries) [])

    ;;;
     ; Returns the earliest creation time of keys in this chain, in seconds since the epoch.  This can return
     ; zero if at least one key does not have that data (was created before key timestamping was implemented).
     ; If there are no keys in the wallet, {@link Long#MAX_VALUE} is returned.
     ;;
    (§ method #_"long" (§ fn getEarliestKeyCreationTime) [])

    ;;;
     ; <p>Gets a bloom filter that contains all of the public keys from this chain, and which will provide the given
     ; false-positive rate if it has size elements.  Keep in mind that you will get 2 elements in the bloom filter for
     ; each key in the key chain, for the public key and the hash of the public key (address form).  For this reason
     ; size should be <i>at least</i> 2x the result of {@link #numKeys()}.</p>
     ;
     ; <p>This is used to generate a {@link BloomFilter} which can be {@link BloomFilter#merge(BloomFilter)}d with
     ; another.  It could also be used if you have a specific target for the filter's size.</p>
     ;
     ; <p>See the docs for {@link org.bitcoinj.core.BloomFilter#BloomFilter(int, double, long)} for a brief
     ; explanation of anonymity when using bloom filters, and for the meaning of these parameters.</p>
     ;;
    (§ method #_"BloomFilter" (§ fn getFilter) [#_"int" __size, #_"double" __falsePositiveRate, #_"long" __tweak])
)

#_(ns org.bitcoinj.wallet #_"KeyChainFactory"
   (:require [org.bitcoinj.crypto DeterministicKey KeyCrypter]))

;;;
 ; Factory interface for creation keychains while de-serializing a wallet.
 ;;
#_public
(§ interface #_"KeyChainFactory"
    ;;;
     ; Make a keychain (but not a watching one).
     ;
     ; @param key The protobuf for the root key.
     ; @param firstSubKey The protobuf for the first child key (normally the parent of the external subchain).
     ; @param seed The seed.
     ; @param crypter The encrypted/decrypter.
     ; @param isMarried Whether the keychain is leading in a marriage.
     ;;
    (§ method #_"DeterministicKeyChain" (§ fn makeKeyChain) [#_"Protos.Key" __key, #_"Protos.Key" __firstSubKey, #_"DeterministicSeed" __seed, #_"KeyCrypter" __crypter, #_"boolean" __isMarried])

    ;;;
     ; Make a watching keychain.
     ;
     ; isMarried and isFollowingKey must not be true at the same time.
     ;
     ; @param key The protobuf for the account key.
     ; @param firstSubKey The protobuf for the first child key (normally the parent of the external subchain).
     ; @param accountKey The account extended public key.
     ; @param isFollowingKey Whether the keychain is following in a marriage.
     ; @param isMarried Whether the keychain is leading in a marriage.
     ;;
    (§ method #_"DeterministicKeyChain" (§ fn makeWatchingKeyChain) [#_"Protos.Key" __key, #_"Protos.Key" __firstSubKey, #_"DeterministicKey" __accountKey, #_"boolean" __isFollowingKey, #_"boolean" __isMarried])
        (§ throws #_"UnreadableWalletException")
)

#_(ns org.bitcoinj.wallet #_"KeyChainGroup"
    (:import [java.security *]
             [java.util *]
             [java.util.concurrent *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect *]
             [com.google.protobuf *]
             [org.slf4j *]
             [org.spongycastle.crypto.params *])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.crypto *]
             [org.bitcoinj.script *]
             [org.bitcoinj.utils *]
             [org.bitcoinj.wallet.listeners KeyChainEventListener]))

;;;
 ; <p>A KeyChainGroup is used by the {@link org.bitcoinj.wallet.Wallet} and manages: a {@link BasicKeyChain} object
 ; (which will normally be empty), and zero or more {@link DeterministicKeyChain}s.  A deterministic key chain will be
 ; created lazily/on demand when a fresh or current key is requested, possibly being initialized from the private key
 ; bytes of the earliest non rotating key in the basic key chain if one is available, or from a fresh random seed if not.</p>
 ;
 ; <p>If a key rotation time is set, it may be necessary to add a new DeterministicKeyChain with a fresh seed and
 ; also preserve the old one, so funds can be swept from the rotating keys.  In this case, there may be more than
 ; one deterministic chain.  The latest chain is called the active chain and is where new keys are served from.</p>
 ;
 ; <p>The wallet delegates most key management tasks to this class.  It is <b>not</b> thread safe and requires external
 ; locking, i.e. by the wallet lock.  The group then in turn delegates most operations to the key chain objects,
 ; combining their responses together when necessary.</p>
 ;
 ; <p>Deterministic key chains have a concept of a lookahead size and threshold.  Please see the discussion in the
 ; class docs for {@link DeterministicKeyChain} for more information on this topic.</p>
 ;;
#_public
(§ class #_"KeyChainGroup" (§ implements #_"KeyBag")
    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )
    )

    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"KeyChainGroup")))

    #_private
    (§ field- #_"BasicKeyChain" (§ name basic))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))
    #_protected
    (§ field #_"LinkedList<DeterministicKeyChain>" (§ name chains))
    ;; currentKeys is used for normal, non-multisig/married wallets.
    ;; currentAddresses is used when we're handing out P2SH addresses.
    ;; They're mutually exclusive.
    #_private
    (§ field- #_"EnumMap<KeyChain.KeyPurpose, DeterministicKey>" (§ name currentKeys))
    #_private
    (§ field- #_"EnumMap<KeyChain.KeyPurpose, Address>" (§ name currentAddresses))

    #_nilable
    #_private
    (§ field- #_"KeyCrypter" (§ name keyCrypter))
    #_private
    (§ field- #_"int" (§ name lookaheadSize) -1)
    #_private
    (§ field- #_"int" (§ name lookaheadThreshold) -1)

    ;;; Creates a keychain group with no basic chain, and a single, lazily created HD chain. ;;
    #_public
    (§ constructor #_"KeyChainGroup" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ this ßparams, nil, (§ new #_"ArrayList<DeterministicKeyChain>" 1), nil, nil)
        this
    )

    ;;; Creates a keychain group with no basic chain, and an HD chain initialized from the given seed. ;;
    #_public
    (§ constructor #_"KeyChainGroup" [#_"NetworkParameters" (§ name ßparams), #_"DeterministicSeed" (§ name ßseed)])
    (§ block
        (§ this ßparams, nil, (ImmutableList/of (§ new #_"DeterministicKeyChain" ßseed)), nil, nil)
        this
    )

    ;;;
     ; Creates a keychain group with no basic chain, and an HD chain that is watching the given watching key.
     ; This HAS to be an account key as returned by {@link DeterministicKeyChain#getWatchingKey()}.
     ;;
    #_public
    (§ constructor #_"KeyChainGroup" [#_"NetworkParameters" (§ name ßparams), #_"DeterministicKey" (§ name ßwatchKey)])
    (§ block
        (§ this ßparams, nil, (ImmutableList/of (DeterministicKeyChain/watch ßwatchKey)), nil, nil)
        this
    )

    ;; Used for deserialization.
    #_private
    (§ constructor- #_"KeyChainGroup" [#_"NetworkParameters" (§ name ßparams), #_nilable #_"BasicKeyChain" (§ name ßbasicKeyChain), #_"List<DeterministicKeyChain>" (§ name ßchains), #_nilable #_"EnumMap<KeyChain.KeyPurpose, DeterministicKey>" (§ name ßcurrentKeys), #_nilable #_"KeyCrypter" (§ name ßcrypter)])
    (§ block
        (§ ass (§ name (.. this ßparams)) ßparams)
        (§ ass (§ name (.. this basic)) (or ßbasicKeyChain (§ new #_"BasicKeyChain")))
        (§ ass (§ name (.. this ßchains)) (§ new #_"LinkedList<>" (Preconditions/checkNotNull ßchains)))
        (§ ass (§ name (.. this keyCrypter)) ßcrypter)
        (§ ass (§ name (.. this ßcurrentKeys)) (or ßcurrentKeys (§ new #_"EnumMap<KeyChain.KeyPurpose, DeterministicKey>" (§ klass #_"KeyChain.KeyPurpose"))))
        (§ ass (§ name (.. this currentAddresses)) (§ new #_"EnumMap<>" (§ klass #_"KeyChain.KeyPurpose")))
        (maybeLookaheadScripts)

        (§ if (isMarried))
        (§ block
            (§ for (§ var #_"Map.Entry<KeyChain.KeyPurpose, DeterministicKey>" (§ name ßentry)) :for (.. this ßcurrentKeys (entrySet)))
            (§ block
                (§ let #_"Address" (§ name ßaddress) (.. (makeP2SHOutputScript (.. ßentry (getValue)), (getActiveKeyChain)) (getToAddress ßparams)))
                (§ block
                    (.. currentAddresses (put (.. ßentry (getKey)), ßaddress))
                )
            )
        )
        this
    )

    ;; This keeps married redeem data in sync with the number of keys issued.
    #_private
    (§ method- #_"void" (§ fn maybeLookaheadScripts) [])
    (§ block
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (.. ßchain (maybeLookAheadScripts))
        )
        nil
    )

    ;;; Adds a new HD chain to the chains list, and make it the default chain (from which keys are issued). ;;
    #_public
    (§ method #_"void" (§ fn createAndActivateNewHDChain) [])
    (§ block
        ;; We can't do auto upgrade here because we don't know the rotation time, if any.
        (§ let #_"DeterministicKeyChain" (§ name ßchain) (§ new #_"DeterministicKeyChain" (§ new #_"SecureRandom")))
        (§ block
            (addAndActivateHDChain ßchain)
            nil
        )
    )

    ;;;
     ; Adds an HD chain to the chains list, and make it the default chain (from which keys are issued).
     ; Useful for adding a complex pre-configured keychain, such as a married wallet.
     ;;
    #_public
    (§ method #_"void" (§ fn addAndActivateHDChain) [#_"DeterministicKeyChain" (§ name ßchain)])
    (§ block
        (.. log (info "Creating and activating a new HD ßchain: {}", ßchain))
        (§ for (§ var #_"ListenerRegistration<KeyChainEventListener>" (§ name ßregistration)) :for (.. basic (getListeners)))
        (§ block
            (.. ßchain (addEventListener (.. ßregistration listener), (.. ßregistration executor)))
        )
        (§ if (<= 0 lookaheadSize))
        (§ block
            (.. ßchain (setLookaheadSize lookaheadSize))
        )
        (§ if (<= 0 lookaheadThreshold))
        (§ block
            (.. ßchain (setLookaheadThreshold lookaheadThreshold))
        )
        (.. chains (add ßchain))
        nil
    )

    ;;;
     ; Returns a key that hasn't been seen in a transaction yet, and which is suitable for displaying in a wallet
     ; user interface as "a convenient key to receive funds on" when the purpose parameter is
     ; {@link KeyChain.KeyPurpose#RECEIVE_FUNDS}.  The returned key is stable until it's actually seen in a pending
     ; or confirmed transaction, at which point this method will start returning a different key (for each purpose
     ; independently).
     ;
     ; <p>This method is not supposed to be used for married keychains and will throw UnsupportedOperationException
     ; if the active chain is married.
     ; For married keychains use {@link #currentAddress(KeyChain.KeyPurpose)} to get a proper P2SH address.</p>
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn currentKey) [#_"KeyChain.KeyPurpose" (§ name ßpurpose)])
    (§ block
        (§ let #_"DeterministicKeyChain" (§ name ßchain) (getActiveKeyChain))
        (§ block
            (§ if (.. ßchain (isMarried)))
            (§ block
                (§ throw (§ new #_"UnsupportedOperationException" "Key is not suitable to receive coins for married keychains. Use freshAddress to get P2SH address instead."))
            )

            (§ let #_"DeterministicKey" (§ name ßcurrent) (.. currentKeys (get ßpurpose)))
            (§ block
                (§ if (nil? ßcurrent))
                (§ block
                    (§ ass (§ name ßcurrent) (freshKey ßpurpose))
                    (.. currentKeys (put ßpurpose, ßcurrent))
                )
                ßcurrent
            )
        )
    )

    ;;;
     ; Returns address for a {@link #currentKey(KeyChain.KeyPurpose)}.
     ;;
    #_public
    (§ method #_"Address" (§ fn currentAddress) [#_"KeyChain.KeyPurpose" (§ name ßpurpose)])
    (§ block
        (§ let #_"DeterministicKeyChain" (§ name ßchain) (getActiveKeyChain))
        (§ block
            (§ if (.. ßchain (isMarried)))
            (§ block
                (§ let #_"Address" (§ name ßcurrent) (.. currentAddresses (get ßpurpose)))
                (§ block
                    (§ if (nil? ßcurrent))
                    (§ block
                        (§ ass (§ name ßcurrent) (freshAddress ßpurpose))
                        (.. currentAddresses (put ßpurpose, ßcurrent))
                    )
                    (§ return ßcurrent)
                )
            )

            (.. (currentKey ßpurpose) (toAddress params))
        )
    )

    ;;;
     ; Returns a key that has not been returned by this method before (fresh).  You can think of this as being a newly
     ; created key, although the notion of "create" is not really valid for a {@link DeterministicKeyChain}.  When the
     ; parameter is {@link KeyChain.KeyPurpose#RECEIVE_FUNDS}, the returned key is suitable for being put into a receive
     ; coins wizard type UI.  You should use this when the user is definitely going to hand this key out to someone who
     ; wishes to send money.
     ; <p>This method is not supposed to be used for married keychains and will throw UnsupportedOperationException if
     ; the active chain is married.
     ; For married keychains use {@link #freshAddress(KeyChain.KeyPurpose)} to get a proper P2SH address.</p>
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn freshKey) [#_"KeyChain.KeyPurpose" (§ name ßpurpose)])
    (§ block
        (.. (freshKeys ßpurpose, 1) (get 0))
    )

    ;;;
     ; Returns a key/s that have not been returned by this method before (fresh).  You can think of this as being newly
     ; created key/s, although the notion of "create" is not really valid for a {@link DeterministicKeyChain}.  When the
     ; parameter is {@link KeyChain.KeyPurpose#RECEIVE_FUNDS}, the returned key is suitable for being put into a receive
     ; coins wizard type UI.  You should use this when the user is definitely going to hand this key out to someone who
     ; wishes to send money.
     ; <p>This method is not supposed to be used for married keychains and will throw UnsupportedOperationException if
     ; the active chain is married.
     ; For married keychains use {@link #freshAddress(KeyChain.KeyPurpose)} to get a proper P2SH address.</p>
     ;;
    #_public
    (§ method #_"List<DeterministicKey>" (§ fn freshKeys) [#_"KeyChain.KeyPurpose" (§ name ßpurpose), #_"int" (§ name ßnumberOfKeys)])
    (§ block
        (§ let #_"DeterministicKeyChain" (§ name ßchain) (getActiveKeyChain))
        (§ block
            (§ if (.. ßchain (isMarried)))
            (§ block
                (§ throw (§ new #_"UnsupportedOperationException" "Key is not suitable to receive coins for married keychains. Use freshAddress to get P2SH address instead."))
            )

            (.. ßchain (getKeys ßpurpose, ßnumberOfKeys)) ;; Always returns the next key along the key chain.
        )
    )

    ;;;
     ; Returns address for a {@link #freshKey(KeyChain.KeyPurpose)}.
     ;;
    #_public
    (§ method #_"Address" (§ fn freshAddress) [#_"KeyChain.KeyPurpose" (§ name ßpurpose)])
    (§ block
        (§ let #_"DeterministicKeyChain" (§ name ßchain) (getActiveKeyChain))
        (§ block
            (§ if (.. ßchain (isMarried)))
            (§ block
                (§ let #_"Script" (§ name ßoutputScript) (.. ßchain (freshOutputScript ßpurpose)))
                (§ block
                    (Preconditions/checkState (.. ßoutputScript (isPayToScriptHash))) ;; Only handle P2SH for now.
                    (§ let #_"Address" (§ name ßfreshAddress) (Address/fromP2SHScript params, ßoutputScript))
                    (§ block
                        (maybeLookaheadScripts)
                        (.. currentAddresses (put ßpurpose, ßfreshAddress))
                        (§ return ßfreshAddress)
                    )
                )
            )

            (.. (freshKey ßpurpose) (toAddress params))
        )
    )

    ;;; Returns the key chain that's used for generation of fresh/current keys.  This is always the newest HD chain. ;;
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn getActiveKeyChain) [])
    (§ block
        (§ if (.. chains (isEmpty)))
        (§ block
            (§ if (< 0 (.. basic (numKeys))))
            (§ block
                (.. log (warn "No HD chain present but random keys are: you probably deserialized an old wallet."))
                ;; If called from the wallet (most likely) it'll try to upgrade us, as it knows the rotation time but not the password.
                (§ throw (§ new #_"DeterministicUpgradeRequiredException"))
            )
            ;; Otherwise we have no HD chains and no random keys: we are a new born!  So a random seed is fine.
            (createAndActivateNewHDChain)
        )
        (.. chains (get (- (.. chains (size)) 1)))
    )

    ;;;
     ; Sets the lookahead buffer size for ALL deterministic key chains as well as for following key chains if any exist.
     ; See {@link DeterministicKeyChain#setLookaheadSize(int)} for more information.
     ;;
    #_public
    (§ method #_"void" (§ fn setLookaheadSize) [#_"int" (§ name ßlookaheadSize)])
    (§ block
        (§ ass (§ name (.. this ßlookaheadSize)) ßlookaheadSize)
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (.. ßchain (setLookaheadSize ßlookaheadSize))
        )
        nil
    )

    ;;;
     ; Gets the current lookahead size being used for ALL deterministic key chains.
     ; See {@link DeterministicKeyChain#setLookaheadSize(int)} for more information.
     ;;
    #_public
    (§ method #_"int" (§ fn getLookaheadSize) [])
    (§ block
        (if (== lookaheadSize -1) (.. (getActiveKeyChain) (getLookaheadSize)) lookaheadSize)
    )

    ;;;
     ; Sets the lookahead buffer threshold for ALL deterministic key chains.
     ; See {@link DeterministicKeyChain#setLookaheadThreshold(int)} for more information.
     ;;
    #_public
    (§ method #_"void" (§ fn setLookaheadThreshold) [#_"int" (§ name ßnum)])
    (§ block
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (.. ßchain (setLookaheadThreshold ßnum))
        )
        nil
    )

    ;;;
     ; Gets the current lookahead threshold being used for ALL deterministic key chains.
     ; See {@link DeterministicKeyChain#setLookaheadThreshold(int)} for more information.
     ;;
    #_public
    (§ method #_"int" (§ fn getLookaheadThreshold) [])
    (§ block
        (if (== lookaheadThreshold -1) (.. (getActiveKeyChain) (getLookaheadThreshold)) lookaheadThreshold)
    )

    ;;; Imports the given keys into the basic chain, creating it if necessary. ;;
    #_public
    (§ method #_"int" (§ fn importKeys) [#_"List<ECKey>" (§ name ßkeys)])
    (§ block
        (.. basic (importKeys ßkeys))
    )

    ;;; Imports the given keys into the basic chain, creating it if necessary. ;;
    #_public
    (§ method #_"int" (§ fn importKeys) [#_"ECKey..." (§ name ßkeys)])
    (§ block
        (importKeys (ImmutableList/copyOf ßkeys))
    )

    #_public
    (§ method #_"boolean" (§ fn checkPassword) [#_"CharSequence" (§ name ßpassword)])
    (§ block
        (Preconditions/checkState (some? keyCrypter), "Not encrypted")

        (checkAESKey (.. keyCrypter (deriveKey ßpassword)))
    )

    #_public
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkState (some? keyCrypter), "Not encrypted")

        (if (< 0 (.. basic (numKeys))) (.. basic (checkAESKey ßaesKey)) (.. (getActiveKeyChain) (checkAESKey ßaesKey)))
    )

    ;;; Imports the given unencrypted keys into the basic chain, encrypting them along the way with the given key. ;;
    #_public
    (§ method #_"int" (§ fn importKeysAndEncrypt) [#_"List<ECKey>" (§ name ßkeys), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        ;; TODO: Firstly check if the aes key can decrypt any of the existing keys successfully.
        (Preconditions/checkState (some? keyCrypter), "Not encrypted")

        (§ let #_"LinkedList<ECKey>" (§ name ßencryptedKeys) (Lists/newLinkedList))
        (§ block
            (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßkeys)
            (§ block
                (§ if (.. ßkey (isEncrypted)))
                (§ block
                    (§ throw (§ new #_"IllegalArgumentException" "Cannot provide already encrypted keys"))
                )

                (.. ßencryptedKeys (add (.. ßkey (encrypt keyCrypter, ßaesKey))))
            )
            (importKeys ßencryptedKeys)
        )
    )

    #_override
    #_nilable
    #_public
    (§ method #_"RedeemData" (§ fn findRedeemDataFromScriptHash) [#_"byte[]" (§ name ßscriptHash)])
    (§ block
        ;; Iterate in reverse order, since the active keychain is the one most likely to have the hit.
        (§ for (§ var #_"Iterator<DeterministicKeyChain>" (§ name ßiter) (.. chains (descendingIterator))) :for (.. ßiter (hasNext)) :for )
        (§ block
            (§ let #_"DeterministicKeyChain" (§ name ßchain) (.. ßiter (next)))
            (§ block
                (§ let #_"RedeemData" (§ name ßredeemData) (.. ßchain (findRedeemDataByScriptHash (ByteString/copyFrom ßscriptHash))))
                (§ block
                    (§ if (some? ßredeemData))
                    (§ block
                        (§ return ßredeemData)
                    )
                )
            )
        )
        nil
    )

    #_public
    (§ method #_"void" (§ fn markP2SHAddressAsUsed) [#_"Address" (§ name ßaddress)])
    (§ block
        (Preconditions/checkArgument (.. ßaddress (isP2SHAddress)))

        (§ let #_"RedeemData" (§ name ßdata) (findRedeemDataFromScriptHash (.. ßaddress (getHash160))))
        (§ block
            (§ if (nil? ßdata))
            (§ block
                (§ return nil) ;; Not our P2SH address.
            )

            (§ for (§ var #_"ECKey" (§ name ßkey)) :for (.. ßdata keys))
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
                (§ block
                    (§ let #_"DeterministicKey" (§ name ßk) (.. ßchain (findKeyFromPubKey (.. ßkey (getPubKey)))))
                    (§ block
                        (§ if (some? ßk))
                        (§ block
                            (.. ßchain (markKeyAsUsed ßk))
                            (maybeMarkCurrentAddressAsUsed ßaddress)
                        )
                    )
                )
            )
            nil
        )
    )

    #_nilable
    #_override
    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" (§ name ßpubkeyHash)])
    (§ block
        (§ let #_"ECKey" (§ name ßresult) (.. basic (findKeyFromPubHash ßpubkeyHash)))
        (§ block
            (§ if (some? ßresult))
            (§ block
                (§ return ßresult)
            )

            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (§ ass (§ name ßresult) (.. ßchain (findKeyFromPubHash ßpubkeyHash)))
                (§ if (some? ßresult))
                (§ block
                    (§ return ßresult)
                )
            )

            nil
        )
    )

    ;;;
     ; Mark the DeterministicKeys as used, if they match the pubkeyHash.
     ; See {@link DeterministicKeyChain#markKeyAsUsed(DeterministicKey)} for more info on this.
     ;;
    #_public
    (§ method #_"void" (§ fn markPubKeyHashAsUsed) [#_"byte[]" (§ name ßpubkeyHash)])
    (§ block
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (§ let #_"DeterministicKey" (§ name ßkey) (.. ßchain (markPubHashAsUsed ßpubkeyHash)))
            (§ block
                (§ if (some? ßkey))
                (§ block
                    (maybeMarkCurrentKeyAsUsed ßkey)
                    (§ return nil)
                )
            )
        )
        nil
    )

    ;;; If the given P2SH address is "current", advance it to a new one. ;;
    #_private
    (§ method- #_"void" (§ fn maybeMarkCurrentAddressAsUsed) [#_"Address" (§ name ßaddress)])
    (§ block
        (Preconditions/checkArgument (.. ßaddress (isP2SHAddress)))

        (§ for (§ var #_"Map.Entry<KeyChain.KeyPurpose, Address>" (§ name ßentry)) :for (.. currentAddresses (entrySet)))
        (§ block
            (§ if (and (some? (.. ßentry (getValue))) (.. ßentry (getValue) (equals ßaddress))))
            (§ block
                (.. log (info "Marking P2SH ßaddress as used: {}", ßaddress))
                (.. currentAddresses (put (.. ßentry (getKey)), (freshAddress (.. ßentry (getKey)))))
                (§ return nil)
            )
        )
        nil
    )

    ;;; If the given key is "current", advance the current key to a new one. ;;
    #_private
    (§ method- #_"void" (§ fn maybeMarkCurrentKeyAsUsed) [#_"DeterministicKey" (§ name ßkey)])
    (§ block
        ;; It's OK for currentKeys to be empty here: it means we're a married wallet and the key may be a part of a rotating chain.
        (§ for (§ var #_"Map.Entry<KeyChain.KeyPurpose, DeterministicKey>" (§ name ßentry)) :for (.. currentKeys (entrySet)))
        (§ block
            (§ if (and (some? (.. ßentry (getValue))) (.. ßentry (getValue) (equals ßkey))))
            (§ block
                (.. log (info "Marking ßkey as used: {}", ßkey))
                (.. currentKeys (put (.. ßentry (getKey)), (freshKey (.. ßentry (getKey)))))
                (§ return nil)
            )
        )
        nil
    )

    #_public
    (§ method #_"boolean" (§ fn hasKey) [#_"ECKey" (§ name ßkey)])
    (§ block
        (§ if (.. basic (hasKey ßkey)))
        (§ block
            (§ return true)
        )
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (§ if (.. ßchain (hasKey ßkey)))
            (§ block
                (§ return true)
            )
        )
        false
    )

    #_nilable
    #_override
    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubKey) [#_"byte[]" (§ name ßpubkey)])
    (§ block
        (§ let #_"ECKey" (§ name ßresult) (.. basic (findKeyFromPubKey ßpubkey)))
        (§ block
            (§ if (some? ßresult))
            (§ block
                (§ return ßresult)
            )

            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (§ ass (§ name ßresult) (.. ßchain (findKeyFromPubKey ßpubkey)))
                (§ if (some? ßresult))
                (§ block
                    (§ return ßresult)
                )
            )

            nil
        )
    )

    ;;;
     ; Mark the DeterministicKeys as used, if they match the pubkey.
     ; See {@link DeterministicKeyChain#markKeyAsUsed(DeterministicKey)} for more info on this.
     ;;
    #_public
    (§ method #_"void" (§ fn markPubKeyAsUsed) [#_"byte[]" (§ name ßpubkey)])
    (§ block
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (§ let #_"DeterministicKey" (§ name ßkey) (.. ßchain (markPubKeyAsUsed ßpubkey)))
            (§ block
                (§ if (some? ßkey))
                (§ block
                    (maybeMarkCurrentKeyAsUsed ßkey)
                    (§ return nil)
                )
            )
        )
        nil
    )

    ;;; Returns the number of keys managed by this group, including the lookahead buffers. ;;
    #_public
    (§ method #_"int" (§ fn numKeys) [])
    (§ block
        (§ let #_"int" (§ name ßresult) (.. basic (numKeys)))
        (§ block
            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (§ ass (§ name ßresult) (+ ßresult (.. ßchain (numKeys))))
            )
            ßresult
        )
    )

    ;;;
     ; Removes a key that was imported into the basic key chain.  You cannot remove deterministic keys.
     ; @throws java.lang.IllegalArgumentException if the key is deterministic.
     ;;
    #_public
    (§ method #_"boolean" (§ fn removeImportedKey) [#_"ECKey" (§ name ßkey)])
    (§ block
        (Preconditions/checkNotNull ßkey)
        (Preconditions/checkArgument (not (§ insta ßkey #_"DeterministicKey")))

        (.. basic (removeKey ßkey))
    )

    ;;;
     ; Whether the active keychain is married.  A keychain is married when it vends P2SH addresses
     ; from multiple keychains in a multisig relationship.
     ; @see org.bitcoinj.wallet.MarriedKeyChain
     ;;
    #_public
    (§ method #_"boolean" (§ fn isMarried) [])
    (§ block
        (and (not (.. chains (isEmpty))) (.. (getActiveKeyChain) (isMarried)))
    )

    ;;;
     ; Encrypt the keys in the group using the KeyCrypter and the AES key.
     ; A good default KeyCrypter to use is {@link org.bitcoinj.crypto.KeyCrypterScrypt}.
     ;
     ; @throws org.bitcoinj.crypto.KeyCrypterException if the wallet encryption fails for some reason,
     ;         leaving the group unchanged.
     ; @throws DeterministicUpgradeRequiredException if there are random keys but no HD chain.
     ;;
    #_public
    (§ method #_"void" (§ fn encrypt) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (Preconditions/checkNotNull ßkeyCrypter)
        (Preconditions/checkNotNull ßaesKey)

        ;; This code must be exception safe.
        (§ let #_"BasicKeyChain" (§ name ßnewBasic) (.. basic (toEncrypted ßkeyCrypter, ßaesKey)))
        (§ block
            (§ let #_"List<DeterministicKeyChain>" (§ name ßnewChains) (§ new #_"ArrayList<>" (.. chains (size))))
            (§ block
                (§ if (and (.. chains (isEmpty)) (== (.. basic (numKeys)) 0)))
                (§ block
                    ;; No HD chains and no random keys: encrypting an entirely empty keychain group.
                    ;; But we can't do that, we must have something to encrypt: so instantiate a new HD chain here.
                    (createAndActivateNewHDChain)
                )
                (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
                (§ block
                    (.. ßnewChains (add (.. ßchain (toEncrypted ßkeyCrypter, ßaesKey))))
                )
                (§ ass (§ name (.. this ßkeyCrypter)) ßkeyCrypter)
                (§ ass (§ name basic) ßnewBasic)
                (.. chains (clear))
                (.. chains (addAll ßnewChains))
                nil
            )
        )
    )

    ;;;
     ; Decrypt the keys in the group using the previously given key crypter and the AES key.
     ; A good default KeyCrypter to use is {@link org.bitcoinj.crypto.KeyCrypterScrypt}.
     ;
     ; @throws org.bitcoinj.crypto.KeyCrypterException if the wallet decryption fails for some reason, leaving the group unchanged.
     ;;
    #_public
    (§ method #_"void" (§ fn decrypt) [#_"KeyParameter" (§ name ßaesKey)])
    (§ block
        ;; This code must be exception safe.
        (Preconditions/checkNotNull ßaesKey)

        (§ let #_"BasicKeyChain" (§ name ßnewBasic) (.. basic (toDecrypted ßaesKey)))
        (§ block
            (§ let #_"List<DeterministicKeyChain>" (§ name ßnewChains) (§ new #_"ArrayList<>" (.. chains (size))))
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
                (§ block
                    (.. ßnewChains (add (.. ßchain (toDecrypted ßaesKey))))
                )

                (§ ass (§ name (.. this keyCrypter)) nil)
                (§ ass (§ name basic) ßnewBasic)
                (.. chains (clear))
                (.. chains (addAll ßnewChains))
                nil
            )
        )
    )

    ;;; Returns true if the group is encrypted. ;;
    #_public
    (§ method #_"boolean" (§ fn isEncrypted) [])
    (§ block
        (some? keyCrypter)
    )

    ;;;
     ; Returns whether this chain has only watching keys (unencrypted keys with no private part).
     ; Mixed chains are forbidden.
     ;
     ; @throws IllegalStateException if there are no keys, or if there is a mix between watching and non-watching keys.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isWatching) [])
    (§ block
        (§ let #_"BasicKeyChain.State" (§ name ßactiveState) BasicKeyChain/State/EMPTY)
        (§ block
            (§ if (not (.. chains (isEmpty))))
            (§ block
                (§ ass (§ name ßactiveState) (if (.. (getActiveKeyChain) (isWatching)) BasicKeyChain/State/WATCHING BasicKeyChain/State/REGULAR))
            )

            (§ let #_"BasicKeyChain.State" (§ name ßbasicState) (.. basic (isWatching)))
            (§ block
                (§ if (== ßbasicState BasicKeyChain/State/EMPTY))
                (§ block
                    (§ if (== ßactiveState BasicKeyChain/State/EMPTY))
                    (§ block
                        (§ throw (§ new #_"IllegalStateException" "Empty key chain group: cannot answer isWatching() query"))
                    )

                    (§ return (== ßactiveState BasicKeyChain/State/WATCHING))
                )

                (§ if (== ßactiveState BasicKeyChain/State/EMPTY))
                (§ block
                    (§ return (== ßbasicState BasicKeyChain/State/WATCHING))
                )

                (§ if (!= ßactiveState ßbasicState))
                (§ block
                    (§ throw (§ new #_"IllegalStateException" "Mix of watching and non-watching keys in wallet"))
                )

                (== ßactiveState BasicKeyChain/State/WATCHING)
            )
        )
    )

    ;;; Returns the key crypter or null if the group is not encrypted. ;;
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        keyCrypter
    )

    ;;;
     ; Returns a list of the non-deterministic keys that have been imported into the wallet, or the empty list if none.
     ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getImportedKeys) [])
    (§ block
        (.. basic (getKeys))
    )

    #_public
    (§ method #_"long" (§ fn getEarliestKeyCreationTime) [])
    (§ block
        (§ let #_"long" (§ name ßtime) (.. basic (getEarliestKeyCreationTime))) ;; Long.MAX_VALUE if empty.
        (§ block
            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (§ ass (§ name ßtime) (Math/min ßtime, (.. ßchain (getEarliestKeyCreationTime))))
            )
            ßtime
        )
    )

    #_public
    (§ method #_"int" (§ fn getBloomFilterElementCount) [])
    (§ block
        (§ let #_"int" (§ name ßresult) (.. basic (numBloomFilterEntries)))
        (§ block
            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (§ ass (§ name ßresult) (+ ßresult (.. ßchain (numBloomFilterEntries))))
            )
            ßresult
        )
    )

    #_public
    (§ method #_"BloomFilter" (§ fn getBloomFilter) [#_"int" (§ name ßsize), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßnTweak)])
    (§ block
        (§ let #_"BloomFilter" (§ name ßfilter) (§ new #_"BloomFilter" ßsize, ßfalsePositiveRate, ßnTweak))
        (§ block
            (§ if (< 0 (.. basic (numKeys))))
            (§ block
                (.. ßfilter (merge (.. basic (getFilter ßsize, ßfalsePositiveRate, ßnTweak))))
            )

            (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
            (§ block
                (.. ßfilter (merge (.. ßchain (getFilter ßsize, ßfalsePositiveRate, ßnTweak))))
            )
            ßfilter
        )
    )

    #_private
    (§ method- #_"Script" (§ fn makeP2SHOutputScript) [#_"DeterministicKey" (§ name ßfollowedKey), #_"DeterministicKeyChain" (§ name ßchain)])
    (§ block
        (ScriptBuilder/createP2SHOutputScript (.. ßchain (getRedeemData ßfollowedKey) redeemScript))
    )

    ;;; Adds a listener for events that are run when keys are added, on the user thread. ;;
    #_public
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" (§ name ßlistener)])
    (§ block
        (addEventListener ßlistener, Threading/USER_THREAD)
        nil
    )

    ;;; Adds a listener for events that are run when keys are added, on the given executor. ;;
    #_public
    (§ method #_"void" (§ fn addEventListener) [#_"KeyChainEventListener" (§ name ßlistener), #_"Executor" (§ name ßexecutor)])
    (§ block
        (Preconditions/checkNotNull ßlistener)
        (Preconditions/checkNotNull ßexecutor)

        (.. basic (addEventListener ßlistener, ßexecutor))
        (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for chains)
        (§ block
            (.. ßchain (addEventListener ßlistener, ßexecutor))
        )
        nil
