    ;;; Saves the wallet first to the given temp file, then renames to the dest file. ;;
    #_public
    (§ method #_"void" (§ fn saveToFile) [#_"File" (§ name ßtemp), #_"File" (§ name ßdestFile)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"FileOutputStream" (§ name ßstream) nil)
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (§ ass (§ name ßstream) (§ new #_"FileOutputStream" ßtemp))
                (saveToFileStream ßstream)
                ;; Attempt to force the bits to hit the disk.  In reality the OS or hard disk itself may still decide
                ;; to not write through to physical media for at least a few seconds, but this is the best we can do.
                (.. ßstream (flush))
                (.. ßstream (getFD) (sync))
                (.. ßstream (close))
                (§ ass (§ name ßstream) nil)
                (§ if (Utils/isWindows))
                (§ block
                    ;; Work around an issue on Windows whereby you can't rename over existing files.
                    (§ let #_"File" (§ name ßcanonical) (.. ßdestFile (getCanonicalFile)))
                    (§ block
