        (§ throws #_"IllegalStateException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"Set<Transaction>" (§ name ßtransactions) (getTransactions true))
            (§ block

                (§ let #_"Set<Sha256Hash>" (§ name ßhashes) (§ new #_"HashSet<>"))
                (§ block
                    (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtransactions)
                    (§ block
                        (.. ßhashes (add (.. ßtx (getHash))))
                    )

                    (§ let #_"int" (§ name ßsize1) (.. ßtransactions (size)))
                    (§ block
                        (§ if (!= ßsize1 (.. ßhashes (size))))
                        (§ block
                            (§ throw (§ new #_"IllegalStateException" "Two transactions with same hash"))
                        )

                        (§ let #_"int" (§ name ßsize2) (+ (.. unspent (size)) (.. spent (size)) (.. pending (size)) (.. dead (size))))
                        (§ block
                            (§ if (!= ßsize1 ßsize2))
                            (§ block
                                (§ throw (§ new #_"IllegalStateException" (str "Inconsistent wallet sizes: " ßsize1 ", " ßsize2)))
                            )

                            (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. unspent (values)))
                            (§ block
                                (§ if (not (isTxConsistent ßtx, false)))
                                (§ block
                                    (§ throw (§ new #_"IllegalStateException" (str "Inconsistent unspent tx: " (.. ßtx (getHashAsString)))))
                                )
                            )

                            (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. spent (values)))
                            (§ block
