                                (§ block
                                    (§ throw (§ new #_"IllegalStateException" (str "Inconsistent spent tx: " (.. ßtx (getHashAsString)))))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;; If isSpent, check that all my outputs spent, otherwise check that there at least one unspent.
    #_testing
