    (§ method #_"boolean" (§ fn isTransactionRelevant) [#_"Transaction" (§ name ßtx)])
        (§ throws #_"ScriptException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (or (< 0 (.. ßtx (getValueSentFromMe this) (signum))) (< 0 (.. ßtx (getValueSentToMe this) (signum))) (not (.. (findDoubleSpendsAgainst ßtx, transactions) (isEmpty)))))
        )
