    ;;;
     ; Finds transactions in the specified candidates that double spend "tx".  Not a general check, but it can work even
     ; if the double spent inputs are not ours.
     ; @return the set of transactions that double spend "tx".
     ;;
    #_private
    (§ method- #_"Set<Transaction>" (§ fn findDoubleSpendsAgainst) [#_"Transaction" (§ name ßtx), #_"Map<Sha256Hash, Transaction>" (§ name ßcandidates)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ if (.. ßtx (isCoinBase)))
        (§ block
            (§ return (Sets/newHashSet))
        )

        ;; Compile a set of outpoints that are spent by tx.
        (§ let #_"HashSet<TransactionOutPoint>" (§ name ßoutpoints) (§ new #_"HashSet<>"))
        (§ block
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
            (§ block
                (.. ßoutpoints (add (.. ßinput (getOutpoint))))
            )

            ;; Now for each pending transaction, see if it shares any outpoints with this tx.
            (§ let #_"Set<Transaction>" (§ name ßdoubleSpendTxns) (Sets/newHashSet))
            (§ block
                (§ for (§ var #_"Transaction" (§ name ßp)) :for (.. ßcandidates (values)))
                (§ block
                    (§ if (.. ßp (equals ßtx)))
                    (§ block
                        (§ continue )
                    )

                    (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßp (getInputs)))
                    (§ block
                        ;; This relies on the fact that TransactionOutPoint equality is defined at the protocol not object
                        ;; level - outpoints from two different inputs that point to the same output compare the same.
                        (§ let #_"TransactionOutPoint" (§ name ßoutpoint) (.. ßinput (getOutpoint)))
                        (§ block
                            ;; If does, it's a double spend against the candidates, which makes it relevant.
                            (§ if (.. ßoutpoints (contains ßoutpoint)))
                            (§ block
                                (.. ßdoubleSpendTxns (add ßp))
                            )
                        )
                    )
                )
                ßdoubleSpendTxns
            )
        )
    )

    ;;;
     ; Adds to txSet all the txns in txPool spending outputs of txns in txSet,
     ; and all txns spending the outputs of those txns, recursively.
     ;;
    (§ method #_"void" (§ fn addTransactionsDependingOn) [#_"Set<Transaction>" (§ name ßtxSet), #_"Set<Transaction>" (§ name ßtxPool)])
    (§ block
        (§ let #_"Map<Sha256Hash, Transaction>" (§ name ßtxQueue) (§ new #_"LinkedHashMap<>"))
        (§ block
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtxSet)
            (§ block
                (.. ßtxQueue (put (.. ßtx (getHash)), ßtx))
            )

            (§ while (not (.. ßtxQueue (isEmpty))))
            (§ block
                (§ let #_"Transaction" (§ name ßtx) (.. ßtxQueue (remove (.. ßtxQueue (keySet) (iterator) (next)))))
                (§ block
                    (§ for (§ var #_"Transaction" (§ name ßanotherTx)) :for ßtxPool)
                    (§ block
                        (§ if (.. ßanotherTx (equals ßtx)))
                        (§ block
                            (§ continue )
                        )

                        (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßanotherTx (getInputs)))
                        (§ block
                            (§ if (.. ßinput (getOutpoint) (getHash) (equals (.. ßtx (getHash)))))
                            (§ block
                                (§ if (== (.. ßtxQueue (get (.. ßanotherTx (getHash)))) nil))
                                (§ block
                                    (.. ßtxQueue (put (.. ßanotherTx (getHash)), ßanotherTx))
                                    (.. ßtxSet (add ßanotherTx))
                                )
                            )
                        )
                    )
                )
            )
            nil
        )
    )

