    ;; Whether to do a saveNow or saveLater when we are notified of the next best block.
    #_private
    (§ field- #_"boolean" (§ name hardSaveOnNextBlock) false)

    #_private
    (§ method- #_"void" (§ fn receive) [#_"Transaction" (§ name ßtx), #_"StoredBlock" (§ name ßblock), #_"BlockChain.NewBlockType" (§ name ßblockType), #_"int" (§ name ßrelativityOffset)])
        (§ throws #_"VerificationException")
    (§ block
        ;; Runs in a peer thread.
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ let #_"Coin" (§ name ßprevBalance) (getBalance))
        (§ block
            (§ let #_"Sha256Hash" (§ name ßtxHash) (.. ßtx (getHash)))
            (§ block
                (§ let #_"boolean" (§ name ßbestChain) (== ßblockType BlockChain/NewBlockType/BEST_CHAIN))
                (§ block
                    (§ let #_"boolean" (§ name ßsideChain) (== ßblockType BlockChain/NewBlockType/SIDE_CHAIN))
                    (§ block

                        (§ let #_"Coin" (§ name ßvalueSentFromMe) (.. ßtx (getValueSentFromMe this)))
                        (§ block
                            (§ let #_"Coin" (§ name ßvalueSentToMe) (.. ßtx (getValueSentToMe this)))
                            (§ block
                                (§ let #_"Coin" (§ name ßvalueDifference) (.. ßvalueSentToMe (subtract ßvalueSentFromMe)))
                                (§ block

                                    (.. log (info "Received ßtx{} for {}: {} [{}] in ßblock {}", (if ßsideChain " on a side chain" ""), (.. ßvalueDifference (toFriendlyString)), (.. ßtx (getHashAsString)), ßrelativityOffset, (if (some? ßblock) (.. ßblock (getHeader) (getHash)) "(unit test)")))

                                    ;; Inform the key chains that the issued keys were observed in a transaction, so they know to
                                    ;; calculate more keys for the next Bloom filters.
                                    (markKeysAsUsed ßtx)

                                    (§ ass (§ name onWalletChangedSuppressions) (+ onWalletChangedSuppressions 1))

                                    ;; If this transaction is already in the wallet, we may need to move it into a different pool.
                                    ;; At the very least we need to ensure we're manipulating the canonical object rather than a duplicate.
                                    (§ block
                                        (§ let #_"Transaction" (§ name ßtmp) (.. transactions (get (.. ßtx (getHash)))))
                                        (§ block
                                            (§ if (some? ßtmp))
                                            (§ block
                                                (§ ass (§ name ßtx) ßtmp)
                                            )
                                        )
                                    )

                                    (§ let #_"boolean" (§ name ßwasPending) (!= (.. pending (remove ßtxHash)) nil))
                                    (§ block
                                        (§ if ßwasPending)
                                        (§ block
                                            (.. log (info "  <-pending"))
                                        )

                                        (§ if ßbestChain)
                                        (§ block
                                            (§ let #_"boolean" (§ name ßwasDead) (!= (.. dead (remove ßtxHash)) nil))
                                            (§ block
                                                (§ if ßwasDead)
                                                (§ block
                                                    (.. log (info "  <-dead"))
                                                )
                                                (§ if ßwasPending)
                                                (§ block
                                                    ;; Was pending and is now confirmed.  Disconnect the outputs in case we spent any already:
                                                    ;; they will be re-connected by processTxFromBestChain below.
                                                    (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                                                    (§ block
                                                        (§ let #_"TransactionInput" (§ name ßspentBy) (.. ßoutput (getSpentBy)))
                                                        (§ block
                                                            (§ if (some? ßspentBy))
                                                            (§ block
                                                                (Preconditions/checkState (.. myUnspents (add ßoutput)))
                                                                (.. ßspentBy (disconnect))
                                                            )
                                                        )
                                                    )
                                                )
                                                (processTxFromBestChain ßtx, (or ßwasPending ßwasDead))
                                            )
                                        )
                                        (§ else )
                                        (§ block
                                            (Preconditions/checkState ßsideChain)
                                            ;; Transactions that appear in a side chain will have that appearance recorded below - we assume
                                            ;; that some miners are also trying to include the transaction into the current best chain too,
                                            ;; so let's treat it as pending, except we don't need to do any risk analysis on it.
                                            (§ if ßwasPending)
                                            (§ block
                                                ;; Just put it back in without touching the connections or confidence.
                                                (addWalletTransaction Pool/PENDING, ßtx)
                                                (.. log (info "  ->pending"))
                                            )
                                            (§ else )
                                            (§ block
                                                ;; Ignore the case where a tx appears on a side chain at the same time as the best chain
                                                ;; (this is quite normal and expected).
                                                (§ let #_"Sha256Hash" (§ name ßhash) (.. ßtx (getHash)))
                                                (§ block
                                                    (§ if (and (not (.. unspent (containsKey ßhash))) (not (.. spent (containsKey ßhash))) (not (.. dead (containsKey ßhash)))))
                                                    (§ block
                                                        ;; Otherwise put it (possibly back) into pending.
                                                        ;; Committing it updates the spent flags and inserts into the pool as well.
                                                        (commitTx ßtx)
                                                    )
                                                )
                                            )
                                        )

                                        (§ if (some? ßblock))
                                        (§ block
                                            ;; Mark the tx as appearing in this block so we can find it later after a re-org.  This also tells
                                            ;; the tx confidence object about the block and sets its depth appropriately.
                                            (.. ßtx (setBlockAppearance ßblock, ßbestChain, ßrelativityOffset))
                                            (§ if ßbestChain)
                                            (§ block
                                                ;; Don't notify this tx of work done in notifyNewBestBlock which will be called immediately
                                                ;; after this method has been called by BlockChain for all relevant transactions.  Otherwise
                                                ;; we'd double count.
                                                (.. ignoreNextNewBlock (add ßtxHash))

                                                ;; When a tx is received from the best chain, if other txns that spend this tx are IN_CONFLICT,
                                                ;; change its confidence to PENDING (Unless they are also spending other txns IN_CONFLICT).
                                                ;; Consider dependency chains.
                                                (§ let #_"Set<Transaction>" (§ name ßcurrentTxDependencies) (Sets/newHashSet ßtx))
                                                (§ block
                                                    (addTransactionsDependingOn ßcurrentTxDependencies, (getTransactions true))
                                                    (.. ßcurrentTxDependencies (remove ßtx))
                                                    (§ let #_"List<Transaction>" (§ name ßcurrentTxDependenciesSorted) (sortTxnsByDependency ßcurrentTxDependencies))
                                                    (§ block
                                                        (§ for (§ var #_"Transaction" (§ name ßtxDependency)) :for ßcurrentTxDependenciesSorted)
                                                        (§ block
                                                            (§ if (.. ßtxDependency (getConfidence) (getConfidenceType) (equals ConfidenceType/IN_CONFLICT)))
                                                            (§ block
                                                                (§ if (isNotSpendingTxnsInConfidenceType ßtxDependency, ConfidenceType/IN_CONFLICT))
                                                                (§ block
                                                                    (.. ßtxDependency (getConfidence) (setConfidenceType ConfidenceType/PENDING))
                                                                    (.. confidenceChanged (put ßtxDependency, TransactionConfidence/Listener/ChangeReason/TYPE))
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )

                                        (§ ass (§ name onWalletChangedSuppressions) (- onWalletChangedSuppressions 1))

                                        ;; Side chains don't affect confidence.
                                        (§ if ßbestChain)
                                        (§ block
                                            ;; notifyNewBestBlock will be invoked next and will then call maybeQueueOnWalletChanged for us.
                                            (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/TYPE))
                                        )
                                        (§ else )
                                        (§ block
                                            (maybeQueueOnWalletChanged)
                                        )

                                        ;; Inform anyone interested that we have received or sent coins but only if:
                                        ;;  - This is not due to a re-org.
                                        ;;  - The coins appeared on the best chain.
                                        ;;  - We did in fact receive some new money.
                                        ;;  - We have not already informed the user about the coins when we received the tx broadcast,
                                        ;;    or for our own spends.  If users want to know when a broadcast tx becomes confirmed,
                                        ;;    they need to use tx confidence listeners.
                                        (§ if (and (not insideReorg) ßbestChain))
                                        (§ block
                                            (§ let #_"Coin" (§ name ßnewBalance) (getBalance)) ;; This is slow.
                                            (§ block
                                                (.. log (info (str "Balance is now: " (.. ßnewBalance (toFriendlyString)))))
                                                (§ if (not ßwasPending))
                                                (§ block
                                                    (§ let #_"int" (§ name ßdiff) (.. ßvalueDifference (signum)))
                                                    (§ block
                                                        ;; We pick one callback based on the value difference, though a tx can of course both
                                                        ;; send and receive coins from the wallet.
                                                        (§ if (< 0 ßdiff))
                                                        (§ block
                                                            (queueOnCoinsReceived ßtx, ßprevBalance, ßnewBalance)
                                                        )
                                                        (§ elseif (< ßdiff 0))
                                                        (§ block
                                                            (queueOnCoinsSent ßtx, ßprevBalance, ßnewBalance)
                                                        )
                                                    )
                                                )
                                                (checkBalanceFuturesLocked ßnewBalance)
                                            )
                                        )

                                        (informConfidenceListenersIfNotReorganizing)
                                        (isConsistentOrThrow)
                                        ;; Optimization for the case where a block has tons of relevant transactions.
                                        (saveLater)
                                        (§ ass (§ name hardSaveOnNextBlock) true)
                                        nil
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;; Finds if tx is NOT spending other txns which are in the specified confidence type. ;;
    #_private
    (§ method- #_"boolean" (§ fn isNotSpendingTxnsInConfidenceType) [#_"Transaction" (§ name ßtx), #_"ConfidenceType" (§ name ßconfidenceType)])
    (§ block
        (§ for (§ var #_"TransactionInput" (§ name ßtxInput)) :for (.. ßtx (getInputs)))
        (§ block
            (§ let #_"Transaction" (§ name ßconnectedTx) (.. this (getTransaction (.. ßtxInput (getOutpoint) (getHash)))))
            (§ block
                (§ if (and (some? ßconnectedTx) (.. ßconnectedTx (getConfidence) (getConfidenceType) (equals ßconfidenceType))))
                (§ block
                    (§ return false)
                )
            )
        )
        true
    )

    ;;;
     ; Creates and returns a new List with the same txns as inputSet, but txns are sorted by depencency
     ; (a topological sort).  If tx B spends tx A, then tx A should be before tx B on the returned List.
     ; Several invocations to this method with the same inputSet could result in lists with txns in different
     ; order, as there is no guarantee on the order of the returned txns besides what was already stated.
     ;;
    (§ method #_"List<Transaction>" (§ fn sortTxnsByDependency) [#_"Set<Transaction>" (§ name ßinputSet)])
    (§ block
        (§ let #_"ArrayList<Transaction>" (§ name ßresult) (§ new #_"ArrayList<>" ßinputSet))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (- (.. ßresult (size)) 1)) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"boolean" (§ name ßtxAtISpendsOtherTxInTheList))
                (§ block
                    (§ do )
                    (§ block
                        (§ ass (§ name ßtxAtISpendsOtherTxInTheList) false)
                        (§ for (§ var #_"int" (§ name ßj) (+ ßi 1)) :for (< ßj (.. ßresult (size))) :for (§ recur (+ ßj 1)))
                        (§ block
                            (§ if (spends (.. ßresult (get ßi)), (.. ßresult (get ßj))))
                            (§ block
                                (§ let #_"Transaction" (§ name ßtransactionAtI) (.. ßresult (remove ßi)))
                                (§ block
                                    (.. ßresult (add ßj, ßtransactionAtI))
                                    (§ ass (§ name ßtxAtISpendsOtherTxInTheList) true)
                                    (§ break )
                                )
                            )
                        )
                    )
                    (§ again ßtxAtISpendsOtherTxInTheList)
                )
            )
            ßresult
        )
    )

    ;;; Finds whether txA spends txB. ;;
    (§ method #_"boolean" (§ fn spends) [#_"Transaction" (§ name ßtxA), #_"Transaction" (§ name ßtxB)])
    (§ block
        (§ for (§ var #_"TransactionInput" (§ name ßtxInput)) :for (.. ßtxA (getInputs)))
        (§ block
            (§ if (.. ßtxInput (getOutpoint) (getHash) (equals (.. ßtxB (getHash)))))
            (§ block
                (§ return true)
            )
        )

        false
    )

    #_private
    (§ method- #_"void" (§ fn informConfidenceListenersIfNotReorganizing) [])
    (§ block
        (§ if insideReorg)
        (§ block
            (§ return nil)
        )

        (§ for (§ var #_"Map.Entry<Transaction, TransactionConfidence.Listener.ChangeReason>" (§ name ßentry)) :for (.. confidenceChanged (entrySet)))
        (§ block
            (§ let #_"Transaction" (§ name ßtx) (.. ßentry (getKey)))
            (§ block
                (.. ßtx (getConfidence) (queueListeners (.. ßentry (getValue))))
                (queueOnTransactionConfidenceChanged ßtx)
            )
        )
        (.. confidenceChanged (clear))
        nil
    )

    ;;;
     ; Called by the {@link BlockChain} when a new block on the best chain is seen, AFTER relevant wallet
     ; transactions are extracted and sent to us UNLESS the new block caused a re-org, in which case this will
     ; not be called (the {@link Wallet#reorganize(StoredBlock, java.util.List, java.util.List)} method will
     ; call this one in that case).
     ;
     ; Used to update confidence data in each transaction and last seen block hash.  Triggers auto saving.
     ; Invokes the onWalletChanged event listener if there were any affected transactions.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn notifyNewBestBlock) [#_"StoredBlock" (§ name ßblock)])
        (§ throws #_"VerificationException")
    (§ block
        ;; Check to see if this block has been seen before.
        (§ let #_"Sha256Hash" (§ name ßnewBlockHash) (.. ßblock (getHeader) (getHash)))
        (§ block
            (§ if (.. ßnewBlockHash (equals (getLastBlockSeenHash))))
            (§ block
                (§ return nil)
            )

            (.. lock (lock))
            (§ try )
            (§ block
                ;; Store the new block hash.
                (setLastBlockSeenHash ßnewBlockHash)
                (setLastBlockSeenHeight (.. ßblock (getHeight)))
                (setLastBlockSeenTimeSecs (.. ßblock (getHeader) (getTimeSeconds)))
                ;; Notify all the BUILDING transactions of the new block.
                ;; This is so that they can update their depth.
                (§ let #_"Set<Transaction>" (§ name ßtransactions) (getTransactions true))
                (§ block
                    (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtransactions)
                    (§ block
                        (§ if (.. ignoreNextNewBlock (contains (.. ßtx (getHash)))))
                        (§ block
                            ;; tx was already processed in receive() due to it appearing in this block, so we don't want
                            ;; to increment the tx confidence depth twice, it'd result in miscounting.
                            (.. ignoreNextNewBlock (remove (.. ßtx (getHash))))
                        )
                        (§ else )
                        (§ block
                            (§ let #_"TransactionConfidence" (§ name ßconfidence) (.. ßtx (getConfidence)))
                            (§ block
                                (§ if (== (.. ßconfidence (getConfidenceType)) ConfidenceType/BUILDING))
                                (§ block
                                    ;; Erase the set of seen peers once the tx is so deep that it seems unlikely to ever go
                                    ;; pending again.  We could clear this data the moment a tx is seen in the block chain,
                                    ;; but in cases where the chain re-orgs, this would mean that wallets would perceive a
                                    ;; newly pending tx has zero confidence at all, which would not be right: we expect it to
                                    ;; be included once again.  We could have a separate was-in-chain-and-now-isn't confidence
                                    ;; type, but this way is backwards compatible with existing software, and the new state
                                    ;; probably wouldn't mean anything different to just remembering peers anyway.
                                    (§ if (< (.. context (getEventHorizon)) (.. ßconfidence (incrementDepthInBlocks))))
                                    (§ block
                                        (.. ßconfidence (clearBroadcastBy))
                                    )
                                    (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/DEPTH))
                                )
                            )
                        )
                    )

                    (informConfidenceListenersIfNotReorganizing)
                    (maybeQueueOnWalletChanged)

                    (§ if hardSaveOnNextBlock)
                    (§ block
                        (saveNow)
                        (§ ass (§ name hardSaveOnNextBlock) false)
                    )
                    (§ else )
                    (§ block
                        ;; Coalesce writes to avoid throttling on disk access when catching up with the chain.
                        (saveLater)
                    )
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            nil
        )
    )

    ;;;
     ; Handle when a transaction becomes newly active on the best chain, either due to receiving a new block or a
     ; re-org.  Places the tx into the right pool, handles coinbase transactions, handles double-spends and so on.
     ;;
    #_private
    (§ method- #_"void" (§ fn processTxFromBestChain) [#_"Transaction" (§ name ßtx), #_"boolean" (§ name ßforceAddToPool)])
        (§ throws #_"VerificationException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (Preconditions/checkState (not (.. pending (containsKey (.. ßtx (getHash))))))

        ;; This TX may spend our existing outputs even though it was not pending.  This can happen in unit tests,
        ;; if keys are moved between wallets, if we're catching up to the chain given only a set of keys,
        ;; or if a dead coinbase transaction has moved back onto the main chain.
        (§ let #_"boolean" (§ name ßisDeadCoinbase) (and (.. ßtx (isCoinBase)) (.. dead (containsKey (.. ßtx (getHash))))))
        (§ block
            (§ if ßisDeadCoinbase)
            (§ block
                ;; There is a dead coinbase tx being received on the best chain.  A coinbase tx is made dead when it moves
                ;; to a side chain but it can be switched back on a reorg and resurrected back to spent or unspent.
                ;; So take it out of the dead pool.  Note that we don't resurrect dependent transactions here, even though
                ;; we could.  Bitcoin Core nodes on the network have deleted the dependent transactions from their mempools
                ;; entirely by this point.  We could and maybe should rebroadcast them so the network remembers and tries
                ;; to confirm them again.  But this is a deeply unusual edge case that due to the maturity rule should never
                ;; happen in practice, thus for simplicities sake we ignore it here.
                (.. log (info "  coinbase ßtx <-dead: confidence {}", (.. ßtx (getHashAsString)), (.. ßtx (getConfidence) (getConfidenceType) (name))))
                (.. dead (remove (.. ßtx (getHash))))
            )

            ;; Update tx and other unspent/pending transactions by connecting inputs/outputs.
            (updateForSpends ßtx, true)

            ;; Now make sure it ends up in the right pool.  Also, handle the case where this TX is double-spending
            ;; against our pending transactions.  Note that a tx may double spend our pending transactions and also
            ;; send us money/spend our money.
            (§ let #_"boolean" (§ name ßhasOutputsToMe) (< 0 (.. ßtx (getValueSentToMe this) (signum))))
            (§ block
                (§ let #_"boolean" (§ name ßhasOutputsFromMe) false)
                (§ block
                    (§ if ßhasOutputsToMe)
                    (§ block
                        ;; Needs to go into either unspent or spent (if the outputs were already spent by a pending tx).
                        (§ if (.. ßtx (isEveryOwnedOutputSpent this)))
                        (§ block
                            (.. log (info "  ßtx {} ->spent (by pending)", (.. ßtx (getHashAsString))))
                            (addWalletTransaction Pool/SPENT, ßtx)
                        )
                        (§ else )
                        (§ block
                            (.. log (info "  ßtx {} ->unspent", (.. ßtx (getHashAsString))))
                            (addWalletTransaction Pool/UNSPENT, ßtx)
                        )
                    )
                    (§ elseif (< 0 (.. ßtx (getValueSentFromMe this) (signum))))
                    (§ block
                        (§ ass (§ name ßhasOutputsFromMe) true)
                        ;; Didn't send us any money, but did spend some.  Keep it around for record keeping purposes.
                        (.. log (info "  ßtx {} ->spent", (.. ßtx (getHashAsString))))
                        (addWalletTransaction Pool/SPENT, ßtx)
                    )
                    (§ elseif ßforceAddToPool)
                    (§ block
                        ;; Was manually added to pending, so we should keep it to notify the user of confidence information.
                        (.. log (info "  ßtx {} ->spent (manually added)", (.. ßtx (getHashAsString))))
                        (addWalletTransaction Pool/SPENT, ßtx)
                    )

                    ;; Kill txns in conflict with this tx.
                    (§ let #_"Set<Transaction>" (§ name ßdoubleSpendTxns) (findDoubleSpendsAgainst ßtx, pending))
                    (§ block
                        (§ if (not (.. ßdoubleSpendTxns (isEmpty))))
                        (§ block
                            ;; No need to addTransactionsDependingOn(doubleSpendTxns), because killTxns() already kills dependencies.
                            (killTxns ßdoubleSpendTxns, ßtx)
                        )
                        (§ if (and (not ßhasOutputsToMe) (not ßhasOutputsFromMe) (not ßforceAddToPool) (not (.. (findDoubleSpendsAgainst ßtx, transactions) (isEmpty)))))
                        (§ block
                            ;; Disconnect irrelevant inputs (otherwise might cause protobuf serialization issue).
                            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
                            (§ block
                                (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßinput (getConnectedOutput)))
                                (§ block
                                    (§ if (and (some? ßoutput) (not (.. ßoutput (isMine this)))))
                                    (§ block
                                        (.. ßinput (disconnect))
                                    )
                                )
                            )
                        )
                        nil
                    )
                )
            )
        )
    )

    ;;;
     ; <p>Updates the wallet by checking if this TX spends any of our outputs, and marking them as spent if so.
     ; If fromChain is true, also checks to see if any pending transaction spends outputs of this transaction and marks
     ; the spent flags appropriately.</p>
     ;
     ; <p>It can be called in two contexts.  One is when we receive a transaction on the best chain but it wasn't pending,
     ; this most commonly happens when we have a set of keys but the wallet transactions were wiped and we are catching
     ; up with the block chain.  It can also happen if a block includes a transaction we never saw at broadcast time.
     ; If this tx double spends, it takes precedence over our pending transactions and the pending tx goes dead.</p>
     ;
     ; <p>The other context it can be called is from {@link Wallet#receivePending(Transaction, java.util.List)},
     ; i.e. we saw a tx be broadcast or one was submitted directly that spends our own coins.  If this tx double spends,
     ; it does NOT take precedence because the winner will be resolved by the miners - we assume that our version will
     ; win, if we are wrong then when a block appears the tx will go dead.</p>
     ;
     ; @param tx The transaction which is being updated.
     ; @param fromChain If true, the tx appeared on the current best chain, if false it was pending.
     ;;
    #_private
    (§ method- #_"void" (§ fn updateForSpends) [#_"Transaction" (§ name ßtx), #_"boolean" (§ name ßfromChain)])
        (§ throws #_"VerificationException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ if ßfromChain)
        (§ block
            (Preconditions/checkState (not (.. pending (containsKey (.. ßtx (getHash))))))
        )

        (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
        (§ block
            (§ let #_"TransactionInput.ConnectionResult" (§ name ßresult) (.. ßinput (connect unspent, TransactionInput/ConnectMode/ABORT_ON_CONFLICT)))
            (§ block
                (§ if (== ßresult TransactionInput/ConnectionResult/NO_SUCH_TX))
                (§ block
                    ;; Not found in the unspent map.  Try again with the spent map.
                    (§ ass (§ name ßresult) (.. ßinput (connect spent, TransactionInput/ConnectMode/ABORT_ON_CONFLICT)))
                    (§ if (== ßresult TransactionInput/ConnectionResult/NO_SUCH_TX))
                    (§ block
                        ;; Not found in the unspent and spent maps.  Try again with the pending map.
                        (§ ass (§ name ßresult) (.. ßinput (connect pending, TransactionInput/ConnectMode/ABORT_ON_CONFLICT)))
                        (§ if (== ßresult TransactionInput/ConnectionResult/NO_SUCH_TX))
                        (§ block
                            ;; Doesn't spend any of our outputs or is coinbase.
                            (§ continue )
                        )
                    )
                )

                (§ let #_"TransactionOutput" (§ name ßoutput) (Preconditions/checkNotNull (.. ßinput (getConnectedOutput))))
                (§ block
                    (§ if (== ßresult TransactionInput/ConnectionResult/ALREADY_SPENT))
                    (§ block
                        (§ if ßfromChain)
                        (§ block
                            ;; Can be:
                            ;; (1) We already marked this output as spent when we saw the pending transaction (most likely).
                            ;;     Now it's being confirmed of course, we cannot mark it as spent again.
                            ;; (2) A double spend from chain: this will be handled later by findDoubleSpendsAgainst()/killTxns().
                            ;;
                            ;; In any case, nothing to do here.
                        )
                        (§ else )
                        (§ block
                            ;; We saw two pending transactions that double spend each other.  We don't know which will win.
                            ;; This can happen in the case of bad network nodes that mutate transactions.  Do a hex dump
                            ;; so the exact nature of the mutation can be examined.
                            (.. log (warn "Saw two pending transactions double spend each other"))
                            (.. log (warn "  offending ßinput is ßinput {}", (.. ßtx (getInputs) (indexOf ßinput))))
                            (.. log (warn "{}: {}", (.. ßtx (getHash)), (.. Utils/HEX (encode (.. ßtx (unsafeBitcoinSerialize))))))
                            (§ let #_"Transaction" (§ name ßother) (.. ßoutput (getSpentBy) (getParentTransaction)))
                            (§ block
                                (.. log (warn "{}: {}", (.. ßother (getHash)), (.. Utils/HEX (encode (.. ßother (unsafeBitcoinSerialize))))))
                            )
                        )
                    )
                    (§ elseif (== ßresult TransactionInput/ConnectionResult/SUCCESS))
                    (§ block
                        ;; Otherwise we saw a transaction spend our coins, but we didn't try and spend them ourselves yet.
                        ;; The outputs are already marked as spent by the connect call above, so check if there are any more for
                        ;; us to use.  Move if not.
                        (§ let #_"Transaction" (§ name ßconnected) (Preconditions/checkNotNull (.. ßinput (getConnectedTransaction))))
                        (§ block
                            (.. log (info "  marked {} as spent by {}", (.. ßinput (getOutpoint)), (.. ßtx (getHashAsString))))
                            (maybeMovePool ßconnected, "prevtx")
                            ;; Just because it's connected, doesn't mean it's actually ours: sometimes we have total visibility.
                            (§ if (.. ßoutput (isMine this)))
                            (§ block
                                (Preconditions/checkState (.. myUnspents (remove ßoutput)))
                            )
                        )
                    )
                )
            )
        )

        ;; Now check each output and see if there is a pending transaction which spends it.  This shouldn't normally
        ;; ever occur because we expect transactions to arrive in temporal order, but this assumption can be violated
        ;; when we receive a pending transaction from the mempool that is relevant to us, which spends coins that we
        ;; didn't see arrive on the best chain yet.  For instance, because of a chain replay or because of our keys were
        ;; used by another wallet somewhere else.  Also, unconfirmed transactions can arrive from the mempool in more
        ;; or less random order.
        (§ for (§ var #_"Transaction" (§ name ßpendingTx)) :for (.. pending (values)))
        (§ block
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßpendingTx (getInputs)))
            (§ block
                (§ let #_"TransactionInput.ConnectionResult" (§ name ßresult) (.. ßinput (connect ßtx, TransactionInput/ConnectMode/ABORT_ON_CONFLICT)))
                (§ block
                    (§ if ßfromChain)
                    (§ block
                        ;; This TX is supposed to have just appeared on the best chain, so its outputs should not be marked
                        ;; as spent yet.  If they are, it means something is happening out of order.
                        (Preconditions/checkState (!= ßresult TransactionInput/ConnectionResult/ALREADY_SPENT))
                    )
                    (§ if (== ßresult TransactionInput/ConnectionResult/SUCCESS))
                    (§ block
                        (.. log (info "Connected pending ßtx ßinput {}:{}", (.. ßpendingTx (getHashAsString)), (.. ßpendingTx (getInputs) (indexOf ßinput))))
                        ;; The unspents map might not have it if we never saw this tx until it was included in the chain
                        ;; and thus becomes spent the moment we become aware of it.
                        (§ if (.. myUnspents (remove (.. ßinput (getConnectedOutput)))))
                        (§ block
                            (.. log (info "Removed from UNSPENTS: {}", (.. ßinput (getConnectedOutput))))
                        )
                    )
                )
            )
        )
        (§ if (not ßfromChain))
        (§ block
            (maybeMovePool ßtx, "pendingtx")
        )
        (§ else )
        (§ block
            ;; If the transactions outputs are now all spent, it will be moved into the spent pool by the
            ;; processTxFromBestChain method.
        )
        nil
    )

    ;; Updates the wallet when a double spend occurs.  overridingTx can be null for the case of coinbases.
    #_private
    (§ method- #_"void" (§ fn killTxns) [#_"Set<Transaction>" (§ name ßtxnsToKill), #_nilable #_"Transaction" (§ name ßoverridingTx)])
    (§ block
        (§ let #_"LinkedList<Transaction>" (§ name ßwork) (§ new #_"LinkedList<>" ßtxnsToKill))
        (§ block
            (§ while (not (.. ßwork (isEmpty))))
            (§ block
                (§ let #_"Transaction" (§ name ßtx) (.. ßwork (poll)))
                (§ block
                    (.. log (warn "TX {} killed{}", (.. ßtx (getHashAsString)), (if (some? ßoverridingTx) (str " by " (.. ßoverridingTx (getHashAsString))) "")))
                    (.. log (warn "Disconnecting each input and moving connected transactions."))
                    ;; TX could be pending (finney attack), or in unspent/spent (coinbase killed by reorg).
                    (.. pending (remove (.. ßtx (getHash))))
                    (.. unspent (remove (.. ßtx (getHash))))
                    (.. spent (remove (.. ßtx (getHash))))
                    (addWalletTransaction Pool/DEAD, ßtx)
                    (§ for (§ var #_"TransactionInput" (§ name ßdeadInput)) :for (.. ßtx (getInputs)))
                    (§ block
                        (§ let #_"Transaction" (§ name ßconnected) (.. ßdeadInput (getConnectedTransaction)))
                        (§ block
                            (§ if (nil? ßconnected))
                            (§ block
                                (§ continue )
                            )
                            (§ if (and (!= (.. ßconnected (getConfidence) (getConfidenceType)) ConfidenceType/DEAD) (some? (.. ßdeadInput (getConnectedOutput) (getSpentBy))) (.. ßdeadInput (getConnectedOutput) (getSpentBy) (equals ßdeadInput))))
                            (§ block
                                (Preconditions/checkState (.. myUnspents (add (.. ßdeadInput (getConnectedOutput)))))
                                (.. log (info "Added to UNSPENTS: {} in {}", (.. ßdeadInput (getConnectedOutput)), (.. ßdeadInput (getConnectedOutput) (getParentTransaction) (getHash))))
                            )
                            (.. ßdeadInput (disconnect))
                            (maybeMovePool ßconnected, "kill")
                        )
                    )
                    (.. ßtx (getConfidence) (setOverridingTransaction ßoverridingTx))
                    (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/TYPE))
                    ;; Now kill any transactions we have that depended on this one.
                    (§ for (§ var #_"TransactionOutput" (§ name ßdeadOutput)) :for (.. ßtx (getOutputs)))
                    (§ block
                        (§ if (.. myUnspents (remove ßdeadOutput)))
                        (§ block
                            (.. log (info "XX Removed from UNSPENTS: {}", ßdeadOutput))
                        )
                        (§ let #_"TransactionInput" (§ name ßconnected) (.. ßdeadOutput (getSpentBy)))
                        (§ block
                            (§ if (nil? ßconnected))
                            (§ block
                                (§ continue )
                            )
                            (§ let #_"Transaction" (§ name ßparentTransaction) (.. ßconnected (getParentTransaction)))
                            (§ block
                                (.. log (info "This death invalidated dependent ßtx {}", (.. ßparentTransaction (getHash))))
                                (.. ßwork (push ßparentTransaction))
                            )
                        )
                    )
                )
            )
            (§ if (nil? ßoverridingTx))
            (§ block
                (§ return nil)
            )

            (.. log (warn "Now attempting to connect the inputs of the overriding transaction."))
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßoverridingTx (getInputs)))
            (§ block
                (§ let #_"TransactionInput.ConnectionResult" (§ name ßresult) (.. ßinput (connect unspent, TransactionInput/ConnectMode/DISCONNECT_ON_CONFLICT)))
                (§ block
                    (§ if (== ßresult TransactionInput/ConnectionResult/SUCCESS))
                    (§ block
                        (maybeMovePool (.. ßinput (getConnectedTransaction)), "kill")
                        (.. myUnspents (remove (.. ßinput (getConnectedOutput))))
                        (.. log (info "Removing from UNSPENTS: {}", (.. ßinput (getConnectedOutput))))
                    )
                    (§ else )
                    (§ block
                        (§ ass (§ name ßresult) (.. ßinput (connect spent, TransactionInput/ConnectMode/DISCONNECT_ON_CONFLICT)))
                        (§ if (== ßresult TransactionInput/ConnectionResult/SUCCESS))
                        (§ block
                            (maybeMovePool (.. ßinput (getConnectedTransaction)), "kill")
                            (.. myUnspents (remove (.. ßinput (getConnectedOutput))))
                            (.. log (info "Removing from UNSPENTS: {}", (.. ßinput (getConnectedOutput))))
                        )
                    )
                )
            )
            nil
        )
    )

    ;;;
     ; If the transactions outputs are all marked as spent, and it's in the unspent map, move it.
     ; If the owned transactions outputs are not all marked as spent, and it's in the spent map, move it.
     ;;
    #_private
    (§ method- #_"void" (§ fn maybeMovePool) [#_"Transaction" (§ name ßtx), #_"String" (§ name ßcontext)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ if (.. ßtx (isEveryOwnedOutputSpent this)))
        (§ block
            ;; There's nothing left I can spend in this transaction.
            (§ if (!= (.. unspent (remove (.. ßtx (getHash)))) nil))
            (§ block
                (§ if (.. log (isInfoEnabled)))
                (§ block
                    (.. log (info "  {} {} <-unspent ->spent", (.. ßtx (getHashAsString)), ßcontext))
                )
                (.. spent (put (.. ßtx (getHash)), ßtx))
            )
        )
        (§ else )
        (§ block
            (§ if (!= (.. spent (remove (.. ßtx (getHash)))) nil))
            (§ block
                (§ if (.. log (isInfoEnabled)))
                (§ block
                    (.. log (info "  {} {} <-spent ->unspent", (.. ßtx (getHashAsString)), ßcontext))
                )
                (.. unspent (put (.. ßtx (getHash)), ßtx))
            )
        )
        nil
    )

    ;;;
     ; Calls {@link Wallet#commitTx} if tx is not already in the pending pool.
     ;
     ; @return true if the tx was added to the wallet, or false if it was already in the pending pool.
     ;;
    #_public
    (§ method #_"boolean" (§ fn maybeCommitTx) [#_"Transaction" (§ name ßtx)])
        (§ throws #_"VerificationException")
    (§ block
        (.. ßtx (verify))
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (.. pending (containsKey (.. ßtx (getHash)))))
            (§ block
                (§ return false)
            )

            (.. log (info "commitTx of {}", (.. ßtx (getHashAsString))))
            (§ let #_"Coin" (§ name ßbalance) (getBalance))
            (§ block
                (.. ßtx (setUpdateTime (Utils/now)))
                ;; Put any outputs that are sending money back to us into the unspents map, and calculate their total value.
                (§ let #_"Coin" (§ name ßvalueSentToMe) Coin/ZERO)
                (§ block
                    (§ for (§ var #_"TransactionOutput" (§ name ßo)) :for (.. ßtx (getOutputs)))
                    (§ block
                        (§ if (.. ßo (isMine this)))
                        (§ block
                            (§ ass (§ name ßvalueSentToMe) (.. ßvalueSentToMe (add (.. ßo (getValue)))))
                        )
                    )
                    ;; Mark the outputs we're spending as spent so we won't try and use them in future creations.  This will also
                    ;; move any transactions that are now fully spent to the spent map so we can skip them when creating future
                    ;; spends.
                    (updateForSpends ßtx, false)

                    (§ let #_"Set<Transaction>" (§ name ßdoubleSpendPendingTxns) (findDoubleSpendsAgainst ßtx, pending))
                    (§ block
                        (§ let #_"Set<Transaction>" (§ name ßdoubleSpendUnspentTxns) (findDoubleSpendsAgainst ßtx, unspent))
                        (§ block
                            (§ let #_"Set<Transaction>" (§ name ßdoubleSpendSpentTxns) (findDoubleSpendsAgainst ßtx, spent))
                            (§ block

                                (§ if (or (not (.. ßdoubleSpendUnspentTxns (isEmpty))) (not (.. ßdoubleSpendSpentTxns (isEmpty))) (not (isNotSpendingTxnsInConfidenceType ßtx, ConfidenceType/DEAD))))
                                (§ block
                                    ;; tx is a double spend against a tx already in the best chain or spends outputs of a DEAD tx.
                                    ;; Add tx to the dead pool and schedule confidence listener notifications.
                                    (.. log (info "->dead: {}", (.. ßtx (getHashAsString))))
                                    (.. ßtx (getConfidence) (setConfidenceType ConfidenceType/DEAD))
                                    (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/TYPE))
                                    (addWalletTransaction Pool/DEAD, ßtx)
                                )
                                (§ elseif (or (not (.. ßdoubleSpendPendingTxns (isEmpty))) (not (isNotSpendingTxnsInConfidenceType ßtx, ConfidenceType/IN_CONFLICT))))
                                (§ block
                                    ;; tx is a double spend against a pending tx or spends outputs of a tx already IN_CONFLICT.
                                    ;; Add tx to the pending pool.  Update the confidence type of tx, the txns in conflict with tx
                                    ;; and all their dependencies to IN_CONFLICT and schedule confidence listener notifications.
                                    (.. log (info "->pending (IN_CONFLICT): {}", (.. ßtx (getHashAsString))))
                                    (addWalletTransaction Pool/PENDING, ßtx)
                                    (.. ßdoubleSpendPendingTxns (add ßtx))
                                    (addTransactionsDependingOn ßdoubleSpendPendingTxns, (getTransactions true))
                                    (§ for (§ var #_"Transaction" (§ name ßdoubleSpendTx)) :for ßdoubleSpendPendingTxns)
                                    (§ block
                                        (.. ßdoubleSpendTx (getConfidence) (setConfidenceType ConfidenceType/IN_CONFLICT))
                                        (.. confidenceChanged (put ßdoubleSpendTx, TransactionConfidence/Listener/ChangeReason/TYPE))
                                    )
                                )
                                (§ else )
                                (§ block
                                    ;; No conflict detected.
                                    ;; Add to the pending pool and schedule confidence listener notifications.
                                    (.. log (info "->pending: {}", (.. ßtx (getHashAsString))))
                                    (.. ßtx (getConfidence) (setConfidenceType ConfidenceType/PENDING))
                                    (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/TYPE))
                                    (addWalletTransaction Pool/PENDING, ßtx)
                                )
                                (§ if (.. log (isInfoEnabled)))
                                (§ block
                                    (.. log (info "Estimated ßbalance is now: {}", (.. (getBalance BalanceType/ESTIMATED) (toFriendlyString))))
                                )

                                ;; Mark any keys used in the outputs as "used", this allows wallet UI's to auto-advance the current key
                                ;; they are showing to the user in qr codes etc.
                                (markKeysAsUsed ßtx)
                                (§ try )
                                (§ block
                                    (§ let #_"Coin" (§ name ßvalueSentFromMe) (.. ßtx (getValueSentFromMe this)))
                                    (§ block
                                        (§ let #_"Coin" (§ name ßnewBalance) (.. ßbalance (add ßvalueSentToMe) (subtract ßvalueSentFromMe)))
                                        (§ block
                                            (§ if (< 0 (.. ßvalueSentToMe (signum))))
                                            (§ block
                                                (checkBalanceFuturesLocked nil)
                                                (queueOnCoinsReceived ßtx, ßbalance, ßnewBalance)
                                            )
                                            (§ if (< 0 (.. ßvalueSentFromMe (signum))))
                                            (§ block
                                                (queueOnCoinsSent ßtx, ßbalance, ßnewBalance)
                                            )

                                            (maybeQueueOnWalletChanged)
                                        )
                                    )
                                )
                                (§ catch #_"ScriptException" (§ name ße))
                                (§ block
                                    ;; Cannot happen as we just created this transaction ourselves.
                                    (§ throw (§ new #_"RuntimeException" ße))
                                )

                                (isConsistentOrThrow)
                                (informConfidenceListenersIfNotReorganizing)
                                (saveNow)
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        true
    )

    ;;;
     ; <p>Updates the wallet with the given transaction: puts it into the pending pool, sets the spent flags
     ; and runs the onCoinsSent/onCoinsReceived event listener.  Used in two situations:</p>
     ;
     ; <ol>
     ;     <li>When we have just successfully transmitted the tx we created to the network.</li>
     ;     <li>When we receive a pending transaction that didn't appear in the chain yet, and we did not create it.</li>
     ; </ol>
     ;
     ; <p>Triggers an auto save.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn commitTx) [#_"Transaction" (§ name ßtx)])
        (§ throws #_"VerificationException")
    (§ block
        (Preconditions/checkArgument (maybeCommitTx ßtx), "commitTx called on the same transaction twice")
        nil
    )

