    ;;;
     ; Returns a set of all transactions in the wallet.
     ; @param includeDead If true, transactions that were overridden by a double spend are included.
     ;;
    #_public
    (§ method #_"Set<Transaction>" (§ fn getTransactions) [#_"boolean" (§ name ßincludeDead)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"Set<Transaction>" (§ name ßall) (§ new #_"HashSet<>"))
            (§ block
                (.. ßall (addAll (.. unspent (values))))
                (.. ßall (addAll (.. spent (values))))
                (.. ßall (addAll (.. pending (values))))
                (§ if ßincludeDead)
                (§ block
                    (.. ßall (addAll (.. dead (values))))
                )
                (§ return ßall)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

