            (§ block
                (addWalletTransactionsToSet ßall, Pool/UNSPENT, (.. unspent (values)))
                (addWalletTransactionsToSet ßall, Pool/SPENT, (.. spent (values)))
                (addWalletTransactionsToSet ßall, Pool/DEAD, (.. dead (values)))
                (addWalletTransactionsToSet ßall, Pool/PENDING, (.. pending (values)))
                (§ return ßall)
