    #_private
    #_static
    (§ defn- #_"void" (§ fn addWalletTransactionsToSet) [#_"Set<WalletTransaction>" (§ name ßtxns), #_"Pool" (§ name ßpoolType), #_"Collection<Transaction>" (§ name ßpool)])
    (§ block
        (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßpool)
        (§ block
            (.. ßtxns (add (§ new #_"WalletTransaction" ßpoolType, ßtx)))
        )
        nil
    )

    ;;;
     ; Adds a transaction that has been associated with a particular wallet pool.  This is intended for usage by
     ; deserialization code, such as the {@link WalletProtobufSerializer} class.  It isn't normally useful for
     ; applications.  It does not trigger auto saving.
     ;;
    #_public
    (§ method #_"void" (§ fn addWalletTransaction) [#_"WalletTransaction" (§ name ßwtx)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (addWalletTransaction (.. ßwtx (getPool)), (.. ßwtx (getTransaction)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Adds the given transaction to the given pools and registers a confidence change listener on it.
     ;;
    #_private
    (§ method- #_"void" (§ fn addWalletTransaction) [#_"Pool" (§ name ßpool), #_"Transaction" (§ name ßtx)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (.. transactions (put (.. ßtx (getHash)), ßtx))
        (§ switch ßpool)
        (§ block
            (§ case UNSPENT)
            (§ block
                (Preconditions/checkState (== (.. unspent (put (.. ßtx (getHash)), ßtx)) nil))
                (§ break )
            )
            (§ case SPENT)
            (§ block
                (Preconditions/checkState (== (.. spent (put (.. ßtx (getHash)), ßtx)) nil))
                (§ break )
            )
            (§ case PENDING)
            (§ block
                (Preconditions/checkState (== (.. pending (put (.. ßtx (getHash)), ßtx)) nil))
                (§ break )
            )
            (§ case DEAD)
            (§ block
                (Preconditions/checkState (== (.. dead (put (.. ßtx (getHash)), ßtx)) nil))
                (§ break )
            )
            (§ default )
            (§ block
                (§ throw (§ new #_"RuntimeException" (str "Unknown wallet transaction type " ßpool)))
            )
        )

        (§ if (or (== ßpool Pool/UNSPENT) (== ßpool Pool/PENDING)))
        (§ block
            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
            (§ block
                (§ if (and (.. ßoutput (isAvailableForSpending)) (.. ßoutput (isMine this))))
                (§ block
                    (.. myUnspents (add ßoutput))
                )
            )
        )
        ;; This is safe even if the listener has been added before, as TransactionConfidence ignores duplicate
        ;; registration requests.  That makes the code in the wallet simpler.
        (.. ßtx (getConfidence) (addEventListener Threading/SAME_THREAD, txConfidenceListener))
        nil
    )

    ;;;
     ; Returns all non-dead, active transactions ordered by recency.
     ;;
    #_public
    (§ method #_"List<Transaction>" (§ fn getTransactionsByTime) [])
    (§ block
        (getRecentTransactions 0, false)
    )

    ;;;
     ; Returns an list of N transactions, ordered by increasing age.  Transactions on side chains are not included.
     ; Dead transactions (overridden by double spends) are optionally included.
     ;
     ; Note: the current implementation is O(num transactions in wallet).  Regardless of how many transactions are
     ; requested, the cost is always the same.  In future, requesting smaller numbers of transactions may be faster
     ; depending on how the wallet is implemented (e.g. if backed by a database).
     ;;
    #_public
    (§ method #_"List<Transaction>" (§ fn getRecentTransactions) [#_"int" (§ name ßnumTransactions), #_"boolean" (§ name ßincludeDead)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkArgument (<= 0 ßnumTransactions))

            ;; Firstly, put all transactions into an array.
            (§ let #_"int" (§ name ßsize) (+ (.. unspent (size)) (.. spent (size)) (.. pending (size))))
            (§ block
                (§ if (or (< ßsize ßnumTransactions) (== ßnumTransactions 0)))
                (§ block
                    (§ ass (§ name ßnumTransactions) ßsize)
                )

                (§ let #_"ArrayList<Transaction>" (§ name ßall) (§ new #_"ArrayList<>" (getTransactions ßincludeDead)))
                (§ block
                    ;; Order by update time.
                    (Collections/sort ßall, Transaction/SORT_TX_BY_UPDATE_TIME)
                    (§ if (== ßnumTransactions (.. ßall (size))))
                    (§ block
                        (§ return ßall)
                    )

                    (.. ßall (subList ßnumTransactions, (.. ßall (size))) (clear))
                    (§ return ßall)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

