    ;;;
     ; Clean up the wallet.  Currently, it only removes risky pending transaction from the wallet and only
     ; if their outputs have not been spent.
     ;;
    #_public
    (§ method #_"void" (§ fn cleanup) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" (§ name ßdirty) false)
            (§ block
                (§ for (§ var #_"Iterator<Transaction>" (§ name ßi) (.. pending (values) (iterator))) :for (.. ßi (hasNext)) :for )
                (§ block
                    (§ let #_"Transaction" (§ name ßtx) (.. ßi (next)))
                    (§ block
                        (§ if (and (isTransactionRisky ßtx, nil) (not acceptRiskyTransactions)))
                        (§ block
                            (.. log (debug "Found risky transaction {} in wallet during cleanup.", (.. ßtx (getHashAsString))))
                            (§ if (not (.. ßtx (isAnyOutputSpent))))
                            (§ block
                                ;; Sync myUnspents with the change.
                                (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
                                (§ block
                                    (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßinput (getConnectedOutput)))
                                    (§ block
                                        (§ if (nil? ßoutput))
                                        (§ block
                                            (§ continue )
                                        )
                                        (§ if (.. ßoutput (isMine this)))
                                        (§ block
                                            (Preconditions/checkState (.. myUnspents (add ßoutput)))
                                        )
                                        (.. ßinput (disconnect))
                                    )
                                )
                                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                                (§ block
                                    (.. myUnspents (remove ßoutput))
                                )

                                (.. ßi (remove))
                                (.. transactions (remove (.. ßtx (getHash))))
                                (§ ass (§ name ßdirty) true)
                                (.. log (info "Removed transaction {} from pending pool during cleanup.", (.. ßtx (getHashAsString))))
                            )
                            (§ else )
                            (§ block
                                (.. log (info "Cannot remove transaction {} from pending pool during cleanup, as it's already spent partially.", (.. ßtx (getHashAsString))))
                            )
                        )
                    )
                )
                (§ if ßdirty)
                (§ block
                    (isConsistentOrThrow)
                    (saveLater)
                    (§ if (.. log (isInfoEnabled)))
                    (§ block
                        (.. log (info "Estimated balance is now: {}", (.. (getBalance BalanceType/ESTIMATED) (toFriendlyString))))
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    (§ method #_"EnumSet<Pool>" (§ fn getContainingPools) [#_"Transaction" (§ name ßtx)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"EnumSet<Pool>" (§ name ßresult) (EnumSet/noneOf (§ klass #_"Pool")))
            (§ block
                (§ let #_"Sha256Hash" (§ name ßtxHash) (.. ßtx (getHash)))
                (§ block
                    (§ if (.. unspent (containsKey ßtxHash)))
                    (§ block
                        (.. ßresult (add Pool/UNSPENT))
                    )
                    (§ if (.. spent (containsKey ßtxHash)))
                    (§ block
                        (.. ßresult (add Pool/SPENT))
                    )
                    (§ if (.. pending (containsKey ßtxHash)))
                    (§ block
                        (.. ßresult (add Pool/PENDING))
                    )
                    (§ if (.. dead (containsKey ßtxHash)))
                    (§ block
                        (.. ßresult (add Pool/DEAD))
                    )
                    (§ return ßresult)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

