    ;;;
     ; Formats the wallet as a human readable piece of text.  Intended for debugging, the format is
     ; not meant to be stable or human readable.
     ; @param includePrivateKeys Whether raw private key data should be included.
     ; @param includeTransactions Whether to print transaction data.
     ; @param chain If set, will be used to estimate lock times for block timelocked transactions.
     ;;
    #_public
    (§ method #_"String" (§ fn toString) [#_"boolean" (§ name ßincludePrivateKeys), #_"boolean" (§ name ßincludeTransactions), #_nilable #_"AbstractBlockChain" (§ name ßchain)])
    (§ block
        (.. lock (lock))
        (.. keyChainGroupLock (lock))
        (§ try )
        (§ block
            (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
            (§ block
                (§ let #_"Coin" (§ name ßestimatedBalance) (getBalance BalanceType/ESTIMATED))
                (§ block
                    (§ let #_"Coin" (§ name ßavailableBalance) (getBalance BalanceType/AVAILABLE_SPENDABLE))
                    (§ block
                        (.. ßsb (append "Wallet containing ") (append (.. ßestimatedBalance (toFriendlyString))) (append " (spendable: ") (append (.. ßavailableBalance (toFriendlyString))) (append ") in:\n"))
                        (.. ßsb (append "  ") (append (.. pending (size))) (append " pending transactions\n"))
                        (.. ßsb (append "  ") (append (.. unspent (size))) (append " unspent transactions\n"))
                        (.. ßsb (append "  ") (append (.. spent (size))) (append " spent transactions\n"))
                        (.. ßsb (append "  ") (append (.. dead (size))) (append " dead transactions\n"))
                        (§ let #_"Date" (§ name ßlastBlockSeenTime) (getLastBlockSeenTime))
                        (§ block
                            (.. ßsb (append "Last seen best block: ") (append (getLastBlockSeenHeight)) (append " (") (append (if (some? ßlastBlockSeenTime) (Utils/dateTimeFormat ßlastBlockSeenTime) "time unknown")) (append "): ") (append (getLastBlockSeenHash)) (append "\n"))
                            (§ let #_"KeyCrypter" (§ name ßcrypter) (.. keyChainGroup (getKeyCrypter)))
                            (§ block
                                (§ if (some? ßcrypter))
                                (§ block
                                    (.. ßsb (append "Encryption: ") (append ßcrypter) (append "\n"))
                                )
                                (§ if (isWatching))
                                (§ block
                                    (.. ßsb (append "Wallet is watching.\n"))
                                )

                                ;; Do the keys.
                                (.. ßsb (append "\nKeys:\n"))
                                (.. ßsb (append "Earliest creation time: ") (append (Utils/dateTimeFormat (* (getEarliestKeyCreationTime) 1000))) (append "\n"))
                                (§ let #_"Date" (§ name ßkeyRotationTime) (getKeyRotationTime))
                                (§ block
                                    (§ if (some? ßkeyRotationTime))
                                    (§ block
                                        (.. ßsb (append "Key rotation time:      ") (append (Utils/dateTimeFormat ßkeyRotationTime)) (append "\n"))
                                    )
                                    (.. ßsb (append (.. keyChainGroup (toString ßincludePrivateKeys))))

                                    (§ if ßincludeTransactions)
                                    (§ block
                                        ;; Print the transactions themselves.
                                        (§ if (< 0 (.. pending (size))))
                                        (§ block
                                            (.. ßsb (append "\n>>> PENDING:\n"))
                                            (toStringHelper ßsb, pending, ßchain, Transaction/SORT_TX_BY_UPDATE_TIME)
                                        )
                                        (§ if (< 0 (.. unspent (size))))
                                        (§ block
                                            (.. ßsb (append "\n>>> UNSPENT:\n"))
                                            (toStringHelper ßsb, unspent, ßchain, Transaction/SORT_TX_BY_HEIGHT)
                                        )
                                        (§ if (< 0 (.. spent (size))))
                                        (§ block
                                            (.. ßsb (append "\n>>> SPENT:\n"))
                                            (toStringHelper ßsb, spent, ßchain, Transaction/SORT_TX_BY_HEIGHT)
                                        )
                                        (§ if (< 0 (.. dead (size))))
                                        (§ block
                                            (.. ßsb (append "\n>>> DEAD:\n"))
                                            (toStringHelper ßsb, dead, ßchain, Transaction/SORT_TX_BY_UPDATE_TIME)
                                        )
                                    )
                                    (§ return (.. ßsb (toString)))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. keyChainGroupLock (unlock))
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn toStringHelper) [#_"StringBuilder" (§ name ßsb), #_"Map<Sha256Hash, Transaction>" (§ name ßtransactionMap), #_nilable #_"AbstractBlockChain" (§ name ßchain), #_nilable #_"Comparator<Transaction>" (§ name ßsortOrder)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ let #_"Collection<Transaction>" (§ name ßtxns))
        (§ block
            (§ if (some? ßsortOrder))
            (§ block
                (§ ass (§ name ßtxns) (§ new #_"TreeSet<>" ßsortOrder))
                (.. ßtxns (addAll (.. ßtransactionMap (values))))
            )
            (§ else )
            (§ block
                (§ ass (§ name ßtxns) (.. ßtransactionMap (values)))
            )

            (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtxns)
            (§ block
                (§ try )
                (§ block
                    (.. ßsb (append (.. ßtx (getValue this) (toFriendlyString))))
                    (.. ßsb (append " total value (sends "))
                    (.. ßsb (append (.. ßtx (getValueSentFromMe this) (toFriendlyString))))
                    (.. ßsb (append " and receives "))
                    (.. ßsb (append (.. ßtx (getValueSentToMe this) (toFriendlyString))))
                    (.. ßsb (append ")\n"))
                )
                (§ catch #_"ScriptException" (§ name _))
                (§ block
                    ;; Ignore and don't print this line.
                )
                (§ if (.. ßtx (hasConfidence)))
                (§ block
                    (.. ßsb (append "  confidence: ") (append (.. ßtx (getConfidence))) (append "\n"))
                )
                (.. ßsb (append (.. ßtx (toString ßchain))))
            )
            nil
        )
    )

