    (§ method #_"long" (§ fn getEarliestKeyCreationTime) [])
    (§ block
        (.. keyChainGroupLock (lock))
        (§ try )
        (§ block
            (§ let #_"long" (§ name ßearliestTime) (.. keyChainGroup (getEarliestKeyCreationTime)))
            (§ block
                (§ return (if (== ßearliestTime Long/MAX_VALUE) (Utils/currentTimeSeconds) ßearliestTime))
            )
        )
        (§ finally )
        (§ block
            (.. keyChainGroupLock (unlock))
        )
    )

