    ;;;
     ; <p>It's possible to calculate a wallets balance from multiple points of view.  This enum selects which
     ; {@link #getBalance(BalanceType)} should use.</p>
     ;
     ; <p>Consider a real-world example: you buy a snack costing $5 but you only have a $10 bill.  At the start
     ; you have $10 viewed from every possible angle.  After you order the snack you hand over your $10 bill.
     ; From the perspective of your wallet you have zero dollars (AVAILABLE).  But you know in a few seconds the
     ; shopkeeper will give you back $5 change so most people in practice would say they have $5 (ESTIMATED).</p>
     ;
     ; <p>The fact that the wallet can track transactions which are not spendable by itself ("watching wallets")
     ; adds another type of balance to the mix.  Although the wallet won't do this by default, advanced use cases
     ; that override the relevancy checks can end up with a mix of spendable and unspendable transactions.</p>
     ;;
    #_public
    (§ enum #_"BalanceType"
        ;;;
         ; Balance calculated assuming all pending transactions are in fact included into the best chain by miners.
         ; This includes the value of immature coinbase transactions.
         ;;
        (§ item ESTIMATED)

        ;;;
         ; Balance that could be safely used to create new spends, if we had all the needed private keys.  This is
         ; whatever the default coin selector would make available, which by default means transaction outputs with at
         ; least 1 confirmation and pending transactions created by our own wallet which have been propagated across
         ; the network.  Whether we <i>actually</i> have the private keys or not is irrelevant for this balance type.
         ;;
        (§ item AVAILABLE)

        ;;; Same as ESTIMATED but only for outputs we have the private keys for and can sign ourselves. ;;
        (§ item ESTIMATED_SPENDABLE)
        ;;; Same as AVAILABLE but only for outputs we have the private keys for and can sign ourselves. ;;
        (§ item AVAILABLE_SPENDABLE)
    )

    ;;;
     ; Returns the AVAILABLE balance of this wallet.
     ; See {@link BalanceType#AVAILABLE} for details on what this means.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getBalance) [])
    (§ block
        (getBalance BalanceType/AVAILABLE)
    )

    ;;;
     ; Returns the balance of this wallet as calculated by the provided balanceType.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getBalance) [#_"BalanceType" (§ name ßbalanceType)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (or (== ßbalanceType BalanceType/AVAILABLE) (== ßbalanceType BalanceType/AVAILABLE_SPENDABLE)))
            (§ block
                (§ let #_"List<TransactionOutput>" (§ name ßcandidates) (calculateAllSpendCandidates true, (== ßbalanceType BalanceType/AVAILABLE_SPENDABLE)))
                (§ block
                    (§ let #_"CoinSelection" (§ name ßselection) (.. coinSelector (select NetworkParameters/MAX_MONEY, ßcandidates)))
                    (§ block
                        (§ return (.. ßselection valueGathered))
                    )
                )
            )
            (§ elseif (or (== ßbalanceType BalanceType/ESTIMATED) (== ßbalanceType BalanceType/ESTIMATED_SPENDABLE)))
            (§ block
                (§ let #_"List<TransactionOutput>" (§ name ßall) (calculateAllSpendCandidates false, (== ßbalanceType BalanceType/ESTIMATED_SPENDABLE)))
                (§ block
                    (§ let #_"Coin" (§ name ßvalue) Coin/ZERO)
                    (§ block
                        (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for ßall)
                        (§ block
                            (§ ass (§ name ßvalue) (.. ßvalue (add (.. ßout (getValue)))))
                        )
                        (§ return ßvalue)
                    )
                )
            )
            (§ else )
            (§ block
                (§ throw (§ new #_"AssertionError" "Unknown balance type")) ;; Unreachable.
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns the balance that would be considered spendable by the given coin selector, including watched outputs
     ; (i.e. balance includes outputs we don't have the private keys for).  Just asks it to select as many coins as
     ; possible and returns the total.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getBalance) [#_"CoinSelector" (§ name ßselector)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkNotNull ßselector)

            (§ let #_"List<TransactionOutput>" (§ name ßcandidates) (calculateAllSpendCandidates true, false))
            (§ block
                (§ let #_"CoinSelection" (§ name ßselection) (.. ßselector (select (.. params (getMaxMoney)), ßcandidates)))
                (§ block
                    (§ return (.. ßselection valueGathered))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    #_static
    (§ class #_"BalanceFutureRequest"
        #_public
        (§ field #_"SettableFuture<Coin>" (§ name future))
        #_public
        (§ field #_"Coin" (§ name value))
        #_public
        (§ field #_"BalanceType" (§ name type))
    )
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"List<BalanceFutureRequest>" (§ name balanceFutureRequests) (Lists/newLinkedList))

    ;;;
     ; <p>Returns a future that will complete when the balance of the given type has become equal or larger to the
     ; given value.  If the wallet already has a large enough balance the future is returned in a pre-completed state.
     ; Note that this method is not blocking, if you want to actually wait immediately, you have to call .get() on
     ; the result.</p>
     ;
     ; <p>Also note that by the time the future completes, the wallet may have changed yet again if something else
     ; is going on in parallel, so you should treat the returned balance as advisory and be prepared for sending
     ; money to fail!  Finally please be aware that any listeners on the future will run either on the calling thread
     ; if it completes immediately, or eventually on a background thread if the balance is not yet at the right
     ; level.  If you do something that means you know the balance should be sufficient to trigger the future,
     ; you can use {@link org.bitcoinj.utils.Threading#waitForUserCode()} to block until the future had a
     ; chance to be updated.</p>
     ;;
    #_public
    (§ method #_"ListenableFuture<Coin>" (§ fn getBalanceFuture) [#_"Coin" (§ name ßvalue), #_"BalanceType" (§ name ßtype)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"SettableFuture<Coin>" (§ name ßfuture) (SettableFuture/create))
            (§ block
                (§ let #_"Coin" (§ name ßcurrent) (getBalance ßtype))
                (§ block
                    (§ if (<= 0 (.. ßcurrent (compareTo ßvalue))))
                    (§ block
                        ;; Already have enough.
                        (.. ßfuture (set ßcurrent))
                    )
                    (§ else )
                    (§ block
                        ;; Will be checked later in checkBalanceFutures.  We don't just add an event listener for ourselves
                        ;; here so that running getBalanceFuture().get() in the user code thread works - generally we must
                        ;; avoid giving the user back futures that require the user code thread to be free.
                        (§ let #_"BalanceFutureRequest" (§ name ßreq) (§ new #_"BalanceFutureRequest"))
                        (§ block
                            (§ ass (§ name (.. ßreq ßfuture)) ßfuture)
                            (§ ass (§ name (.. ßreq ßvalue)) ßvalue)
                            (§ ass (§ name (.. ßreq ßtype)) ßtype)
                            (.. balanceFutureRequests (add ßreq))
                        )
                    )
                    (§ return ßfuture)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;; Runs any balance futures in the user code thread.
    #_suppress(§ opt "FieldAccessNotGuarded")
    #_private
    (§ method- #_"void" (§ fn checkBalanceFuturesLocked) [#_nilable #_"Coin" (§ name ßavail)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ let #_"ListIterator<BalanceFutureRequest>" (§ name ßit) (.. balanceFutureRequests (listIterator)))
        (§ block
            (§ while (.. ßit (hasNext)))
            (§ block
                (§ let #_"BalanceFutureRequest" (§ name ßreq) (.. ßit (next)))
                (§ block
                    (§ let #_"Coin" (§ name ßval) (getBalance (.. ßreq type))) ;; This could be slow for lots of futures.
                    (§ block
                        (§ if (< (.. ßval (compareTo (.. ßreq value))) 0))
                        (§ block
                            (§ continue )
                        )
                        ;; Found one that's finished.
                        (.. ßit (remove))
                        (§ let #_"Coin" (§ name ßv) ßval)
                        (§ block
                            ;; Don't run any user-provided future listeners with our lock held.
                            (.. Threading/USER_THREAD (execute (§ new #_"Runnable")
                            (§ anon
                                #_override
                                #_public
                                (§ method #_"void" (§ fn run) [])
                                (§ block
                                    (.. ßreq future (set ßv))
                                    nil
                                )
                            )))
                        )
                    )
                )
            )
            nil
        )
    )

    ;;;
     ; Returns the amount of bitcoin ever received via output.  <b>This is not the balance!</b>  If an output
     ; spends from a transaction whose inputs are also to our wallet, the input amounts are deducted from the
     ; outputs contribution, with a minimum of zero contribution.  The idea behind this is we avoid double
     ; counting money sent to us.
     ; @return the total amount of satoshis received, regardless of whether it was spent or not.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getTotalReceived) [])
    (§ block
        (§ let #_"Coin" (§ name ßtotal) Coin/ZERO)
        (§ block

            ;; Include outputs to us if they were not just change outputs, i.e. the inputs to us summed to less
            ;; than the outputs to us.
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. transactions (values)))
            (§ block
                (§ let #_"Coin" (§ name ßtxTotal) Coin/ZERO)
                (§ block
                    (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                    (§ block
                        (§ if (.. ßoutput (isMine this)))
                        (§ block
                            (§ ass (§ name ßtxTotal) (.. ßtxTotal (add (.. ßoutput (getValue)))))
                        )
                    )
                    (§ for (§ var #_"TransactionInput" (§ name ßin)) :for (.. ßtx (getInputs)))
                    (§ block
                        (§ let #_"TransactionOutput" (§ name ßprevOut) (.. ßin (getConnectedOutput)))
                        (§ block
                            (§ if (and (some? ßprevOut) (.. ßprevOut (isMine this))))
                            (§ block
                                (§ ass (§ name ßtxTotal) (.. ßtxTotal (subtract (.. ßprevOut (getValue)))))
                            )
                        )
                    )
                    (§ if (.. ßtxTotal (isPositive)))
                    (§ block
                        (§ ass (§ name ßtotal) (.. ßtotal (add ßtxTotal)))
                    )
                )
            )
            ßtotal
        )
    )

    ;;;
     ; Returns the amount of bitcoin ever sent via output.  If an output is sent to our own wallet, because of
     ; change or rotating keys or whatever, we do not count it.  If the wallet was involved in a shared transaction,
     ; i.e. there is some input to the transaction that we don't have the key for, then we multiply the sum of
     ; the output values by the proportion of satoshi coming in to our inputs.  Essentially we treat inputs as
     ; pooling into the transaction, becoming fungible and being equally distributed to all outputs.
     ; @return the total amount of satoshis sent by us.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getTotalSent) [])
    (§ block
        (§ let #_"Coin" (§ name ßtotal) Coin/ZERO)
        (§ block

            (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. transactions (values)))
            (§ block
                ;; Count spent outputs to only if they were not to us.  This means we don't count change outputs.
                (§ let #_"Coin" (§ name ßtxOutputTotal) Coin/ZERO)
                (§ block
                    (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for (.. ßtx (getOutputs)))
                    (§ block
                        (§ if (not (.. ßout (isMine this))))
                        (§ block
                            (§ ass (§ name ßtxOutputTotal) (.. ßtxOutputTotal (add (.. ßout (getValue)))))
                        )
                    )

                    ;; Count the input values to us
                    (§ let #_"Coin" (§ name ßtxOwnedInputsTotal) Coin/ZERO)
                    (§ block
                        (§ for (§ var #_"TransactionInput" (§ name ßin)) :for (.. ßtx (getInputs)))
                        (§ block
                            (§ let #_"TransactionOutput" (§ name ßprevOut) (.. ßin (getConnectedOutput)))
                            (§ block
                                (§ if (and (some? ßprevOut) (.. ßprevOut (isMine this))))
                                (§ block
                                    (§ ass (§ name ßtxOwnedInputsTotal) (.. ßtxOwnedInputsTotal (add (.. ßprevOut (getValue)))))
                                )
                            )
                        )

                        ;; If there is an input that isn't from us, i.e. this is a shared transaction.
                        (§ let #_"Coin" (§ name ßtxInputsTotal) (.. ßtx (getInputSum)))
                        (§ block
                            (§ if (!= ßtxOwnedInputsTotal ßtxInputsTotal))
                            (§ block
                                ;; Multiply our output total by the appropriate proportion to account for the inputs that we don't own.
                                (§ let #_"BigInteger" (§ name ßtxOutputTotalNum) (§ new #_"BigInteger" (.. ßtxOutputTotal (toString))))
                                (§ block
                                    (§ ass (§ name ßtxOutputTotalNum) (.. ßtxOutputTotalNum (multiply (§ new #_"BigInteger" (.. ßtxOwnedInputsTotal (toString))))))
                                    (§ ass (§ name ßtxOutputTotalNum) (.. ßtxOutputTotalNum (divide (§ new #_"BigInteger" (.. ßtxInputsTotal (toString))))))
                                    (§ ass (§ name ßtxOutputTotal) (Coin/valueOf (.. ßtxOutputTotalNum (longValue))))
                                )
                            )
                            (§ ass (§ name ßtotal) (.. ßtotal (add ßtxOutputTotal)))
                        )
                    )
                )
            )
            ßtotal
        )
    )

    ;;; A SendResult is returned to you as part of sending coins to a recipient. ;;
    #_public
    #_static
    (§ class #_"SendResult"
        ;;; The Bitcoin transaction message that moves the money. ;;
        #_public
        (§ field #_"Transaction" (§ name tx))
        ;;;
         ; A future that will complete once the tx message has been successfully broadcast to the network.
         ; This is just the result of calling broadcast.future().
         ;;
        #_public
        (§ field #_"ListenableFuture<Transaction>" (§ name broadcastComplete))
        ;;; The broadcast object returned by the linked TransactionBroadcaster. ;;
        #_public
        (§ field #_"TransactionBroadcast" (§ name broadcast))
    )

    ;;;
     ; Enumerates possible resolutions for missing signatures.
     ;;
    #_public
    (§ enum #_"MissingSigsMode"
        ;;; Input script will have OP_0 instead of missing signatures. ;;
        (§ item USE_OP_ZERO)
        ;;;
         ; Missing signatures will be replaced by dummy sigs.  This is useful when you'd like to know the
         ; fee for a transaction without knowing the user's password, as fee depends on size.
         ;;
        (§ item USE_DUMMY_SIG)
        ;;;
         ; If signature is missing, {@link org.bitcoinj.signers.TransactionSigner.MissingSignatureException}
         ; will be thrown for P2SH and {@link ECKey.MissingPrivateKeyException} for other tx types.
         ;;
        (§ item THROW)
    )

    ;;;
     ; <p>Statelessly creates a transaction that sends the given value to address.  The change is sent to
     ; {@link Wallet#currentChangeAddress()}, so you must have added at least one key.</p>
     ;
     ; <p>If you just want to send money quickly, you probably want
     ; {@link Wallet#sendCoins(TransactionBroadcaster, Address, Coin)} instead.  That will create the sending
     ; transaction, commit to the wallet and broadcast it to the network all in one go.  This method is lower
     ; level and lets you see the proposed transaction before anything is done with it.</p>
     ;
     ; <p>This is a helper method that is equivalent to using {@link SendRequest#to(Address, Coin)}
     ; followed by {@link Wallet#completeTx(Wallet.SendRequest)} and returning the requests transaction object.
     ; Note that this means a fee may be automatically added if required, if you want more control over the process,
     ; just do those two steps yourself.</p>
     ;
     ; <p>IMPORTANT: This method does NOT update the wallet.  If you call createSend again you may get two transactions
     ; that spend the same coins.  You have to call {@link Wallet#commitTx(Transaction)} on the created transaction to
     ; prevent this, but that should only occur once the transaction has been accepted by the network.  This implies
     ; you cannot have more than one outstanding sending tx at once.</p>
     ;
     ; <p>You MUST ensure that the value is not smaller than {@link Transaction#MIN_NONDUST_OUTPUT} or the transaction
     ; will almost certainly be rejected by the network as dust.</p>
     ;
     ; @param address The Bitcoin address to send the money to.
     ; @param value How much currency to send.
     ; @return either the created Transaction or null if there are insufficient coins.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"Transaction" (§ fn createSend) [#_"Address" (§ name ßaddress), #_"Coin" (§ name ßvalue)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (§ let #_"SendRequest" (§ name ßreq) (SendRequest/to ßaddress, ßvalue))
        (§ block
            (§ if (.. params (getId) (equals NetworkParameters/ID_UNITTESTNET)))
            (§ block
                (§ ass (§ name (.. ßreq shuffleOutputs)) false)
            )
            (completeTx ßreq)
            (.. ßreq tx)
        )
    )

    ;;;
     ; Sends coins to the given address but does not broadcast the resulting pending transaction.  It is still
     ; stored in the wallet, so when the wallet is added to a {@link PeerGroup} or {@link Peer} the transaction
     ; will be announced to the network.  The given {@link SendRequest} is completed first using
     ; {@link Wallet#completeTx(Wallet.SendRequest)} to make it valid.
     ;
     ; @return the Transaction that was created.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws IllegalArgumentException if you try and complete the same SendRequest twice.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"Transaction" (§ fn sendCoinsOffline) [#_"SendRequest" (§ name ßrequest)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (completeTx ßrequest)
            (commitTx (.. ßrequest tx))
            (§ return (.. ßrequest tx))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; <p>Sends coins to the given address, via the given {@link PeerGroup}.  Change is returned to
     ; {@link Wallet#currentChangeAddress()}.  Note that a fee may be automatically added if one may be required
     ; for the transaction to be confirmed.</p>
     ;
     ; <p>The returned object provides both the transaction, and a future that can be used to learn when the broadcast
     ; is complete.  Complete means, if the PeerGroup is limited to only one connection, when it was written out to
     ; the socket.  Otherwise when the transaction is written out and we heard it back from a different peer.</p>
     ;
     ; <p>Note that the sending transaction is committed to the wallet immediately, not when the transaction is
     ; successfully broadcast.  This means that even if the network hasn't heard about your transaction you won't be
     ; able to spend those same coins again.</p>
     ;
     ; <p>You MUST ensure that value is not smaller than {@link Transaction#MIN_NONDUST_OUTPUT} or the transaction will
     ; almost certainly be rejected by the network as dust.</p>
     ;
     ; @param broadcaster A {@link TransactionBroadcaster} to use to send the transactions out.
     ; @param to Which address to send coins to.
     ; @param value How much value to send.
     ; @return an object containing the transaction that was created, and a future for the broadcast of it.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"SendResult" (§ fn sendCoins) [#_"TransactionBroadcaster" (§ name ßbroadcaster), #_"Address" (§ name ßto), #_"Coin" (§ name ßvalue)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (§ let #_"SendRequest" (§ name ßrequest) (SendRequest/to ßto, ßvalue))
        (§ block
            (sendCoins ßbroadcaster, ßrequest)
        )
    )

    ;;;
     ; <p>Sends coins according to the given request, via the given {@link TransactionBroadcaster}.</p>
     ;
     ; <p>The returned object provides both the transaction, and a future that can be used to learn when the broadcast
     ; is complete.  Complete means, if the PeerGroup is limited to only one connection, when it was written out to
     ; the socket.  Otherwise when the transaction is written out and we heard it back from a different peer.</p>
     ;
     ; <p>Note that the sending transaction is committed to the wallet immediately, not when the transaction is
     ; successfully broadcast.  This means that even if the network hasn't heard about your transaction you won't be
     ; able to spend those same coins again.</p>
     ;
     ; @param broadcaster The target to use for broadcast.
     ; @param request The SendRequest that describes what to do, get one using static methods on SendRequest itself.
     ; @return an object containing the transaction that was created, and a future for the broadcast of it.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws IllegalArgumentException if you try and complete the same SendRequest twice.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"SendResult" (§ fn sendCoins) [#_"TransactionBroadcaster" (§ name ßbroadcaster), #_"SendRequest" (§ name ßrequest)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        ;; Should not be locked here, as we're going to call into the broadcaster and that might want to hold its
        ;; own lock.  sendCoinsOffline handles everything that needs to be locked.
        (Preconditions/checkState (not (.. lock (isHeldByCurrentThread))))

        ;; Commit the TX to the wallet immediately so the spent coins won't be reused.
        ;; TODO: We should probably allow the request to specify tx commit only after the network has accepted it.
        (§ let #_"Transaction" (§ name ßtx) (sendCoinsOffline ßrequest))
        (§ block
            (§ let #_"SendResult" (§ name ßresult) (§ new #_"SendResult"))
            (§ block
                (§ ass (§ name (.. ßresult ßtx)) ßtx)
                ;; The tx has been committed to the pending pool by this point (via sendCoinsOffline -> commitTx), so it has
                ;; a txConfidenceListener registered.  Once the tx is broadcast the peers will update the memory pool with the
                ;; count of seen peers, the memory pool will update the transaction confidence object, that will invoke the
                ;; txConfidenceListener which will in turn invoke the wallets event listener onTransactionConfidenceChanged
                ;; method.
                (§ ass (§ name (.. ßresult broadcast)) (.. ßbroadcaster (broadcastTransaction ßtx)))
                (§ ass (§ name (.. ßresult broadcastComplete)) (.. ßresult broadcast (future)))
                ßresult
            )
        )
    )

    ;;;
     ; Satisfies the given {@link SendRequest} using the default transaction broadcaster configured either via
     ; {@link PeerGroup#addWallet(Wallet)} or directly with {@link #setTransactionBroadcaster(TransactionBroadcaster)}.
     ;
     ; @param request The SendRequest that describes what to do, get one using static methods on SendRequest itself.
     ; @return an object containing the transaction that was created, and a future for the broadcast of it.
     ; @throws IllegalStateException if no transaction broadcaster has been configured.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws IllegalArgumentException if you try and complete the same SendRequest twice.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"SendResult" (§ fn sendCoins) [#_"SendRequest" (§ name ßrequest)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (§ let #_"TransactionBroadcaster" (§ name ßbroadcaster) vTransactionBroadcaster)
        (§ block
            (Preconditions/checkState (some? ßbroadcaster), "No transaction ßbroadcaster is configured")
            (sendCoins ßbroadcaster, ßrequest)
        )
    )

    ;;;
     ; Sends coins to the given address, via the given {@link Peer}.  Change is returned to {@link Wallet#currentChangeAddress()}.
     ; If an exception is thrown by {@link Peer#sendMessage(Message)} the transaction is still committed, so the pending
     ; transaction must be broadcast <b>by you</b> at some other time.  Note that a fee may be automatically added
     ; if one may be required for the transaction to be confirmed.
     ;
     ; @return the {@link Transaction} that was created or null if there was insufficient balance to send the coins.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws IllegalArgumentException if you try and complete the same SendRequest twice.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"Transaction" (§ fn sendCoins) [#_"Peer" (§ name ßpeer), #_"SendRequest" (§ name ßrequest)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (§ let #_"Transaction" (§ name ßtx) (sendCoinsOffline ßrequest))
        (§ block
            (.. ßpeer (sendMessage ßtx))
            ßtx
        )
    )

    ;;;
     ; Class of exceptions thrown in {@link Wallet#completeTx(SendRequest)}.
     ;;
    #_public
    #_static
    (§ class #_"CompletionException" (§ extends #_"RuntimeException"))

    ;;;
     ; Thrown if the resultant transaction would violate the dust rules (an output that's too small to be worthwhile).
     ;;
    #_public
    #_static
    (§ class #_"DustySendRequested" (§ extends #_"CompletionException"))

    ;;;
     ; Thrown if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    #_static
    (§ class #_"MultipleOpReturnRequested" (§ extends #_"CompletionException"))

    ;;;
     ; Thrown when we were trying to empty the wallet, and the total amount of money we were trying to empty after
     ; being reduced for the fee was smaller than the min payment.
     ; Note that the missing field will be null in this case.
     ;;
    #_public
    #_static
    (§ class #_"CouldNotAdjustDownwards" (§ extends #_"CompletionException"))

    ;;;
     ; Thrown if the resultant transaction is too big for Bitcoin to process.  Try breaking up the amounts of value.
     ;;
    #_public
    #_static
    (§ class #_"ExceededMaxTransactionSize" (§ extends #_"CompletionException"))

    ;;;
     ; Given a spend request containing an incomplete transaction, makes it valid by adding outputs and signed inputs
     ; according to the instructions in the request.  The transaction in the request is modified by this method.
     ;
     ; @param req A SendRequest that contains the incomplete transaction and details for how to make it valid.
     ; @throws InsufficientMoneyException if the request could not be completed due to not enough balance.
     ; @throws IllegalArgumentException if you try and complete the same SendRequest twice.
     ; @throws DustySendRequested if the resultant transaction would violate the dust rules.
     ; @throws CouldNotAdjustDownwards if emptying the wallet was requested and the output can't be shrunk for fees without violating a protocol rule.
     ; @throws ExceededMaxTransactionSize if the resultant transaction is too big for Bitcoin to process.
     ; @throws MultipleOpReturnRequested if there is more than one OP_RETURN output for the resultant transaction.
     ;;
    #_public
    (§ method #_"void" (§ fn completeTx) [#_"SendRequest" (§ name ßreq)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkArgument (not (.. ßreq completed)), "Given SendRequest has already been completed.")

            ;; Calculate the amount of value we need to import.
            (§ let #_"Coin" (§ name ßvalue) Coin/ZERO)
            (§ block
                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßreq tx (getOutputs)))
                (§ block
                    (§ ass (§ name ßvalue) (.. ßvalue (add (.. ßoutput (getValue)))))
                )

                (.. log (info "Completing send tx with {} outputs totalling {} and a fee of {}/kB", (.. ßreq tx (getOutputs) (size)), (.. ßvalue (toFriendlyString)), (.. ßreq feePerKb (toFriendlyString))))

                ;; If any inputs have already been added, we don't need to get their value from wallet.
                (§ let #_"Coin" (§ name ßtotalInput) Coin/ZERO)
                (§ block
                    (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßreq tx (getInputs)))
                    (§ block
                        (§ if (!= (.. ßinput (getConnectedOutput)) nil))
                        (§ block
                            (§ ass (§ name ßtotalInput) (.. ßtotalInput (add (.. ßinput (getConnectedOutput) (getValue)))))
                        )
                        (§ else )
                        (§ block
                            (.. log (warn "SendRequest transaction already has inputs but we don't know how much they are worth - they will be added to fee."))
                        )
                    )
                    (§ ass (§ name ßvalue) (.. ßvalue (subtract ßtotalInput)))

                    (§ let #_"List<TransactionInput>" (§ name ßoriginalInputs) (§ new #_"ArrayList<>" (.. ßreq tx (getInputs))))
                    (§ block

                        ;; Check for dusty sends and the OP_RETURN limit.
                        (§ if (and (.. ßreq ensureMinRequiredFee) (not (.. ßreq emptyWallet)))) ;; Min fee checking is handled later for emptyWallet.
                        (§ block
                            (§ let #_"int" (§ name ßopReturnCount) 0)
                            (§ block
                                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßreq tx (getOutputs)))
                                (§ block
                                    (§ if (.. ßoutput (isDust)))
                                    (§ block
                                        (§ throw (§ new #_"DustySendRequested"))
                                    )
                                    (§ if (.. ßoutput (getScriptPubKey) (isOpReturn)))
                                    (§ block
                                        (§ ass (§ name ßopReturnCount) (+ ßopReturnCount 1))
                                    )
                                )
                                (§ if (< 1 ßopReturnCount)) ;; Only 1 OP_RETURN per transaction allowed.
                                (§ block
                                    (§ throw (§ new #_"MultipleOpReturnRequested"))
                                )
                            )
                        )

                        ;; Calculate a list of ALL potential candidates for spending and then ask a coin selector to provide us
                        ;; with the actual outputs that'll be used to gather the required amount of value.  In this way, users
                        ;; can customize coin selection policies.  The call below will ignore immature coinbases and outputs
                        ;; we don't have the keys for.
                        (§ let #_"List<TransactionOutput>" (§ name ßcandidates) (calculateAllSpendCandidates true, (== (.. ßreq missingSigsMode) MissingSigsMode/THROW)))
                        (§ block

                            (§ let #_"CoinSelection" (§ name ßbestCoinSelection))
                            (§ block
                                (§ let #_"TransactionOutput" (§ name ßbestChangeOutput) nil)
                                (§ block
                                    (§ let #_"List<Coin>" (§ name ßupdatedOutputValues) nil)
                                    (§ block
                                        (§ if (not (.. ßreq emptyWallet)))
                                        (§ block
                                            ;; This can throw InsufficientMoneyException.
                                            (§ let #_"FeeCalculation" (§ name ßfeeCalculation) (calculateFee ßreq, ßvalue, ßoriginalInputs, (.. ßreq ensureMinRequiredFee), ßcandidates))
                                            (§ block
                                                (§ ass (§ name ßbestCoinSelection) (.. ßfeeCalculation ßbestCoinSelection))
                                                (§ ass (§ name ßbestChangeOutput) (.. ßfeeCalculation ßbestChangeOutput))
                                                (§ ass (§ name ßupdatedOutputValues) (.. ßfeeCalculation ßupdatedOutputValues))
                                            )
                                        )
                                        (§ else )
                                        (§ block
                                            ;; We're being asked to empty the wallet.  What this means is ensuring "tx" has only a single output
                                            ;; of the total value we can currently spend as determined by the selector, and then subtracting the fee.
                                            (Preconditions/checkState (== (.. ßreq tx (getOutputs) (size)) 1), "Empty wallet TX must have a single output only.")

                                            (§ let #_"CoinSelector" (§ name ßselector) (or (.. ßreq coinSelector) coinSelector))
                                            (§ block
                                                (§ ass (§ name ßbestCoinSelection) (.. ßselector (select (.. params (getMaxMoney)), ßcandidates)))
                                                (§ ass (§ name ßcandidates) nil) ;; Selector took ownership and might have changed candidates.  Don't access again.
                                                (.. ßreq tx (getOutput 0) (setValue (.. ßbestCoinSelection valueGathered)))
                                                (.. log (info "  emptying {}", (.. ßbestCoinSelection valueGathered (toFriendlyString))))
                                            )
                                        )

                                        (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßbestCoinSelection gathered))
                                        (§ block
                                            (.. ßreq tx (addInput ßoutput))
                                        )

                                        (§ if (.. ßreq emptyWallet))
                                        (§ block
                                            (§ let #_"Coin" (§ name ßfeePerKb) (or (.. ßreq feePerKb) Coin/ZERO))
                                            (§ block
                                                (§ if (not (adjustOutputDownwardsForFee (.. ßreq tx), ßbestCoinSelection, ßfeePerKb, (.. ßreq ensureMinRequiredFee))))
                                                (§ block
                                                    (§ throw (§ new #_"CouldNotAdjustDownwards"))
                                                )
                                            )
                                        )

                                        (§ if (some? ßupdatedOutputValues))
                                        (§ block
                                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßupdatedOutputValues (size))) :for (§ recur (+ ßi 1)))
                                            (§ block
                                                (.. ßreq tx (getOutput ßi) (setValue (.. ßupdatedOutputValues (get ßi))))
                                            )
                                        )

                                        (§ if (some? ßbestChangeOutput))
                                        (§ block
                                            (.. ßreq tx (addOutput ßbestChangeOutput))
                                            (.. log (info "  with {} change", (.. ßbestChangeOutput (getValue) (toFriendlyString))))
                                        )

                                        ;; Now shuffle the outputs to obfuscate which is the change.
                                        (§ if (.. ßreq shuffleOutputs))
                                        (§ block
                                            (.. ßreq tx (shuffleOutputs))
                                        )

                                        ;; Now sign the inputs, thus proving that we are entitled to redeem the connected outputs.
                                        (§ if (.. ßreq signInputs))
                                        (§ block
                                            (signTransaction ßreq)
                                        )

                                        ;; Check size.
                                        (§ let #_"int" (§ name ßsize) (.. ßreq tx (unsafeBitcoinSerialize) length))
                                        (§ block
                                            (§ if (< Transaction/MAX_STANDARD_TX_SIZE ßsize))
                                            (§ block
                                                (§ throw (§ new #_"ExceededMaxTransactionSize"))
                                            )

                                            ;; Label the transaction as being self created.  We can use this later to spend its change output even before
                                            ;; the transaction is confirmed.  We deliberately won't bother notifying listeners here as there's not much
                                            ;; point - the user isn't interested in a confidence transition they made themselves.
                                            (.. ßreq tx (getConfidence) (setSource TransactionConfidence/Source/SELF))
                                            ;; Label the transaction as being a user requested payment.  This can be used to render GUI wallet
                                            ;; transaction lists more appropriately, especially when the wallet starts to generate transactions itself
                                            ;; for internal purposes.
                                            (.. ßreq tx (setPurpose Transaction/Purpose/USER_PAYMENT))
                                            ;; Record the exchange rate that was valid when the transaction was completed.
                                            (.. ßreq tx (setExchangeRate (.. ßreq exchangeRate)))
                                            (.. ßreq tx (setMemo (.. ßreq memo)))
                                            (§ ass (§ name (.. ßreq completed)) true)
                                            (.. log (info "  completed: {}", (.. ßreq tx)))
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; <p>Given a send request containing transaction, attempts to sign it's inputs.  This method expects
     ; the transaction to have all necessary inputs connected or they will be ignored.</p>
     ; <p>Actual signing is done by pluggable {@link #signers} and it's not guaranteed that
     ; the transaction will be complete in the end.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn signTransaction) [#_"SendRequest" (§ name ßreq)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"Transaction" (§ name ßtx) (.. ßreq tx))
            (§ block
                (§ let #_"List<TransactionInput>" (§ name ßinputs) (.. ßtx (getInputs)))
                (§ block
                    (§ let #_"List<TransactionOutput>" (§ name ßoutputs) (.. ßtx (getOutputs)))
                    (§ block
                        (Preconditions/checkState (< 0 (.. ßinputs (size))))
                        (Preconditions/checkState (< 0 (.. ßoutputs (size))))

                        (§ let #_"KeyBag" (§ name ßmaybeDecryptingKeyBag) (§ new #_"DecryptingKeyBag" this, (.. ßreq aesKey)))
                        (§ block

                            (§ let #_"int" (§ name ßnumInputs) (.. ßtx (getInputs) (size)))
                            (§ block
                                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumInputs) :for (§ recur (+ ßi 1)))
                                (§ block
                                    (§ let #_"TransactionInput" (§ name ßtxIn) (.. ßtx (getInput ßi)))
                                    (§ block
                                        ;; Missing connected output, assuming already signed.
                                        (§ if (== (.. ßtxIn (getConnectedOutput)) nil))
                                        (§ block
                                            (§ continue )
                                        )

                                        (§ try )
                                        (§ block
                                            ;; We assume if its already signed, its hopefully got a SIGHASH type that will not invalidate when
                                            ;; we sign missing pieces (to check this would require either assuming any signatures are signing
                                            ;; standard output types or a way to get processed signatures out of script execution).
                                            (.. ßtxIn (getScriptSig) (correctlySpends ßtx, ßi, (.. ßtxIn (getConnectedOutput) (getScriptPubKey))))
                                            (.. log (warn "Input {} already correctly spends output, assuming SIGHASH type used will be safe and skipping signing.", ßi))
                                            (§ continue )
                                        )
                                        (§ catch #_"ScriptException" (§ name ße))
                                        (§ block
                                            (.. log (debug "Input contained an incorrect signature", ße))
                                            ;; Expected.
                                        )

                                        (§ let #_"Script" (§ name ßscriptPubKey) (.. ßtxIn (getConnectedOutput) (getScriptPubKey)))
                                        (§ block
                                            (§ let #_"RedeemData" (§ name ßredeemData) (.. ßtxIn (getConnectedRedeemData ßmaybeDecryptingKeyBag)))
                                            (§ block
                                                (Preconditions/checkNotNull ßredeemData, "Transaction exists in wallet that we cannot redeem: %s", (.. ßtxIn (getOutpoint) (getHash)))
                                                (.. ßtxIn (setScriptSig (.. ßscriptPubKey (createEmptyInputScript (.. ßredeemData keys (get 0)), (.. ßredeemData redeemScript)))))
                                            )
                                        )
                                    )
                                )

                                (§ let #_"TransactionSigner.ProposedTransaction" (§ name ßproposal) (§ new #_"TransactionSigner.ProposedTransaction" ßtx))
                                (§ block
                                    (§ for (§ var #_"TransactionSigner" (§ name ßsigner)) :for signers)
                                    (§ block
                                        (§ if (not (.. ßsigner (signInputs ßproposal, ßmaybeDecryptingKeyBag))))
                                        (§ block
                                            (.. log (info "{} returned false for the ßtx", (.. ßsigner (getClass) (getName))))
                                        )
                                    )

                                    ;; Resolve missing sigs if any.
                                    (.. (§ new #_"MissingSigResolutionSigner" (.. ßreq missingSigsMode)) (signInputs ßproposal, ßmaybeDecryptingKeyBag))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;; Reduce the value of the first output of a transaction to pay the given feePerKb as appropriate for its size. ;;
    #_private
    (§ method- #_"boolean" (§ fn adjustOutputDownwardsForFee) [#_"Transaction" (§ name ßtx), #_"CoinSelection" (§ name ßcoinSelection), #_"Coin" (§ name ßfeePerKb), #_"boolean" (§ name ßensureMinRequiredFee)])
    (§ block
        (§ let #_"int" (§ name ßsize) (+ (.. ßtx (unsafeBitcoinSerialize) length) (estimateBytesForSigning ßcoinSelection)))
        (§ block
            (§ let #_"Coin" (§ name ßfee) (.. ßfeePerKb (multiply ßsize) (divide 1000)))
            (§ block
                (§ if (and ßensureMinRequiredFee (< (.. ßfee (compareTo Transaction/REFERENCE_DEFAULT_MIN_TX_FEE)) 0)))
                (§ block
                    (§ ass (§ name ßfee) Transaction/REFERENCE_DEFAULT_MIN_TX_FEE)
                )
                (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßtx (getOutput 0)))
                (§ block
                    (.. ßoutput (setValue (.. ßoutput (getValue) (subtract ßfee))))
                    (not (.. ßoutput (isDust)))
                )
            )
        )
    )

    ;;;
     ; Returns a list of the outputs that can potentially be spent, i.e. that we have the keys for and are unspent
     ; according to our knowledge of the block chain.
     ;;
    #_public
    (§ method #_"List<TransactionOutput>" (§ fn calculateAllSpendCandidates) [])
    (§ block
        (calculateAllSpendCandidates true, true)
    )

    ;;;
     ; Returns a list of all outputs that are being tracked by this wallet taking into account the flags.
     ;
     ; @param excludeImmatureCoinbases Whether to ignore coinbase outputs that we will be able to spend in future once they mature.
     ; @param excludeUnsignable Whether to ignore outputs that we are tracking but don't have the keys to sign for.
     ;;
    #_public
    (§ method #_"List<TransactionOutput>" (§ fn calculateAllSpendCandidates) [#_"boolean" (§ name ßexcludeImmatureCoinbases), #_"boolean" (§ name ßexcludeUnsignable)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"List<TransactionOutput>" (§ name ßcandidates) (§ new #_"ArrayList<>" (.. myUnspents (size))))
            (§ block
                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for myUnspents)
                (§ block
                    (§ if (and ßexcludeUnsignable (not (canSignFor (.. ßoutput (getScriptPubKey))))))
                    (§ block
                        (§ continue )
                    )
                    (§ let #_"Transaction" (§ name ßtransaction) (Preconditions/checkNotNull (.. ßoutput (getParentTransaction))))
                    (§ block
                        (§ if (and ßexcludeImmatureCoinbases (not (.. ßtransaction (isMature)))))
                        (§ block
                            (§ continue )
                        )
                        (.. ßcandidates (add ßoutput))
                    )
                )
                (§ return ßcandidates)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns true if this wallet has at least one of the private keys needed to sign for this scriptPubKey.
     ; Returns false if the form of the script is not known or if the script is OP_RETURN.
     ;;
    #_public
    (§ method #_"boolean" (§ fn canSignFor) [#_"Script" (§ name ßscript)])
    (§ block
        (§ if (.. ßscript (isSentToRawPubKey)))
        (§ block
            (§ let #_"byte[]" (§ name ßpubkey) (.. ßscript (getPubKey)))
            (§ block
                (§ let #_"ECKey" (§ name ßkey) (findKeyFromPubKey ßpubkey))
                (§ block
                    (§ return (and (some? ßkey) (or (.. ßkey (isEncrypted)) (.. ßkey (hasPrivKey)))))
                )
            )
        )

        (§ if (.. ßscript (isPayToScriptHash)))
        (§ block
            (§ let #_"RedeemData" (§ name ßdata) (findRedeemDataFromScriptHash (.. ßscript (getPubKeyHash))))
            (§ block
                (§ return (and (some? ßdata) (canSignFor (.. ßdata redeemScript))))
            )
        )

        (§ if (.. ßscript (isSentToAddress)))
        (§ block
            (§ let #_"ECKey" (§ name ßkey) (findKeyFromPubHash (.. ßscript (getPubKeyHash))))
            (§ block
                (§ return (and (some? ßkey) (or (.. ßkey (isEncrypted)) (.. ßkey (hasPrivKey)))))
            )
        )

        (§ if (.. ßscript (isSentToMultiSig)))
        (§ block
            (§ for (§ var #_"ECKey" (§ name ßpubkey)) :for (.. ßscript (getPubKeys)))
            (§ block
                (§ let #_"ECKey" (§ name ßkey) (findKeyFromPubKey (.. ßpubkey (getPubKey))))
                (§ block
                    (§ if (and (some? ßkey) (or (.. ßkey (isEncrypted)) (.. ßkey (hasPrivKey)))))
                    (§ block
                        (§ return true)
                    )
                )
            )
        )
        (§ elseif (.. ßscript (isSentToCLTVPaymentChannel)))
        (§ block
            ;; Any script for which we are the recipient or sender counts.
            (§ let #_"byte[]" (§ name ßsender) (.. ßscript (getCLTVPaymentChannelSenderPubKey)))
            (§ block
                (§ let #_"ECKey" (§ name ßsenderKey) (findKeyFromPubKey ßsender))
                (§ block
                    (§ if (and (some? ßsenderKey) (or (.. ßsenderKey (isEncrypted)) (.. ßsenderKey (hasPrivKey)))))
                    (§ block
                        (§ return true)
                    )

                    (§ let #_"byte[]" (§ name ßrecipient) (.. ßscript (getCLTVPaymentChannelRecipientPubKey)))
                    (§ block
                        (§ let #_"ECKey" (§ name ßrecipientKey) (findKeyFromPubKey ßsender))
                        (§ block
                            (§ if (and (some? ßrecipientKey) (or (.. ßrecipientKey (isEncrypted)) (.. ßrecipientKey (hasPrivKey)))))
                            (§ block
                                (§ return true)
                            )
                        )
                    )
                )
            )
        )
        false
    )

    ;;; Returns the {@link CoinSelector} object which controls which outputs can be spent by this wallet. ;;
    #_public
    (§ method #_"CoinSelector" (§ fn getCoinSelector) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return coinSelector)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; A coin selector is responsible for choosing which outputs to spend when creating transactions.  The default
     ; selector implements a policy of spending transactions that appeared in the best chain and pending transactions
     ; that were created by this wallet, but not others.  You can override the coin selector for any given send
     ; operation by changing {@link SendRequest#coinSelector}.
     ;;
    #_public
    (§ method #_"void" (§ fn setCoinSelector) [#_"CoinSelector" (§ name ßcoinSelector)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name (.. this ßcoinSelector)) (Preconditions/checkNotNull ßcoinSelector))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Convenience wrapper for <tt>setCoinSelector(Wallet.AllowUnconfirmedCoinSelector.get())</tt>.  If this method
     ; is called on the wallet then transactions will be used for spending regardless of their confidence.  This can
     ; be dangerous - only use this if you absolutely know what you're doing!
     ;;
    #_public
    (§ method #_"void" (§ fn allowSpendingUnconfirmedTransactions) [])
    (§ block
        (setCoinSelector (AllowUnconfirmedCoinSelector/get))
        nil
    )

    ;;;
     ; A custom {@link TransactionOutput} that is free standing.  This contains all the information
     ; required for spending without actually having all the linked data (i.e parent tx).
     ;
     ;;
    #_private
    (§ class #_"FreeStandingTransactionOutput" (§ extends #_"TransactionOutput")
        #_private
        (§ field- #_"UTXO" (§ name output))
        #_private
        (§ field- #_"int" (§ name chainHeight))

        ;;;
         ; Construct a free standing Transaction Output.
         ; @param params The network parameters.
         ; @param output The stored output (free standing).
         ;;
        #_public
        (§ constructor #_"FreeStandingTransactionOutput" [#_"NetworkParameters" (§ name ßparams), #_"UTXO" (§ name ßoutput), #_"int" (§ name ßchainHeight)])
        (§ block
            (§ super ßparams, nil, (.. ßoutput (getValue)), (.. ßoutput (getScript) (getProgram)))

            (§ ass (§ name (.. this ßoutput)) ßoutput)
            (§ ass (§ name (.. this ßchainHeight)) ßchainHeight)
            this
        )

        ;;;
         ; Get the {@link UTXO}.
         ; @return the stored output.
         ;;
        #_public
        (§ method #_"UTXO" (§ fn getUTXO) [])
        (§ block
            output
        )

        ;;;
         ; Get the depth within the chain of the parent tx, depth is 1 if the output height is the height
         ; of the latest block.
         ; @return the depth.
         ;;
        #_override
        #_public
        (§ method #_"int" (§ fn getParentTransactionDepthInBlocks) [])
        (§ block
            (+ (- chainHeight (.. output (getHeight))) 1)
        )

        #_override
        #_public
        (§ method #_"int" (§ fn getIndex) [])
        (§ block
            (§ cast #_"int" (.. output (getIndex)))
        )

        #_override
        #_public
        (§ method #_"Sha256Hash" (§ fn getParentTransactionHash) [])
        (§ block
            (.. output (getHash))
        )
    )

    #_private
    #_static
    (§ class #_"TxOffsetPair" (§ implements #_"Comparable<TxOffsetPair>")
        #_public
        (§ field #_"Transaction" (§ name tx))
        #_public
        (§ field #_"int" (§ name offset))

        #_public
        (§ constructor #_"TxOffsetPair" [#_"Transaction" (§ name ßtx), #_"int" (§ name ßoffset)])
        (§ block
            (§ ass (§ name (.. this ßtx)) ßtx)
            (§ ass (§ name (.. this ßoffset)) ßoffset)
            this
        )

        #_override
        #_public
        (§ method #_"int" (§ fn compareTo) [#_"TxOffsetPair" (§ name ßo)])
        (§ block
            ;; Note that in this implementation compareTo() is not consistent with equals().
            (Ints/compare offset, (.. ßo offset))
        )
    )

    ;;;
     ; <p>Don't call this directly.  It's not intended for API users.</p>
     ;
     ; <p>Called by the {@link BlockChain} when the best chain (representing total work done) has changed.
     ; This can cause the number of confirmations of a transaction to go higher, lower, drop to zero and
     ; can even result in a transaction going dead (will never confirm) due to a double spend.</p>
     ;
     ; <p>The oldBlocks/newBlocks lists are ordered height-wise from top first to bottom last.</p>
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn reorganize) [#_"StoredBlock" (§ name ßsplitPoint), #_"List<StoredBlock>" (§ name ßoldBlocks), #_"List<StoredBlock>" (§ name ßnewBlocks)])
        (§ throws #_"VerificationException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; This runs on any peer thread with the block chain locked.
            ;;
            ;; The reorganize functionality of the wallet is tested in ChainSplitTest.java
            ;;
            ;; receive() has been called on the block that is triggering the re-org before this is called,
            ;; with type of SIDE_CHAIN.
            ;;
            ;; Note that this code assumes blocks are not invalid - if blocks contain duplicated transactions,
            ;; transactions that double spend etc then we can calculate the incorrect result.  This could open up
            ;; obscure DoS attacks if someone successfully mines a throwaway invalid block and feeds it to us, just
            ;; to try and corrupt the internal data structures.  We should try harder to avoid this but it's tricky
            ;; because there are so many ways the block can be invalid.

            ;; Avoid spuriously informing the user of wallet/tx confidence changes whilst we're re-organizing.
            (Preconditions/checkState (== (.. confidenceChanged (size)) 0))
            (Preconditions/checkState (not insideReorg))
            (§ ass (§ name insideReorg) true)
            (Preconditions/checkState (== onWalletChangedSuppressions 0))
            (§ ass (§ name onWalletChangedSuppressions) (+ onWalletChangedSuppressions 1))

            ;; Map block hash to transactions that appear in it.  We ensure that the map values are sorted according
            ;; to their relative position within those blocks.
            (§ let #_"ArrayListMultimap<Sha256Hash, TxOffsetPair>" (§ name ßmapBlockTx) (ArrayListMultimap/create))
            (§ block
                (§ for (§ var #_"Transaction" (§ name ßtx)) :for (getTransactions true))
                (§ block
                    (§ let #_"Map<Sha256Hash, Integer>" (§ name ßappearsIn) (.. ßtx (getAppearsInHashes)))
                    (§ block
                        (§ if (nil? ßappearsIn))
                        (§ block
                            (§ continue ) ;; Pending.
                        )
                        (§ for (§ var #_"Map.Entry<Sha256Hash, Integer>" (§ name ßblock)) :for (.. ßappearsIn (entrySet)))
                        (§ block
                            (.. ßmapBlockTx (put (.. ßblock (getKey)), (§ new #_"TxOffsetPair" ßtx, (.. ßblock (getValue)))))
                        )
                    )
                )
                (§ for (§ var #_"Sha256Hash" (§ name ßblockHash)) :for (.. ßmapBlockTx (keySet)))
                (§ block
                    (Collections/sort (.. ßmapBlockTx (get ßblockHash)))
                )

                (§ let #_"List<Sha256Hash>" (§ name ßoldBlockHashes) (§ new #_"ArrayList<>" (.. ßoldBlocks (size))))
                (§ block
                    (.. log (info "Old part of chain (top to bottom):"))
                    (§ for (§ var #_"StoredBlock" (§ name ßb)) :for ßoldBlocks)
                    (§ block
                        (.. log (info "  {}", (.. ßb (getHeader) (getHashAsString))))
                        (.. ßoldBlockHashes (add (.. ßb (getHeader) (getHash))))
                    )
                    (.. log (info "New part of chain (top to bottom):"))
                    (§ for (§ var #_"StoredBlock" (§ name ßb)) :for ßnewBlocks)
                    (§ block
                        (.. log (info "  {}", (.. ßb (getHeader) (getHashAsString))))
                    )

                    (Collections/reverse ßnewBlocks) ;; Need bottom-to-top but we get top-to-bottom.

                    ;; For each block in the old chain, disconnect the transactions in reverse order.
                    (§ let #_"LinkedList<Transaction>" (§ name ßoldChainTxns) (Lists/newLinkedList))
                    (§ block
                        (§ for (§ var #_"Sha256Hash" (§ name ßblockHash)) :for ßoldBlockHashes)
                        (§ block
                            (§ for (§ var #_"TxOffsetPair" (§ name ßpair)) :for (.. ßmapBlockTx (get ßblockHash)))
                            (§ block
                                (§ let #_"Transaction" (§ name ßtx) (.. ßpair tx))
                                (§ block
                                    (§ let #_"Sha256Hash" (§ name ßtxHash) (.. ßtx (getHash)))
                                    (§ block
                                        (§ if (.. ßtx (isCoinBase)))
                                        (§ block
                                            ;; All the transactions that we have in our wallet which spent this coinbase are now invalid
                                            ;; and will never confirm.  Hopefully this should never happen - that's the point of the maturity
                                            ;; rule that forbids spending of coinbase transactions for 100 blocks.
                                            ;;
                                            ;; This could be recursive, although of course because we don't have the full transaction
                                            ;; graph we can never reliably kill all transactions we might have that were rooted in
                                            ;; this coinbase tx.  Some can just go pending forever, like the Bitcoin Core.  However we
                                            ;; can do our best.
                                            (.. log (warn "Coinbase killed by re-org: {}", (.. ßtx (getHashAsString))))
                                            (killTxns (ImmutableSet/of ßtx), nil)
                                        )
                                        (§ else )
                                        (§ block
                                            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                                            (§ block
                                                (§ let #_"TransactionInput" (§ name ßinput) (.. ßoutput (getSpentBy)))
                                                (§ block
                                                    (§ if (some? ßinput))
                                                    (§ block
                                                        (§ if (.. ßoutput (isMine this)))
                                                        (§ block
                                                            (Preconditions/checkState (.. myUnspents (add ßoutput)))
                                                        )
                                                        (.. ßinput (disconnect))
                                                    )
                                                )
                                            )
                                            (.. ßoldChainTxns (add ßtx))
                                            (.. unspent (remove ßtxHash))
                                            (.. spent (remove ßtxHash))
                                            (Preconditions/checkState (not (.. pending (containsKey ßtxHash))))
                                            (Preconditions/checkState (not (.. dead (containsKey ßtxHash))))
                                        )
                                    )
                                )
                            )
                        )

                        ;; Put all the disconnected transactions back into the pending pool and re-connect them.
                        (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßoldChainTxns)
                        (§ block
                            ;; Coinbase transactions on the old part of the chain are dead for good and won't come back unless
                            ;; there's another re-org.
                            (§ if (.. ßtx (isCoinBase)))
                            (§ block
                                (§ continue )
                            )
                            (.. log (info "  ->pending {}", (.. ßtx (getHash))))

                            (.. ßtx (getConfidence) (setConfidenceType ConfidenceType/PENDING)) ;; Wipe height/depth/work data.
                            (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/TYPE))
                            (addWalletTransaction Pool/PENDING, ßtx)
                            (updateForSpends ßtx, false)
                        )

                        ;; Note that dead transactions stay dead.  Consider a chain that Finney attacks T1 and replaces it with
                        ;; T2, so we move T1 into the dead pool.  If there's now a re-org to a chain that doesn't include T2, it
                        ;; doesn't matter - the miners deleted T1 from their mempool, will resurrect T2 and put that into the
                        ;; mempool and so T1 is still seen as a losing double spend.

                        ;; The old blocks have contributed to the depth for all the transactions in the
                        ;; wallet that are in blocks up to and including the chain split block.
                        ;; The total depth is calculated here and then subtracted from the appropriate transactions.
                        (§ let #_"int" (§ name ßdepthToSubtract) (.. ßoldBlocks (size)))
                        (§ block
                            (.. log (info (str "depthToSubtract = " ßdepthToSubtract)))
                            ;; Remove depthToSubtract from all transactions in the wallet except for pending.
                            (subtractDepth ßdepthToSubtract, (.. spent (values)))
                            (subtractDepth ßdepthToSubtract, (.. unspent (values)))
                            (subtractDepth ßdepthToSubtract, (.. dead (values)))

                            ;; The effective last seen block is now the split point so set the lastSeenBlockHash.
                            (setLastBlockSeenHash (.. ßsplitPoint (getHeader) (getHash)))

                            ;; For each block in the new chain, work forwards calling receive() and notifyNewBestBlock().
                            ;; This will pull them back out of the pending pool, or if the tx didn't appear in the old chain
                            ;; and does appear in the new chain, will treat it as such and possibly kill pending transactions
                            ;; that conflict.
                            (§ for (§ var #_"StoredBlock" (§ name ßblock)) :for ßnewBlocks)
                            (§ block
                                (.. log (info "Replaying ßblock {}", (.. ßblock (getHeader) (getHashAsString))))
                                (§ for (§ var #_"TxOffsetPair" (§ name ßpair)) :for (.. ßmapBlockTx (get (.. ßblock (getHeader) (getHash)))))
                                (§ block
                                    (.. log (info "  tx {}", (.. ßpair tx (getHash))))
                                    (§ try )
                                    (§ block
                                        (receive (.. ßpair tx), ßblock, BlockChain/NewBlockType/BEST_CHAIN, (.. ßpair offset))
                                    )
                                    (§ catch #_"ScriptException" (§ name ße))
                                    (§ block
                                        (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen as these blocks were already verified.
                                    )
                                )
                                (notifyNewBestBlock ßblock)
                            )
                            (isConsistentOrThrow)
                            (§ let #_"Coin" (§ name ßbalance) (getBalance))
                            (§ block
                                (.. log (info "post-reorg ßbalance is {}", (.. ßbalance (toFriendlyString))))
                                ;; Inform event listeners that a re-org took place.
                                (queueOnReorganize)
                                (§ ass (§ name insideReorg) false)
                                (§ ass (§ name onWalletChangedSuppressions) (- onWalletChangedSuppressions 1))
                                (maybeQueueOnWalletChanged)
                                (checkBalanceFuturesLocked ßbalance)
                                (informConfidenceListenersIfNotReorganizing)
                                (saveLater)
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Subtract the supplied depth from the given transactions.
     ;;
    #_private
    (§ method- #_"void" (§ fn subtractDepth) [#_"int" (§ name ßdepthToSubtract), #_"Collection<Transaction>" (§ name ßtransactions)])
    (§ block
        (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtransactions)
        (§ block
            (§ if (== (.. ßtx (getConfidence) (getConfidenceType)) ConfidenceType/BUILDING))
            (§ block
                (.. ßtx (getConfidence) (setDepthInBlocks (- (.. ßtx (getConfidence) (getDepthInBlocks)) ßdepthToSubtract)))
                (.. confidenceChanged (put ßtx, TransactionConfidence/Listener/ChangeReason/DEPTH))
            )
        )
        nil
    )

    #_private
    (§ field- #_"ArrayList<TransactionOutPoint>" (§ name bloomOutPoints) (Lists/newArrayList))
    ;; Used to track whether we must automatically begin/end a filter calculation and calc outpoints/take the locks.
    #_private
    (§ field- #_"AtomicInteger" (§ name bloomFilterGuard) (§ new #_"AtomicInteger" 0))

    #_override
    #_public
    (§ method #_"void" (§ fn beginBloomFilterCalculation) [])
    (§ block
        (§ if (< 1 (.. bloomFilterGuard (incrementAndGet))))
        (§ block
            (§ return nil)
        )

        (.. lock (lock))
        (.. keyChainGroupLock (lock))
        ;; noinspection FieldAccessNotGuarded
        (calcBloomOutPointsLocked)
        nil
    )

    #_private
    (§ method- #_"void" (§ fn calcBloomOutPointsLocked) [])
    (§ block
        ;; TODO: This could be done once and then kept up to date.
        (.. bloomOutPoints (clear))
        (§ let #_"Set<Transaction>" (§ name ßall) (§ new #_"HashSet<>"))
        (§ block
            (.. ßall (addAll (.. unspent (values))))
            (.. ßall (addAll (.. spent (values))))
            (.. ßall (addAll (.. pending (values))))

            (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßall)
            (§ block
                (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for (.. ßtx (getOutputs)))
                (§ block
                    (§ try )
                    (§ block
                        (§ if (isTxOutputBloomFilterable ßout))
                        (§ block
                            (.. bloomOutPoints (add (.. ßout (getOutPointFor))))
                        )
                    )
                    (§ catch #_"ScriptException" (§ name ße))
                    (§ block
                        ;; If it is ours, we parsed the script correctly, so this shouldn't happen.
                        (§ throw (§ new #_"RuntimeException" ße))
                    )
                )
            )
            nil
        )
    )

    #_override
    #_guarded-by(§ opt "keyChainGroupLock")
    #_public
    (§ method #_"void" (§ fn endBloomFilterCalculation) [])
    (§ block
        (§ if (< 0 (.. bloomFilterGuard (decrementAndGet))))
        (§ block
            (§ return nil)
        )

        (.. bloomOutPoints (clear))
        (.. keyChainGroupLock (unlock))
        (.. lock (unlock))
        nil
    )

    ;;;
     ; Returns the number of distinct data items (note: NOT keys) that will be inserted into a bloom filter,
     ; when it is constructed.
     ;;
    #_override
    #_public
    (§ method #_"int" (§ fn getBloomFilterElementCount) [])
    (§ block
        (beginBloomFilterCalculation)
        (§ try )
        (§ block
            (§ return (+ (.. bloomOutPoints (size)) (.. keyChainGroup (getBloomFilterElementCount))))
        )
        (§ finally )
        (§ block
            (endBloomFilterCalculation)
        )
    )

    ;;;
     ; Gets a bloom filter that contains all of the public keys from this wallet, and which will provide the given
     ; false-positive rate.  See the docs for {@link BloomFilter} for a brief explanation of anonymity when using filters.
     ;;
    #_public
    (§ method #_"BloomFilter" (§ fn getBloomFilter) [#_"double" (§ name ßfalsePositiveRate)])
    (§ block
        (beginBloomFilterCalculation)
        (§ try )
        (§ block
            (§ return (getBloomFilter (getBloomFilterElementCount), ßfalsePositiveRate, (§ cast #_"long" (* (Math/random) Long/MAX_VALUE))))
        )
        (§ finally )
        (§ block
            (endBloomFilterCalculation)
        )
    )

    ;;;
     ; <p>Gets a bloom filter that contains all of the public keys from this wallet, and which will provide the given
     ; false-positive rate if it has size elements.  Keep in mind that you will get 2 elements in the bloom filter for
     ; each key in the wallet, for the public key and the hash of the public key (address form).</p>
     ;
     ; <p>This is used to generate a BloomFilter which can be {@link BloomFilter#merge(BloomFilter)}d with another.
     ; It could also be used if you have a specific target for the filter's size.</p>
     ;
     ; <p>See the docs for {@link BloomFilter(int, double)} for a brief explanation of anonymity when using bloom
     ; filters.</p>
     ;;
    #_override
    #_guarded-by(§ opt "keyChainGroupLock")
    #_public
    (§ method #_"BloomFilter" (§ fn getBloomFilter) [#_"int" (§ name ßsize), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßnTweak)])
    (§ block
        (beginBloomFilterCalculation)
        (§ try )
        (§ block
            (§ let #_"BloomFilter" (§ name ßfilter) (.. keyChainGroup (getBloomFilter ßsize, ßfalsePositiveRate, ßnTweak)))
            (§ block
                (§ for (§ var #_"TransactionOutPoint" (§ name ßpoint)) :for bloomOutPoints)
                (§ block
                    (.. ßfilter (insert (.. ßpoint (unsafeBitcoinSerialize))))
                )
                (§ return ßfilter)
            )
        )
        (§ finally )
        (§ block
            (endBloomFilterCalculation)
        )
    )

    ;; Returns true if the output is one that won't be selected by a data element matching in the scriptSig.
    #_private
    (§ method- #_"boolean" (§ fn isTxOutputBloomFilterable) [#_"TransactionOutput" (§ name ßout)])
    (§ block
        (§ let #_"Script" (§ name ßscript) (.. ßout (getScriptPubKey)))
        (§ block
            (§ let #_"boolean" (§ name ßisScriptTypeSupported) (or (.. ßscript (isSentToRawPubKey)) (.. ßscript (isPayToScriptHash))))
            (§ block
                (and ßisScriptTypeSupported (.. myUnspents (contains ßout)))
            )
        )
    )

    ;;;
     ; Used by {@link Peer} to decide whether or not to discard this block and any blocks building upon it, in case
     ; the Bloom filter used to request them may be exhausted, that is, not have sufficient keys in the deterministic
     ; sequence within it to reliably find relevant transactions.
     ;;
    #_public
    (§ method #_"boolean" (§ fn checkForFilterExhaustion) [#_"FilteredBlock" (§ name ßblock)])
    (§ block
        (.. keyChainGroupLock (lock))
        (§ try )
        (§ block
            (§ let #_"int" (§ name ßepoch) (.. keyChainGroup (getCombinedKeyLookaheadEpochs)))
            (§ block
                (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. ßblock (getAssociatedTransactions) (values)))
                (§ block
                    (markKeysAsUsed ßtx)
                )
                (§ let #_"int" (§ name ßnewEpoch) (.. keyChainGroup (getCombinedKeyLookaheadEpochs)))
                (§ block
                    (Preconditions/checkState (<= ßepoch ßnewEpoch))
                    ;; If the key lookahead epoch has advanced, there was a call to importKeys and the PeerGroup already has a
                    ;; pending request to recalculate the filter queued up on another thread.  The calling Peer should abandon
                    ;; block at this point and await a new filter before restarting the download.
                    (§ return (< ßepoch ßnewEpoch))
                )
            )
        )
        (§ finally )
        (§ block
            (.. keyChainGroupLock (unlock))
        )
    )

    #_private
    #_static
    (§ class #_"FeeCalculation"
        ;; Selected UTXOs to spend.
        #_public
        (§ field #_"CoinSelection" (§ name bestCoinSelection))
        ;; Change output (may be null if no change).
        #_public
        (§ field #_"TransactionOutput" (§ name bestChangeOutput))
        ;; List of output values adjusted downwards when recipients pay fees (may be null if no adjustment needed).
        #_public
        (§ field #_"List<Coin>" (§ name updatedOutputValues))
    )

    #_private
    (§ method- #_"FeeCalculation" (§ fn calculateFee) [#_"SendRequest" (§ name ßreq), #_"Coin" (§ name ßvalue), #_"List<TransactionInput>" (§ name ßoriginalInputs), #_"boolean" (§ name ßneedAtLeastReferenceFee), #_"List<TransactionOutput>" (§ name ßcandidates)])
        (§ throws #_"InsufficientMoneyException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ let #_"FeeCalculation" (§ name ßresult))
        (§ block
            (§ let #_"Coin" (§ name ßfee) Coin/ZERO)
            (§ block
                (§ while true)
                (§ block
                    (§ ass (§ name ßresult) (§ new #_"FeeCalculation"))
                    (§ let #_"Transaction" (§ name ßtx) (§ new #_"Transaction" params))
                    (§ block
                        (addSuppliedInputs ßtx, (.. ßreq ßtx (getInputs)))

                        (§ let #_"Coin" (§ name ßvalueNeeded) ßvalue)
                        (§ block
                            (§ if (not (.. ßreq recipientsPayFees)))
                            (§ block
                                (§ ass (§ name ßvalueNeeded) (.. ßvalueNeeded (add ßfee)))
                            )
                            (§ if (.. ßreq recipientsPayFees))
                            (§ block
                                (§ ass (§ name (.. ßresult updatedOutputValues)) (§ new #_"ArrayList<Coin>"))
                            )

                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßreq ßtx (getOutputs) (size))) :for (§ recur (+ ßi 1)))
                            (§ block
                                (§ let #_"TransactionOutput" (§ name ßoutput) (§ new #_"TransactionOutput" params, ßtx, (.. ßreq ßtx (getOutputs) (get ßi) (bitcoinSerialize)), 0))
                                (§ block
                                    (§ if (.. ßreq recipientsPayFees))
                                    (§ block
                                        ;; Subtract fee equally from each selected recipient.
                                        (.. ßoutput (setValue (.. ßoutput (getValue) (subtract (.. ßfee (divide (.. ßreq ßtx (getOutputs) (size))))))))
                                        ;; First receiver pays the remainder not divisible by output count.
                                        (§ if (== ßi 0))
                                        (§ block
                                            ;; Subtract fee equally from each selected recipient.
                                            (.. ßoutput (setValue (.. ßoutput (getValue) (subtract (§ ai (.. ßfee (divideAndRemainder (.. ßreq ßtx (getOutputs) (size)))) 1)))))
                                        )
                                        (.. ßresult updatedOutputValues (add (.. ßoutput (getValue))))
                                        (§ if (.. ßoutput (getMinNonDustValue) (isGreaterThan (.. ßoutput (getValue)))))
                                        (§ block
                                            (§ throw (§ new #_"CouldNotAdjustDownwards"))
                                        )
                                    )
                                    (.. ßtx (addOutput ßoutput))
                                )
                            )

                            (§ let #_"CoinSelector" (§ name ßselector) (or (.. ßreq coinSelector) coinSelector))
                            (§ block
                                ;; selector is allowed to modify candidates list.
                                (§ let #_"CoinSelection" (§ name ßselection) (.. ßselector (select ßvalueNeeded, (§ new #_"LinkedList<>" ßcandidates))))
                                (§ block
                                    (§ ass (§ name (.. ßresult bestCoinSelection)) ßselection)
                                    ;; Can we afford this?
                                    (§ if (< (.. ßselection valueGathered (compareTo ßvalueNeeded)) 0))
                                    (§ block
                                        (§ let #_"Coin" (§ name ßvalueMissing) (.. ßvalueNeeded (subtract (.. ßselection valueGathered))))
                                        (§ block
                                            (§ throw (§ new #_"InsufficientMoneyException" ßvalueMissing))
                                        )
                                    )

                                    (§ let #_"Coin" (§ name ßchange) (.. ßselection valueGathered (subtract ßvalueNeeded)))
                                    (§ block
                                        (§ if (.. ßchange (isGreaterThan Coin/ZERO)))
                                        (§ block
                                            ;; The value of the inputs is greater than what we want to send.  Just like in real life then,
                                            ;; we need to take back some coins ... this is called "change".  Add another output that sends the change
                                            ;; back to us.  The address comes either from the request or currentChangeAddress() as a default.
                                            (§ let #_"Address" (§ name ßchangeAddress) (.. ßreq changeAddress))
                                            (§ block
                                                (§ if (nil? ßchangeAddress))
                                                (§ block
                                                    (§ ass (§ name ßchangeAddress) (currentChangeAddress))
                                                )

                                                (§ let #_"TransactionOutput" (§ name ßchangeOutput) (§ new #_"TransactionOutput" params, ßtx, ßchange, ßchangeAddress))
                                                (§ block
                                                    (§ if (and (.. ßreq recipientsPayFees) (.. ßchangeOutput (isDust))))
                                                    (§ block
                                                        ;; We do not move dust-change to fees, because the sender would end up paying more than requested.
                                                        ;; This would be against the purpose of the all-inclusive feature.
                                                        ;; So instead we raise the change and deduct from the first recipient.
                                                        (§ let #_"Coin" (§ name ßmissingToNotBeDust) (.. ßchangeOutput (getMinNonDustValue) (subtract (.. ßchangeOutput (getValue)))))
                                                        (§ block
                                                            (.. ßchangeOutput (setValue (.. ßchangeOutput (getValue) (add ßmissingToNotBeDust))))
                                                            (§ let #_"TransactionOutput" (§ name ßfirstOutput) (.. ßtx (getOutputs) (get 0)))
                                                            (§ block
                                                                (.. ßfirstOutput (setValue (.. ßfirstOutput (getValue) (subtract ßmissingToNotBeDust))))
                                                                (.. ßresult updatedOutputValues (set 0, (.. ßfirstOutput (getValue))))
                                                                (§ if (.. ßfirstOutput (isDust)))
                                                                (§ block
                                                                    (§ throw (§ new #_"CouldNotAdjustDownwards"))
                                                                )
                                                            )
                                                        )
                                                    )

                                                    (§ if (.. ßchangeOutput (isDust)))
                                                    (§ block
                                                        ;; Never create dust outputs; if we would, just add the dust to the fee.
                                                        ;; Oscar comment: This seems like a way to make the condition below "if (!fee.isLessThan(feeNeeded))" to become true.
                                                        ;; This is a non-easy to understand way to do that.
                                                        ;; Maybe there are other effects I am missing.
                                                        (§ ass (§ name ßfee) (.. ßfee (add (.. ßchangeOutput (getValue)))))
                                                    )
                                                    (§ else )
                                                    (§ block
                                                        (.. ßtx (addOutput ßchangeOutput))
                                                        (§ ass (§ name (.. ßresult bestChangeOutput)) ßchangeOutput)
                                                    )
                                                )
                                            )
                                        )

                                        (§ for (§ var #_"TransactionOutput" (§ name ßselectedOutput)) :for (.. ßselection gathered))
                                        (§ block
                                            (§ let #_"TransactionInput" (§ name ßinput) (.. ßtx (addInput ßselectedOutput)))
                                            (§ block
                                                ;; If the scriptBytes don't default to none, our size calculations will be thrown off.
                                                (Preconditions/checkState (== (.. ßinput (getScriptBytes) length) 0))
                                            )
                                        )

                                        (§ let #_"int" (§ name ßsize) (.. ßtx (unsafeBitcoinSerialize) length))
                                        (§ block
                                            (§ ass (§ name ßsize) (+ ßsize (estimateBytesForSigning ßselection)))

                                            (§ let #_"Coin" (§ name ßfeePerKb) (.. ßreq feePerKb))
                                            (§ block
                                                (§ if (and ßneedAtLeastReferenceFee (< (.. ßfeePerKb (compareTo Transaction/REFERENCE_DEFAULT_MIN_TX_FEE)) 0)))
                                                (§ block
                                                    (§ ass (§ name ßfeePerKb) Transaction/REFERENCE_DEFAULT_MIN_TX_FEE)
                                                )

                                                (§ let #_"Coin" (§ name ßfeeNeeded) (.. ßfeePerKb (multiply ßsize) (divide 1000)))
                                                (§ block

                                                    (§ if (not (.. ßfee (isLessThan ßfeeNeeded))))
                                                    (§ block
                                                        ;; Done, enough fee included.
                                                        (§ break )
                                                    )

                                                    ;; Include more fee and try again.
                                                    (§ ass (§ name ßfee) ßfeeNeeded)
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
                ßresult
            )
        )
    )

    #_private
    (§ method- #_"void" (§ fn addSuppliedInputs) [#_"Transaction" (§ name ßtx), #_"List<TransactionInput>" (§ name ßoriginalInputs)])
    (§ block
        (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for ßoriginalInputs)
        (§ block
            (.. ßtx (addInput (§ new #_"TransactionInput" params, ßtx, (.. ßinput (bitcoinSerialize)))))
        )
        nil
    )

    #_private
    (§ method- #_"int" (§ fn estimateBytesForSigning) [#_"CoinSelection" (§ name ßselection)])
    (§ block
        (§ let #_"int" (§ name ßsize) 0)
        (§ block
            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßselection gathered))
            (§ block
                (§ try )
                (§ block
                    (§ let #_"Script" (§ name ßscript) (.. ßoutput (getScriptPubKey)))
                    (§ block
                        (§ let #_"ECKey" (§ name ßkey) nil)
                        (§ block
                            (§ let #_"Script" (§ name ßredeemScript) nil)
                            (§ block
                                (§ if (.. ßscript (isSentToAddress)))
                                (§ block
                                    (§ ass (§ name ßkey) (findKeyFromPubHash (.. ßscript (getPubKeyHash))))
                                    (Preconditions/checkNotNull ßkey, "Coin ßselection includes unspendable outputs")
                                )
                                (§ elseif (.. ßscript (isPayToScriptHash)))
                                (§ block
                                    (§ ass (§ name ßredeemScript) (.. (findRedeemDataFromScriptHash (.. ßscript (getPubKeyHash))) ßredeemScript))
                                    (Preconditions/checkNotNull ßredeemScript, "Coin ßselection includes unspendable outputs")
                                )
                                (§ ass (§ name ßsize) (+ ßsize (.. ßscript (getNumberOfBytesRequiredToSpend ßkey, ßredeemScript))))
                            )
                        )
                    )
                )
                (§ catch #_"ScriptException" (§ name ße))
                (§ block
                    ;; If this happens it means an output script in a wallet tx could not be understood.  That
                    ;; should never happen, if it does it means the wallet has got into an inconsistent state.
                    (§ throw (§ new #_"IllegalStateException" ße))
                )
            )
            ßsize
        )
    )

    ;; Wallet maintenance transactions.  These transactions may not be directly connected to a payment the user is
    ;; making.  They may be instead key rotation transactions for when old keys are suspected to be compromised,
    ;; de/re-fragmentation transactions for when our output sizes are inappropriate or suboptimal, privacy transactions
    ;; and so on.  Because these transactions may require user intervention in some way (e.g. entering their password)
    ;; the wallet application is expected to poll the Wallet class to get SendRequests.  Ideally security systems like
    ;; hardware wallets or risk analysis providers are programmed to auto-approve transactions that send from our own
    ;; keys back to our own keys.

    ;;;
     ; <p>Specifies that the given {@link TransactionBroadcaster}, typically a {@link PeerGroup}, should be used for
     ; sending transactions to the Bitcoin network by default.  Some sendCoins methods let you specify a broadcaster
     ; explicitly, in that case, they don't use this broadcaster.  If null is specified then the wallet won't attempt
     ; to broadcast transactions itself.</p>
     ;
     ; <p>You don't normally need to call this.  A {@link PeerGroup} will automatically set itself as the wallets
     ; broadcaster when you use {@link PeerGroup#addWallet(Wallet)}.  A wallet can use the broadcaster when you ask
     ; it to send money, but in future also at other times to implement various features that may require asynchronous
     ; re-organisation of the wallet contents on the block chain.  For instance, in future the wallet may choose to
     ; optimise itself to reduce fees or improve privacy.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn setTransactionBroadcaster) [#_nilable #_"org.bitcoinj.core.TransactionBroadcaster" (§ name ßbroadcaster)])
    (§ block
        (§ let #_"Transaction[]" (§ name ßtoBroadcast) (§ new #_"Transaction[]"))
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (§ if (== vTransactionBroadcaster ßbroadcaster))
                (§ block
                    (§ return nil)
                )
                (§ ass (§ name vTransactionBroadcaster) ßbroadcaster)
                (§ if (nil? ßbroadcaster))
                (§ block
                    (§ return nil)
                )
                (§ ass (§ name ßtoBroadcast) (.. pending (values) (toArray ßtoBroadcast)))
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            ;; Now use it to upload any pending transactions we have that are marked as not being seen by any peers yet.
            ;; Don't hold the wallet lock whilst doing this, so if the broadcaster accesses the wallet at some point there
            ;; is no inversion.
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtoBroadcast)
            (§ block
                (§ let #_"ConfidenceType" (§ name ßconfidenceType) (.. ßtx (getConfidence) (getConfidenceType)))
                (§ block
                    (Preconditions/checkState (or (== ßconfidenceType ConfidenceType/PENDING) (== ßconfidenceType ConfidenceType/IN_CONFLICT)), "Expected PENDING or IN_CONFLICT, was %s.", ßconfidenceType)
                    ;; Re-broadcast even if it's marked as already seen for two reasons:
                    ;; 1. Old wallets may have transactions marked as broadcast by 1 peer when
                    ;;    in reality the network never saw it, due to bugs.
                    ;; 2. It can't really hurt.
                    (.. log (info "New ßbroadcaster so uploading waiting ßtx {}", (.. ßtx (getHash))))
                    (.. ßbroadcaster (broadcastTransaction ßtx))
                )
            )
            nil
        )
    )

    ;;;
     ; When a key rotation time is set, and money controlled by keys created before the given timestamp T will be
     ; automatically respent to any key that was created after T.  This can be used to recover from a situation where
     ; a set of keys is believed to be compromised.  Once the time is set transactions will be created and broadcast
     ; immediately.  New coins that come in after calling this method will be automatically respent immediately.
     ; The rotation time is persisted to the wallet. You can stop key rotation by calling this method again with zero
     ; as the argument.
     ;;
    #_public
    (§ method #_"void" (§ fn setKeyRotationTime) [#_"Date" (§ name ßtime)])
    (§ block
        (setKeyRotationTime (/ (.. ßtime (getTime)) 1000))
        nil
    )

    ;;;
     ; Returns the key rotation time, or null if unconfigured.
     ; See {@link #setKeyRotationTime(Date)} for a description of the field.
     ;;
    #_public
    #_nilable
    (§ method #_"Date" (§ fn getKeyRotationTime) [])
    (§ block
        (§ let #_"long" (§ name ßkeyRotationTimestamp) vKeyRotationTimestamp)
        (§ block
            (when (!= ßkeyRotationTimestamp 0) (§ new #_"Date" (* ßkeyRotationTimestamp 1000)))
        )
    )

    ;;;
     ; <p>When a key rotation time is set, any money controlled by keys created before the given timestamp T will be
     ; automatically respent to any key that was created after T.  This can be used to recover from a situation where
     ; a set of keys is believed to be compromised.  You can stop key rotation by calling this method again with zero
     ; as the argument.  Once set up, calling {@link #doMaintenance(org.spongycastle.crypto.params.KeyParameter, boolean)}
     ; will create and possibly send rotation transactions: but it won't be done automatically (because you might have
     ; to ask for the users password).</p>
     ;
     ; <p>The given time cannot be in the future.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn setKeyRotationTime) [#_"long" (§ name ßunixTimeSeconds)])
    (§ block
        (Preconditions/checkArgument (<= ßunixTimeSeconds (Utils/currentTimeSeconds)), "Given time (%s) cannot be in the future.", (Utils/dateTimeFormat (* ßunixTimeSeconds 1000)))
        (§ ass (§ name vKeyRotationTimestamp) ßunixTimeSeconds)
        (saveNow)
        nil
    )

    ;;; Returns whether the keys creation time is before the key rotation time, if one was set. ;;
    #_public
    (§ method #_"boolean" (§ fn isKeyRotating) [#_"ECKey" (§ name ßkey)])
    (§ block
        (§ let #_"long" (§ name ßtime) vKeyRotationTimestamp)
        (§ block
            (and (!= ßtime 0) (< (.. ßkey (getCreationTimeSeconds)) ßtime))
        )
    )

    ;;;
     ; A wallet app should call this from time to time in order to let the wallet craft and send transactions needed
     ; to re-organise coins internally.  A good time to call this would be after receiving coins for an unencrypted
     ; wallet, or after sending money for an encrypted wallet.  If you have an encrypted wallet and just want to know
     ; if some maintenance needs doing, call this method with andSend set to false and look at the returned list of
     ; transactions.  Maintenance might also include internal changes that involve some processing or work but
     ; which don't require making transactions - these will happen automatically unless the password is required
     ; in which case an exception will be thrown.
     ;
     ; @param aesKey The users password, if any.
     ; @param signAndSend If true, send the transactions via the tx broadcaster and return them, if false just return them.
     ; @return a list of transactions that the wallet just made/will make for internal maintenance.  Might be empty.
     ; @throws org.bitcoinj.wallet.DeterministicUpgradeRequiresPassword if key rotation requires the users password.
     ;;
    #_public
    (§ method #_"ListenableFuture<List<Transaction>>" (§ fn doMaintenance) [#_nilable #_"KeyParameter" (§ name ßaesKey), #_"boolean" (§ name ßsignAndSend)])
        (§ throws #_"DeterministicUpgradeRequiresPassword")
    (§ block
        (§ let #_"List<Transaction>" (§ name ßtxns))
        (§ block
            (.. lock (lock))
            (.. keyChainGroupLock (lock))
            (§ try )
            (§ block
                (§ ass (§ name ßtxns) (maybeRotateKeys ßaesKey, ßsignAndSend))
                (§ if (not ßsignAndSend))
                (§ block
                    (§ return (Futures/immediateFuture ßtxns))
                )
            )
            (§ finally )
            (§ block
                (.. keyChainGroupLock (unlock))
                (.. lock (unlock))
            )
            (Preconditions/checkState (not (.. lock (isHeldByCurrentThread))))
            (§ let #_"ArrayList<ListenableFuture<Transaction>>" (§ name ßfutures) (§ new #_"ArrayList<>" (.. ßtxns (size))))
            (§ block
                (§ let #_"TransactionBroadcaster" (§ name ßbroadcaster) vTransactionBroadcaster)
                (§ block
                    (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtxns)
                    (§ block
                        (§ try )
                        (§ block
                            (§ let #_"ListenableFuture<Transaction>" (§ name ßfuture) (.. ßbroadcaster (broadcastTransaction ßtx) (future)))
                            (§ block
                                (.. ßfutures (add ßfuture))
                                (Futures/addCallback ßfuture, (§ new #_"FutureCallback<Transaction>")
                                (§ anon
                                    #_override
                                    #_public
                                    (§ method #_"void" (§ fn onSuccess) [#_"Transaction" (§ name ßtransaction)])
                                    (§ block
                                        (.. log (info "Successfully broadcast key rotation ßtx: {}", ßtransaction))
                                        nil
                                    )

                                    #_override
                                    #_public
                                    (§ method #_"void" (§ fn onFailure) [#_"Throwable" (§ name ßthrowable)])
                                    (§ block
                                        (.. log (error "Failed to broadcast key rotation ßtx", ßthrowable))
                                        nil
                                    )
                                ))
                            )
                        )
                        (§ catch #_"Exception" (§ name ße))
                        (§ block
                            (.. log (error "Failed to broadcast rekey ßtx", ße))
                        )
                    )
                    (Futures/allAsList ßfutures)
                )
            )
        )
    )

    ;; Checks to see if any coins are controlled by rotating keys and if so, spends them.
    #_guarded-by(§ opt "keyChainGroupLock")
    #_private
    (§ method- #_"List<Transaction>" (§ fn maybeRotateKeys) [#_nilable #_"KeyParameter" (§ name ßaesKey), #_"boolean" (§ name ßsign)])
        (§ throws #_"DeterministicUpgradeRequiresPassword")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (Preconditions/checkState (.. keyChainGroupLock (isHeldByCurrentThread)))

        (§ let #_"List<Transaction>" (§ name ßresults) (Lists/newLinkedList))
        (§ block
            ;; TODO: Handle chain replays here.
            (§ let #_"long" (§ name ßkeyRotationTimestamp) vKeyRotationTimestamp)
            (§ block
                (§ if (== ßkeyRotationTimestamp 0))
                (§ block
                    (§ return ßresults) ;; Nothing to do.
                )

                ;; We might have to create a new HD hierarchy if the previous ones are now rotating.
                (§ let #_"boolean" (§ name ßallChainsRotating) true)
                (§ block
                    (§ for (§ var #_"DeterministicKeyChain" (§ name ßchain)) :for (.. keyChainGroup (getDeterministicKeyChains)))
                    (§ block
                        (§ if (<= ßkeyRotationTimestamp (.. ßchain (getEarliestKeyCreationTime))))
                        (§ block
                            (§ ass (§ name ßallChainsRotating) false)
                            (§ break )
                        )
                    )
                    (§ if ßallChainsRotating)
                    (§ block
                        (§ try )
                        (§ block
                            (§ if (.. keyChainGroup (getImportedKeys) (isEmpty)))
                            (§ block
                                (.. log (info "All HD chains are currently rotating and we have no random keys, creating fresh HD chain ..."))
                                (.. keyChainGroup (createAndActivateNewHDChain))
                            )
                            (§ else )
                            (§ block
                                (.. log (info "All HD chains are currently rotating, attempting to create a new one from the next oldest non-rotating key material ..."))
                                (.. keyChainGroup (upgradeToDeterministic ßkeyRotationTimestamp, ßaesKey))
                                (.. log (info " ... upgraded to HD again, based on next best oldest key."))
                            )
                        )
                        (§ catch #_"AllRandomKeysRotating" (§ name _))
                        (§ block
                            (.. log (info " ... no non-rotating random keys available, generating entirely new HD tree: backup required after this."))
                            (.. keyChainGroup (createAndActivateNewHDChain))
                        )
                        (saveNow)
                    )

                    ;; Because transactions are size limited, we might not be able to re-key the entire wallet in one go.  So loop
                    ;; around here until we no longer produce transactions with the max number of inputs.  That means we're fully
                    ;; done, at least for now (we may still get more transactions later and this method will be reinvoked).
                    (§ let #_"Transaction" (§ name ßtx))
                    (§ block
                        (§ do )
                        (§ block
                            (§ ass (§ name ßtx) (rekeyOneBatch ßkeyRotationTimestamp, ßaesKey, ßresults, ßsign))
                            (§ if (some? ßtx))
                            (§ block
                                (.. ßresults (add ßtx))
                            )
                        )
                        (§ again (and (some? ßtx) (== (.. ßtx (getInputs) (size)) KeyTimeCoinSelector/MAX_SIMULTANEOUS_INPUTS)))

                        ßresults
                    )
                )
            )
        )
    )

    #_nilable
    #_private
    (§ method- #_"Transaction" (§ fn rekeyOneBatch) [#_"long" (§ name ßtimeSecs), #_nilable #_"KeyParameter" (§ name ßaesKey), #_"List<Transaction>" (§ name ßothers), #_"boolean" (§ name ßsign)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; Build the transaction using some custom logic for our special needs.  Last parameter to
            ;; KeyTimeCoinSelector is whether to ignore pending transactions or not.
            ;;
            ;; We ignore pending outputs because trying to rotate these is basically racing an attacker, and
            ;; we're quite likely to lose and create stuck double spends.  Also, some users who have 0.9 wallets
            ;; have already got stuck double spends in their wallet due to the Bloom-filtering block reordering
            ;; bug that was fixed in 0.10, thus, making a re-key transaction depend on those would cause it to
            ;; never confirm at all.
            (§ let #_"CoinSelector" (§ name ßkeyTimeSelector) (§ new #_"KeyTimeCoinSelector" this, ßtimeSecs, true))
            (§ block
                (§ let #_"FilteringCoinSelector" (§ name ßselector) (§ new #_"FilteringCoinSelector" ßkeyTimeSelector))
                (§ block
                    (§ for (§ var #_"Transaction" (§ name ßother)) :for ßothers)
                    (§ block
                        (.. ßselector (excludeOutputsSpentBy ßother))
                    )
                    ;; TODO: Make this use the standard SendRequest.
                    (§ let #_"CoinSelection" (§ name ßtoMove) (.. ßselector (select Coin/ZERO, (calculateAllSpendCandidates))))
                    (§ block
                        (§ if (.. ßtoMove valueGathered (equals Coin/ZERO)))
                        (§ block
                            (§ return nil) ;; Nothing to do.
                        )

                        (maybeUpgradeToHD ßaesKey)
                        (§ let #_"Transaction" (§ name ßrekeyTx) (§ new #_"Transaction" params))
                        (§ block
                            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtoMove gathered))
                            (§ block
                                (.. ßrekeyTx (addInput ßoutput))
                            )
                            ;; When not signing, don't waste addresses.
                            (.. ßrekeyTx (addOutput (.. ßtoMove valueGathered), (if ßsign (freshReceiveAddress) (currentReceiveAddress))))
                            (§ if (not (adjustOutputDownwardsForFee ßrekeyTx, ßtoMove, Transaction/DEFAULT_TX_FEE, true)))
                            (§ block
                                (.. log (error "Failed to adjust rekey tx for fees."))
                                (§ return nil)
                            )

                            (.. ßrekeyTx (getConfidence) (setSource TransactionConfidence/Source/SELF))
                            (.. ßrekeyTx (setPurpose Transaction/Purpose/KEY_ROTATION))
                            (§ let #_"SendRequest" (§ name ßreq) (SendRequest/forTx ßrekeyTx))
                            (§ block
                                (§ ass (§ name (.. ßreq ßaesKey)) ßaesKey)
                                (§ if ßsign)
                                (§ block
                                    (signTransaction ßreq)
                                )
                                ;; KeyTimeCoinSelector should never select enough inputs to push us oversize.
                                (Preconditions/checkState (< (.. ßrekeyTx (unsafeBitcoinSerialize) length) Transaction/MAX_STANDARD_TX_SIZE))
                                (§ return ßrekeyTx)
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"VerificationException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )
)

#_(ns org.bitcoinj.wallet #_"WalletFiles"
    (:import [java.io *]
             [java.util Date]
             [java.util.concurrent *]
             [java.util.concurrent.atomic *])
    (:import [com.google.common.base Preconditions Stopwatch]
             [org.slf4j *])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.utils *]))

;;;
 ; A class that handles atomic and optionally delayed writing of the wallet file to disk.  In future: backups too.
 ; It can be useful to delay writing of a wallet file to disk on slow devices where disk and serialization overhead
 ; can come to dominate the chain processing speed, i.e. on Android phones.  By coalescing writes and doing
 ; serialization and disk IO on a background thread performance can be improved.
 ;;
#_public
(§ class #_"WalletFiles"
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"WalletFiles")))

    #_private
    (§ field- #_"Wallet" (§ name wallet))
    #_private
    (§ field- #_"ScheduledThreadPoolExecutor" (§ name executor))
    #_private
    (§ field- #_"File" (§ name file))
    #_private
    (§ field- #_"AtomicBoolean" (§ name savePending))
    #_private
    (§ field- #_"long" (§ name delay))
    #_private
    (§ field- #_"TimeUnit" (§ name delayTimeUnit))
    #_private
    (§ field- #_"Callable<Void>" (§ name saver))

    #_private
    #_volatile
    (§ field- #_"Listener" (§ name vListener))

    ;;;
     ; Implementors can do pre/post treatment of the wallet file.  Useful for adjusting permissions and other things.
     ;;
    #_public
    (§ interface #_"Listener"
        ;;;
         ; Called on the auto-save thread when a new temporary file is created but before the wallet data is saved
         ; to it.  If you want to do something here like adjust permissions, go ahead and do so.
         ;;
        (§ method #_"void" (§ fn onBeforeAutoSave) [#_"File" __tempFile])

        ;;;
         ; Called on the auto-save thread after the newly created temporary file has been filled with data and renamed.
         ;;
        (§ method #_"void" (§ fn onAfterAutoSave) [#_"File" __newlySavedFile])
    )

    ;;;
     ; Initialize atomic and optionally delayed writing of the wallet file to disk.  Note the initial wallet state isn't
     ; saved automatically.  The {@link Wallet} calls {@link #saveNow()} or {@link #saveLater()} as wallet state changes,
     ; depending on the urgency of the changes.
     ;;
    #_public
    (§ constructor #_"WalletFiles" [#_"Wallet" (§ name ßwallet), #_"File" (§ name ßfile), #_"long" (§ name ßdelay), #_"TimeUnit" (§ name ßdelayTimeUnit)])
    (§ block
        ;; An executor that starts up threads when needed and shuts them down later.
        (§ ass (§ name (.. this executor)) (§ new #_"ScheduledThreadPoolExecutor" 1, (§ new #_"ContextPropagatingThreadFactory" "Wallet autosave thread", Thread/MIN_PRIORITY)))
        (.. this executor (setKeepAliveTime 5, TimeUnit/SECONDS))
        (.. this executor (allowCoreThreadTimeOut true))
        (.. this executor (setExecuteExistingDelayedTasksAfterShutdownPolicy false))
        (§ ass (§ name (.. this ßwallet)) (Preconditions/checkNotNull ßwallet))
        ;; File must only be accessed from the auto-save executor from now on, to avoid simultaneous access.
        (§ ass (§ name (.. this ßfile)) (Preconditions/checkNotNull ßfile))
        (§ ass (§ name (.. this savePending)) (§ new #_"AtomicBoolean"))
        (§ ass (§ name (.. this ßdelay)) ßdelay)
        (§ ass (§ name (.. this ßdelayTimeUnit)) (Preconditions/checkNotNull ßdelayTimeUnit))

        (§ ass (§ name (.. this saver)) (§ new #_"Callable<Void>"
        (§ anon
            #_override
            #_public
            (§ method #_"Void" (§ fn call) [])
                (§ throws #_"Exception")
            (§ block
                ;; Runs in an auto save thread.
                (§ if (not (.. savePending (getAndSet false))))
                (§ block
                    ;; Some other scheduled request already beat us to it.
                    (§ return nil)
                )
                (§ let #_"Date" (§ name ßlastBlockSeenTime) (.. ßwallet (getLastBlockSeenTime)))
                (§ block
                    (.. log (info "Background saving ßwallet; last seen block is height {}, date {}, hash {}", (.. ßwallet (getLastBlockSeenHeight)), (if (some? ßlastBlockSeenTime) (Utils/dateTimeFormat ßlastBlockSeenTime) "unknown"), (.. ßwallet (getLastBlockSeenHash))))
                    (saveNowInternal)
                    nil
                )
            )
        )))
        this
    )

    ;;;
     ; The given listener will be called on the autosave thread before and after the wallet is saved to disk.
     ;;
    #_public
    (§ method #_"void" (§ fn setListener) [#_non-nil #_"Listener" (§ name ßlistener)])
    (§ block
        (§ ass (§ name (.. this vListener)) (Preconditions/checkNotNull ßlistener))
        nil
    )

    ;;; Actually write the wallet file to disk, using an atomic rename when possible.  Runs on the current thread. ;;
    #_public
    (§ method #_"void" (§ fn saveNow) [])
        (§ throws #_"IOException")
    (§ block
        ;; Can be called by any thread. However the wallet is locked whilst saving, so we can have two saves
        ;; in flight, but they will serialize (using different temp files).
        (§ let #_"Date" (§ name ßlastBlockSeenTime) (.. wallet (getLastBlockSeenTime)))
        (§ block
            (.. log (info "Saving wallet; last seen block is height {}, date {}, hash {}", (.. wallet (getLastBlockSeenHeight)), (if (some? ßlastBlockSeenTime) (Utils/dateTimeFormat ßlastBlockSeenTime) "unknown"), (.. wallet (getLastBlockSeenHash))))
            (saveNowInternal)
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn saveNowInternal) [])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
        (§ block
            (§ let #_"File" (§ name ßdirectory) (.. file (getAbsoluteFile) (getParentFile)))
            (§ block
                (§ let #_"File" (§ name ßtemp) (File/createTempFile "wallet", nil, ßdirectory))
                (§ block
                    (§ let #_"Listener" (§ name ßlistener) vListener)
                    (§ block
                        (§ if (some? ßlistener))
                        (§ block
                            (.. ßlistener (onBeforeAutoSave ßtemp))
                        )
                        (.. wallet (saveToFile ßtemp, file))
                        (§ if (some? ßlistener))
                        (§ block
                            (.. ßlistener (onAfterAutoSave file))
                        )
                        (.. ßwatch (stop))
                        (.. log (info "Save completed in {}", ßwatch))
                        nil
                    )
                )
            )
        )
    )

    ;;; Queues up a save in the background.  Useful for not very important wallet changes. ;;
    #_public
    (§ method #_"void" (§ fn saveLater) [])
    (§ block
        (§ if (.. savePending (getAndSet true)))
        (§ block
            (§ return nil) ;; Already pending.
        )

        (.. executor (schedule saver, delay, delayTimeUnit))
        nil
    )

    ;;; Shut down auto-saving. ;;
    #_public
    (§ method #_"void" (§ fn shutdownAndWait) [])
    (§ block
        (.. executor (shutdown))
        (§ try )
        (§ block
            (.. executor (awaitTermination Long/MAX_VALUE, TimeUnit/DAYS)) ;; forever
        )
        (§ catch #_"InterruptedException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße))
        )
        nil
    )
)

#_(ns org.bitcoinj.wallet #_"WalletProtobufSerializer"
    (:import [java.io IOException InputStream OutputStream]
             [java.math BigInteger]
             [java.net InetAddress UnknownHostException]
             [java.util *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists]
             [com.google.protobuf ByteString CodedInputStream CodedOutputStream TextFormat WireFormat]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.core.TransactionConfidence ConfidenceType]
             [org.bitcoinj.crypto KeyCrypter KeyCrypterScrypt]
             [org.bitcoinj.script Script]
             [org.bitcoinj.signers LocalTransactionSigner TransactionSigner]
             [org.bitcoinj.utils ExchangeRate Fiat]
             [org.bitcoinj.wallet.Protos.Wallet EncryptionType]))

;;;
 ; Serialize and de-serialize a wallet to a byte stream containing a
 ; <a href="https://developers.google.com/protocol-buffers/docs/overview">protocol buffer</a>.  Protocol buffers are
 ; a data interchange format developed by Google with an efficient binary representation, a type safe specification
 ; language and compilers that generate code to work with those data structures for many languages.  Protocol buffers
 ; can have their format evolved over time: conceptually they represent data using (tag, length, value) tuples.  The
 ; format is defined by the <tt>wallet.proto</tt> file in the bitcoinj source distribution.
 ;
 ; This class is used through its static methods.  The most common operations are writeWallet and readWallet, which do
 ; the obvious operations on Output/InputStreams.  You can use a {@link java.io.ByteArrayInputStream} and equivalent
 ; {@link java.io.ByteArrayOutputStream} if you'd like byte arrays instead.  The protocol buffer can also be manipulated
 ; in its object form if you'd like to modify the flattened data structure before serialization to binary.
 ;
 ; You can extend the wallet format with additional fields specific to your application if you want, but make sure
 ; to either put the extra data in the provided extension areas, or select tag numbers that are unlikely to be used
 ; by anyone else.
 ;
 ; @author Miron Cuperman
 ; @author Andreas Schildbach
 ;;
#_public
(§ class #_"WalletProtobufSerializer"
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"WalletProtobufSerializer")))

    ;;; Current version used for serializing wallets.  A version higher than this is considered from the future. ;;
    #_public
    #_static
    (§ def #_"int" (§ name CURRENT_WALLET_VERSION) (.. (Protos/Wallet/getDefaultInstance) (getVersion)))
    ;; 512 MB
    #_private
    #_static
    (§ def- #_"int" (§ name WALLET_SIZE_LIMIT) (* 512 1024 1024))
    ;; Used for de-serialization.
    #_protected
    (§ field #_"Map<ByteString, Transaction>" (§ name txMap))

    #_private
    (§ field- #_"int" (§ name walletWriteBufferSize) CodedOutputStream/DEFAULT_BUFFER_SIZE)

    #_public
    (§ interface #_"WalletFactory"
        (§ method #_"Wallet" (§ fn create) [#_"NetworkParameters" __params, #_"KeyChainGroup" __keyChainGroup])
    )

    #_private
    (§ field- #_"WalletFactory" (§ name factory))
    #_private
    (§ field- #_"KeyChainFactory" (§ name keyChainFactory))

    #_public
    (§ constructor #_"WalletProtobufSerializer" [])
    (§ block
        (§ this (§ new #_"WalletFactory"
        (§ anon
            #_override
            #_public
            (§ method #_"Wallet" (§ fn create) [#_"NetworkParameters" (§ name ßparams), #_"KeyChainGroup" (§ name ßkeyChainGroup)])
            (§ block
                (§ new #_"Wallet" ßparams, ßkeyChainGroup)
            )
        )))
        this
    )

    #_public
    (§ constructor #_"WalletProtobufSerializer" [#_"WalletFactory" (§ name ßfactory)])
    (§ block
        (§ ass (§ name txMap) (§ new #_"HashMap<>"))
        (§ ass (§ name (.. this ßfactory)) ßfactory)
        (§ ass (§ name (.. this keyChainFactory)) (§ new #_"DefaultKeyChainFactory"))
        this
    )

    #_public
    (§ method #_"void" (§ fn setKeyChainFactory) [#_"KeyChainFactory" (§ name ßkeyChainFactory)])
    (§ block
        (§ ass (§ name (.. this ßkeyChainFactory)) ßkeyChainFactory)
        nil
    )

    ;;;
     ; Change buffer size for writing wallet to output stream.
     ; Default is {@link com.google.protobuf.CodedOutputStream.DEFAULT_BUFFER_SIZE}.
     ; @param walletWriteBufferSize Buffer size in bytes.
     ;;
    #_public
    (§ method #_"void" (§ fn setWalletWriteBufferSize) [#_"int" (§ name ßwalletWriteBufferSize)])
    (§ block
        (§ ass (§ name (.. this ßwalletWriteBufferSize)) ßwalletWriteBufferSize)
        nil
    )

    ;;;
     ; Formats the given wallet (transactions and keys) to the given output stream in protocol buffer format.
     ;
     ; Equivalent to <tt>walletToProto(wallet).writeTo(output)</tt>.
     ;;
    #_public
    (§ method #_"void" (§ fn writeWallet) [#_"Wallet" (§ name ßwallet), #_"OutputStream" (§ name ßoutput)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"Protos.Wallet" (§ name ßwalletProto) (walletToProto ßwallet))
        (§ block
            (§ let #_"CodedOutputStream" (§ name ßcodedOutput) (CodedOutputStream/newInstance ßoutput, (.. this walletWriteBufferSize)))
            (§ block
                (.. ßwalletProto (writeTo ßcodedOutput))
                (.. ßcodedOutput (flush))
                nil
            )
        )
    )

    ;;;
     ; Returns the given wallet formatted as text.  The text format is that used by protocol buffers and although
     ; it can also be parsed using {@link TextFormat#merge(CharSequence, com.google.protobuf.Message.Builder)},
     ; it is designed more for debugging than storage.  It is not well specified and wallets are largely binary data
     ; structures anyway, consisting as they do of keys (large random numbers) and {@link Transaction}s which also
     ; mostly contain keys and hashes.
     ;;
    #_public
    (§ method #_"String" (§ fn walletToText) [#_"Wallet" (§ name ßwallet)])
    (§ block
        (§ let #_"Protos.Wallet" (§ name ßwalletProto) (walletToProto ßwallet))
        (§ block
            (TextFormat/printToString ßwalletProto)
        )
    )

    ;;;
     ; Converts the given wallet to the object representation of the protocol buffers.  This can be modified, or
     ; additional data fields set, before serialization takes place.
     ;;
    #_public
    (§ method #_"Protos.Wallet" (§ fn walletToProto) [#_"Wallet" (§ name ßwallet)])
    (§ block
        (§ let #_"Protos.Wallet.Builder" (§ name ßwalletBuilder) (.. Protos/Wallet (newBuilder)))
        (§ block
            (.. ßwalletBuilder (setNetworkIdentifier (.. ßwallet (getNetworkParameters) (getId))))
            (§ if (!= (.. ßwallet (getDescription)) nil))
            (§ block
                (.. ßwalletBuilder (setDescription (.. ßwallet (getDescription))))
            )

            (§ for (§ var #_"WalletTransaction" (§ name ßwtx)) :for (.. ßwallet (getWalletTransactions)))
            (§ block
                (§ let #_"Protos.Transaction" (§ name ßtxProto) (makeTxProto ßwtx))
                (§ block
                    (.. ßwalletBuilder (addTransaction ßtxProto))
                )
            )

            (.. ßwalletBuilder (addAllKey (.. ßwallet (serializeKeyChainGroupToProtobuf))))

            ;; Populate the lastSeenBlockHash field.
            (§ let #_"Sha256Hash" (§ name ßlastSeenBlockHash) (.. ßwallet (getLastBlockSeenHash)))
            (§ block
                (§ if (some? ßlastSeenBlockHash))
                (§ block
                    (.. ßwalletBuilder (setLastSeenBlockHash (hashToByteString ßlastSeenBlockHash)))
                    (.. ßwalletBuilder (setLastSeenBlockHeight (.. ßwallet (getLastBlockSeenHeight))))
                )
                (§ if (< 0 (.. ßwallet (getLastBlockSeenTimeSecs))))
                (§ block
                    (.. ßwalletBuilder (setLastSeenBlockTimeSecs (.. ßwallet (getLastBlockSeenTimeSecs))))
                )

                ;; Populate the scrypt parameters.
                (§ let #_"KeyCrypter" (§ name ßkeyCrypter) (.. ßwallet (getKeyCrypter)))
                (§ block
                    (§ if (nil? ßkeyCrypter))
                    (§ block
                        ;; The wallet is unencrypted.
                        (.. ßwalletBuilder (setEncryptionType EncryptionType/UNENCRYPTED))
                    )
                    (§ else )
                    (§ block
                        ;; The wallet is encrypted.
                        (.. ßwalletBuilder (setEncryptionType (.. ßkeyCrypter (getUnderstoodEncryptionType))))
                        (§ if (§ insta ßkeyCrypter #_"KeyCrypterScrypt"))
                        (§ block
                            (§ let #_"KeyCrypterScrypt" (§ name ßkeyCrypterScrypt) (§ cast #_"KeyCrypterScrypt" ßkeyCrypter))
                            (§ block
                                (.. ßwalletBuilder (setEncryptionParameters (.. ßkeyCrypterScrypt (getScryptParameters))))
                            )
                        )
                        (§ else )
                        (§ block
                            ;; Some other form of encryption has been specified that we do not know how to persist.
                            (§ throw (§ new #_"RuntimeException" (str "The wallet has encryption of type '" (.. ßkeyCrypter (getUnderstoodEncryptionType)) "' but this WalletProtobufSerializer does not know how to persist this.")))
                        )
                    )

                    (§ if (!= (.. ßwallet (getKeyRotationTime)) nil))
                    (§ block
                        (§ let #_"long" (§ name ßtimeSecs) (/ (.. ßwallet (getKeyRotationTime) (getTime)) 1000))
                        (§ block
                            (.. ßwalletBuilder (setKeyRotationTime ßtimeSecs))
                        )
                    )

                    (§ for (§ var #_"TransactionSigner" (§ name ßsigner)) :for (.. ßwallet (getTransactionSigners)))
                    (§ block
                        ;; Do not serialize LocalTransactionSigner as it's being added implicitly.
                        (§ if (§ insta ßsigner #_"LocalTransactionSigner"))
                        (§ block
                            (§ continue )
                        )

                        (§ let #_"Protos.TransactionSigner.Builder" (§ name ßprotoSigner) (.. Protos/TransactionSigner (newBuilder)))
                        (§ block
                            (.. ßprotoSigner (setClassName (.. ßsigner (getClass) (getName))))
                            (.. ßprotoSigner (setData (ByteString/copyFrom (.. ßsigner (serialize)))))
                            (.. ßwalletBuilder (addTransactionSigners ßprotoSigner))
                        )
                    )

                    ;; Populate the wallet version.
                    (.. ßwalletBuilder (setVersion (.. ßwallet (getVersion))))

                    (.. ßwalletBuilder (build))
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"Protos.Transaction" (§ fn makeTxProto) [#_"WalletTransaction" (§ name ßwtx)])
    (§ block
        (§ let #_"Transaction" (§ name ßtx) (.. ßwtx (getTransaction)))
        (§ block
            (§ let #_"Protos.Transaction.Builder" (§ name ßtxBuilder) (.. Protos/Transaction (newBuilder)))
            (§ block

                (.. ßtxBuilder (setPool (getProtoPool ßwtx)) (setHash (hashToByteString (.. ßtx (getHash)))) (setVersion (§ cast #_"int" (.. ßtx (getVersion)))))

                (§ if (!= (.. ßtx (getUpdateTime)) nil))
                (§ block
                    (.. ßtxBuilder (setUpdatedAt (.. ßtx (getUpdateTime) (getTime))))
                )

                (§ if (< 0 (.. ßtx (getLockTime))))
                (§ block
                    (.. ßtxBuilder (setLockTime (§ cast #_"int" (.. ßtx (getLockTime)))))
                )

                ;; Handle inputs.
                (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
                (§ block
                    (§ let #_"Protos.TransactionInput.Builder" (§ name ßinputBuilder) (.. (Protos/TransactionInput/newBuilder) (setScriptBytes (ByteString/copyFrom (.. ßinput (getScriptBytes)))) (setTransactionOutPointHash (hashToByteString (.. ßinput (getOutpoint) (getHash)))) (setTransactionOutPointIndex (§ cast #_"int" (.. ßinput (getOutpoint) (getIndex))))))
                    (§ block
                        (§ if (.. ßinput (hasSequence)))
                        (§ block
                            (.. ßinputBuilder (setSequence (§ cast #_"int" (.. ßinput (getSequenceNumber)))))
                        )
                        (§ if (!= (.. ßinput (getValue)) nil))
                        (§ block
                            (.. ßinputBuilder (setValue (.. ßinput (getValue) value)))
                        )
                        (.. ßtxBuilder (addTransactionInput ßinputBuilder))
                    )
                )

                ;; Handle outputs.
                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                (§ block
                    (§ let #_"Protos.TransactionOutput.Builder" (§ name ßoutputBuilder) (.. (Protos/TransactionOutput/newBuilder) (setScriptBytes (ByteString/copyFrom (.. ßoutput (getScriptBytes)))) (setValue (.. ßoutput (getValue) value))))
                    (§ block
                        (§ let #_"TransactionInput" (§ name ßspentBy) (.. ßoutput (getSpentBy)))
                        (§ block
                            (§ if (some? ßspentBy))
                            (§ block
                                (§ let #_"Sha256Hash" (§ name ßspendingHash) (.. ßspentBy (getParentTransaction) (getHash)))
                                (§ block
                                    (§ let #_"int" (§ name ßspentByTransactionIndex) (.. ßspentBy (getParentTransaction) (getInputs) (indexOf ßspentBy)))
                                    (§ block
                                        (.. ßoutputBuilder (setSpentByTransactionHash (hashToByteString ßspendingHash)) (setSpentByTransactionIndex ßspentByTransactionIndex))
                                    )
                                )
                            )
                            (.. ßtxBuilder (addTransactionOutput ßoutputBuilder))
                        )
                    )
                )

                ;; Handle which blocks tx was seen in.
                (§ let #_"Map<Sha256Hash, Integer>" (§ name ßappearsInHashes) (.. ßtx (getAppearsInHashes)))
                (§ block
                    (§ if (some? ßappearsInHashes))
                    (§ block
                        (§ for (§ var #_"Map.Entry<Sha256Hash, Integer>" (§ name ßentry)) :for (.. ßappearsInHashes (entrySet)))
                        (§ block
                            (.. ßtxBuilder (addBlockHash (hashToByteString (.. ßentry (getKey)))))
                            (.. ßtxBuilder (addBlockRelativityOffsets (.. ßentry (getValue))))
                        )
                    )

                    (§ if (.. ßtx (hasConfidence)))
                    (§ block
                        (§ let #_"TransactionConfidence" (§ name ßconfidence) (.. ßtx (getConfidence)))
                        (§ block
                            (§ let #_"Protos.TransactionConfidence.Builder" (§ name ßconfidenceBuilder) (.. Protos/TransactionConfidence (newBuilder)))
                            (§ block
                                (writeConfidence ßtxBuilder, ßconfidence, ßconfidenceBuilder)
                            )
                        )
                    )

                    (§ let #_"Protos.Transaction.Purpose" (§ name ßpurpose))
                    (§ block
                        (§ switch (.. ßtx (getPurpose)))
                        (§ block
                            (§ case UNKNOWN)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/UNKNOWN)
                                (§ break )
                            )
                            (§ case USER_PAYMENT)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/USER_PAYMENT)
                                (§ break )
                            )
                            (§ case KEY_ROTATION)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/KEY_ROTATION)
                                (§ break )
                            )
                            (§ case ASSURANCE_CONTRACT_CLAIM)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/ASSURANCE_CONTRACT_CLAIM)
                                (§ break )
                            )
                            (§ case ASSURANCE_CONTRACT_PLEDGE)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/ASSURANCE_CONTRACT_PLEDGE)
                                (§ break )
                            )
                            (§ case ASSURANCE_CONTRACT_STUB)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/ASSURANCE_CONTRACT_STUB)
                                (§ break )
                            )
                            (§ case RAISE_FEE)
                            (§ block
                                (§ ass (§ name ßpurpose) Protos/Transaction/Purpose/RAISE_FEE)
                                (§ break )
                            )
                            (§ default )
                            (§ block
                                (§ throw (§ new #_"RuntimeException" "New tx purpose serialization not implemented."))
                            )
                        )
                        (.. ßtxBuilder (setPurpose ßpurpose))

                        (§ let #_"ExchangeRate" (§ name ßexchangeRate) (.. ßtx (getExchangeRate)))
                        (§ block
                            (§ if (some? ßexchangeRate))
                            (§ block
                                (§ let #_"Protos.ExchangeRate.Builder" (§ name ßexchangeRateBuilder) (.. (Protos/ExchangeRate/newBuilder) (setCoinValue (.. ßexchangeRate coin value)) (setFiatValue (.. ßexchangeRate fiat value)) (setFiatCurrencyCode (.. ßexchangeRate fiat currencyCode))))
                                (§ block
                                    (.. ßtxBuilder (setExchangeRate ßexchangeRateBuilder))
                                )
                            )

                            (§ if (!= (.. ßtx (getMemo)) nil))
                            (§ block
                                (.. ßtxBuilder (setMemo (.. ßtx (getMemo))))
                            )

                            (.. ßtxBuilder (build))
                        )
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"Protos.Transaction.Pool" (§ fn getProtoPool) [#_"WalletTransaction" (§ name ßwtx)])
    (§ block
        (§ switch (.. ßwtx (getPool)))
        (§ block
            (§ case UNSPENT)
            (§ block
                (§ return Protos/Transaction/Pool/UNSPENT)
            )
            (§ case SPENT)
            (§ block
                (§ return Protos/Transaction/Pool/SPENT)
            )
            (§ case DEAD)
            (§ block
                (§ return Protos/Transaction/Pool/DEAD)
            )
            (§ case PENDING)
            (§ block
                (§ return Protos/Transaction/Pool/PENDING)
            )
            (§ default )
            (§ block
                (§ throw (§ new #_"RuntimeException" "Unreachable"))
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn writeConfidence) [#_"Protos.Transaction.Builder" (§ name ßtxBuilder), #_"TransactionConfidence" (§ name ßconfidence), #_"Protos.TransactionConfidence.Builder" (§ name ßconfidenceBuilder)])
    (§ block
        (§ sync ßconfidence)
        (§ block
            (.. ßconfidenceBuilder (setType (Protos/TransactionConfidence/Type/valueOf (.. ßconfidence (getConfidenceType) (getValue)))))
            (§ if (== (.. ßconfidence (getConfidenceType)) ConfidenceType/BUILDING))
            (§ block
                (.. ßconfidenceBuilder (setAppearedAtHeight (.. ßconfidence (getAppearedAtChainHeight))))
                (.. ßconfidenceBuilder (setDepth (.. ßconfidence (getDepthInBlocks))))
            )
            (§ if (== (.. ßconfidence (getConfidenceType)) ConfidenceType/DEAD))
            (§ block
                ;; Copy in the overriding transaction, if available.
                ;; (A dead coinbase transaction has no overriding transaction).
                (§ if (!= (.. ßconfidence (getOverridingTransaction)) nil))
                (§ block
                    (§ let #_"Sha256Hash" (§ name ßoverridingHash) (.. ßconfidence (getOverridingTransaction) (getHash)))
                    (§ block
                        (.. ßconfidenceBuilder (setOverridingTransaction (hashToByteString ßoverridingHash)))
                    )
                )
            )
            (§ let #_"TransactionConfidence.Source" (§ name ßsource) (.. ßconfidence (getSource)))
            (§ block
                (§ switch ßsource)
                (§ block
                    (§ case SELF)
                    (§ block
                        (.. ßconfidenceBuilder (setSource Protos/TransactionConfidence/Source/SOURCE_SELF))
                        (§ break )
                    )
                    (§ case NETWORK)
                    (§ block
                        (.. ßconfidenceBuilder (setSource Protos/TransactionConfidence/Source/SOURCE_NETWORK))
                        (§ break )
                    )
                    (§ case UNKNOWN)
                    (§ block
                        ;; Fall through.
                    )
                    (§ default )
                    (§ block
                        (.. ßconfidenceBuilder (setSource Protos/TransactionConfidence/Source/SOURCE_UNKNOWN))
                        (§ break )
                    )
                )
            )
        )

        (§ for (§ var #_"PeerAddress" (§ name ßaddress)) :for (.. ßconfidence (getBroadcastBy)))
        (§ block
            (§ let #_"Protos.PeerAddress" (§ name ßproto) (.. (Protos/PeerAddress/newBuilder) (setIpAddress (ByteString/copyFrom (.. ßaddress (getAddr) (getAddress)))) (setPort (.. ßaddress (getPort))) (setServices (.. ßaddress (getServices) (longValue))) (build)))
            (§ block
                (.. ßconfidenceBuilder (addBroadcastBy ßproto))
            )
        )
        (§ let #_"Date" (§ name ßlastBroadcastedAt) (.. ßconfidence (getLastBroadcastedAt)))
        (§ block
            (§ if (some? ßlastBroadcastedAt))
            (§ block
                (.. ßconfidenceBuilder (setLastBroadcastedAt (.. ßlastBroadcastedAt (getTime))))
            )
            (.. ßtxBuilder (setConfidence ßconfidenceBuilder))
            nil
        )
    )

    #_public
    #_static
    (§ defn #_"ByteString" (§ fn hashToByteString) [#_"Sha256Hash" (§ name ßhash)])
    (§ block
        (ByteString/copyFrom (.. ßhash (getBytes)))
    )

    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn byteStringToHash) [#_"ByteString" (§ name ßbs)])
    (§ block
        (Sha256Hash/wrap (.. ßbs (toByteArray)))
    )

    ;;;
     ; <p>Loads wallet data from the given protocol buffer and inserts it into the given Wallet object.
     ; This is primarily useful when you wish to pre-register extension objects.  Note that if loading
     ; fails the provided Wallet object may be in an indeterminate state and should be thrown away.</p>
     ;
     ; <p>A wallet can be unreadable for various reasons, such as inability to open the file, corrupt data,
     ; internally inconsistent data and so on.  You should always handle
     ; {@link UnreadableWalletException} and communicate failure to the user in an appropriate manner.</p>
     ;
     ; @throws UnreadableWalletException in various error conditions (see description).
     ;;
    #_public
    (§ method #_"Wallet" (§ fn readWallet) [#_"InputStream" (§ name ßinput)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (readWallet ßinput, false)
    )

    ;;;
     ; <p>Loads wallet data from the given protocol buffer and inserts it into the given Wallet object.
     ; This is primarily useful when you wish to pre-register extension objects.  Note that if loading
     ; fails the provided Wallet object may be in an indeterminate state and should be thrown away.  Do not
     ; simply call this method again on the same Wallet object with {@code forceReset} set {@code true}.
     ; It won't work.</p>
     ;
     ; <p>If {@code forceReset} is {@code true}, then no transactions are loaded from the wallet, and
     ; it is configured to replay transactions from the blockchain (as if the wallet had been loaded and
     ; {@link Wallet.reset} had been called immediately thereafter).
     ;
     ; <p>A wallet can be unreadable for various reasons, such as inability to open the file, corrupt data,
     ; internally inconsistent data and so on. You should always handle
     ; {@link UnreadableWalletException} and communicate failure to the user in an appropriate manner.</p>
     ;
     ; @throws UnreadableWalletException in various error conditions (see description).
     ;;
    #_public
    (§ method #_"Wallet" (§ fn readWallet) [#_"InputStream" (§ name ßinput), #_"boolean" (§ name ßforceReset)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"Protos.Wallet" (§ name ßwalletProto) (parseToProto ßinput))
            (§ block
                (§ let #_"String" (§ name ßparamsID) (.. ßwalletProto (getNetworkIdentifier)))
                (§ block
                    (§ let #_"NetworkParameters" (§ name ßparams) (NetworkParameters/fromID ßparamsID))
                    (§ block
                        (§ if (nil? ßparams))
                        (§ block
                            (§ throw (§ new #_"UnreadableWalletException" (str "Unknown network parameters ID " ßparamsID)))
                        )

                        (§ return (readWallet ßparams, ßwalletProto, ßforceReset))
                    )
                )
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException" "Could not parse input stream to protobuf", ße))
        )
        (§ catch #_"IllegalStateException" (§ name ße))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException" "Could not parse input stream to protobuf", ße))
        )
        (§ catch #_"IllegalArgumentException" (§ name ße))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException" "Could not parse input stream to protobuf", ße))
        )
    )

    ;;;
     ; <p>Loads wallet data from the given protocol buffer and inserts it into the given Wallet object.
     ; This is primarily useful when you wish to pre-register extension objects.  Note that if loading
     ; fails, the provided Wallet object may be in an indeterminate state and should be thrown away.</p>
     ;
     ; <p>A wallet can be unreadable for various reasons, such as inability to open the file, corrupt
     ; data, internally inconsistent data and so on.  You should always handle
     ; {@link UnreadableWalletException} and communicate failure to the user in an appropriate manner.</p>
     ;
     ; @throws UnreadableWalletException in various error conditions (see description).
     ;;
    #_public
    (§ method #_"Wallet" (§ fn readWallet) [#_"NetworkParameters" (§ name ßparams), #_"Protos.Wallet" (§ name ßwalletProto)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (readWallet ßparams, ßwalletProto, false)
    )

    ;;;
     ; <p>Loads wallet data from the given protocol buffer and inserts it into the given Wallet object.
     ; This is primarily useful when you wish to pre-register extension objects.  Note that if loading
     ; fails, the provided Wallet object may be in an indeterminate state and should be thrown away.  Do not
     ; simply call this method again on the same Wallet object with {@code forceReset} set {@code true}.
     ; It won't work.</p>
     ;
     ; <p>If {@code forceReset} is {@code true}, then no transactions are loaded from the wallet, and it is
     ; configured to replay transactions from the blockchain (as if the wallet had been loaded and
     ; {@link Wallet.reset} had been called immediately thereafter).
     ;
     ; <p>A wallet can be unreadable for various reasons, such as inability to open the file, corrupt data,
     ; internally inconsistent data and so on.  You should always handle
     ; {@link UnreadableWalletException} and communicate failure to the user in an appropriate manner.</p>
     ;
     ; @throws UnreadableWalletException in various error conditions (see description).
     ;;
    #_public
    (§ method #_"Wallet" (§ fn readWallet) [#_"NetworkParameters" (§ name ßparams), #_"Protos.Wallet" (§ name ßwalletProto), #_"boolean" (§ name ßforceReset)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ if (< CURRENT_WALLET_VERSION (.. ßwalletProto (getVersion))))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException.FutureVersion"))
        )
        (§ if (not (.. ßwalletProto (getNetworkIdentifier) (equals (.. ßparams (getId))))))
        (§ block
            (§ throw (§ new #_"UnreadableWalletException.WrongNetwork"))
        )

        ;; Read the scrypt parameters that specify how encryption and decryption is performed.
        (§ let #_"KeyChainGroup" (§ name ßkeyChainGroup))
        (§ block
            (§ if (.. ßwalletProto (hasEncryptionParameters)))
            (§ block
                (§ let #_"Protos.ScryptParameters" (§ name ßencryptionParameters) (.. ßwalletProto (getEncryptionParameters)))
                (§ block
                    (§ let #_"KeyCrypterScrypt" (§ name ßkeyCrypter) (§ new #_"KeyCrypterScrypt" ßencryptionParameters))
                    (§ block
                        (§ ass (§ name ßkeyChainGroup) (KeyChainGroup/fromProtobufEncrypted ßparams, (.. ßwalletProto (getKeyList)), ßkeyCrypter, keyChainFactory))
                    )
                )
            )
            (§ else )
            (§ block
                (§ ass (§ name ßkeyChainGroup) (KeyChainGroup/fromProtobufUnencrypted ßparams, (.. ßwalletProto (getKeyList)), keyChainFactory))
            )

            (§ let #_"Wallet" (§ name ßwallet) (.. factory (create ßparams, ßkeyChainGroup)))
            (§ block
                (§ if (.. ßwalletProto (hasDescription)))
                (§ block
                    (.. ßwallet (setDescription (.. ßwalletProto (getDescription))))
                )

                (§ if ßforceReset)
                (§ block
                    ;; Should mirror Wallet.reset().
                    (.. ßwallet (setLastBlockSeenHash nil))
                    (.. ßwallet (setLastBlockSeenHeight -1))
                    (.. ßwallet (setLastBlockSeenTimeSecs 0))
                )
                (§ else )
                (§ block
                    ;; Read all transactions and insert into the txMap.
                    (§ for (§ var #_"Protos.Transaction" (§ name ßtxProto)) :for (.. ßwalletProto (getTransactionList)))
                    (§ block
                        (readTransaction ßtxProto, (.. ßwallet (getParams)))
                    )

                    ;; Update transaction outputs to point to inputs that spend them.
                    (§ for (§ var #_"Protos.Transaction" (§ name ßtxProto)) :for (.. ßwalletProto (getTransactionList)))
                    (§ block
                        (§ let #_"WalletTransaction" (§ name ßwtx) (connectTransactionOutputs ßparams, ßtxProto))
                        (§ block
                            (.. ßwallet (addWalletTransaction ßwtx))
                        )
                    )

                    ;; Update the lastBlockSeenHash.
                    (§ if (not (.. ßwalletProto (hasLastSeenBlockHash))))
                    (§ block
                        (.. ßwallet (setLastBlockSeenHash nil))
                    )
                    (§ else )
                    (§ block
                        (.. ßwallet (setLastBlockSeenHash (byteStringToHash (.. ßwalletProto (getLastSeenBlockHash)))))
                    )

                    (§ if (not (.. ßwalletProto (hasLastSeenBlockHeight))))
                    (§ block
                        (.. ßwallet (setLastBlockSeenHeight -1))
                    )
                    (§ else )
                    (§ block
                        (.. ßwallet (setLastBlockSeenHeight (.. ßwalletProto (getLastSeenBlockHeight))))
                    )

                    ;; Will default to zero if not present.
                    (.. ßwallet (setLastBlockSeenTimeSecs (.. ßwalletProto (getLastSeenBlockTimeSecs))))

                    (§ if (.. ßwalletProto (hasKeyRotationTime)))
                    (§ block
                        (.. ßwallet (setKeyRotationTime (§ new #_"Date" (* (.. ßwalletProto (getKeyRotationTime)) 1000))))
                    )
                )

                (§ for (§ var #_"Protos.TransactionSigner" (§ name ßsignerProto)) :for (.. ßwalletProto (getTransactionSignersList)))
                (§ block
                    (§ try )
                    (§ block
                        (§ let #_"Class" (§ name ßsignerClass) (Class/forName (.. ßsignerProto (getClassName))))
                        (§ block
                            (§ let #_"TransactionSigner" (§ name ßsigner) (§ cast #_"TransactionSigner" (.. ßsignerClass (newInstance))))
                            (§ block
                                (.. ßsigner (deserialize (.. ßsignerProto (getData) (toByteArray))))
                                (.. ßwallet (addTransactionSigner ßsigner))
                            )
                        )
                    )
                    (§ catch #_"Exception" (§ name ße))
                    (§ block
                        (§ throw (§ new #_"UnreadableWalletException" (str "Unable to deserialize TransactionSigner instance: " (.. ßsignerProto (getClassName))), ße))
                    )
                )

                (§ if (.. ßwalletProto (hasVersion)))
                (§ block
                    (.. ßwallet (setVersion (.. ßwalletProto (getVersion))))
                )

                ;; Make sure the object can be re-used to read another wallet without corruption.
                (.. txMap (clear))

                ßwallet
            )
        )
    )

    ;;;
     ; Returns the loaded protocol buffer from the given byte stream.  You normally want
     ; {@link Wallet#loadFromFile(java.io.File)} instead - this method is designed for
     ; low level work involving the wallet file format itself.
     ;;
    #_public
    #_static
    (§ defn #_"Protos.Wallet" (§ fn parseToProto) [#_"InputStream" (§ name ßinput)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"CodedInputStream" (§ name ßcodedInput) (CodedInputStream/newInstance ßinput))
        (§ block
            (.. ßcodedInput (setSizeLimit WALLET_SIZE_LIMIT))
            (.. Protos/Wallet (parseFrom ßcodedInput))
        )
    )

    #_private
    (§ method- #_"void" (§ fn readTransaction) [#_"Protos.Transaction" (§ name ßtxProto), #_"NetworkParameters" (§ name ßparams)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"Transaction" (§ name ßtx) (§ new #_"Transaction" ßparams))
        (§ block

            (.. ßtx (setVersion (.. ßtxProto (getVersion))))

            (§ if (.. ßtxProto (hasUpdatedAt)))
            (§ block
                (.. ßtx (setUpdateTime (§ new #_"Date" (.. ßtxProto (getUpdatedAt)))))
            )

            (§ for (§ var #_"Protos.TransactionOutput" (§ name ßoutputProto)) :for (.. ßtxProto (getTransactionOutputList)))
            (§ block
                (§ let #_"Coin" (§ name ßvalue) (Coin/valueOf (.. ßoutputProto (getValue))))
                (§ block
                    (§ let #_"byte[]" (§ name ßscriptBytes) (.. ßoutputProto (getScriptBytes) (toByteArray)))
                    (§ block
                        (§ let #_"TransactionOutput" (§ name ßoutput) (§ new #_"TransactionOutput" ßparams, ßtx, ßvalue, ßscriptBytes))
                        (§ block
                            (.. ßtx (addOutput ßoutput))
                        )
                    )
                )
            )

            (§ for (§ var #_"Protos.TransactionInput" (§ name ßinputProto)) :for (.. ßtxProto (getTransactionInputList)))
            (§ block
                (§ let #_"byte[]" (§ name ßscriptBytes) (.. ßinputProto (getScriptBytes) (toByteArray)))
                (§ block
                    (§ let #_"TransactionOutPoint" (§ name ßoutpoint) (§ new #_"TransactionOutPoint" ßparams, (& 0xffffffff (.. ßinputProto (getTransactionOutPointIndex))), (byteStringToHash (.. ßinputProto (getTransactionOutPointHash)))))
                    (§ block
                        (§ let #_"Coin" (§ name ßvalue) (when (.. ßinputProto (hasValue)) (Coin/valueOf (.. ßinputProto (getValue)))))
                        (§ block
                            (§ let #_"TransactionInput" (§ name ßinput) (§ new #_"TransactionInput" ßparams, ßtx, ßscriptBytes, ßoutpoint, ßvalue))
                            (§ block
                                (§ if (.. ßinputProto (hasSequence)))
                                (§ block
                                    (.. ßinput (setSequenceNumber (& 0xffffffff (.. ßinputProto (getSequence)))))
                                )
                                (.. ßtx (addInput ßinput))
                            )
                        )
                    )
                )
            )

            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßtxProto (getBlockHashCount))) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"ByteString" (§ name ßblockHash) (.. ßtxProto (getBlockHash ßi)))
                (§ block
                    (§ let #_"int" (§ name ßrelativityOffset) 0)
                    (§ block
                        (§ if (< 0 (.. ßtxProto (getBlockRelativityOffsetsCount))))
                        (§ block
                            (§ ass (§ name ßrelativityOffset) (.. ßtxProto (getBlockRelativityOffsets ßi)))
                        )
                        (.. ßtx (addBlockAppearance (byteStringToHash ßblockHash), ßrelativityOffset))
                    )
                )
            )

            (§ if (.. ßtxProto (hasLockTime)))
            (§ block
                (.. ßtx (setLockTime (& 0xffffffff (.. ßtxProto (getLockTime)))))
            )

            (§ if (.. ßtxProto (hasPurpose)))
            (§ block
                (§ switch (.. ßtxProto (getPurpose)))
                (§ block
                    (§ case UNKNOWN)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/UNKNOWN))
                        (§ break )
                    )
                    (§ case USER_PAYMENT)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/USER_PAYMENT))
                        (§ break )
                    )
                    (§ case KEY_ROTATION)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/KEY_ROTATION))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_CLAIM)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_CLAIM))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_PLEDGE)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_PLEDGE))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_STUB)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_STUB))
                        (§ break )
                    )
                    (§ case RAISE_FEE)
                    (§ block
                        (.. ßtx (setPurpose Transaction/Purpose/RAISE_FEE))
                        (§ break )
                    )
                    (§ default )
                    (§ block
                        (§ throw (§ new #_"RuntimeException" "New purpose serialization not implemented"))
                    )
                )
            )
            (§ else )
            (§ block
                ;; Old wallet: assume a user payment as that's the only reason a new tx would have been created back then.
                (.. ßtx (setPurpose Transaction/Purpose/USER_PAYMENT))
            )

            (§ if (.. ßtxProto (hasExchangeRate)))
            (§ block
                (§ let #_"Protos.ExchangeRate" (§ name ßexchangeRateProto) (.. ßtxProto (getExchangeRate)))
                (§ block
                    (.. ßtx (setExchangeRate (§ new #_"ExchangeRate" (Coin/valueOf (.. ßexchangeRateProto (getCoinValue))), (Fiat/valueOf (.. ßexchangeRateProto (getFiatCurrencyCode)), (.. ßexchangeRateProto (getFiatValue))))))
                )
            )

            (§ if (.. ßtxProto (hasMemo)))
            (§ block
                (.. ßtx (setMemo (.. ßtxProto (getMemo))))
            )

            ;; Transaction should now be complete.
            (§ let #_"Sha256Hash" (§ name ßprotoHash) (byteStringToHash (.. ßtxProto (getHash))))
            (§ block
                (§ if (not (.. ßtx (getHash) (equals ßprotoHash))))
                (§ block
                    (§ throw (§ new #_"UnreadableWalletException" (String/format Locale/US, "Transaction did not deserialize completely: %s vs %s", (.. ßtx (getHash)), ßprotoHash)))
                )
                (§ if (.. txMap (containsKey (.. ßtxProto (getHash)))))
                (§ block
                    (§ throw (§ new #_"UnreadableWalletException" (str "Wallet contained duplicate transaction " (byteStringToHash (.. ßtxProto (getHash))))))
                )
                (.. txMap (put (.. ßtxProto (getHash)), ßtx))
                nil
            )
        )
    )

    #_private
    (§ method- #_"WalletTransaction" (§ fn connectTransactionOutputs) [#_"NetworkParameters" (§ name ßparams), #_"org.bitcoinj.wallet.Protos.Transaction" (§ name ßtxProto)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"Transaction" (§ name ßtx) (.. txMap (get (.. ßtxProto (getHash)))))
        (§ block
            (§ let #_"WalletTransaction.Pool" (§ name ßpool))
            (§ block
                (§ switch (.. ßtxProto (getPool)))
                (§ block
                    (§ case DEAD)
                    (§ block
                        (§ ass (§ name ßpool) WalletTransaction/Pool/DEAD)
                        (§ break )
                    )
                    (§ case PENDING)
                    (§ block
                        (§ ass (§ name ßpool) WalletTransaction/Pool/PENDING)
                        (§ break )
                    )
                    (§ case SPENT)
                    (§ block
                        (§ ass (§ name ßpool) WalletTransaction/Pool/SPENT)
                        (§ break )
                    )
                    (§ case UNSPENT)
                    (§ block
                        (§ ass (§ name ßpool) WalletTransaction/Pool/UNSPENT)
                        (§ break )
                    )
                    ;; Upgrade old wallets: inactive pool has been merged with the pending pool.
                    ;; Remove this some time after 0.9 is old and everyone has upgraded.
                    ;; There should not be any spent outputs in this tx as old wallets would not allow them to be spent in this state.
                    (§ case INACTIVE)
                    (§ case PENDING_INACTIVE)
                    (§ block
                        (§ ass (§ name ßpool) WalletTransaction/Pool/PENDING)
                        (§ break )
                    )
                    (§ default )
                    (§ block
                        (§ throw (§ new #_"UnreadableWalletException" (str "Unknown transaction pool: " (.. ßtxProto (getPool)))))
                    )
                )

                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßtx (getOutputs) (size))) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ let #_"TransactionOutput" (§ name ßoutput) (.. ßtx (getOutputs) (get ßi)))
                    (§ block
                        (§ let #_"Protos.TransactionOutput" (§ name ßtransactionOutput) (.. ßtxProto (getTransactionOutput ßi)))
                        (§ block
                            (§ if (.. ßtransactionOutput (hasSpentByTransactionHash)))
                            (§ block
                                (§ let #_"ByteString" (§ name ßspentByTransactionHash) (.. ßtransactionOutput (getSpentByTransactionHash)))
                                (§ block
                                    (§ let #_"Transaction" (§ name ßspendingTx) (.. txMap (get ßspentByTransactionHash)))
                                    (§ block
                                        (§ if (nil? ßspendingTx))
                                        (§ block
                                            (§ throw (§ new #_"UnreadableWalletException" (String/format Locale/US, "Could not connect %s to %s", (.. ßtx (getHashAsString)), (byteStringToHash ßspentByTransactionHash))))
                                        )

                                        (§ let #_"int" (§ name ßspendingIndex) (.. ßtransactionOutput (getSpentByTransactionIndex)))
                                        (§ block
                                            (§ let #_"TransactionInput" (§ name ßinput) (Preconditions/checkNotNull (.. ßspendingTx (getInput ßspendingIndex))))
                                            (§ block
                                                (.. ßinput (connect ßoutput))
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )

                (§ if (.. ßtxProto (hasConfidence)))
                (§ block
                    (§ let #_"Protos.TransactionConfidence" (§ name ßconfidenceProto) (.. ßtxProto (getConfidence)))
                    (§ block
                        (§ let #_"TransactionConfidence" (§ name ßconfidence) (.. ßtx (getConfidence)))
                        (§ block
                            (readConfidence ßparams, ßtx, ßconfidenceProto, ßconfidence)
                        )
                    )
                )

                (§ new #_"WalletTransaction" ßpool, ßtx)
            )
        )
    )

    #_private
    (§ method- #_"void" (§ fn readConfidence) [#_"NetworkParameters" (§ name ßparams), #_"Transaction" (§ name ßtx), #_"Protos.TransactionConfidence" (§ name ßconfidenceProto), #_"TransactionConfidence" (§ name ßconfidence)])
        (§ throws #_"UnreadableWalletException")
    (§ block
        ;; We are lenient here because tx confidence is not an essential part of the wallet.
        ;; If the tx has an unknown type of confidence, ignore.
        (§ if (not (.. ßconfidenceProto (hasType))))
        (§ block
            (.. log (warn "Unknown ßconfidence type for ßtx {}", (.. ßtx (getHashAsString))))
            (§ return nil)
        )

        (§ let #_"ConfidenceType" (§ name ßconfidenceType))
        (§ block
            (§ switch (.. ßconfidenceProto (getType)))
            (§ block
                (§ case BUILDING)
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/BUILDING)
                    (§ break )
                )
                (§ case DEAD)
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/DEAD)
                    (§ break )
                )
                ;; These two are equivalent (must be able to read old wallets).
                (§ case NOT_IN_BEST_CHAIN)
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/PENDING)
                    (§ break )
                )
                (§ case PENDING)
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/PENDING)
                    (§ break )
                )
                (§ case IN_CONFLICT)
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/IN_CONFLICT)
                    (§ break )
                )
                (§ case UNKNOWN)
                (§ block
                    ;; Fall through.
                )
                (§ default )
                (§ block
                    (§ ass (§ name ßconfidenceType) ConfidenceType/UNKNOWN)
                    (§ break )
                )
            )
            (.. ßconfidence (setConfidenceType ßconfidenceType))
            (§ if (.. ßconfidenceProto (hasAppearedAtHeight)))
            (§ block
                (§ if (!= (.. ßconfidence (getConfidenceType)) ConfidenceType/BUILDING))
                (§ block
                    (.. log (warn "Have appearedAtHeight but not BUILDING for ßtx {}", (.. ßtx (getHashAsString))))
                    (§ return nil)
                )
                (.. ßconfidence (setAppearedAtChainHeight (.. ßconfidenceProto (getAppearedAtHeight))))
            )

            (§ if (.. ßconfidenceProto (hasDepth)))
            (§ block
                (§ if (!= (.. ßconfidence (getConfidenceType)) ConfidenceType/BUILDING))
                (§ block
                    (.. log (warn "Have depth but not BUILDING for ßtx {}", (.. ßtx (getHashAsString))))
                    (§ return nil)
                )
                (.. ßconfidence (setDepthInBlocks (.. ßconfidenceProto (getDepth))))
            )

            (§ if (.. ßconfidenceProto (hasOverridingTransaction)))
            (§ block
                (§ if (!= (.. ßconfidence (getConfidenceType)) ConfidenceType/DEAD))
                (§ block
                    (.. log (warn "Have overridingTransaction but not OVERRIDDEN for ßtx {}", (.. ßtx (getHashAsString))))
                    (§ return nil)
                )
                (§ let #_"Transaction" (§ name ßoverridingTransaction) (.. txMap (get (.. ßconfidenceProto (getOverridingTransaction)))))
                (§ block
                    (§ if (nil? ßoverridingTransaction))
                    (§ block
                        (.. log (warn "Have ßoverridingTransaction that is not in wallet for ßtx {}", (.. ßtx (getHashAsString))))
                        (§ return nil)
                    )
                    (.. ßconfidence (setOverridingTransaction ßoverridingTransaction))
                )
            )

            (§ for (§ var #_"Protos.PeerAddress" (§ name ßproto)) :for (.. ßconfidenceProto (getBroadcastByList)))
            (§ block
                (§ let #_"InetAddress" (§ name ßip))
                (§ block
                    (§ try )
                    (§ block
                        (§ ass (§ name ßip) (InetAddress/getByAddress (.. ßproto (getIpAddress) (toByteArray))))
                    )
                    (§ catch #_"UnknownHostException" (§ name ße))
                    (§ block
                        (§ throw (§ new #_"UnreadableWalletException" "Peer IP address does not have the right length", ße))
                    )

                    (§ let #_"int" (§ name ßport) (.. ßproto (getPort)))
                    (§ block
                        (§ let #_"int" (§ name ßprotocolVersion) (.. ßparams (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)))
                        (§ block
                            (§ let #_"BigInteger" (§ name ßservices) (BigInteger/valueOf (.. ßproto (getServices))))
                            (§ block
                                (§ let #_"PeerAddress" (§ name ßaddress) (§ new #_"PeerAddress" ßparams, ßip, ßport, ßprotocolVersion, ßservices))
                                (§ block
                                    (.. ßconfidence (markBroadcastBy ßaddress))
                                )
                            )
                        )
                    )
                )
            )

            (§ if (.. ßconfidenceProto (hasLastBroadcastedAt)))
            (§ block
                (.. ßconfidence (setLastBroadcastedAt (§ new #_"Date" (.. ßconfidenceProto (getLastBroadcastedAt)))))
            )

            (§ switch (.. ßconfidenceProto (getSource)))
            (§ block
                (§ case SOURCE_SELF)
                (§ block
                    (.. ßconfidence (setSource TransactionConfidence/Source/SELF))
                    (§ break )
                )
                (§ case SOURCE_NETWORK)
                (§ block
                    (.. ßconfidence (setSource TransactionConfidence/Source/NETWORK))
                    (§ break )
                )
                (§ case SOURCE_UNKNOWN)
                (§ block
                    ;; Fall through.
                )
                (§ default )
                (§ block
                    (.. ßconfidence (setSource TransactionConfidence/Source/UNKNOWN))
                    (§ break )
                )
            )
            nil
        )
    )

    ;;;
     ; Cheap test to see if input stream is a wallet.  This checks for a magic value at the beginning of the stream.
     ;
     ; @param is Input stream to test.
     ; @return true if input stream is a wallet.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn isWallet) [#_"InputStream" (§ name ßis)])
    (§ block
        (§ try )
        (§ block
            (§ let #_"CodedInputStream" (§ name ßcis) (CodedInputStream/newInstance ßis))
            (§ block
                (§ let #_"int" (§ name ßtag) (.. ßcis (readTag)))
                (§ block
                    (§ let #_"int" (§ name ßfield) (WireFormat/getTagFieldNumber ßtag))
                    (§ block
                        (§ if (!= ßfield 1)) ;; network_identifier
                        (§ block
                            (§ return false)
                        )

                        (§ let #_"String" (§ name ßnetwork) (.. ßcis (readString)))
                        (§ block
                            (§ return (some? (NetworkParameters/fromID ßnetwork)))
                        )
                    )
                )
            )
        )
        (§ catch #_"IOException" (§ name _))
        (§ block
            (§ return false)
        )
    )
)

#_(ns org.bitcoinj.wallet #_"WalletTransaction"
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Transaction]))

;;;
 ; Stores data about a transaction that is only relevant to the {@link org.bitcoinj.wallet.Wallet} class.
 ;;
#_public
(§ class #_"WalletTransaction"
    #_public
    (§ enum #_"Pool"
        (§ item UNSPENT) ;; unspent in best chain
        (§ item SPENT) ;; spent in best chain
        (§ item DEAD) ;; double-spend in alt chain
        (§ item PENDING) ;; a pending tx we would like to go into the best chain
    )
    #_private
    (§ field- #_"Transaction" (§ name transaction))
    #_private
    (§ field- #_"Pool" (§ name pool))

    #_public
    (§ constructor #_"WalletTransaction" [#_"Pool" (§ name ßpool), #_"Transaction" (§ name ßtransaction)])
    (§ block
        (§ ass (§ name (.. this ßpool)) (Preconditions/checkNotNull ßpool))
        (§ ass (§ name (.. this ßtransaction)) ßtransaction)
        this
    )

    #_public
    (§ method #_"Transaction" (§ fn getTransaction) [])
    (§ block
        transaction
    )

    #_public
    (§ method #_"Pool" (§ fn getPool) [])
    (§ block
        pool
    )
)

