    (§ constructor InventoryMessage [#_"NetworkParameters" __params, #_"byte[]" __bytes]
        (§ super __params, __bytes)
        this
    )

    ;;;
     ; Deserializes an 'inv' message.
     ; @param params NetworkParameters object.
     ; @param payload Bitcoin protocol formatted byte array containing message content.
     ; @param serializer The serializer to use for this message.
     ; @param length The length of message if known.  Usually this is provided when deserializing of the wire
     ; as the length will be provided as part of the header.  Set to Message.UNKNOWN_LENGTH, if not known.
     ; @throws ProtocolException
     ;;
    #_public
    #_throws #_[ "ProtocolException" ]
    (§ constructor InventoryMessage [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"MessageSerializer" __serializer, #_"int" __length]
        (§ super __params, __payload, __serializer, __length)
        this
    )

    #_public
    (§ constructor InventoryMessage [#_"NetworkParameters" __params]
        (§ super __params)
        this
    )

    #_public
    (§ method #_"void" addBlock [#_"Block" __block]
        (.. this (addItem (InventoryItem. InventoryItem.Type/Block, (.. __block (getHash)))))
        nil
    )

    #_public
    (§ method #_"void" addTransaction [#_"Transaction" __tx]
        (.. this (addItem (InventoryItem. InventoryItem.Type/Transaction, (.. __tx (getHash)))))
        nil
    )

    ;;; Creates a new inv message for the given transactions. ;;
    #_public
    #_static
    (§ defn #_"InventoryMessage" InventoryMessage/with [#_"Transaction..." __txs]
        (Preconditions/checkArgument (< 0 (.. __txs (alength))))
        (let [#_"InventoryMessage" __result (InventoryMessage. (.. (aget __txs 0) (getParams)))]
            (doseq [#_"Transaction" __tx __txs]
                (.. __result (addTransaction __tx))
            )
            __result
        )
    )
)

;;;
 ; <p>Abstract superclass of classes with list based payload, i.e. InventoryMessage and GetDataMessage.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class ListMessage (§ extends Message)
    #_public
    #_static
    (§ def #_"long" ListMessage/MAX_INVENTORY_ITEMS 50000)

    #_private
    (§ field- #_"long" :array-len)
    ;; For some reason the compiler complains if this is inside InventoryItem.
    #_protected
    (§ field #_"List<InventoryItem>" :items)

    #_public
    #_throws #_[ "ProtocolException" ]
    (§ constructor ListMessage [#_"NetworkParameters" __params, #_"byte[]" __bytes]
        (§ super __params, __bytes, 0)
        this
    )

    #_public
    #_throws #_[ "ProtocolException" ]
    (§ constructor ListMessage [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"MessageSerializer" __serializer, #_"int" __length]
        (§ super __params, __payload, 0, __serializer, __length)
        this
    )

    #_public
    (§ constructor ListMessage [#_"NetworkParameters" __params]
        (§ super __params)

        (§ assoc this :items (ArrayList. #_"<>"))
        (§ assoc this :length 1) ;; length of 0 varint
        this
    )

    #_public
    (§ method #_"List<InventoryItem>" getItems []
        (Collections/unmodifiableList (:items this))
    )

    #_public
    (§ method #_"void" addItem [#_"InventoryItem" __item]
        (.. this (unCache))
        (§ assoc this :length (- (:length this) (VarInt/sizeOf (.. (:items this) (size)))))
        (.. (:items this) (add __item))
        (§ assoc this :length (+ (:length this) (VarInt/sizeOf (.. (:items this) (size))) InventoryItem/MESSAGE_LENGTH))
        nil
    )

    #_public
    (§ method #_"void" removeItem [#_"int" __index]
        (.. this (unCache))
        (§ assoc this :length (- (:length this) (VarInt/sizeOf (.. (:items this) (size)))))
        (.. (:items this) (remove __index))
        (§ assoc this :length (+ (:length this) (VarInt/sizeOf (.. (:items this) (size))) (- InventoryItem/MESSAGE_LENGTH)))
        nil
    )

    #_override
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parse []
        (§ assoc this :array-len (.. this (readVarInt)))
        (when (< ListMessage/MAX_INVENTORY_ITEMS (:array-len this))
            (throw (ProtocolException. (str "Too many items in INV message: " (:array-len this))))
        )
        (§ assoc this :length (int (+ (- (:cursor this) (:offset this)) (* (:array-len this) InventoryItem/MESSAGE_LENGTH))))

        ;; An inv is vector<CInv> where CInv is int+hash.  The int is either 1 or 2 for tx or block.
        (§ assoc this :items (ArrayList. #_"<>" (int (:array-len this))))
        (loop-when-recur [#_"int" __i 0] (< __i (:array-len this)) [(inc __i)]
            (when (< (.. (:payload this) (alength)) (+ (:cursor this) InventoryItem/MESSAGE_LENGTH))
                (throw (ProtocolException. "Ran off the end of the INV"))
            )

            (let [#_"int" __typeCode (int (.. this (readUint32)))
                  ;; see ppszTypeName in net.h
                  #_"InventoryItem.Type" __type
                    (case __typeCode
                        0 InventoryItem.Type/Error
                        1 InventoryItem.Type/Transaction
                        2 InventoryItem.Type/Block
                        3 InventoryItem.Type/FilteredBlock
                        (throw (ProtocolException. (str "Unknown CInv type: " __typeCode)))
                    )]
                (.. (:items this) (add (InventoryItem. __type, (.. this (readHash)))))
            )
        )
        (§ assoc this :payload nil)
        nil
    )

    #_override
    #_public
    #_throws #_[ "IOException" ]
    (§ method #_"void" bitcoinSerializeToStream [#_"OutputStream" __stream]
        (.. __stream (write (.. (VarInt. (.. (:items this) (size))) (encode))))
        (doseq [#_"InventoryItem" __i (:items this)]
            ;; Write out the type code.
            (Utils/uint32ToByteStreamLE (.. (:type __i) (ordinal)), __stream)
            ;; And now the hash.
            (.. __stream (write (.. (:hash __i) (getReversedBytes))))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" __o]
        (when (== this __o)
            (§ return true)
        )
        (when (or (nil? __o) (!= (getClass) (.. __o (getClass))))
            (§ return false)
        )
        (.. (:items this) (equals (:items (cast ListMessage __o))))
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (.. (:items this) (hashCode))
    )
)

