            (let [#_"LinkedList<Message>" __transactions (LinkedList. #_"<>")
                  #_"LinkedList<InventoryItem>" __items (LinkedList. #_"<>" (.. __m (getItems)))
                  #_"Iterator<InventoryItem>" __it (.. __items (iterator))]
                (while (.. __it (hasNext))
