            (.. PeerGroup/log (info "Awaiting PeerGroup shutdown ..."))
            (.. (:executor this) (awaitTermination Long/MAX_VALUE, TimeUnit/SECONDS))
            (catch InterruptedException __e
                (throw (RuntimeException. __e))
            )
        )
        nil
    )

