            (.. __broadcast (setMinConnections __minConnections))
            ;; Send the TX to the wallet once we have a successful broadcast.
            (Futures/addCallback (.. __broadcast (future)), (FutureCallback. #_"<Transaction>")
            (§ anon
                #_override
