    #_public
    (§ method #_"void" setWriteTarget [#_"MessageWriteTarget" __writeTarget]
        (Preconditions/checkArgument (some? __writeTarget))

        (.. (:lock this) (lock))
        (let [#_"boolean" __closeNow false]
            (try
                (Preconditions/checkArgument (nil? (:write-target this)))

                (§ ass __closeNow (:close-pending this))
                (§ assoc this :write-target __writeTarget)
                (finally
                    (.. (:lock this) (unlock))
                )
            )
            (when __closeNow
                (.. __writeTarget (closeConnection))
            )
            nil
        )
    )

    #_override
    #_public
    (§ method #_"int" getMaxMessageSize []
        Message/MAX_SIZE
    )

    ;;;
     ; @return the IP address and port of peer.
     ;;
    #_public
    (§ method #_"PeerAddress" getAddress []
        (:peer-address this)
    )

    ;;; Catch any exceptions, logging them and then closing the channel. ;;
    #_private
    (§ method- #_"void" exceptionCaught [#_"Exception" __e]
        (let [#_"PeerAddress" __addr (.. this (getAddress))
              #_"String" __s (if (some? __addr) (.. __addr (toString)) "?")]
