    #_protected
    #_static
    (§ defn #_"int" Transaction/calcLength [#_"byte[]" __buf, #_"int" __offset]
        ;; jump past version (uint32)
        (let [#_"int" __cursor (+ __offset 4)]

            (let [#_"VarInt" __varint (VarInt. __buf, __cursor)
                  #_"long" __txInCount (:value __varint)]
                (§ ass __cursor (+ __cursor (.. __varint (getOriginalSizeInBytes))))

                (loop-when-recur [#_"int" __i 0] (< __i __txInCount) [(inc __i)]
                    ;; 36 = length of previous_outpoint
                    (§ ass __cursor (+ __cursor 36))
                    (§ ass __varint (VarInt. __buf, __cursor))
                    (let [#_"long" __scriptLen (:value __varint)]
                        ;; 4 = length of sequence field (unint32)
                        (§ ass __cursor (+ __cursor __scriptLen 4 (.. __varint (getOriginalSizeInBytes))))
                    )
                )

                (§ ass __varint (VarInt. __buf, __cursor))
                (let [#_"long" __txOutCount (:value __varint)]
                    (§ ass __cursor (+ __cursor (.. __varint (getOriginalSizeInBytes))))

                    (loop-when-recur [#_"int" __i 0] (< __i __txOutCount) [(inc __i)]
                        ;; 8 = length of tx value field (uint64)
                        (§ ass __cursor (+ __cursor 8))
                        (§ ass __varint (VarInt. __buf, __cursor))
                        (let [#_"long" __scriptLen (:value __varint)]
                            (§ ass __cursor (+ __cursor __scriptLen (.. __varint (getOriginalSizeInBytes))))
                        )
                    )
                    ;; 4 = length of lock_time field (uint32)
                    (+ (- __cursor __offset) 4)
                )
            )
        )
    )

    #_override
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parse []
        (§ assoc this :cursor (:offset this))

        (§ assoc this :version (.. this (readUint32)))
        (§ assoc this :optimal-encoding-message-size 4)

        ;; First come the inputs.
        (let [#_"long" __numInputs (.. this (readVarInt))]
            (§ assoc this :optimal-encoding-message-size (+ (:optimal-encoding-message-size this) (VarInt/sizeOf __numInputs)))
            (§ assoc this :inputs (ArrayList. #_"<>" (int __numInputs)))
            (loop-when-recur [#_"long" __i 0] (< __i __numInputs) [(inc __i)]
                (let [#_"TransactionInput" __input (TransactionInput. (:params this), this, (:payload this), (:cursor this), (:serializer this))]
                    (.. (:inputs this) (add __input))
                    (let [#_"long" __scriptLen (.. this (readVarInt TransactionOutPoint/MESSAGE_LENGTH))]
                        (§ assoc this :optimal-encoding-message-size (+ (:optimal-encoding-message-size this) TransactionOutPoint/MESSAGE_LENGTH (VarInt/sizeOf __scriptLen) __scriptLen 4))
                        (§ assoc this :cursor (+ (:cursor this) __scriptLen 4))
                    )
                )
            )
            ;; Now the outputs.
            (let [#_"long" __numOutputs (.. this (readVarInt))]
                (§ assoc this :optimal-encoding-message-size (+ (:optimal-encoding-message-size this) (VarInt/sizeOf __numOutputs)))
                (§ assoc this :outputs (ArrayList. #_"<>" (int __numOutputs)))
                (loop-when-recur [#_"long" __i 0] (< __i __numOutputs) [(inc __i)]
                    (let [#_"TransactionOutput" __output (TransactionOutput. (:params this), this, (:payload this), (:cursor this), (:serializer this))]
                        (.. (:outputs this) (add __output))
                        (let [#_"long" __scriptLen (.. this (readVarInt 8))]
                            (§ assoc this :optimal-encoding-message-size (+ (:optimal-encoding-message-size this) 8 (VarInt/sizeOf __scriptLen) __scriptLen))
                            (§ assoc this :cursor (+ (:cursor this) __scriptLen))
                        )
                    )
                )
                (§ assoc this :lock-time (.. this (readUint32)))
                (§ assoc this :optimal-encoding-message-size (+ (:optimal-encoding-message-size this) 4))
                (§ assoc this :length (- (:cursor this) (:offset this)))
                nil
            )
        )
    )

    #_public
    (§ method #_"int" getOptimalEncodingMessageSize []
        (when (!= (:optimal-encoding-message-size this) 0)
            (§ return (:optimal-encoding-message-size this))
        )

        (§ assoc this :optimal-encoding-message-size (.. this (getMessageSize)))
        (:optimal-encoding-message-size this)
