    (§ method #_"int" getSigOpCount []
        (let [#_"int" __sigOps 0]
            (doseq [#_"TransactionInput" __input (:inputs this)]
                (§ ass __sigOps (+ __sigOps (Script/getSigOpCount (.. __input (getScriptBytes)))))
            )
            (doseq [#_"TransactionOutput" __output (:outputs this)]
                (§ ass __sigOps (+ __sigOps (Script/getSigOpCount (.. __output (getScriptBytes)))))
