    #_public
    (§ method #_"Date" estimateLockTime [#_"AbstractBlockChain" __chain]
        (if (< (:lock-time this) Transaction/LOCKTIME_THRESHOLD) (.. __chain (estimateBlockTime (int (.. this (getLockTime))))) (Date. (* (.. this (getLockTime)) 1000)))
    )

