        (§ assoc this :tx __tx)
        (§ assoc this :min-connections (Math/max 1, (.. __peerGroup (getMinBroadcastConnections))))
        this
    )

    ;; Only for mock broadcasts.
    #_private
    (§ constructor- #_"TransactionBroadcast" [#_"Transaction" __tx]
        (§ assoc this :peer-group nil)
        (§ assoc this :tx __tx)
        this
