    #_testing
    #_public
    #_static
    (§ defn #_"TransactionBroadcast" TransactionBroadcast/createMockBroadcast [#_"Transaction" __tx, #_"SettableFuture<Transaction>" __future]
        (§ return (TransactionBroadcast. __tx)
        (§ anon
            #_override
            #_public
