                        (§ assoc this :height (UTXO/read4x8le __is))

                        (let [#_"byte[]" __coinbaseByte (byte-array 1)]
                            (.. __is (read __coinbaseByte))
                            (§ assoc this :coinbase (== (aget __coinbaseByte 0) 1))
                            nil
                        )
                    )
                )
            )
