        (.. __stream (write (int (& 0xff __val))))
        (.. __stream (write (int (& 0xff (>> __val 8)))))
        (.. __stream (write (int (& 0xff (>> __val 16)))))
        (.. __stream (write (int (& 0xff (>> __val 24)))))
