    ;;;
     ; <p>Given a textual message, returns a byte buffer formatted as follows:</p>
     ;
     ; <p><tt>[24] "Bitcoin Signed Message:\n" [message.length as a varint] message</tt>.</p>
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" Utils/formatMessageForSigning [#_"String" __message]
        (try
            (let [#_"ByteArrayOutputStream" __bos (ByteArrayOutputStream.)]
                (.. __bos (write (.. Utils/BITCOIN_SIGNED_MESSAGE_HEADER_BYTES (alength))))
                (.. __bos (write Utils/BITCOIN_SIGNED_MESSAGE_HEADER_BYTES))
                (let [#_"byte[]" __messageBytes (.. __message (getBytes Charsets/UTF_8))
                      #_"VarInt" __size (VarInt. (.. __messageBytes (alength)))]
                    (.. __bos (write (.. __size (encode))))
                    (.. __bos (write __messageBytes))
                    (§ return (.. __bos (toByteArray)))
                )
            )
            (catch IOException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
        )
    )

    ;; 00000001, 00000010, 00000100, 00001000, ...
    #_private
    #_static
    (§ def- #_"int[]" Utils/BIT_MASK (int-array [ 0x01, 0x02, 0x04, 0x08, 0x10, 0x20, 0x40, 0x80 ]))

    ;;; Checks if the given bit is set in data, using little endian (not the same as Java native big endian). ;;
    #_public
    #_static
    (§ defn #_"boolean" Utils/checkBitLE [#_"byte[]" __data, #_"int" __index]
        (!= (& (aget __data (>>> __index 3)) (aget Utils/BIT_MASK (& 7 __index))) 0)
    )

    ;;; Sets the given bit in data to one, using little endian (not the same as Java native big endian). ;;
    #_public
    #_static
    (§ defn #_"void" Utils/setBitLE [#_"byte[]" __data, #_"int" __index]
        (aset __data (>>> __index 3) (| (aget __data (>>> __index 3)) (aget Utils/BIT_MASK (& 7 __index))))
        nil
    )

    ;;; Sleep for a span of time, or mock sleep if enabled. ;;
    #_public
    #_static
    (§ defn #_"void" Utils/sleep [#_"long" __millis]
        (cond (nil? Utils/MOCK_SLEEP_QUEUE)
            (do
                (Uninterruptibles/sleepUninterruptibly __millis, TimeUnit/MILLISECONDS)
            )
            :else
            (do
                (try
                    (let [#_"boolean" __isMultiPass (.. Utils/MOCK_SLEEP_QUEUE (take))]
                        (Utils/rollMockClockMillis __millis)
                        (when __isMultiPass
                            (.. Utils/MOCK_SLEEP_QUEUE (offer true))
                        )
                    )
                    (catch InterruptedException _
                        ;; Ignored.
                    )
                )
            )
        )
        nil
    )

    ;;; Enable or disable mock sleep.  If enabled, set mock time to current time. ;;
    #_public
    #_static
    (§ defn #_"void" Utils/setMockSleep [#_"boolean" __isEnable]
        (cond __isEnable
            (do
                (§ ass Utils/MOCK_SLEEP_QUEUE (ArrayBlockingQueue. #_"<>" 1))
                (§ ass Utils/MOCK_TIME (Date. (System/currentTimeMillis)))
            )
            :else
            (do
                (§ ass Utils/MOCK_SLEEP_QUEUE nil)
            )
        )
        nil
    )

    ;;; Let sleeping thread pass the synchronization point. ;;
    #_public
    #_static
    (§ defn #_"void" Utils/passMockSleep []
        (.. Utils/MOCK_SLEEP_QUEUE (offer false))
        nil
    )

    ;;; Let the sleeping thread pass the synchronization point any number of times. ;;
    #_public
    #_static
    (§ defn #_"void" Utils/finishMockSleep []
        (when (some? Utils/MOCK_SLEEP_QUEUE)
            (.. Utils/MOCK_SLEEP_QUEUE (offer true))
        )
        nil
    )

    #_private
    #_static
    (§ def- #_"int" Utils/IS_ANDROID -1)
    #_public
    #_static
    (§ defn #_"boolean" Utils/isAndroidRuntime []
        (when (== Utils/IS_ANDROID -1)
            (let [#_"String" __runtime (System/getProperty "java.runtime.name")]
                (§ ass Utils/IS_ANDROID (if (and (some? __runtime) (.. __runtime (equals "Android Runtime"))) 1 0))
            )
        )
        (== Utils/IS_ANDROID 1)
    )

    #_private
    #_static
    (§ class- Utils.Pair (§ implements Comparable #_"<Utils.Pair>")
        (§ field- #_"int" :item)
        (§ field- #_"int" :count)

        #_public
        (§ constructor Utils.Pair [#_"int" __item, #_"int" __count]
            (§ assoc this :count __count)
            (§ assoc this :item __item)
            this
        )

        ;; Note that in this implementation compareTo() is not consistent with equals().
        #_override
        #_public
        (§ method #_"int" compareTo [#_"Utils.Pair" __o]
            (- (Ints/compare (:count this), (:count __o)))
        )
    )

    #_public
    #_static
    (§ defn #_"int" Utils/maxOfMostFreq [#_"int..." __items]
        ;; Java 6 sucks.
        (let [#_"ArrayList<Integer>" __list (ArrayList. #_"<>" (.. __items (alength)))]
            (doseq [#_"int" __item __items]
                (.. __list (add __item))
            )
            (Utils/maxOfMostFreq __list)
        )
    )

    #_public
    #_static
    (§ defn #_"int" Utils/maxOfMostFreq [#_"List<Integer>" __items]
        (when (.. __items (isEmpty))
            (§ return 0)
        )

        ;; This would be much easier in a functional language (or in Java 8).
        (§ ass __items (.. (Ordering/natural) (reverse) (sortedCopy __items)))
        (let [#_"LinkedList<Utils.Pair>" __pairs (Lists/newLinkedList)]
            (.. __pairs (add (Utils.Pair. (.. __items (get 0)), 0)))
            (doseq [#_"int" __item __items]
                (let [#_"Utils.Pair" __pair (.. __pairs (getLast))]
                    (when (!= (:item __pair) __item)
                        (.. __pairs (add (§ ass __pair (Utils.Pair. __item, 0))))
                    )
                    (§ assoc __pair :count (inc (:count __pair)))
                )
            )
            ;; pairs now contains a uniqified list of the sorted inputs, with counts for how often that item appeared.
            ;; Now sort by how frequently they occur, and pick the max of the most frequent.
            (Collections/sort __pairs)
            (let [#_"int" __maxCount (:count (.. __pairs (getFirst)))
                  #_"int" __maxItem (:item (.. __pairs (getFirst)))]
                (doseq [#_"Utils.Pair" __pair __pairs]
                    (when (!= (:count __pair) __maxCount)
                        (§ break )
                    )
                    (§ ass __maxItem (Math/max __maxItem, (:item __pair)))
                )
                __maxItem
            )
        )
    )

    ;;;
     ; Reads and joins together with LF char (\n) all the lines from given file.
     ; It's assumed that file is in UTF-8.
     ;;
    #_public
    #_static
    #_throws #_[ "IOException" ]
    (§ defn #_"String" Utils/getResourceAsString [#_"URL" __url]
        (let [#_"List<String>" __lines (Resources/readLines __url, Charsets/UTF_8)]
            (.. (Joiner/on "\n") (join __lines))
        )
    )

    ;; Can't use Closeable here because it's Java 7 only and Android devices only got that with KitKat.
    #_public
    #_static
    (§ defn #_"InputStream" Utils/closeUnchecked [#_"InputStream" __stream]
        (try
            (.. __stream (close))
            (§ return __stream)
            (catch IOException __e
                (throw (RuntimeException. __e))
            )
        )
    )

    #_public
    #_static
    (§ defn #_"OutputStream" Utils/closeUnchecked [#_"OutputStream" __stream]
        (try
            (.. __stream (close))
            (§ return __stream)
            (catch IOException __e
                (throw (RuntimeException. __e))
            )
        )
    )
)

;;;
 ; A variable-length encoded unsigned integer using Satoshi's encoding (a.k.a. "CompactSize").
 ;;
#_public
(§ class VarInt
    #_public
    (§ field #_"long" :value)
    #_private
    (§ field- #_"int" :originally-encoded-size)

    ;;;
     ; Constructs a new VarInt with the given unsigned long value.
     ;
     ; @param value the unsigned long value (beware widening conversion of negatives!)
     ;;
    #_public
    (§ constructor VarInt [#_"long" __value]
        (§ assoc this :value __value)
        (§ assoc this :originally-encoded-size (.. this (getSizeInBytes)))
        this
    )

    ;;;
     ; Constructs a new VarInt with the value parsed from the specified offset of the given buffer.
     ;
     ; @param buf The buffer containing the value.
     ; @param offset The offset of the value.
     ;;
    #_public
    (§ constructor VarInt [#_"byte[]" __buf, #_"int" __offset]
        (let [#_"int" __first (& 0xff (aget __buf __offset))]
            (cond (< __first 253)
                (do
                    (§ assoc this :value __first)
                    (§ assoc this :originally-encoded-size 1) ;; 1 data byte (8 bits)
                )
                (== __first 253)
                (do
                    (§ assoc this :value (| (& 0xff (aget __buf (inc __offset))) (<< (& 0xff (aget __buf (+ __offset 2))) 8)))
                    (§ assoc this :originally-encoded-size 3) ;; 1 marker + 2 data bytes (16 bits)
                )
                (== __first 254)
                (do
                    (§ assoc this :value (Utils/readUint32 __buf, (inc __offset)))
                    (§ assoc this :originally-encoded-size 5) ;; 1 marker + 4 data bytes (32 bits)
                )
                :else
                (do
                    (§ assoc this :value (Utils/readInt64 __buf, (inc __offset)))
                    (§ assoc this :originally-encoded-size 9) ;; 1 marker + 8 data bytes (64 bits)
                )
            )
            this
        )
    )

    ;;;
     ; Returns the original number of bytes used to encode the value if it was
     ; deserialized from a byte array, or the minimum encoded size if it was not.
     ;;
    #_public
    (§ method #_"int" getOriginalSizeInBytes []
        (:originally-encoded-size this)
    )

    ;;;
     ; Returns the minimum encoded size of the value.
     ;;
    #_public
    (§ method #_"int" getSizeInBytes []
        (VarInt/sizeOf (:value this))
    )

    ;;;
     ; Returns the minimum encoded size of the given unsigned long value.
     ;
     ; @param value the unsigned long value (beware widening conversion of negatives!)
     ;;
    #_public
    #_static
    (§ defn #_"int" VarInt/sizeOf [#_"long" __value]
        ;; if negative, it's actually a very large unsigned long value
        (when (< __value 0)
            (§ return 9) ;; 1 marker + 8 data bytes
        )
        (when (< __value 253)
            (§ return 1) ;; 1 data byte
        )
        (when (<= __value 0xffff)
            (§ return 3) ;; 1 marker + 2 data bytes
        )
        (when (<= __value 0xffffffff)
            (§ return 5) ;; 1 marker + 4 data bytes
        )
        9 ;; 1 marker + 8 data bytes
    )

    ;;;
     ; Encodes the value into its minimal representation.
     ;
     ; @return the minimal encoded bytes of the value.
     ;;
    #_public
    (§ method #_"byte[]" encode []
        (case (VarInt/sizeOf (:value this))
            1 (byte-array [ (byte (:value this)) ])
            3 (byte-array [ (byte 253), (byte (:value this)), (byte (>> (:value this) 8)) ])
            5 (let [#_"byte[]" __bytes (byte-array 5)]
                    (aset __bytes 0 (byte 254))
                    (Utils/uint32ToByteArrayLE (:value this), __bytes, 1)
                    __bytes)
              (let [#_"byte[]" __bytes (byte-array 9)]
                    (aset __bytes 0 (byte 255))
                    (Utils/uint64ToByteArrayLE (:value this), __bytes, 1)
                    __bytes)
        )
    )
)

