;;;
 ; A HashMap<KeyType, ValueType> that is DB transaction-aware.
 ; This class is not thread-safe.
 ;;
(§ class TransactionalHashMap #_"<KeyType, ValueType>"
    (§ field #_"ThreadLocal<HashMap<KeyType, ValueType>>" :temp-map)
    (§ field #_"ThreadLocal<HashSet<KeyType>>" :temp-set-removed)
    #_private
    (§ field- #_"ThreadLocal<Boolean>" :in-transaction)

    (§ field #_"HashMap<KeyType, ValueType>" :map)

    #_public
    (§ constructor TransactionalHashMap []
        (§ assoc this :temp-map (ThreadLocal. #_"<>"))
        (§ assoc this :temp-set-removed (ThreadLocal. #_"<>"))
        (§ assoc this :in-transaction (ThreadLocal. #_"<>"))
        (§ assoc this :map (HashMap. #_"<>"))
        this
    )

    #_public
    (§ method #_"void" beginDatabaseBatchWrite []
        (.. (:in-transaction this) (set true))
        nil
    )

    #_public
    (§ method #_"void" commitDatabaseBatchWrite []
        (when (some? (.. (:temp-set-removed this) (get)))
            (doseq [#_"KeyType" __key (.. (:temp-set-removed this) (get))]
                (.. (:map this) (remove __key))
            )
        )
        (when (some? (.. (:temp-map this) (get)))
            (doseq [#_"Map.Entry<KeyType, ValueType>" __entry (.. (:temp-map this) (get) (entrySet))]
                (.. (:map this) (put (.. __entry (getKey)), (.. __entry (getValue))))
            )
        )
        (.. this (abortDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" abortDatabaseBatchWrite []
        (.. (:in-transaction this) (set false))
        (.. (:temp-set-removed this) (remove))
        (.. (:temp-map this) (remove))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" get [#_"KeyType" __key]
        (when (.. Boolean/TRUE (equals (.. (:in-transaction this) (get))))
            (when (some? (.. (:temp-map this) (get)))
                (let [#_"ValueType" __value (.. (:temp-map this) (get) (get __key))]
                    (when (some? __value)
                        (§ return __value)
                    )
                )
            )
            (when (and (some? (.. (:temp-set-removed this) (get))) (.. (:temp-set-removed this) (get) (contains __key)))
                (§ return nil)
            )
        )
        (.. (:map this) (get __key))
    )

    #_public
    (§ method #_"List<ValueType>" values []
        (let [#_"List<ValueType>" __valueTypes (ArrayList. #_"<>")]
            (doseq [#_"KeyType" __keyType (.. (:map this) (keySet))]
                (.. __valueTypes (add (.. this (get __keyType))))
            )
            __valueTypes
        )
    )

    #_public
    (§ method #_"void" put [#_"KeyType" __key, #_"ValueType" __value]
        (cond (.. Boolean/TRUE (equals (.. (:in-transaction this) (get))))
            (do
                (when (some? (.. (:temp-set-removed this) (get)))
                    (.. (:temp-set-removed this) (get) (remove __key))
                )
                (when (nil? (.. (:temp-map this) (get)))
                    (.. (:temp-map this) (set (HashMap. #_"<KeyType, ValueType>")))
                )
                (.. (:temp-map this) (get) (put __key, __value))
            )
            :else
            (do
                (.. (:map this) (put __key, __value))
            )
        )
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" remove [#_"KeyType" __key]
        (cond (.. Boolean/TRUE (equals (.. (:in-transaction this) (get))))
            (do
                (let [#_"ValueType" __retVal (.. (:map this) (get __key))]
                    (when (some? __retVal)
                        (when (nil? (.. (:temp-set-removed this) (get)))
                            (.. (:temp-set-removed this) (set (HashSet. #_"<KeyType>")))
                        )
                        (.. (:temp-set-removed this) (get) (add __key))
                    )
                    (when (some? (.. (:temp-map this) (get)))
                        (let [#_"ValueType" __tempVal (.. (:temp-map this) (get) (remove __key))]
                            (when (some? __tempVal)
                                (§ return __tempVal)
                            )
                        )
                    )
                    (§ return __retVal)
                )
            )
            :else
            (do
                (§ return (.. (:map this) (remove __key)))
            )
        )
    )
)

;;;
 ; A Map with multiple key types that is DB per-thread-transaction-aware.
 ; However, this class is not thread-safe.
 ; @param <UniqueKeyType> Is a key that must be unique per object.
 ; @param <MultiKeyType> Is a key that can have multiple values.
 ;;
(§ class TransactionalMultiKeyHashMap #_"<UniqueKeyType, MultiKeyType, ValueType>"
    (§ field #_"TransactionalHashMap<UniqueKeyType, ValueType>" :map-values)
    (§ field #_"HashMap<MultiKeyType, Set<UniqueKeyType>>" :map-keys)

    #_public
    (§ constructor TransactionalMultiKeyHashMap []
        (§ assoc this :map-values (TransactionalHashMap. #_"<>"))
        (§ assoc this :map-keys (HashMap. #_"<>"))
        this
    )

    #_public
    (§ method #_"void" beginTransaction []
        (.. (:map-values this) (beginDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" commitTransaction []
        (.. (:map-values this) (commitDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" abortTransaction []
        (.. (:map-values this) (abortDatabaseBatchWrite))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" get [#_"UniqueKeyType" __key]
        (.. (:map-values this) (get __key))
    )

    #_public
    (§ method #_"void" put [#_"UniqueKeyType" __uniqueKey, #_"MultiKeyType" __multiKey, #_"ValueType" __value]
        (.. (:map-values this) (put __uniqueKey, __value))
        (let [#_"Set<UniqueKeyType>" __set (.. (:map-keys this) (get __multiKey))]
            (cond (nil? __set)
                (do
                    (§ ass __set (HashSet. #_"<>"))
                    (.. __set (add __uniqueKey))
                    (.. (:map-keys this) (put __multiKey, __set))
                )
                :else
                (do
                    (.. __set (add __uniqueKey))
                )
            )
            nil
        )
    )

    #_nilable
    #_public
    (§ method #_"ValueType" removeByUniqueKey [#_"UniqueKeyType" __key]
        (.. (:map-values this) (remove __key))
    )

    #_public
    (§ method #_"void" removeByMultiKey [#_"MultiKeyType" __key]
        (let [#_"Set<UniqueKeyType>" __set (.. (:map-keys this) (remove __key))]
            (when (some? __set)
                (doseq [#_"UniqueKeyType" __uniqueKey __set]
                    (.. this (removeByUniqueKey __uniqueKey))
                )
            )
            nil
        )
    )
)

;;;
 ; Keeps {@link StoredBlock}s, {@link StoredUndoableBlock}s and {@link org.bitcoinj.core.UTXO}s in memory.
 ; Used primarily for unit testing.
 ;;
#_public
(§ class MemoryFullPrunedBlockStore (§ implements FullPrunedBlockStore)
    #_protected
    #_static
    (§ class MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag
        #_public
        (§ field #_"StoredBlock" :block)
        #_public
        (§ field #_"boolean" :was-undoable)
        #_public
        (§ constructor MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag [#_"StoredBlock" __block, #_"boolean" __wasUndoable]
            (§ assoc this :block __block)
            (§ assoc this :was-undoable __wasUndoable)
            this
        )
    )
    #_private
    (§ field- #_"TransactionalHashMap<Sha256Hash, MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag>" :block-map)
    #_private
    (§ field- #_"TransactionalMultiKeyHashMap<Sha256Hash, Integer, StoredUndoableBlock>" :full-block-map)
    ;; TODO: Use something more suited to remove-heavy use?
    #_private
    (§ field- #_"TransactionalHashMap<StoredTransactionOutPoint, UTXO>" :transaction-output-map)
    #_private
    (§ field- #_"StoredBlock" :chain-head)
    #_private
    (§ field- #_"StoredBlock" :verified-chain-head)
    #_private
    (§ field- #_"int" :full-store-depth)
    #_private
    (§ field- #_"NetworkParameters" :params)

    ;;;
     ; Set up the MemoryFullPrunedBlockStore.
     ; @param params The network parameters of this block store - used to get genesis block.
     ; @param fullStoreDepth The depth of blocks to keep FullStoredBlocks instead of StoredBlocks.
     ;;
    #_public
    (§ constructor MemoryFullPrunedBlockStore [#_"NetworkParameters" __params, #_"int" __fullStoreDepth]
        (§ assoc this :block-map (TransactionalHashMap. #_"<>"))
        (§ assoc this :full-block-map (TransactionalMultiKeyHashMap. #_"<>"))
        (§ assoc this :transaction-output-map (TransactionalHashMap. #_"<>"))
        (§ assoc this :full-store-depth (if (< 0 __fullStoreDepth) __fullStoreDepth 1))
        ;; Insert the genesis block.
        (try
            (let [#_"StoredBlock" __storedGenesisHeader (StoredBlock. (.. __params (getGenesisBlock) (cloneAsHeader)), (.. __params (getGenesisBlock) (getWork)), 0)]
                ;; The coinbase in the genesis block is not spendable.
                (let [#_"List<Transaction>" __genesisTransactions (Lists/newLinkedList)
                      #_"StoredUndoableBlock" __storedGenesis (StoredUndoableBlock. (.. __params (getGenesisBlock) (getHash)), __genesisTransactions)]
                    (.. this (put __storedGenesisHeader, __storedGenesis))
                    (.. this (setChainHead __storedGenesisHeader))
                    (.. this (setVerifiedChainHead __storedGenesisHeader))
                    (§ assoc this :params __params)
                )
            )
            (catch BlockStoreException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
            (catch VerificationException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
        )
        this
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" put [#_"StoredBlock" __block]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (let [#_"Sha256Hash" __hash (.. __block (getHeader) (getHash))]
            (.. (:block-map this) (put __hash, (MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag. __block, false)))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" put [#_"StoredBlock" __storedBlock, #_"StoredUndoableBlock" __undoableBlock]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (let [#_"Sha256Hash" __hash (.. __storedBlock (getHeader) (getHash))]
            (.. (:full-block-map this) (put __hash, (.. __storedBlock (getHeight)), __undoableBlock))
            (.. (:block-map this) (put __hash, (MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag. __storedBlock, true)))
            nil
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" get [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (let [#_"MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag" __storedBlock (.. (:block-map this) (get __hash))]
            (when (some? __storedBlock) (:block __storedBlock))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getOnceUndoableStoredBlock [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (let [#_"MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag" __storedBlock (.. (:block-map this) (get __hash))]
            (when (and (some? __storedBlock) (:was-undoable __storedBlock)) (:block __storedBlock))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredUndoableBlock" getUndoBlock [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (:full-block-map this), "MemoryFullPrunedBlockStore is closed")
        (.. (:full-block-map this) (get __hash))
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getChainHead []
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (:chain-head this)
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" setChainHead [#_"StoredBlock" __chainHead]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (§ assoc this :chain-head __chainHead)
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getVerifiedChainHead []
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (:verified-chain-head this)
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" setVerifiedChainHead [#_"StoredBlock" __chainHead]
        (Preconditions/checkNotNull (:block-map this), "MemoryFullPrunedBlockStore is closed")
        (§ assoc this :verified-chain-head __chainHead)
        (when (< (.. (:chain-head this) (getHeight)) (.. __chainHead (getHeight)))
            (.. this (setChainHead __chainHead))
        )
        ;; Potential leak here if not all blocks get setChainHead'd.
        ;; Though the FullPrunedBlockStore allows for this, the current AbstractBlockChain will not do it.
        (.. (:full-block-map this) (removeByMultiKey (- (.. __chainHead (getHeight)) (:full-store-depth this))))
        nil
    )

    #_override
    #_public
    (§ method #_"void" close []
        (§ assoc this :block-map nil)
        (§ assoc this :full-block-map nil)
        (§ assoc this :transaction-output-map nil)
        nil
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"UTXO" getTransactionOutput [#_"Sha256Hash" __hash, #_"long" __index]
