                (doseq [#_"ECKey" __key (.. (:hash-to-keys this) (values))]
                    (.. __decrypted (importKeyLocked (.. __key (decrypt __aesKey))))
                )
                (§ return __decrypted)
            )
            (finally
                (.. (:lock this) (unlock))
            )
        )
    )

    ;;;
