    #_private
    (§ constructor- #_"DefaultRiskAnalysis" [#_"Wallet" __wallet, #_"Transaction" __tx, #_"List<Transaction>" __dependencies]
        (§ assoc this :tx __tx)
        (§ assoc this :dependencies __dependencies)
        (§ assoc this :wallet __wallet)
