    #_throws #_[ "UnreadableWalletException" ]
    (§ defn #_"KeyChainGroup" KeyChainGroup/fromProtobufEncrypted [#_"NetworkParameters" __params, #_"List<Protos.Key>" __keys, #_"KeyCrypter" __crypter]
