                            (when (.. __input (getOutpoint) (getHash) (equals (.. __tx (getHash))))
                                (when (nil? (.. __txQueue (get (.. __anotherTx (getHash)))))
                                    (.. __txQueue (put (.. __anotherTx (getHash)), __anotherTx))
