    #_private
    #_static
    (§ class- Wallet.TxOffsetPair (§ implements Comparable #_"<Wallet.TxOffsetPair>")
        #_public
        (§ field #_"Transaction" :tx)
        #_public
        (§ field #_"int" :offset)

        #_public
        (§ constructor Wallet.TxOffsetPair [#_"Transaction" __tx, #_"int" __offset]
