(letfn [(e' [v] (or (empty v) (vector)))]
    (defn append* [m k & a]                            (update m k #(apply conj (or % (vector)) a)))
    (defn update* [m k f & a] (let [f' #(apply f % a)] (update m k #(into (e' %) (map f' %)))))
    (defn remove* [m k f & a] (let [f' #(apply f % a)] (update m k #(into (e' %) (remove f' %)))))

