        (let [#_"int" hash (bit-xor (int (:version this)) (.hashCode "getheaders") (.hashCode (:stop-hash this)))]
            (reduce bit-xor hash (map #(.hashCode %) (:locator this))) ;; ignores locator ordering
        )
