        (.. this (createNextBlock to, Block'BLOCK_VERSION_GENESIS, __prevOut, (+ (.. this (getTimeSeconds)) 5), Block'PUBKEY_FOR_TESTING, Coin'FIFTY_COINS, Block'BLOCK_HEIGHT_UNKNOWN))
