 ; any Bloom filter set on the connection.  The list of transaction hashes comes back in an inv message.  Note that
 ; this is different to the {@link TxConfidenceTable} object which doesn't try to keep track of all pending transactions,
 ; it's just a holding area for transactions that a part of the app may find interesting.  The mempool message has
 ; no fields.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class MemoryPoolMessage (§ extends Message)
    #_override
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parse []
        nil
    )

    #_override
    #_protected
    #_throws #_[ "IOException" ]
    (§ method #_"void" bitcoinSerializeToStream [#_"OutputStream" stream]
        nil
    )
)

;;;
 ; <p>A Message is a data structure that can be serialized/deserialized using the Bitcoin serialization format.
 ; Specific types of messages that are used both in the block chain, and on the wire, are derived from this class.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class Message
    #_private
    #_static
    (def- #_"Logger" Message'log (LoggerFactory/getLogger Message))

    #_public
    #_static
    (def #_"int" Message'MAX_SIZE 0x02000000) ;; 32MB

    #_public
    #_static
    (def #_"int" Message'UNKNOWN_LENGTH Integer/MIN_VALUE)

    ;; Useful to ensure serialize/deserialize are consistent with each other.
    #_private
    #_static
    (def- #_"boolean" Message'SELF_CHECK false)

    ;; The offset is how many bytes into the provided byte array this message payload starts at.
    #_protected
    (§ field #_"int" :offset)
    ;; The cursor keeps track of where we are in the byte array as we parse it.
    ;; Note that it's relative to the start of the array NOT the start of the message payload.
    #_protected
    (§ field #_"int" :cursor)

    #_protected
    (§ field #_"int" :length Message'UNKNOWN_LENGTH)

    ;; The raw message payload bytes themselves.
    #_protected
    (§ field #_"byte[]" :payload)

    #_protected
    (§ field #_"boolean" :recached false)
    #_protected
    (§ field #_"MessageSerializer" :serializer)

    #_protected
    (§ field #_"int" :protocol-version)

    #_protected
    (§ field #_"NetworkParameters" :params)

    #_protected
    (§ constructor Message [#_"NetworkParameters" params]
        (§ assoc this :params params)
        (§ assoc this :serializer (.. params (getDefaultSerializer)))
        this
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ constructor Message [#_"NetworkParameters" params, #_"byte[]" payload, #_"int" offset, #_"int" __protocolVersion]
        (§ this params, payload, offset, __protocolVersion, (.. params (getDefaultSerializer)), Message'UNKNOWN_LENGTH)
        this
    )

    ;;;
     ; @param params NetworkParameters object.
     ; @param payload Bitcoin protocol formatted byte array containing message content.
     ; @param offset The location of the first payload byte within the array.
     ; @param protocolVersion Bitcoin protocol version.
     ; @param serializer The serializer to use for this message.
     ; @param length The length of message payload if known.  Usually this is provided when deserializing of the wire
     ; as the length will be provided as part of the header.  Set to Message.UNKNOWN_LENGTH, if not known.
     ; @throws ProtocolException
     ;;
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ constructor Message [#_"NetworkParameters" params, #_"byte[]" payload, #_"int" offset, #_"int" __protocolVersion, #_"MessageSerializer" serializer, #_"int" length]
        (§ assoc this :serializer serializer)
        (§ assoc this :protocol-version __protocolVersion)
        (§ assoc this :params params)
        (§ assoc this :payload payload)
        (§ assoc this :cursor (§ assoc this :offset offset))
        (§ assoc this :length length)

        (.. this (parse))

        (when (= (:length this) Message'UNKNOWN_LENGTH)
            (Preconditions/checkState false, "Length field has not been set in constructor for %s after parse.", (.. (getClass) (getSimpleName)))
        )

        (when Message'SELF_CHECK
            (.. this (selfCheck payload, offset))
        )

        (when (not (.. serializer (isParseRetainMode)))
            (§ assoc this :payload nil)
        )
        this
    )

    #_private
    (§ method- #_"void" selfCheck [#_"byte[]" payload, #_"int" offset]
        (when (not (instance? VersionMessage this))
            (let [#_"byte[]" __payloadBytes (byte-array (- (:cursor this) offset))]
                (System/arraycopy payload, offset, __payloadBytes, 0, (- (:cursor this) offset))
                (let [#_"byte[]" reserialized (.. this (bitcoinSerialize))]
                    (when (not (Arrays/equals reserialized, __payloadBytes))
                        (throw (RuntimeException. (str "Serialization is wrong: \n" (.. Utils'HEX (encode reserialized)) " vs \n" (.. Utils'HEX (encode __payloadBytes)))))
                    )
                )
            )
        )
        nil
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ constructor Message [#_"NetworkParameters" params, #_"byte[]" payload, #_"int" offset]
        (§ this params, payload, offset, ProtocolVersion'CURRENT, (.. params (getDefaultSerializer)), Message'UNKNOWN_LENGTH)
        this
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ constructor Message [#_"NetworkParameters" params, #_"byte[]" payload, #_"int" offset, #_"MessageSerializer" serializer, #_"int" length]
        (§ this params, payload, offset, ProtocolVersion'CURRENT, serializer, length)
        this
    )

    ;; These methods handle the serialization/deserialization using the custom Bitcoin protocol.

    #_protected
    #_abstract
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parse [])

    ;;;
     ; <p>To be called before any change of internal values including any setters.  This ensures any cached byte array is
     ; removed.<p/>
     ; <p>Child messages of this object (e.g. Transactions belonging to a Block) will not have their internal byte caches
     ; invalidated unless they are also modified internally.</p>
     ;;
    #_protected
    (§ method #_"void" unCache []
        (§ assoc this :payload nil)
        (§ assoc this :recached false)
        nil
    )

    #_protected
    (§ method #_"void" adjustLength [#_"int" __newArraySize, #_"int" adjustment]
        (when (= (:length this) Message'UNKNOWN_LENGTH)
            (§ return nil)
        )
        ;; Our own length is now unknown if we have an unknown length adjustment.
        (when (= adjustment Message'UNKNOWN_LENGTH)
            (§ assoc this :length Message'UNKNOWN_LENGTH)
            (§ return nil)
        )
        (§ assoc this :length (+ (:length this) adjustment))
        ;; Check if we will need more bytes to encode the length prefix.
        (cond (= __newArraySize 1)
            (do
                (§ assoc this :length (inc (:length this))) ;; The assumption here is we never call adjustLength with the same arraySize as before.
            )
            (not= __newArraySize 0)
            (do
                (§ assoc this :length (+ (:length this) (- (VarInt'sizeOf __newArraySize) (VarInt'sizeOf (dec __newArraySize)))))
            )
        )
        nil
    )

    ;;;
     ; used for unit testing
     ;;
    #_public
    (§ method #_"boolean" isCached []
        (some? (:payload this))
    )

    #_public
    (§ method #_"boolean" isRecached []
        (:recached this)
    )

    ;;;
     ; Returns a copy of the array returned by {@link Message#unsafeBitcoinSerialize()}, which is safe to mutate.
     ; If you need extra performance and can guarantee you won't write to the array, you can use the unsafe version.
     ;
     ; @return a freshly allocated serialized byte array.
     ;;
    #_public
    (§ method #_"byte[]" bitcoinSerialize []
        (let [#_"byte[]" bytes (.. this (unsafeBitcoinSerialize))
              #_"byte[]" copy (byte-array (alength bytes))]
            (System/arraycopy bytes, 0, copy, 0, (alength bytes))
            copy
        )
    )

    ;;;
     ; Serialize this message to a byte array that conforms to the bitcoin wire protocol.
     ;
     ; This method may return the original byte array used to construct this message if the
     ; following conditions are met:
     ; <ol>
     ; <li>1. The message was parsed from a byte array with parseRetain = true.</li>
     ; <li>2. The message has not been modified.</li>
     ; <li>3. The array had an offset of 0 and no surplus bytes.</li>
     ; </ol>
     ;
     ; If condition 3 is not met then an copy of the relevant portion of the array will be returned.
     ; Otherwise a full serialize will occur.  For this reason you should only use this API
     ; if you can guarantee you will treat the resulting array as read only.
     ;
     ; @return a byte array owned by this object, do NOT mutate it.
     ;;
    #_public
    (§ method #_"byte[]" unsafeBitcoinSerialize []
        ;; 1st attempt to use a cached array.
        (when (some? (:payload this))
            ;; Cached byte array is the entire message with no extras so we can return as is and avoid an array copy.
            (when (and (= (:offset this) 0) (= (:length this) (alength (:payload this))))
                (§ return (:payload this))
            )

            (let [#_"byte[]" buf (byte-array (:length this))]
                (System/arraycopy (:payload this), (:offset this), buf, 0, (:length this))
                (§ return buf)
            )
        )

        ;; No cached array available so serialize parts by stream.
        (let [#_"ByteArrayOutputStream" stream (ByteArrayOutputStream. (if (< (:length this) 32) 32 (+ (:length this) 32)))]
            (try
                (.. this (bitcoinSerializeToStream stream))
                (catch IOException _
                    ;; Cannot happen, we are serializing to a memory stream.
                )
            )

            (when (.. (:serializer this) (isParseRetainMode))
                ;; A free set of steak knives!
                ;; If there happens to be a call to this method we gain an opportunity to recache
                ;; the byte array and in this case it contains no bytes from parent messages.
                ;; This give a dual benefit.  Releasing references to the larger byte array so that it
                ;; it is more likely to be GC'd.  And preventing double serializations.  E.g. calculating
                ;; merkle root calls this method.  It is will frequently happen prior to serializing the block
                ;; which means another call to bitcoinSerialize is coming.  If we didn't recache then internal
                ;; serialization would occur a 2nd time and every subsequent time the message is serialized.
                (§ assoc this :payload (.. stream (toByteArray)))
                (§ assoc this :cursor (- (:cursor this) (:offset this)))
                (§ assoc this :offset 0)
                (§ assoc this :recached true)
                (§ assoc this :length (alength (:payload this)))
                (§ return (:payload this))
            )
            ;; Record length.  If this Message wasn't parsed from a byte stream it won't have length field
            ;; set (except for static length message types).  Setting it makes future streaming more efficient
            ;; because we can preallocate the ByteArrayOutputStream buffer and avoid resizing.
            (let [#_"byte[]" buf (.. stream (toByteArray))]
                (§ assoc this :length (alength buf))
                buf
            )
        )
    )

    ;;;
     ; Serialize this message to the provided OutputStream using the bitcoin wire format.
     ;
     ; @param stream
     ; @throws IOException
     ;;
    #_public
    #_throws #_[ "IOException" ]
    (§ method #_"void" bitcoinSerialize [#_"OutputStream" stream]
        ;; 1st check for cached bytes.
        (when (and (some? (:payload this)) (not= (:length this) Message'UNKNOWN_LENGTH))
            (.. stream (write (:payload this), (:offset this), (:length this)))
            (§ return nil)
        )

        (.. this (bitcoinSerializeToStream stream))
        nil
    )

    ;;;
     ; Serializes this message to the provided stream.  If you just want the raw bytes use bitcoinSerialize().
     ;;
    #_protected
    #_throws #_[ "IOException" ]
    (§ method #_"void" bitcoinSerializeToStream [#_"OutputStream" stream]
        (.. Message'log (error "Error: {} class has not implemented bitcoinSerializeToStream method.  Generating message with no payload", (getClass)))
        nil
    )

    ;;;
     ; This method is a NOP for all classes except Block and Transaction.  It is only declared in Message
     ; so BitcoinSerializer can avoid 2 instanceof checks + a casting.
     ;;
    #_public
    (§ method #_"Sha256Hash" getHash []
        (throw (UnsupportedOperationException.))
    )

    ;;;
     ; This returns a correct value by parsing the message.
     ;;
    #_public
    (§ method #_"int" getMessageSize []
        (when (= (:length this) Message'UNKNOWN_LENGTH)
            (Preconditions/checkState false, "Length field has not been set in %s.", (.. (getClass) (getSimpleName)))
        )
        (:length this)
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"long" readUint32 []
        (try
            (let [#_"long" u (Utils'readUint32 (:payload this), (:cursor this))]
                (§ assoc this :cursor (+ (:cursor this) 4))
                (§ return u)
            )
            (catch ArrayIndexOutOfBoundsException e
                (throw (ProtocolException. e))
            )
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"long" readInt64 []
        (try
            (let [#_"long" u (Utils'readInt64 (:payload this), (:cursor this))]
                (§ assoc this :cursor (+ (:cursor this) 8))
                (§ return u)
            )
            (catch ArrayIndexOutOfBoundsException e
                (throw (ProtocolException. e))
            )
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"BigInteger" readUint64 []
        ;; Java does not have an unsigned 64 bit type. So scrape it off the wire then flip.
        (BigInteger. (Utils'reverseBytes (.. this (readBytes 8))))
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"long" readVarInt []
        (.. this (readVarInt 0))
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"long" readVarInt [#_"int" offset]
        (try
            (let [#_"VarInt" varint (VarInt. (:payload this), (+ (:cursor this) offset))]
                (§ assoc this :cursor (+ (:cursor this) offset (.. varint (getOriginalSizeInBytes))))
                (§ return (:value varint))
            )
            (catch ArrayIndexOutOfBoundsException e
                (throw (ProtocolException. e))
            )
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"byte[]" readBytes [#_"int" length]
        (when (< Message'MAX_SIZE length)
            (throw (ProtocolException. (str "Claimed value length too large: " length)))
        )

        (try
            (let [#_"byte[]" b (byte-array length)]
                (System/arraycopy (:payload this), (:cursor this), b, 0, length)
                (§ assoc this :cursor (+ (:cursor this) length))
                (§ return b)
            )
            (catch IndexOutOfBoundsException e
                (throw (ProtocolException. e))
            )
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"byte[]" readByteArray []
        (let [#_"long" len (.. this (readVarInt))]
            (.. this (readBytes (int len)))
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"String" readStr []
        (let [#_"long" length (.. this (readVarInt))]
            (if (= length 0) "" (Utils'toString (.. this (readBytes (int length))), "UTF-8")) ;; optimization for empty strings
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"Sha256Hash" readHash []
        ;; We have to flip it around, as it's been read off the wire in little endian.
        ;; Not the most efficient way to do this but the clearest.
        (Sha256Hash'wrapReversed (.. this (readBytes 32)))
    )

    #_protected
    (§ method #_"boolean" hasMoreBytes []
        (< (:cursor this) (alength (:payload this)))
    )

    ;;; Network parameters this message was created with. ;;
    #_public
    (§ method #_"NetworkParameters" getParams []
        (:params this)
    )
)

;;;
 ; Generic interface for classes which serialize/deserialize messages.
 ; Implementing classes should be immutable.
 ;;
#_public
#_abstract
(§ class MessageSerializer
    ;;;
     ; Reads a message from the given ByteBuffer and returns it.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "IOException", "UnsupportedOperationException" ]
    (§ method #_"Message" deserialize [#_"ByteBuffer" in])

    ;;;
     ; Deserializes only the header in case packet meta data is needed before decoding
     ; the payload.  This method assumes you have already called seekPastMagicBytes().
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "IOException", "UnsupportedOperationException" ]
    (§ method #_"BitcoinPacketHeader" deserializeHeader [#_"ByteBuffer" in])

    ;;;
     ; Deserialize payload only.  You must provide a header, typically obtained by calling
     ; {@link BitcoinSerializer#deserializeHeader}.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "BufferUnderflowException", "UnsupportedOperationException" ]
    (§ method #_"Message" deserializePayload [#_"BitcoinPacketHeader" header, #_"ByteBuffer" in])

    ;;;
     ; Whether the serializer will produce cached mode Messages.
     ;;
    #_public
    #_abstract
    (§ method #_"boolean" isParseRetainMode [])

    ;;;
     ; Make an address message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"AddressMessage" makeAddressMessage [#_"byte[]" __payloadBytes, #_"int" length])

    ;;;
     ; Make an alert message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"Message" makeAlertMessage [#_"byte[]" __payloadBytes])

    ;;;
     ; Make a block from the payload, using an offset of zero and the payload
     ; length as block length.
     ;;
    #_public
    #_throws #_[ "ProtocolException" ]
    (§ method #_"Block" makeBlock [#_"byte[]" __payloadBytes]
        (.. this (makeBlock __payloadBytes, 0, (alength __payloadBytes)))
    )

    ;;;
     ; Make a block from the payload, using an offset of zero and the provided
     ; length as block length.
     ;;
    #_public
    #_throws #_[ "ProtocolException" ]
    (§ method #_"Block" makeBlock [#_"byte[]" __payloadBytes, #_"int" length]
        (.. this (makeBlock __payloadBytes, 0, length))
    )

    ;;;
     ; Make a block from the payload, using an offset of zero and the provided
     ; length as block length.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"Block" makeBlock [#_"byte[]" __payloadBytes, #_"int" offset, #_"int" length])

    ;;;
     ; Make an filter message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"Message" makeBloomFilter [#_"byte[]" __payloadBytes])

    ;;;
     ; Make a filtered block from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"FilteredBlock" makeFilteredBlock [#_"byte[]" __payloadBytes])

    ;;;
     ; Make an inventory message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"InventoryMessage" makeInventoryMessage [#_"byte[]" __payloadBytes, #_"int" length])

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    #_abstract
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"Transaction" makeTransaction [#_"byte[]" __payloadBytes, #_"int" offset, #_"int" length, #_"byte[]" hash])

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    #_throws #_[ "ProtocolException", "UnsupportedOperationException" ]
    (§ method #_"Transaction" makeTransaction [#_"byte[]" __payloadBytes]
        (.. this (makeTransaction __payloadBytes, 0))
    )

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    #_throws #_[ "ProtocolException" ]
    (§ method #_"Transaction" makeTransaction [#_"byte[]" __payloadBytes, #_"int" offset]
        (.. this (makeTransaction __payloadBytes, offset, (alength __payloadBytes), nil))
    )

    #_public
    #_abstract
    #_throws #_[ "BufferUnderflowException" ]
    (§ method #_"void" seekPastMagicBytes [#_"ByteBuffer" in])

    ;;;
     ; Writes message to to the output stream.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support serialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support serializing the given message.
     ;;
    #_public
    #_abstract
    #_throws #_[ "IOException", "UnsupportedOperationException" ]
    (§ method #_"void" serialize [#_"String" name, #_"byte[]" message, #_"OutputStream" out])

    ;;;
     ; Writes message to to the output stream.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support serialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support serializing the given message.
     ;;
    #_public
    #_abstract
    #_throws #_[ "IOException", "UnsupportedOperationException" ]
    (§ method #_"void" serialize [#_"Message" message, #_"OutputStream" out])
)

