        (doseq [#_"ListenerRegistration<PeerDisconnectedEventListener>" registration (:disconnected-event-listeners this)]
            (.. (:executor registration) (execute (Runnable.)
            (§ anon
                #_override
                #_public
                (§ method #_"void" run []
                    (.. (:listener registration) (onPeerDisconnected (§ dhis Peer), 0))
                    nil
