                (doseq [#_"Peer" peer (:peers this)]
                    (when (= (& (:local-services (.. peer (getPeerVersionMessage))) mask) mask)
                        (.. results (add peer))
                    )
                )
                (§ return results)
            )
            (finally
                (.. (:lock this) (unlock))
            )
